INSERT INTO `tgp_cms` (`id`, `cat`, `ten`, `chu_thich`, `hinh`, `hinh_note`, `noi_dung`, `hien_thi`, `time`, `user`, `luot_xem`, `noi_bat`) VALUES
(11, 2, 'Ngã từ tầng 21, một công nhân lắp cửa kính tử vong', 'Vào hồi 8h15 phút sáng 14-7, tại công trình xây dựng tòa nhà 26 tầng địa chỉ tại 194 Trần Quang Khải, Q.Hoàn Kiếm, Hà Nội, đă xảy ra một vụ tai nạn lao động đặc biệt nghięm trọng làm một công nhân tử vong tại chỗ.', '11.jpg', '', '<div style="text-align: center;"><img src="/uploads/image/Tin-tuc/Roi_tu_tang_21_01.jpg" style="width: 340px; height: 225px;" alt="" /><br />\r\n<em>T&ograve;a nh&agrave; xảy ra vụ việc</em><br />\r\n<br />\r\n<img src="/uploads/image/Tin-tuc/Roi_tu_tang_21_02.jpg" style="width: 340px; height: 226px;" alt="" /></div>\r\n<div style="text-align: center;"><em>Khu vực người c&ocirc;ng nh&acirc;n rơi<br />\r\n<br />\r\n</em></div>\r\nNgười thiệt mạng l&agrave; anh Nguyễn Đức Quang (SN 1986, qu&ecirc; ở Trung H&ograve;a, Hiệp H&ograve;a, Bắc Giang). Anh Quang l&agrave; c&ocirc;ng nh&acirc;n lắp đặt cửa k&iacute;nh trong l&uacute;c thi c&ocirc;ng tr&ecirc;n tầng 21 của t&ograve;a nh&agrave; đ&atilde; bị ng&atilde; xuống đất, tử vong ngay tại chỗ. Nguy&ecirc;n nh&acirc;n ban đầu của vụ tai nạn thương t&acirc;m n&agrave;y được x&aacute;c định l&agrave; do anh Quang khi đứng ở trong nh&agrave; với tay ra ngo&agrave;i để lấy tấm k&iacute;nh v&agrave;o lắp đặt th&igrave; bất ngờ d&acirc;y c&aacute;p treo mang vật liệu x&acirc;y dựng bị đứt, khiến anh Quang bị mất đ&agrave;, ng&atilde; nh&agrave;o xuống đất.<br />\r\n<br />\r\nSau khi vụ việc xảy ra, c&aacute;c lực lượng chức năng, Đội CSGT số 1, CAP L&yacute; Th&aacute;i Tổ v&agrave; lực lượng bảo vệ t&ograve;a nh&agrave; đ&atilde; phong tỏa hiện trường, đưa x&aacute;c nạn nh&acirc;n ra b&ecirc;n ngo&agrave;i, để l&agrave;m c&ocirc;ng t&aacute;c kh&aacute;m nghiệm hiện trường. Ngay sau khi nhận được hung tin, vợ người c&ocirc;ng nh&acirc;n xấu số đă chạy đến v&agrave; bất tỉnh. Hai vợ chồng anh Quang mới cưới nhau v&agrave; c&ugrave;ng đang l&agrave;m thu&ecirc; ngo&agrave;i H&agrave; Nội.<br />\r\n<ul>\r\n    <li><em><strong>Theo TT&amp;VH Online</strong></em></li>\r\n</ul>', 1, 1247908077, 1, 61, 0),
(12, 2, 'Cháy rụi 500 m2 cơ sở phế liệu', '11h trưa 12/7, một cột khói đen kịt bốc cao nghi ngút tại cơ sở chế biến phế liệu huyện Thuận An, tỉnh Bình Dương. Sau một giờ cháy, nhà xưởng, với toàn bộ máy móc nguyên, vật liệu nơi đây bị thiêu rụi.', '12.jpg', 'Toàn bộ cơ sở bị chìm trong biển lửa trước khi lực lượng chữa cháy có mặt. Ảnh: Phạm Văn.', 'Ngọn lửa xuất ph&aacute;t từ khu vực m&aacute;y nghiền m&uacute;t xốp, khi c&oacute; hơn 10 c&ocirc;ng nh&acirc;n đang l&agrave;m việc. Tất cả đều hoảng hốt bỏ chạy t&aacute;n loạn. Do nơi đ&acirc;y c&oacute; nhiều nguy&ecirc;n liệu l&agrave; nhựa, m&uacute;t xốp, cao su&hellip; n&ecirc;n lửa b&eacute;n nhanh. Cộng th&ecirc;m nh&agrave; xưởng cũng x&acirc;y cất chắp v&aacute; bằng những vất dụng dễ ch&aacute;y như gỗ, bạt nhựa&hellip;, n&ecirc;n chỉ mất 5 ph&uacute;t sau to&agrave;n bộ cơ sở ch&igrave;m trong biển lửa.<br />\r\n<br />\r\n4 xe chữa ch&aacute;y, gần 30 l&iacute;nh cứu hỏa của tỉnh B&igrave;nh Dương nhanh ch&oacute;ng được điều động đến hiện trường ứng cứu. Tuy nhi&ecirc;n, do đ&aacute;m ch&aacute;y dữ dội n&ecirc;n sau đ&oacute; phải chi viện th&ecirc;m 2 xe chữa ch&aacute;y của quận 9 (TP HCM).<br />\r\n<br />\r\nĐến 12h, sau một giờ chiến đấu, ngọn lửa được dập tắt. Tuy nhi&ecirc;n, to&agrave;n bộ cơ sở chứa phế liệu tr&ecirc;n rộng hơn 500 m2 bị đổ sập, chỉ c&ograve;n lại b&atilde;i đất trống. Tất cả nguy&ecirc;n liệu, m&aacute;y m&oacute;c đều bị ch&aacute;y rụi, trong đ&oacute; c&oacute; cả một xe tải nhẹ v&agrave; một xe m&aacute;y bị ch&aacute;y rụi. Rất may cơ sở chứa phế liệu n&agrave;y nằm giữa khu đất trống n&ecirc;n kh&ocirc;ng g&acirc;y ảnh hưởng đến nh&agrave; d&acirc;n.<br />\r\n<br />\r\n<div style="text-align: center;"><img width="480" height="350" src="/uploads/image/Tin-tuc/13-7.jpg" alt="" /></div>\r\n<div style="text-align: center;"><em>To&agrave;n bộ cơ sở với diện t&iacute;ch khoảng 500m2 bgị ch&aacute;y rụi ho&agrave;n to&agrave;n. Ảnh: Phạm Văn.</em></div>\r\n<br />\r\nHiện c&aacute;c cơ quan chức năng tỉnh B&igrave;nh Dương đang x&aacute;c định thiệt hại v&agrave; điều tra l&agrave;m r&otilde; nguy&ecirc;n nh&acirc;n.<br />\r\n<ul>\r\n    <li><em><strong>Theo antoanlaodong.gov.vn</strong></em></li>\r\n</ul>', 1, 1247909052, 1, 41, 0),
(13, 2, ' Hiện trường vụ tai nạn - Thìn vẫn chưa hết bàng hoàng Hà Nội: Sập giàn giáo, một công nhân thiệt mạng', 'Khoảng 15h30 chiều nay 22/6, một vụ tai nạn sập giàn giáo đã xảy ra tại công trường số 173 Xuân Thủy, Cầu Giấy (nơi từng xảy ra một vụ tai nạn tương tự), làm một công nhân tử vong ngay tại chỗ.', '13.jpg', '', 'Đứng tr&ecirc;n đống đổ n&aacute;t, Ho&agrave;ng Văn Th&igrave;n (qu&ecirc; Th&aacute;i Nguy&ecirc;n) vẫn chưa hết b&agrave;ng ho&agrave;ng khi phải tận mắt chứng kiến c&aacute;i chết thương t&acirc;m của người em họ m&igrave;nh, Trần Văn Ki&ecirc;n.<br />\r\n&nbsp;<br />\r\nTh&igrave;n kể lại sự việc như sau: v&agrave;o khoảng 15h30, Th&igrave;n c&ugrave;ng Ki&ecirc;n v&agrave; 2 c&ocirc;ng nh&acirc;n kh&aacute;c l&agrave; Lục Văn Dũng (SN 1983), Diệp Văn H&ugrave;ng (SN 1988) l&agrave;m nhiệm vụ qu&eacute;t dọn, x&uacute;c đất v&agrave; th&aacute;o v&aacute;n ở v&aacute;ch thang m&aacute;y tr&ecirc;n tầng thứ 6 của c&ocirc;ng trường.<br />\r\n&nbsp;<br />\r\nTh&igrave;n, H&ugrave;ng v&agrave; Dũng đứng b&ecirc;n trong x&uacute;c r&aacute;c thải x&acirc;y dựng c&ograve;n một m&igrave;nh Ki&ecirc;n đứng ngo&agrave;i gi&agrave;n gi&aacute;o hộp 10 (loại gi&agrave;n gi&aacute;o c&oacute; trụ chống l&agrave; sắt hộp cạnh rộng 10 cm) để th&aacute;o v&aacute;n ở v&aacute;ch thang m&aacute;y.<br />\r\n<br />\r\n<div style="text-align: center;"><img width="280" height="374" src="/uploads/image/Tin-tuc/Sap_gian_01.jpg" alt="" /></div>\r\n<div style="text-align: center;"><em>Hiện trường vụ tai nạn - Th&igrave;n vẫn chưa hết b&agrave;ng ho&agrave;ng</em></div>\r\n&nbsp;<br />\r\nĐột nhi&ecirc;n, gi&agrave;n gi&aacute;o sập mạnh, khiến Ki&ecirc;n kh&ocirc;ng kịp chống đỡ, rơi nhanh xuống tận dưới tầng hầm của t&ograve;a nh&agrave; đang x&acirc;y dựng, tử vong ngay tại chỗ. Th&igrave;n c&ugrave;ng hai người bạn chỉ biết đứng nh&igrave;n.<br />\r\n<br />\r\n<div style="text-align: center;"><img width="350" height="263" src="/uploads/image/Tin-tuc/Sap_gian_02.jpg" alt="" /></div>\r\n<div style="text-align: center;"><em>Ph&iacute;a b&ecirc;n ngo&agrave;i c&ocirc;ng trường số 173 Xu&acirc;n Thủy, nơi xảy ra vụ tai nạn.</em></div>\r\n&nbsp;<br />\r\nTheo những th&ocirc;ng tin ban đầu, nạn nh&acirc;n Trần Văn Ki&ecirc;n (SN 1990), tr&uacute; qu&aacute;n tại x&oacute;m B&igrave;a, x&atilde; Th&agrave;nh C&ocirc;ng, huyện Phổ Y&ecirc;n, tỉnh Th&aacute;i Nguy&ecirc;n. Ki&ecirc;n mới v&agrave;o l&agrave;m tại c&ocirc;ng trường n&agrave;y khoảng 2 th&aacute;ng nay.<br />\r\n&nbsp;<br />\r\nHiện trường vụ tai nạn rất kh&oacute; c&oacute; thể tiếp cận do nằm s&acirc;u trong t&ograve;a nh&agrave;, lại ở tầng hầm. Đường đi tối om, ngổn ngang vật liệu x&acirc;y dựng. Sau khi ghi nhận hiện trường vụ tai nạn, ch&uacute;ng t&ocirc;i c&oacute; li&ecirc;n hệ với ban chỉ huy c&ocirc;ng trường v&agrave; chủ đầu tư song kh&ocirc;ng nhận được c&acirc;u trả lời n&agrave;o.<br />\r\n&nbsp;<br />\r\nĐược biết, c&ocirc;ng trường 173 Xu&acirc;n Thủy đang x&acirc;y dựng thuộc dự &aacute;n Trung t&acirc;m thương mại dịch vụ v&agrave; nh&agrave; ở (được thiết kế thi c&ocirc;ng 25 tầng, chưa kể 2 tầng hầm), do C&ocirc;ng ty Cổ phần đầu tư v&agrave; ph&aacute;t triển đ&ocirc; thị Long Giang l&agrave;m chủ đầu tư.<br />\r\n&nbsp;<br />\r\nTrước đ&oacute;, ng&agrave;y 25/3, một vụ tai nạn lao động tương tự đ&atilde; xảy ra, khiến 1 c&ocirc;ng nh&acirc;n thiệt mạng v&agrave; một c&ocirc;ng nh&acirc;n kh&aacute;c bị thương. Nhiều nguồn tin cho hay, vụ việc ng&agrave;y 25/3 vẫn chưa được giải quyết xong th&igrave; lại xảy ra vụ tai nạn n&agrave;y.<br />\r\n&nbsp;<br />\r\nHiện vụ việc đang được c&aacute;c cơ quan chức năng tiếp tục điều tra l&agrave;m r&otilde;.<br />\r\n<ul>\r\n    <li><em><strong>Theo D&acirc;n Tr&iacute;</strong></em></li>\r\n</ul>', 1, 1247996582, 1, 43, 0),
(14, 2, 'Đà Nẵng: 2 vụ tai nạn lao động làm 4 người chết', 'Vụ đầu tiên xảy ra lúc 6 giờ 30 sáng 3.7, tại mỏ khai thác đá của DN tư nhân Huỳnh Đức May - ở xã Hoà Nhơn, huyện Hoà Vang, Đà Nẵng.', '14.jpg', '', 'Một tảng đ&aacute; to từ tr&ecirc;n n&uacute;i đ&atilde; lăn xuống khi c&oacute; 4 c&ocirc;ng nh&acirc;n đang khoan đ&aacute;, nhồi thuốc m&igrave;n, khiến hai anh em &ocirc;ng B&ugrave;i Cải (39 tuổi), B&ugrave;i Thiện (49 tuổi) c&ugrave;ng tr&uacute; tại địa phương n&agrave;y chết tại chỗ.<br />\r\n<br />\r\n&Ocirc;ng Nguyễn Phước (50 tuổi) - tr&uacute; quận Cẩm Lệ, chết khi đang được cấp cứu. Sau khi xảy ra tai nạn, LĐLĐ, Thanh tra Sở LĐTBXH Đ&agrave; Nẵng v&agrave; c&ocirc;ng an đ&atilde; c&oacute; mặt để kh&aacute;m nghiệm hiện trường. Nhiều khả năng sẽ khởi tố h&igrave;nh sự vụ &aacute;n về h&agrave;nh vi vi phạm c&aacute;c quy định về an to&agrave;n lao động, g&acirc;y hậu quả nghi&ecirc;m trọng tại mỏ đ&aacute; n&agrave;y.<br />\r\n<br />\r\nNgay sau đ&oacute;, l&uacute;c 10 giờ 30, tại x&atilde; Ho&agrave; Sơn, Ho&agrave; Vang xảy ra tiếp vụ tai nạn kh&aacute;c, l&agrave;m một người chết, một người bị thương. Vụ việc xảy ra khi Cty TNHH Thuận Th&agrave;nh thi c&ocirc;ng lắp đặt trụ điện thoại Viettel.<br />\r\n<br />\r\nC&ocirc;ng nh&acirc;n L&ecirc; Thiện Thịnh trong l&uacute;c điều khiển cẩu để đưa trụ điện v&agrave;o hố trồng th&igrave; bất ngờ d&acirc;y x&iacute;ch n&acirc;ng trụ bị đứt, bung tr&uacute;ng đường d&acirc;y cao thế. Trụ điện rơi xuống, l&agrave;m đứt đường d&acirc;y hạ thế, ph&oacute;ng điện khiến Thịnh chết tại chỗ, một người kh&aacute;c bị thương nặng./.\r\n<ul>\r\n    <li><em><strong>Theo Lao Động</strong></em></li>\r\n</ul>', 1, 1247996732, 1, 42, 0),
(15, 2, ' Đằng sau những vụ tai nạn lao động: Nỗi đau chất chồng... ', 'Người lao động phải đối diện với tình trạng thất nghiệp, tật nguyền, nợ nần, bị bỏ rơi và vô số nỗi đau vô hình khác...', '15.jpg', '', 'Mười năm trước, trong l&uacute;c đi chở nguy&ecirc;n liệu, anh Trần Hoa C. - đầu bếp của một nh&agrave; h&agrave;ng ở quận 1 bị xe tải h&uacute;c rồi c&aacute;n ngang đ&ocirc;i ch&acirc;n khiến ch&acirc;n tr&aacute;i anh bị cưa qu&aacute; đầu gối. C&ocirc; người y&ecirc;u l&agrave;m c&ugrave;ng c&ocirc;ng ty ngồi sau xe trong vụ tai nạn thấy vậy bỏ anh đi biệt t&iacute;ch....<br />\r\n<br />\r\n<strong>Cửa n&aacute;t nh&agrave; tan</strong><br />\r\n<br />\r\nB&ecirc;n g&acirc;y ra tai nạn trốn tr&aacute;nh kh&ocirc;ng chịu đền b&ugrave; thiệt hại. Sau hơn hai năm r&ograve;ng r&atilde; đệ đơn l&ecirc;n xuống anh mới được đền b&ugrave; 70 triệu đồng, chỉ đủ để trả tiền viện ph&iacute;. Khoản tiền 700.000 đồng/th&aacute;ng do bảo hiểm x&atilde; hội chi trả cũng chưa đủ tiền ăn h&agrave;ng th&aacute;ng. Từ một lao động ch&iacute;nh trong gia đ&igrave;nh, anh trở th&agrave;nh g&aacute;nh nặng cho cha mẹ gi&agrave; v&agrave; người chị tật nguyền. Anh C. n&oacute;i trong nước mắt: &ldquo;Từng n&agrave;y tuổi rồi m&agrave; c&ograve;n phải sống b&aacute;m v&agrave;o chị, với một người đ&agrave;n &ocirc;ng th&igrave; c&ograve;n g&igrave; đau đớn bằng&rdquo;.<br />\r\n<br />\r\nD&ugrave; kh&oacute; khăn anh vẫn cố gắng hết sức để xin học in lụa tại trung t&acirc;m dạy nghề cho người khuyết tật. Thế nhưng chẳng nơi n&agrave;o chịu nhận khi nh&igrave;n thấy đ&ocirc;i ch&acirc;n tật nguyền của anh. Nhắc đến chuyện vợ con, anh thở d&agrave;i: &ldquo;C&aacute;i th&acirc;n m&igrave;nh thế n&agrave;y, sao d&aacute;m l&agrave;m khổ ai&rdquo;.<br />\r\n<br />\r\n<em>\r\n<div style="text-align: center;"><img width="360" height="235" alt="" src="/uploads/image/Tin-tuc/Tainan_laodong.jpg" /></div>\r\n</em>\r\n<div style="text-align: center;"><em>Từ ng&agrave;y chị Yến bị tai nạn, bữa cơm n&agrave;o của gia đ&igrave;nh cũng chan nước mắt</em></div>\r\n<br />\r\nNăm 1999, trong l&uacute;c đang điều khiển m&aacute;y chải sợi, vạt &aacute;o bị quấn v&agrave;o m&aacute;y khiến chị Đặng Thị Yến bị k&eacute;o ng&atilde;, chống tay v&agrave;o trục. Do hai c&aacute;nh tay bị dập n&aacute;t n&ecirc;n c&aacute;c b&aacute;c sĩ kh&ocirc;ng thể r&aacute;p nối. Tay tr&aacute;i chị bị đứt l&igrave;a gần đến vai, c&ograve;n tay phải bị đứt l&igrave;a dưới khuỷu tay. Gia đ&igrave;nh chị phải b&aacute;n nh&agrave; ở quận 7 để trả nợ v&agrave; dắt d&iacute;u nhau về Nh&agrave; B&egrave; thu&ecirc; một căn ph&ograve;ng trọ sống qua ng&agrave;y.<br />\r\n<br />\r\nSau tai nạn, chị kh&ocirc;ng c&ograve;n c&oacute; thể tự m&igrave;nh l&agrave;m được g&igrave;. Ngay cả những chuyện c&aacute; nh&acirc;n đơn giản nhất như đ&aacute;nh răng, ti&ecirc;u tiểu v&agrave; cả chuyện vệ sinh phụ nữ cũng một tay mẹ phụ gi&uacute;p. Chị thầm th&igrave;, nhiều đ&ecirc;m nằm tr&ecirc;n nền đất n&oacute;ng bức, kiến b&ograve; cắn ngứa, r&aacute;t cả lưng nhưng thấy mẹ ngủ ngon, chị kh&ocirc;ng d&aacute;m đ&aacute;nh thức, cắn răng chịu đựng, s&aacute;ng ra sưng v&ugrave; cả lưng. &ldquo;Nhiều người n&oacute;i tui sao l&agrave;m biếng dữ, kh&ocirc;ng chịu đi l&agrave;m. Nhưng tui đi l&agrave;m rồi lấy ai lo cho n&oacute;. Đến k&eacute;o quần đi tiểu n&oacute; c&ograve;n kh&ocirc;ng tự l&agrave;m được m&agrave;&rdquo; - mẹ chị Yến thở d&agrave;i.<br />\r\n<br />\r\nChị bệnh, cha chị cũng l&acirc;m bệnh theo. &Ocirc;ng đưa b&agrave;n tay run lẩy bẩy của m&igrave;nh l&ecirc;n m&eacute;p ch&ugrave;i d&ograve;ng nước d&atilde;i cứ chực tu&ocirc;n ra ngo&agrave;i mỗi khi &ocirc;ng mở miệng, mắt rưng rưng: &ldquo;Ng&agrave;y n&agrave;o tui cũng nhịn ăn s&aacute;ng mua b&aacute;o đọc coi c&oacute; ai tuyển g&igrave; l&agrave;m kh&ocirc;ng. Nhưng 62 tuổi rồi, ai m&agrave; th&egrave;m nhận. Giờ chỉ tha thiết mong nh&agrave; ai c&oacute; vườn tược thu&ecirc; tui tr&ocirc;ng coi để kiếm ch&uacute;t tiền phụ gia đ&igrave;nh chứ sống thế n&agrave;y khổ qu&aacute;&rdquo;. C&ograve;n chị Yến th&uacute;t th&iacute;t: &ldquo;Gi&aacute; m&agrave; tay t&ocirc;i cụt tới khuỷu th&ocirc;i th&igrave; ba mẹ đỡ cực hơn&rdquo;.<br />\r\n<br />\r\n<strong>Thắt l&ograve;ng người ở lại</strong><br />\r\n<br />\r\nS&aacute;ng 20-4, sau một cơn mưa to v&agrave;o rạng s&aacute;ng, căn nh&agrave; của anh Trần C&ocirc;ng Thạch nằm trong căn hẻm nhỏ ở quận 4 như ch&igrave;m trong biển nước. Dưới m&uacute;c nước tạt, tr&ecirc;n lấy x&ocirc; hứng, chập điện, nh&agrave; kh&ocirc;ng c&oacute; đ&agrave;n &ocirc;ng, kh&ocirc;ng ai biết c&aacute;ch sửa. Gần một năm trước, trong khi leo l&ecirc;n cao bốc dỡ h&agrave;ng cho một c&ocirc;ng ty tại cảng, anh Thạch bị t&eacute; xuống, rớt đập v&ocirc; k&ecirc;nh văng ngược lại mạn thuyền rồi lọt xuống s&ocirc;ng mất t&iacute;ch. Mấy tiếng đồng hồ sau người ta mới t&igrave;m thấy x&aacute;c anh.<br />\r\nTừ ng&agrave;y anh mất, cuộc sống gia đ&igrave;nh bị đảo lộn ho&agrave;n to&agrave;n. G&aacute;nh h&agrave;ng rong của vợ v&agrave; chị anh kh&ocirc;ng thể kham nổi hai đứa con đang tuổi lớn v&agrave; người mẹ gi&agrave; 92 tuổi bệnh tật triền mi&ecirc;n. Năm người phải chui ra chui v&agrave;o trong căn nh&agrave; chưa đầy 20m2 tr&ograve;n 60 năm tuổi v&agrave; c&oacute; thể sập bất cứ l&uacute;c n&agrave;o. Mới h&ocirc;m trước, chị anh đạp phải tấm gỗ mục ở tr&ecirc;n g&aacute;c, lọt từ tr&ecirc;n xuống tưởng chết, may m&agrave; c&oacute; song gỗ n&iacute;u lại. Chị C&uacute;c - chị anh bộc bạch: &ldquo;L&uacute;c trước c&ocirc;ng ty hứa sẽ gi&uacute;p sửa lại c&aacute;i g&aacute;c nhưng chưa kịp l&agrave;m th&igrave; Thạch chết. Gia đ&igrave;nh đ&atilde; kiệt quệ, biết lấy tiền đ&acirc;u m&agrave; sửa. Ở m&agrave; cứ nơm nớp lo kh&ocirc;ng biết bị đ&egrave; chết l&uacute;c n&agrave;o&rdquo;.<br />\r\nGiờ đ&acirc;y đứa con trai lớn của vợ chồng anh cũng được nhận v&agrave;o l&agrave;m c&ocirc;ng việc giống như cha m&igrave;nh. &ldquo;Người ta hứa sẽ c&oacute; d&acirc;y đeo, đồ bảo hộ, vậy m&agrave; tới giờ vẫn chưa c&oacute;. Mỗi s&aacute;ng khi n&oacute; cắp n&oacute;n ra cảng, kh&ocirc;ng ai n&oacute;i ra nhưng cả nh&agrave; ai cũng lo lắng. Rủi c&oacute; chuyện g&igrave; với n&oacute; th&igrave; t&ocirc;i kh&ocirc;ng sống nổi&rdquo; - vợ anh Thạch nghẹn ng&agrave;o...<br />\r\n<br />\r\nTrường hợp của chị N.T.C c&ograve;n thương t&acirc;m hơn. Do nh&agrave; ngh&egrave;o, hai vợ chồng chị N.T.C mới bỏ qu&ecirc; An Giang, đ&ugrave;m t&uacute;m hai đứa con nhỏ l&ecirc;n S&agrave;i G&ograve;n kiếm sống. Chồng chị kiếm được một ch&acirc;n phụ hồ ngay, c&ograve;n chị m&atilde;i vẫn chưa kiếm được việc g&igrave;. Thấy vậy, anh mới k&eacute;o chị v&agrave;o l&agrave;m phụ hồ chung. H&ocirc;m đ&oacute; trời mưa, khi chị C. bật cầu dao điện để đưa r&ograve;ng rọc hồ l&ecirc;n tr&ecirc;n cho thợ x&acirc;y th&igrave; bị điện giật chết.<br />\r\n<br />\r\nC&ocirc;ng ty lấy xe tải chở x&aacute;c về qu&ecirc; v&agrave; lo ma chay, ch&ocirc;n cất. Nhưng sau đ&oacute; họ lại t&iacute;nh tổng chi ph&iacute; chuyển x&aacute;c, ma chay l&agrave; 30 triệu đồng v&agrave; trừ v&agrave;o tiền lương của người vợ rồi đuổi việc người chồng. Viện cớ lỗi l&agrave; do chị C. sơ xuất n&ecirc;n c&ocirc;ng ty kh&ocirc;ng chịu bồi thường. Trong đau đớn người chồng bồng đứa con nhỏ về qu&ecirc; sinh sống, đứa con lớn ở lại S&agrave;i G&ograve;n tiếp tục l&agrave;m thu&ecirc; l&agrave;m mướn. Anh c&oacute; dắt con l&ecirc;n lại S&agrave;i G&ograve;n để kiện nhưng kh&ocirc;ng c&oacute; tiền theo tiếp vụ kiện n&ecirc;n đ&agrave;nh bu&ocirc;ng xu&ocirc;i...<br />\r\n<br />\r\n<strong>Hạnh ph&uacute;c mong manh</strong><br />\r\n<br />\r\nVới những số phận bất hạnh n&agrave;y, hạnh ph&uacute;c với họ qu&aacute; đỗi mong manh. Hiếm hoi lắm mới bắt gặp một người sống t&igrave;m được hạnh ph&uacute;c sau khi xảy ra tai nạn. Sau khi bị m&aacute;y &oacute; keo cuốn khiến bốn ng&oacute;n tay đứt l&igrave;a khỏi b&agrave;n tay, chị Đo&agrave;n Thị Th&uacute;y (nh&acirc;n vi&ecirc;n một c&ocirc;ng ty về chất dẻo) trở về ph&acirc;n xưởng v&agrave; may mắn được anh tổ trưởng đem l&ograve;ng thương y&ecirc;u, chăm s&oacute;c. Đến nay, c&aacute;i tổ ấm đầy l&ograve;ng bao dung của người cha v&agrave; đức hy sinh của người mẹ khuyết tật n&agrave;y đ&atilde; c&oacute; th&ecirc;m hai thi&ecirc;n thần nhỏ.<br />\r\n<br />\r\nBị điện giật t&eacute; từ tr&ecirc;n cao xuống cụt cả hai tay nhưng anh Phạm Tuấn Sơn (c&ocirc;ng nh&acirc;n một c&ocirc;ng ty điện) vẫn được vợ ở b&ecirc;n cạnh thay anh g&aacute;nh v&aacute;c gia đ&igrave;nh, chăm lo cho người mẹ gi&agrave; v&agrave; đứa con dại. Chị gi&uacute;p anh tập l&agrave;m mọi việc bằng hai khuỷu tay của m&igrave;nh. Kết quả l&agrave; giờ đ&acirc;y vợ chồng chị đ&atilde; l&agrave; chủ một cửa h&agrave;ng c&aacute; dĩa được nhiều người m&ecirc; c&aacute; biết đến ở S&agrave;i G&ograve;n. Hiện anh c&ograve;n đang dự định cho ra đời một cơ sở c&aacute; dĩa đầu ti&ecirc;n ở Long An qu&ecirc; anh. &ldquo;Thiệt t&igrave;nh hồi ảnh nằm xuống, t&ocirc;i kh&ocirc;ng bao giờ nghĩ c&oacute; được ng&agrave;y h&ocirc;m nay&rdquo; - chị cười hạnh ph&uacute;c...<br />\r\n<br />\r\n<strong>Tai nạn lao động chết người tăng đột biến</strong><br />\r\n<br />\r\nThống k&ecirc; mới nhất từ Sở Lao động-Thương binh v&agrave; X&atilde; hội TP.HCM cho thấy trong ba th&aacute;ng đầu năm 2009, tr&ecirc;n địa b&agrave;n th&agrave;nh phố đ&atilde; xảy ra 20 vụ tai nạn lao động chết người, l&agrave;m chết 20 người v&agrave; bị thương hai người, tăng 300% so với năm 2008. T&igrave;nh h&igrave;nh tai nạn lao động do điện tăng đột biến so với c&ugrave;ng kỳ năm 2008: 11 vụ/1 vụ, tăng 1.000%, do yếu tố g&acirc;y chết người trong lĩnh vực x&acirc;y dựng tăng 225%.<br />\r\n<br />\r\nTrong số c&aacute;c vụ tai nạn, 75% l&agrave; do người lao động vi phạm quy tr&igrave;nh, biện ph&aacute;p l&agrave;m việc an to&agrave;n; 10% l&agrave; do kh&ocirc;ng c&oacute; quy tr&igrave;nh, biện ph&aacute;p l&agrave;m việc an to&agrave;n; 95% rơi v&agrave;o c&aacute;c doanh nghiệp tư nh&acirc;n v&agrave; c&aacute;c cơ sở sản xuất, hộ kinh doanh<em><strong>. <br />\r\n</strong></em>\r\n<ul>\r\n    <li><em><strong>Theo Ph&aacute;p luật</strong></em></li>\r\n</ul>', 1, 1247996974, 1, 55, 0),
(16, 2, 'An toàn lao động trong xây dựng: Lại sống chết mặc bay?', '3 tháng đầu năm 2009, số vụ tai nạn lao động (TNLĐ) gây chết người xảy ra trên địa bàn TPHCM tăng gấp 3 lần so với cùng kỳ năm ngoái. Riêng trong nửa đầu tháng 3 - thời kỳ cao điểm thực hiện tuần lễ an toàn vệ sinh lao động và phòng chống cháy nổ - số vụ TNLĐ không những không giảm mà còn tăng gấp đôi so số lượng xảy ra trong 2 tháng đầu năm 2008.', '16.jpg', '', '<strong>Những c&aacute;i chết trẻ</strong><br />\r\n<br />\r\nS&aacute;ng 9-2, tại c&ocirc;ng tr&igrave;nh &eacute;p cọc b&ecirc; t&ocirc;ng (l&ocirc; R6, cư x&aacute; Văn Th&aacute;nh Bắc, quận B&igrave;nh Thạnh) đ&atilde; xảy ra vụ TNLĐ v&ocirc; c&ugrave;ng thương t&acirc;m l&agrave;m chết anh M.V.H. (SN 1983) c&ocirc;ng nh&acirc;n (CN) của C&ocirc;ng ty TNHH X&acirc;y dựng v&agrave; DV-TM Trung Trực. Anh H. được ph&acirc;n c&ocirc;ng điều chỉnh cọc &eacute;p v&agrave; ph&aacute;t t&iacute;n hiệu cho người vận h&agrave;nh m&aacute;y &eacute;p. Khi đang &eacute;p đến đoạn cọc thứ 3 th&igrave; mọi người nghe tiếng k&ecirc;u thất thanh của H. Anh bị ch&egrave;n chết trong tư thế ngồi. Đầu nạn nh&acirc;n bị kẹt trong khe hở giữa gi&agrave;n khung cố định v&agrave; ngo&agrave;m trượt của Ti-ben.<br />\r\n<div style="text-align: center;"><img width="350" height="277" src="/uploads/image/An_toan_laodong_01.jpg" alt="" /></div>\r\n<div style="text-align: center;"><em>Vụ sập gi&agrave;n gi&aacute;o c&ocirc;ng tr&igrave;nh cao ốc tại khu đ&ocirc; thị Ph&uacute; Mỹ Hưng, Q7, TPHCM v&agrave;o rạng s&aacute;ng 30-12-2008, l&agrave;m 2 người chết, 14 người bị thương. Ảnh: Việt Dũng</em></div>\r\n<br />\r\nV&agrave;o khoảng 14g ng&agrave;y 8-3-2009, tại trạm trộn b&ecirc; t&ocirc;ng của C&ocirc;ng ty L&ecirc; Phan (quận 12), sau khi bơm b&ecirc; t&ocirc;ng xong, nh&oacute;m CN m&oacute;c ngo&agrave;m của m&aacute;y bơm b&ecirc; t&ocirc;ng v&agrave;o ngo&agrave;m xe tải v&agrave; m&oacute;c d&acirc;y x&iacute;ch cột m&aacute;y bơm v&agrave;o xe. Tuy nhi&ecirc;n, ngo&agrave;m m&oacute;c kh&ocirc;ng đ&uacute;ng vị tr&iacute; n&ecirc;n trong qu&aacute; tr&igrave;nh vận chuyển, ngo&agrave;m nối v&agrave; d&acirc;y x&iacute;ch đ&atilde; bật ra &eacute;p anh N.V.V. v&agrave;o xe tải khiến anh V. tử vong. Ng&agrave;y 13-2, em H.T.D., mới 17 tuổi, l&agrave; CN thi c&ocirc;ng tại c&ocirc;ng tr&igrave;nh x&acirc;y dựng Trạm Kiểm dịch động vật H&oacute;c M&ocirc;n bị điện giật chết tại chỗ khi đang khi&ecirc;ng 1 thanh sắt d&agrave;i gần nguồn điện.<br />\r\n<br />\r\nMới đ&acirc;y nhất, ng&agrave;y 9-3, tại c&ocirc;ng tr&igrave;nh thi c&ocirc;ng tuyến ống cấp nước ở Thủ Đức đ&atilde; xảy ra vụ tai nạn lao động điện giật l&agrave;m chết c&ocirc;ng nh&acirc;n H.V.P. (SN 1986). Trong l&uacute;c l&agrave;m vệ sinh đ&aacute;y giếng, c&aacute;c CN sử dụng 1 bơm nước (loại bơm hỏa tiễn) để bơm nước từ đ&aacute;y giếng l&ecirc;n. V&igrave; nước ở đ&aacute;y giếng kh&ocirc;ng c&ograve;n nhiều n&ecirc;n anh P. được ph&acirc;n c&ocirc;ng đứng cầm bơm để bơm kh&ocirc;ng bị đổ. Khi đồng nghiệp cắm điện v&agrave;o m&aacute;y bơm, m&aacute;y bơm bị r&ograve; điện giật chết anh P.<br />\r\n<br />\r\nTrước đ&oacute;, v&agrave;o cuối th&aacute;ng 2, khi đang bơm nước tại c&ocirc;ng tr&igrave;nh x&acirc;y dựng cao ốc văn ph&ograve;ng tại quận B&igrave;nh Thạnh, anh T.V.P. bị điện giật ng&atilde; xuống hầm nước thải. D&ugrave; đ&atilde; được h&ocirc; hấp nh&acirc;n tạo v&agrave; chuyển ngay đến bệnh viện cấp cứu nhưng hiện anh P. vẫn đang c&ograve;n trong t&igrave;nh trạng nguy kịch.<br />\r\n<br />\r\nDanh s&aacute;ch những CN bị chết do TNLĐ trong thời gian gần đ&acirc;y cho thấy c&oacute; đến tr&ecirc;n 80% trong độ tuổi từ 17 - 30, đa phần l&agrave; người lao động ngh&egrave;o v&agrave; hầu hết kh&ocirc;ng được k&yacute; hợp đồng lao động. Một số trường hợp l&agrave; CN thử việc v&agrave; kh&ocirc;ng được phổ biến những kiến thức về an to&agrave;n lao động, bảo hộ lao động.<br />\r\n<br />\r\n<div style="text-align: center;"><img width="300" height="195" src="/uploads/image/An_toan_laodong_02.jpg" alt="" /></div>\r\n<div style="text-align: center;"><em>C&ocirc;ng nh&acirc;n đang l&agrave;m &ldquo;xiếc&rdquo;, qu&ecirc;n t&iacute;nh mạng của ch&iacute;nh m&igrave;nh Ảnh: HỒ VIỆT</em></div>\r\n<br />\r\nBao nhi&ecirc;u năm nay, t&igrave;nh trạng TNLĐ sở dĩ vẫn kh&ocirc;ng chấm dứt l&agrave; do 2 nguy&ecirc;n nh&acirc;n ch&iacute;nh dẫn đến TNLĐ vẫn kh&ocirc;ng được khắc phục: &yacute; thức của người sử dụng lao động v&agrave; nhận thức của CN. Nhiều trường hợp, do thiếu kiến thức tối thiểu về an to&agrave;n n&ecirc;n nhiều CN đ&atilde; chết oan uổng.<br />\r\n<br />\r\nTại c&ocirc;ng tr&igrave;nh đ&agrave;o đường g&oacute;c ng&atilde; tư đường L&yacute; Ch&iacute;nh Thắng - B&agrave; Huyện Thanh Quan, mặc d&ugrave; l&agrave;m việc ở độ s&acirc;u gần 4m, 2 b&ecirc;n đường xe cộ chạy v&ugrave;n vụt nhưng 2 c&ocirc;ng nh&acirc;n mặc &aacute;o đồng phục m&agrave;u xanh vẫn kh&ocirc;ng đội n&oacute;n bảo hộ. C&ocirc;ng tr&igrave;nh thi c&ocirc;ng kh&ocirc;ng đ&oacute;ng cừ chống sạt lở đường theo quy định, d&atilde;y t&ocirc;n r&agrave;o chắn dựng xi&ecirc;u vẹo tr&ecirc;n những ch&acirc;n đế bằng xi măng kh&ocirc;ng đảm bảo an to&agrave;n...<br />\r\n<br />\r\n<div style="text-align: center;"><img width="232" height="300" src="/uploads/image/An_toan_laodong_03.jpg" alt="" /></div>\r\n<div style="text-align: center;"><em>L&agrave;m việc ở độ cao 30m tr&ecirc;n chiếc thang tre mỏng manh v&agrave; kh&ocirc;ng c&oacute; d&acirc;y đeo an to&agrave;n! Ảnh: Th&aacute;i Bằng</em></div>\r\n<br />\r\nChưa hết, một số c&ocirc;ng tr&igrave;nh đang thi c&ocirc;ng tr&ecirc;n đường L&ecirc; Hồng Phong, Cao Thắng, Lạc Long Qu&acirc;n việc che chắn hết sức sơ s&agrave;i, thậm ch&iacute; chỉ chăng mấy sợi d&acirc;y ni l&ocirc;ng phản quang để cảnh b&aacute;o!<br />\r\n<br />\r\nĐ&atilde; c&oacute; kh&ocirc;ng &iacute;t vụ tai nạn xuất ph&aacute;t từ sự chủ quan, cẩu thả kiểu như vậy. Điển h&igrave;nh như vụ tai nạn xảy ra tại Doanh nghiệp tư nh&acirc;n Thương mại- Sản xuất Hoa T&acirc;n (quận 8). Anh T.V.H. đi v&agrave;o khu vực m&aacute;y nghiền giấy để l&agrave;m vệ sinh trong khi cả khu vực tr&ecirc;n đều ẩm ướt, đầy b&ugrave;n s&igrave;nh ngập ướt l&ecirc;n cả m&ocirc; tơ m&aacute;y h&uacute;t bột m&agrave; kh&ocirc;ng mang gi&agrave;y. Khi c&oacute; người cắm điện vận h&agrave;nh m&aacute;y, T.V.H. bị điện giật chết ngay tại chỗ.<br />\r\n<br />\r\nVụ tai nạn xảy ra tại C&ocirc;ng ty TNHH Điện lạnh Ho&agrave;ng Ph&aacute;t (quận B&igrave;nh T&acirc;n) cũng c&oacute; nguy&ecirc;n nh&acirc;n từ sự bất cẩn của CN. Anh H.V.T. l&agrave; CN học việc, chưa được huấn luyện nghề h&agrave;n đ&atilde; d&ugrave;ng mỏ h&agrave;n cắt vỏ m&aacute;y n&eacute;n m&aacute;y lạnh nhưng kh&ocirc;ng xả gas c&ograve;n lại trong m&aacute;y n&eacute;n lạnh khiến m&aacute;y nổ g&acirc;y tai nạn. Anh H. được đưa đi cấp cứu nhưng đ&atilde; chết tại bệnh viện.<br />\r\n<br />\r\nTheo quy định, hội đồng bảo hộ lao động tại c&aacute;c đơn vị c&oacute; chức năng huấn luyện an to&agrave;n lao động cho người lao động. Tuy nhi&ecirc;n, do lượng lao động thường xuy&ecirc;n biến động, lại kh&ocirc;ng được k&yacute; kết hợp đồng lao động n&ecirc;n chủ sử dụng lao động kh&ocirc;ng quan t&acirc;m đến vấn đề n&agrave;y.<br />\r\n<br />\r\n<strong>Kiểm tra, xử l&yacute;: Nhiều bất cập<br />\r\n<br />\r\n</strong>\r\n<div style="text-align: center;"><img src="/uploads/image/An_toan_laodong_04.jpg" style="width: 190px; height: 300px;" alt="" /></div>\r\n<div style="text-align: center;"><em>Mạng điện kh&ocirc;ng đảm bảo an to&agrave;n xuất hiện nhiều trong c&aacute;c khu sản xuất</em></div>\r\n<br />\r\nL&yacute; giải nguy&ecirc;n nh&acirc;n khiến c&aacute;c vụ TNLĐ gia tăng, thanh tra Sở LĐ-TBXH cho rằng, lực lượng thanh tra qu&aacute; mỏng kh&ocirc;ng thể thanh tra hết ATLĐ tr&ecirc;n địa b&agrave;n. Thanh tra Sở LĐ-TBXH c&ograve;n cho rằng, hiện chưa c&oacute; cơ chế phối hợp kiểm tra đối với c&ocirc;ng trường x&acirc;y dựng n&ecirc;n để kiểm tra to&agrave;n diện về an to&agrave;n lao động phải lập đo&agrave;n kiểm tra li&ecirc;n ng&agrave;nh.<br />\r\n<br />\r\nVề c&ocirc;ng t&aacute;c xử l&yacute; TNLĐ, phải chờ cơ quan c&ocirc;ng an chuyển hồ sơ nhưng đề xuất th&igrave; nhiều m&agrave; phản hồi th&igrave; &iacute;t! Đ&acirc;y cũng l&agrave; một r&agrave;o cản rất lớn cho việc tuy&ecirc;n truyền nhằm răn đe c&aacute;c doanh nghiệp kh&ocirc;ng vi phạm.<br />\r\n<br />\r\nHiện th&agrave;nh phố c&oacute; gần 50% số doanh nghiệp c&oacute; quy m&ocirc; từ 50 lao động trở l&ecirc;n kh&ocirc;ng c&oacute; hội đồng BHLĐ. Phần lớn c&aacute;n bộ hội đồng đều ki&ecirc;m nhiệm n&ecirc;n việc kiểm tra c&ocirc;ng t&aacute;c an to&agrave;n lao động tại c&aacute;c doanh nghiệp được giao cho c&aacute;c quản đốc, tổ trưởng v&agrave; bộ phận n&agrave;y thường kiểm tra theo đợt.<br />\r\n<br />\r\n<div style="text-align: center;"><img width="300" height="236" src="/uploads/image/An_toan_laodong_05.jpg" alt="" /></div>\r\n<div style="text-align: center;"><em>Hiện trường vụ g&atilde;y dầm cầu Chợ Đệm (H.B&igrave;nh Ch&aacute;nh, TPHCM) ng&agrave;y 10-3-2009 l&agrave;m 1 c&ocirc;ng nh&acirc;n chết v&agrave; 1 người kh&aacute;c bị thương. Ảnh: SONG PHA</em></div>\r\n<br />\r\nB&ecirc;n cạnh đ&oacute; vai tr&ograve; của ch&iacute;nh quyền cơ sở chưa thực sự được ph&aacute;t huy. Nếu ph&aacute;t huy tốt tr&aacute;ch nhiệm của ch&iacute;nh quyền cơ sở, chắc chắn sẽ l&agrave; biện ph&aacute;p hữu hiệu giải quyết tận gốc vấn đề về vệ sinh ATLĐ. Bởi lẽ, d&ugrave; c&oacute; tăng bi&ecirc;n chế số lượng thanh tra vi&ecirc;n ATLĐ l&ecirc;n gấp 10 lần như hiện nay m&agrave; ch&iacute;nh quyền cơ sở b&agrave;ng quan, thờ ơ với những vi phạm của doanh nghiệp v&agrave; người lao động ngay tr&ecirc;n địa b&agrave;n do m&igrave;nh quản l&yacute; th&igrave; vẫn kh&ocirc;ng thể cải thiện t&igrave;nh h&igrave;nh&hellip;<br />\r\n<br />\r\nMặt kh&aacute;c, t&igrave;nh trạng &ldquo;kho&aacute;n trắng&rdquo; cho c&aacute;c tổ thầu tư nh&acirc;n kh&aacute; phổ biến. Điều n&agrave;y dễ l&agrave;m nảy sinh t&igrave;nh trạng thu&ecirc; mướn lao động thời vụ n&ecirc;n người lao động kh&ocirc;ng được tập huấn, kh&ocirc;ng được trang bị đầy đủ phương tiện BHLĐ...<br />\r\n<br />\r\nBất cập kh&aacute;c nữa l&agrave; mức xử phạt vi phạm trong lĩnh vực ATLĐ qu&aacute; nhẹ. Theo Nghị định 113/2004/NĐ-CP, vi phạm về vệ sinh ATLĐ bị xử phạt h&agrave;nh ch&iacute;nh từ 200.000đ đến 20 triệu đồng. Với mức phạt n&agrave;y, kh&ocirc;ng &iacute;t chủ doanh nghiệp &ldquo;th&agrave; nộp phạt c&ograve;n hơn phải đầu tư h&agrave;ng trăm triệu đồng để bảo đảm vệ sinh ATLĐ theo đ&uacute;ng quy chuẩn&rdquo;. R&otilde; r&agrave;ng, TNLĐ &ldquo;n&oacute;ng&rdquo; v&igrave; kh&acirc;u kiểm so&aacute;t hiện nay qu&aacute; lỏng lẻo n&ecirc;n TNLĐ thường xuy&ecirc;n xảy ra l&agrave; điều tất yếu. <br />\r\n<ul>\r\n    <li><em><strong>Theo www.sggp.org.vn</strong></em></li>\r\n</ul>', 1, 1247997558, 1, 164, 0),
(17, 2, 'Mất an toàn lao động trách nhiệm của ai?', 'Theo thống kê của Bộ Lao động - Thương binh và Xã hội, năm 2008 toàn quốc xảy ra 5.836 vụ tai nạn lao động (TNLĐ) làm 6.047 người bị nạn, có 508 vụ TNLĐ chết người làm 573 người chết, 1.262 người bị thương nặng.', '17.jpg', '', 'Trong đ&oacute;, loại h&igrave;nh c&ocirc;ng ty TNHH, c&ocirc;ng ty cổ phần vốn tư nh&acirc;n trong nước chiếm 40,90% tổng số vụ tai nạn v&agrave; 38,68% tổng số người chết. Lĩnh vực x&acirc;y lắp c&aacute;c c&ocirc;ng tr&igrave;nh d&acirc;n dụng, c&ocirc;ng nghiệp v&agrave; c&ocirc;ng tr&igrave;nh giao th&ocirc;ng chiếm tỉ lệ cao nhất, 29,54% tổng số vụ tai nạn v&agrave; 29,72% tổng số người chết.<br />\r\n<br />\r\nC&oacute; một thực tế l&agrave;, người lao động kh&ocirc;ng c&oacute; hoặc c&oacute; &yacute; thức r&otilde; r&agrave;ng đối với vấn đề an to&agrave;n lao động. Khi được k&yacute; hợp đồng lao động, người lao động chưa c&oacute; th&oacute;i quen xem x&eacute;t kỹ c&agrave;ng điều mục về điều kiện bảo hộ lao động. T&acirc;m l&yacute; chung c&oacute; việc l&agrave;m đ&atilde; l&agrave; tốt, vấn đề ưu ti&ecirc;n h&agrave;ng đầu l&agrave; thu nhập sẽ nhận được h&agrave;ng th&aacute;ng l&agrave; bao nhi&ecirc;u. Những lao động n&agrave;y kh&ocirc;ng &yacute; thức được vấn đề, c&agrave;ng kh&ocirc;ng lường trước được khi tai nạn xảy ra th&igrave; bản th&acirc;n họ v&agrave; gia đ&igrave;nh sẽ phải chịu thiệt th&ograve;i ra sao. Cũng v&igrave; thế, đ&atilde; c&oacute; bao cảnh thương t&acirc;m đến với c&aacute;c gia đ&igrave;nh c&oacute; người bị nạn m&agrave; phần lớn ch&iacute;nh họ l&agrave; những trụ cột gia đ&igrave;nh.<br />\r\n<br />\r\n<div style="text-align: center;"><img width="400" height="300" src="/uploads/image/Tin-tuc/Mat_an_toan.jpg" alt="" /><br />\r\n&nbsp;</div>\r\nNhiều trường hợp, người lao động lờ mờ &yacute; thức được điều n&agrave;y nhưng kh&ocirc;ng d&aacute;m đ&ograve;i hỏi, y&ecirc;u cầu v&igrave; qu&aacute; cần việc l&agrave;m, sợ kh&ocirc;ng được k&yacute; hợp đồng&hellip; Cũng c&oacute; khi chỉ l&agrave; một tho&aacute;ng chủ quan, kh&ocirc;ng ai nghĩ tai họa c&oacute; thể gi&aacute;ng xuống đầu m&igrave;nh. Rất &iacute;t trường hợp, người lao động đấu tranh đ&ograve;i hỏi quyền lợi bảo hộ an to&agrave;n lao động.<br />\r\n<br />\r\n&nbsp;<br />\r\nT&acirc;m l&yacute; người sử dụng lao động kh&ocirc;ng ai muốn tai nạn xảy ra. Tuy nhi&ecirc;n, h&agrave;nh động t&iacute;ch cực để ph&ograve;ng ngừa th&igrave; kh&ocirc;ng phải doanh nghiệp, c&aacute; nh&acirc;n&hellip; sử dụng lao động n&agrave;o cũng thực hiện. Tất cả đều kh&ocirc;ng c&oacute; &yacute; thức tr&aacute;ch nhiệm khi tai họa xảy ra. Điển h&igrave;nh nhất hiện nay l&agrave; tại c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng do tư nh&acirc;n l&agrave;m chủ đầu tư. Dễ quan s&aacute;t nhất l&agrave; tại c&aacute;c khu đ&ocirc; thị mới trong th&agrave;nh phố. Tốc độ x&acirc;y dựng nhanh ch&oacute;ng mặt v&agrave; t&igrave;nh trạng c&ocirc;ng nh&acirc;n thi c&ocirc;ng trong t&igrave;nh trạng thiếu an to&agrave;n cũng&hellip; ch&oacute;ng mặt.<br />\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\nC&ocirc;ng nh&acirc;n l&agrave;m việc tại c&aacute;c c&ocirc;ng tr&igrave;nh n&agrave;y đa số l&agrave; lao động ngoại tỉnh. Điều h&agrave;nh chủ yếu l&agrave; một &ldquo;cai thầu&rdquo; cũng l&agrave; người ngoại tỉnh, chủ yếu l&agrave; giao k&egrave;o miệng, kh&ocirc;ng c&oacute; hợp đồng lao động. Phần nhiều họ l&agrave; những anh em đồng hương, c&ugrave;ng l&agrave;ng, c&ugrave;ng x&atilde;&hellip; tin tưởng nhau l&agrave; ch&iacute;nh. Hợp đồng lao động theo nhận thức của họ chỉ l&agrave; để chốt c&ocirc;ng x&aacute; sẽ được nhận. M&agrave; to&agrave;n anh em trong l&agrave;ng, trong x&atilde;&hellip; l&agrave;m với nhau, thậm ch&iacute; rủ nhau đi l&agrave;m cho c&oacute; thu nhập, chẳng ai c&oacute; mặt mũi n&agrave;o &ldquo;b&ugrave;ng&rdquo; tiền c&ocirc;ng x&aacute; cả. Thế th&igrave; l&agrave;m hợp đồng lao động l&agrave;m g&igrave; cho th&ecirc;m c&aacute;ch r&aacute;ch.<br />\r\n<br />\r\n&nbsp;<br />\r\n<br />\r\nKhi c&oacute; tai nạn lao động, nh&agrave; chủ x&aacute;c định như c&aacute;i hạn, biếu nạn nh&acirc;n v&agrave; gia đ&igrave;nh &iacute;t tiền, tuỳ theo mức độ nặng nhẹ v&agrave; l&ograve;ng hảo t&acirc;m. Bản th&acirc;n những người l&agrave;m c&ugrave;ng v&agrave; gia đ&igrave;nh người bị nạn cũng chỉ coi như c&aacute;i số m&igrave;nh xui xẻo, chẳng mấy ai nghĩ đến tr&aacute;ch nhiệm n&agrave;y nọ cho nặng nề. Đường m&ograve;n nhận thức về an to&agrave;n lao động trong nhiều v&agrave; rất nhiều người lao động đ&atilde; hằn s&acirc;u như thế. Nếu c&oacute; một ai đ&oacute; tự nhi&ecirc;n lại đ&ograve;i hỏi hưởng quyền lợi về an to&agrave;n lao động ở một c&ocirc;ng tr&igrave;nh x&acirc;y dựng tư nh&acirc;n, chắc sẽ bị cho ra ngo&agrave;i cuộc bởi v&igrave; chẳng c&oacute; ai &ldquo;r&aacute;ch việc&rdquo; như thế.<br />\r\n<br />\r\n<br />\r\nC&ocirc;ng nh&acirc;n rất cần c&oacute; việc, mọi chi ti&ecirc;u đều tr&ocirc;ng đợi v&agrave;o họ. Nhưng kh&ocirc;ng n&ecirc;n v&igrave; thế m&agrave; &ldquo;bắt &eacute;p&rdquo; họ khi sử dụng lao động. Trong khi việc thanh tra, kiểm tra thực hiện an to&agrave;n lao động chưa đạt hiệu quả cao, trước hết bản th&acirc;n người lao động phải biết bảo vệ m&igrave;nh trước những ẩn họa tai nạn lao động khi l&agrave;m việc. <br />\r\n<ul>\r\n    <li><em><strong>Theo Vietnamnet</strong></em></li>\r\n</ul>', 1, 1247998846, 1, 49, 0);
INSERT INTO `tgp_cms` (`id`, `cat`, `ten`, `chu_thich`, `hinh`, `hinh_note`, `noi_dung`, `hien_thi`, `time`, `user`, `luot_xem`, `noi_bat`) VALUES
(18, 2, 'Chăm lo đời sống người lao động tại các KCN ở Bà Rịa - Vũng Tàu', 'Nhiều năm trở lại đây, cùng với đẩy mạnh sản xuất, kinh doanh, thu hút đầu tư, duy trì tăng trưởng, tỉnh Bà Rịa - Vũng Tàu luôn chú trọng công tác chăm lo đời sống, bảo vệ quyền và lợi ích hợp pháp của người lao động.', '18.jpg', '', '<strong>Nh&agrave; ở cho người lao động</strong><br />\r\n<br />\r\nNếu kh&ocirc;ng được giới thiệu trước, &iacute;t ai ngờ rằng những khu chung cư hiện đại, tiện nghi lại l&agrave; khu nh&agrave; ở d&agrave;nh cho c&ocirc;ng nh&acirc;n đang l&agrave;m việc tại c&aacute;c khu c&ocirc;ng nghiệp (KCN). Khu nh&agrave; ở của c&ocirc;ng nh&acirc;n C&ocirc;ng ty TNHH Sanfang Việt Nam (KCN Mỹ Xu&acirc;n A2, huyện T&acirc;n Th&agrave;nh) c&oacute; kiến tr&uacute;c hiện đại v&agrave; đẹp. Năm 2003, được Ban Quản l&yacute; KCN tạo điều kiện, C&ocirc;ng ty Sangfang đ&atilde; lập dự &aacute;n thu&ecirc; 5.000 m2 đất trong KCN Mỹ Xu&acirc;n A2 để x&acirc;y dựng nh&agrave; ở cho c&ocirc;ng nh&acirc;n. Hơn 50 ph&ograve;ng tập thể, mỗi ph&ograve;ng rộng 30 m2, đầy đủ tiện nghi, bao gồm cả c&aacute;c khu vui chơi giải tr&iacute;, đ&atilde; giải quyết 100% nhu cầu về nh&agrave; ở cho những lao động ở c&aacute;ch xa c&ocirc;ng ty hơn 25 km. Người lao động được c&ocirc;ng ty đ&agrave;i thọ miễn ph&iacute; tiền ở. Khu nh&agrave; d&agrave;nh cho c&ocirc;ng nh&acirc;n C&ocirc;ng ty giấy S&agrave;i G&ograve;n (KCN Mỹ Xu&acirc;n A) mang d&aacute;ng dấp của những d&atilde;y nh&agrave; cao tầng phục vụ t&aacute;i định cư, b&ecirc;n ngo&agrave;i c&oacute; bảo vệ, nh&agrave; để xe, s&acirc;n tập thể thao v&agrave; một thư viện nhỏ...<br />\r\n<br />\r\nChị Cao Thị Kim Tuyến, qu&ecirc; Ki&ecirc;n Giang, tiếp ch&uacute;ng t&ocirc;i trong căn hộ tập thể đơn sơ nhưng ngăn nắp, gọn g&agrave;ng. Chị Tuyến cho biết, n&oacute;i c&ocirc;ng ty cho thu&ecirc; l&agrave; kh&ocirc;ng đ&uacute;ng bởi v&igrave; số tiền phải đ&oacute;ng 250 ngh&igrave;n đồng mỗi th&aacute;ng chỉ l&agrave; ph&iacute; dịch vụ (vệ sinh, tiền điện nước v&agrave; bảo vệ...). Một căn hộ thế n&agrave;y, b&ecirc;n ngo&agrave;i gi&aacute; thu&ecirc; &iacute;t nhất cũng gấp năm lần như thế. Chị vừa n&oacute;i, vừa dẫn t&ocirc;i đi thăm căn hộ. Căn hộ rộng chừng 50 m2, ba ph&ograve;ng, kh&eacute;p k&iacute;n, d&agrave;nh cho hộ gia đ&igrave;nh. Chị Tuyến cho biết th&ecirc;m, vợ chồng chị đều l&agrave;m ở C&ocirc;ng ty giấy S&agrave;i G&ograve;n. Thu nhập của hai người cỡ s&aacute;u triệu đồng/th&aacute;ng. Trừ tiền ăn, tiền học cho con, mỗi th&aacute;ng để ra được v&agrave;i trăm ngh&igrave;n đồng phụ gi&uacute;p gia đ&igrave;nh. Nếu phải thu&ecirc; nh&agrave;&nbsp; b&ecirc;n ngo&agrave;i chắc kh&ocirc;ng đủ chi ti&ecirc;u.<br />\r\n<br />\r\nS&aacute;t b&ecirc;n căn hộ chị Tuyến l&agrave; hộ anh V&otilde; &ETH;ức To&agrave;n v&agrave; chị Phạm Thị Hồng Nga. Anh To&agrave;n cũng l&agrave; c&ocirc;ng nh&acirc;n C&ocirc;ng ty giấy S&agrave;i G&ograve;n, c&ograve;n chị Nga đang ở nh&agrave; tr&ocirc;ng con nhỏ. Chị Nga qu&ecirc; ở Kh&aacute;nh H&ograve;a, c&ograve;n anh To&agrave;n ở Quảng Ng&atilde;i, v&agrave;o đ&acirc;y đ&atilde; bốn năm. Căn hộ n&agrave;y c&ocirc;ng ty &quot;ph&acirc;n&quot; cho chồng chị. C&oacute; nơi ở đ&agrave;ng ho&agrave;ng vợ chồng chị mới quyết định sinh em b&eacute;.<br />\r\n<br />\r\nTheo anh Trần &ETH;&igrave;nh Ho&agrave;ng, Trưởng ph&ograve;ng H&agrave;nh ch&iacute;nh - C&ocirc;ng ty giấy S&agrave;i G&ograve;n (KCN Mỹ Xu&acirc;n A):&nbsp; khu nh&agrave; ở d&agrave;nh cho c&ocirc;ng nh&acirc;n của c&ocirc;ng ty c&ograve;n c&oacute; cả s&acirc;n thể thao v&agrave; một thư viện nhỏ. &ETH;ược sự gi&uacute;p đỡ của Thư viện tỉnh B&agrave; Rịa - Vũng T&agrave;u, đến nay, thư viện của c&ocirc;ng ty đ&atilde; c&oacute; h&agrave;ng trăm đầu s&aacute;ch phục vụ c&ocirc;ng nh&acirc;n v&agrave; con em người lao động. Sắp tới, do nhu cầu mở rộng sản xuất, c&ocirc;ng ty tiếp tục triển khai x&acirc;y dựng th&ecirc;m hai khu nh&agrave; mới, bao gồm cả nh&agrave; giữ trẻ, để phục vụ người lao động. &quot;Kể từ khi chuyển v&agrave;o ở, &yacute; thức, tr&aacute;ch nhiệm của c&ocirc;ng nh&acirc;n được n&acirc;ng l&ecirc;n, hiệu quả lao động cũng v&igrave; thế m&agrave; tăng&quot;. Anh Ho&agrave;ng cho biết.<br />\r\n<br />\r\nQua khảo s&aacute;t sơ bộ, trong số hơn 20 ngh&igrave;n lao động đang l&agrave;m việc tại 12 KCN tr&ecirc;n địa b&agrave;n tỉnh B&agrave; Rịa - Vũng T&agrave;u, c&oacute; hơn 60% l&agrave; lao động đến từ tỉnh kh&aacute;c. Nhằm gi&uacute;p người lao động y&ecirc;n t&acirc;m sản xuất, tỉnh đ&atilde; ban h&agrave;nh nhiều ch&iacute;nh s&aacute;ch hỗ trợ, n&acirc;ng cao đời sống vật chất, tinh thần cho người lao động. Theo Chủ tịch UBND tỉnh B&agrave; Rịa - Vũng T&agrave;u Trần Minh Sanh, do số lượng lao động ngoại tỉnh chiếm tỷ lệ cao, n&ecirc;n nhu cầu về nh&agrave; ở của c&ocirc;ng nh&acirc;n tại c&aacute;c KCN rất lớn. &ETH;&aacute;p ứng nhu cầu ch&iacute;nh đ&aacute;ng, bức thiết n&agrave;y của người lao động, Tỉnh ủy, UBND tỉnh B&agrave; Rịa - Vũng T&agrave;u đ&atilde; sớm x&acirc;y dựng cơ chế, ch&iacute;nh s&aacute;ch ưu đ&atilde;i, khuyến kh&iacute;ch c&aacute;c nh&agrave; đầu tư tham gia x&acirc;y dựng nh&agrave; ở cho người lao động.<br />\r\n<br />\r\nTheo đ&oacute;, tỉnh x&aacute;c định quy hoạch KCN phải gắn với quy hoạch khu nh&agrave; ở cho c&ocirc;ng nh&acirc;n, bao gồm cả c&aacute;c c&ocirc;ng tr&igrave;nh ph&uacute;c lợi như trường mẫu gi&aacute;o, khu vui chơi giải tr&iacute;, thể thao, y tế v&agrave; c&aacute;c dịch vụ kh&aacute;c. Coi x&acirc;y dựng nh&agrave; ở cho c&ocirc;ng nh&acirc;n l&agrave; ti&ecirc;u ch&iacute; để x&eacute;t duyệt dự &aacute;n đầu tư cho c&aacute;c doanh nghiệp. &ETH;ến nay, trong số hơn 100 đơn vị đầu tư tại 12 KCN tr&ecirc;n địa b&agrave;n tỉnh c&oacute; h&agrave;ng chục doanh nghiệp triển khai c&aacute;c dự &aacute;n x&acirc;y dựng nh&agrave; ở cho c&ocirc;ng nh&acirc;n, từng bước đ&aacute;p ứng nhu cầu bức thiết về nh&agrave; ở của người lao động.<br />\r\n<br />\r\n<strong>Bảo vệ quyền v&agrave; lợi &iacute;ch hợp ph&aacute;p của người lao động</strong><br />\r\n<br />\r\nSố lượng c&ocirc;ng nh&acirc;n l&agrave;m việc tại c&aacute;c KCN tr&ecirc;n địa b&agrave;n tỉnh B&agrave; Rịa- Vũng T&agrave;u kh&ocirc;ng đ&ocirc;ng so với nhiều địa phương như: TP Hồ Ch&iacute; Minh, &ETH;ồng Nai, B&igrave;nh Dương... nhưng kh&ocirc;ng v&igrave; thế m&agrave; c&ocirc;ng t&aacute;c giải quyết c&aacute;c tranh chấp, bảo vệ quyền v&agrave; lợi &iacute;ch hợp ph&aacute;p của người lao động ở đ&acirc;y bị xem nhẹ. Theo Chủ tịch L&ETH;L&ETH; tỉnh B&agrave; Rịa - Vũng T&agrave;u Trương Minh Thủy, thời gian qua, trước những t&aacute;c động ti&ecirc;u cực của cuộc khủng hoảng kinh tế to&agrave;n cầu, nhiều doanh nghiệp tr&ecirc;n địa b&agrave;n tỉnh gặp kh&oacute; khăn trong kinh doanh, sản xuất. Một số doanh nghiệp nợ lương, nợ bảo hiểm x&atilde; hội (BHXH) của người lao động, l&agrave;m ph&aacute;t sinh m&acirc;u thuẫn giữa người sử dụng lao động v&agrave; người lao động.<br />\r\n<br />\r\n&ETH;ể bảo vệ quyền v&agrave; lợi &iacute;ch ch&iacute;nh đ&aacute;ng của người lao động, Li&ecirc;n đo&agrave;n Lao động (L&ETH;L&ETH;) tỉnh c&ugrave;ng c&aacute;c ban, ng&agrave;nh li&ecirc;n quan đ&atilde; tổ chức tuy&ecirc;n truyền, phổ biến c&aacute;c quy định của ph&aacute;p luật về lao động; đổi mới, n&acirc;ng cao chất lượng, hiệu quả phối hợp; tổ chức kiểm tra, gi&aacute;m s&aacute;t, kịp thời uốn nắn những sai phạm trong việc thực hiện ph&aacute;p luật về lao động; chủ động nắm t&igrave;nh h&igrave;nh, t&acirc;m tư, nguyện vọng của người lao động, kiến nghị, y&ecirc;u cầu doanh nghiệp, người sử dụng lao động chấp h&agrave;nh nghi&ecirc;m c&aacute;c quy định của ph&aacute;p luật. Nhờ đ&oacute;, đ&atilde; can thiệp, giải quyết hiệu quả c&aacute;c tranh chấp lao động ph&aacute;t sinh, hạn chế đến mức thấp nhất c&aacute;c vụ đ&igrave;nh c&ocirc;ng, l&atilde;n c&ocirc;ng tự ph&aacute;t. <br />\r\n<br />\r\nAnh L&ecirc; Xu&acirc;n H&ugrave;ng, c&ocirc;ng nh&acirc;n C&ocirc;ng ty Amigos (KCN &ETH;&ocirc;ng Xuy&ecirc;n, TP Vũng T&agrave;u) cho biết, ngay khi nhận được th&ocirc;ng tin về việc C&ocirc;ng ty Amigos sa thải lao động, đại diện L&ETH;L&ETH;, Sở L&ETH;-TB v&agrave; XH, Ban Quản l&yacute; c&aacute;c KCN tỉnh B&agrave; Rịa - Vũng T&agrave;u đ&atilde; c&oacute; mặt, hướng dẫn, phổ biến c&aacute;c quy định, ch&iacute;nh s&aacute;ch d&agrave;nh cho người lao động bị mất việc l&agrave;m, đồng thời tổ chức khảo s&aacute;t, đ&aacute;nh gi&aacute;, t&igrave;m hiểu nguyện vọng, qua đ&oacute; tư vấn, hướng nghiệp gi&uacute;p người lao động chuyển đổi nghề nghiệp, nhanh ch&oacute;ng t&igrave;m việc l&agrave;m mới.&nbsp; Ph&oacute; Chủ tịch C&ocirc;ng đo&agrave;n C&ocirc;ng ty Amigos&nbsp; Nguyễn Văn Cơ khẳng định:&nbsp; D&ugrave; C&ocirc;ng ty Amigos vẫn nợ lương v&agrave; BHXH của người lao động, nhưng trước sự quan t&acirc;m, động vi&ecirc;n của c&aacute;c ban, ng&agrave;nh hữu quan, ch&uacute;ng t&ocirc;i lu&ocirc;n cảm thấy ấm l&ograve;ng. Bởi hơn bao giờ hết, đ&acirc;y mới l&agrave; l&uacute;c người lao động cần sự hướng dẫn, quan t&acirc;m của tổ chức c&ocirc;ng đo&agrave;n v&agrave; c&aacute;c ban, ng&agrave;nh, đo&agrave;n thể.<br />\r\n<br />\r\nTheo đ&aacute;nh gi&aacute; của L&ETH;L&ETH; tỉnh B&agrave; Rịa - Vũng T&agrave;u, những năm qua, trong mối quan hệ lao động ở c&aacute;c doanh nghiệp c&oacute; nảy sinh một số tranh chấp phức tạp, t&aacute;c động tới việc l&agrave;m, đời sống v&agrave; t&acirc;m trạng người lao động. Nguy&ecirc;n nh&acirc;n chủ yếu vẫn l&agrave; do doanh nghiệp kh&ocirc;ng thực hiện đ&uacute;ng c&aacute;c quy định ph&aacute;p luật về lao động, kh&ocirc;ng c&ocirc;ng khai minh bạch, chấm dứt hợp đồng lao động tr&aacute;i ph&aacute;p luật; kh&ocirc;ng thực hiện đầy đủ chế độ bảo hiểm x&atilde; hội; tăng giờ, tăng ca t&ugrave;y tiện; c&aacute; biệt c&oacute; nơi c&ograve;n x&uacute;c phạm nh&acirc;n phẩm v&agrave; danh dự người lao động... &ETH;ể giải quyết t&igrave;nh trạng n&agrave;y đ&ograve;i hỏi c&aacute;c c&aacute;n bộ c&ocirc;ng đo&agrave;n phải am hiểu ph&aacute;p luật, c&oacute; tr&igrave;nh độ nghiệp vụ vững v&agrave;ng, c&oacute; khả năng tham gia gi&aacute;m s&aacute;t việc thực hiện c&aacute;c quy định về ph&aacute;p luật lao động tại doanh nghiệp.<br />\r\n<br />\r\nTuy nhi&ecirc;n, theo Chủ tịch L&ETH;L&ETH; tỉnh Trương Minh Thủy, c&aacute;n bộ c&ocirc;ng đo&agrave;n cơ sở hiện đang chịu sự chi phối rất lớn từ chủ doanh nghiệp, bởi họ trực tiếp k&yacute; hợp đồng lao động với chủ doanh nghiệp, thực hiện nhiệm vụ của chủ doanh nghiệp giao, lương do doanh nghiệp trả, n&ecirc;n kh&ocirc;ng tr&aacute;nh khỏi những hạn chế, kh&oacute; khăn trong thực thi nhiệm vụ của m&igrave;nh. Thực tế đ&atilde; xảy ra t&igrave;nh trạng c&aacute;n bộ c&ocirc;ng đo&agrave;n cơ sở bị chủ doanh nghiệp g&acirc;y kh&oacute; dễ hoặc sa thải do bảo vệ quyền v&agrave; lợi &iacute;ch hợp ph&aacute;p của người lao động. Vậy n&ecirc;n, để c&aacute;n bộ c&ocirc;ng đo&agrave;n v&agrave; tổ chức c&ocirc;ng đo&agrave;n thật sự l&agrave; chỗ dựa tin cậy, l&agrave; người bạn đồng h&agrave;nh của người lao động, Nh&agrave; nước cần sớm c&oacute; c&aacute;c cơ chế, ch&iacute;nh s&aacute;ch ph&ugrave; hợp thực tế, x&aacute;c định r&otilde; vị tr&iacute;, vai tr&ograve;, quyền v&agrave; nghĩa vụ của đội ngũ c&aacute;n bộ c&ocirc;ng đo&agrave;n cơ sở, x&acirc;y dựng một h&agrave;nh lang ph&aacute;p l&yacute; đủ mạnh để bảo vệ c&aacute;n bộ c&ocirc;ng đo&agrave;n trước sự s&aacute;ch nhiễu, tr&ugrave; dập, nếu c&oacute;, của chủ doanh nghiệp, nhằm bảo vệ c&aacute;c quyền lợi ch&iacute;nh đ&aacute;ng v&agrave; hợp ph&aacute;p của người lao động.<br />\r\n<ul>\r\n    <li><em><strong>Theo Nh&acirc;n D&acirc;n</strong></em></li>\r\n</ul>', 1, 1247999039, 1, 63, 0),
(19, 2, 'Đẩy mạnh công tác An toàn Vệ sinh lao động trong các Doanh nghiệp ', 'Thời gian qua, công tác an toàn vệ sinh lao động (ATVSLĐ) trong các doanh nghiệp (DN) trên địa bàn thành phố luôn được các cấp, các ngành triển khai đồng bộ, đáp ứng với yêu cầu cải thiện điều kiện làm việc, hạn chế tai nạn lao động, bệnh nghề nghiệp, phòng chống cháy nổ ở các DN. Những tháng đầu năm 2009, công tác này được thể hiện rõ nét trên các hoạt động nâng cao nhận thức, hành động của người lao động và người sử dụng lao động trong các DN.', '19.jpg', '', 'H&agrave;ng ngh&igrave;n tờ rơi, tranh ảnh tuy&ecirc;n truyền c&aacute;c nội dung về cảnh b&aacute;o nguy cơ khi l&agrave;m việc tr&ecirc;n cao, cảnh b&aacute;o nguy hiểm điện v&agrave; c&aacute;c phương ph&aacute;p l&agrave;m việc an to&agrave;n, v.v... được ph&aacute;t h&agrave;nh đến tận tay CN-LĐ, c&aacute;c pa-n&ocirc;, &aacute;p-ph&iacute;ch dựng ở c&aacute;c khu c&ocirc;ng nghiệp do LĐLĐ th&agrave;nh phố thực hiện. Việc tuy&ecirc;n truyền miệng, hội thi về c&ocirc;ng t&aacute;c ATVSLĐ được nhiều DN triển khai, th&ocirc;ng qua những buổi n&oacute;i chuyện chuy&ecirc;n đề, hội thi &ldquo;An to&agrave;n Vệ sinh vi&ecirc;n giỏi&rdquo;, thu h&uacute;t h&agrave;ng ngh&igrave;n CN-LĐ tham gia. <br />\r\n<br />\r\nB&ecirc;n cạnh đ&oacute;, c&aacute;c hoạt động trồng c&acirc;y xanh, chăm lo sức khỏe cho CN-LĐ trong DN cũng được triển khai. Hơn 2000 c&acirc;y xanh, gần 10.000 m&eacute;t vu&ocirc;ng cỏ được trồng chung quanh c&aacute;c nh&agrave; xưởng, gần 3.000 CN-LĐ được kh&aacute;m sức khỏe định kỳ, được hưởng c&aacute;c chế độ điều trị c&aacute;c loại bệnh, kể cả bệnh nghề nghiệp.<br />\r\n<br />\r\n<div style="text-align: center;"><img width="380" height="285" src="/uploads/image/Tin-tuc/Vesinh_antoan_laodong.jpg" alt="" /><br />\r\n&nbsp;</div>\r\nN&eacute;t mới của c&ocirc;ng t&aacute;c ATVSLĐ trong c&aacute;c DN thời gian gần đ&acirc;y, l&agrave; việc tăng cường c&ocirc;ng t&aacute;c kiểm tra ở cấp th&agrave;nh phố v&agrave; ngay ở cơ sở, tạo bước chuyển mạnh cho c&aacute;c DN thực hiện c&oacute; hiệu quả. Ở cấp th&agrave;nh phố, Sở LĐ-TB-XH v&agrave; LĐLĐ th&agrave;nh phố phối hợp tổ chức kiểm tra ở 15 DN, cơ sở sản xuất kinh doanh c&oacute; nguy cơ xảy ra tai nạn lao động, ch&aacute;y nổ. <br />\r\n<br />\r\nQua đ&oacute;, đo&agrave;n kiểm tra đ&atilde; g&oacute;p &yacute;, y&ecirc;u cầu chấn chỉnh kịp thời t&igrave;nh trạng thiếu an to&agrave;n, kh&ocirc;ng bảo đảm vệ sinh c&ocirc;ng nghiệp v&agrave; kiện to&agrave;n, củng cố mạng lưới An to&agrave;n vệ sinh vi&ecirc;n. Ri&ecirc;ng ở từng DN đ&atilde; c&oacute; nhiều nỗ lực v&agrave; chủ động thực hiện. Gần 60 đơn vị tự tổ chức đo m&ocirc;i trường về độ bụi, độ n&oacute;ng, tiếng ồn v&agrave; gần 180 đơn vị tự kiểm tra to&agrave;n diện c&aacute;c điều kiện l&agrave;m việc. Qua đ&oacute;, từng DN r&uacute;t ra những kinh nghiệm v&agrave; tiến h&agrave;nh khắc phục, sửa chữa ph&ugrave; hợp, g&oacute;p phần cải thiện một bước điều kiện l&agrave;m việc, tr&aacute;nh những điều bất trắc ảnh hưởng đến t&iacute;nh mạng, sức khỏe của người lao động.<br />\r\n<br />\r\nTuy nhi&ecirc;n, c&ocirc;ng t&aacute;c ATVSLĐ trong DN tr&ecirc;n địa b&agrave;n th&agrave;nh phố vẫn c&ograve;n nhiều mặt hạn chế, nhất l&agrave; ở c&aacute;c doanh nghiệp d&acirc;n doanh chưa quan t&acirc;m hoặc nhận thức chưa đầy đủ tầm quan trọng của c&ocirc;ng t&aacute;c n&agrave;y. V&igrave; vậy, ở c&aacute;c DN n&agrave;y người lao động phải l&agrave;m việc trong điều kiện thiếu an to&agrave;n, n&oacute;ng bức, tiếng ồn vượt qu&aacute; quy định. Mặt kh&aacute;c, người lao động trong nhiều DN chưa được huấn luyện về ATVSLĐ chiếm tỷ lệ c&ograve;n cao, do vậy thiếu kiến thức để l&agrave;m việc an to&agrave;n, bảo vệ sức khỏe bản th&acirc;n. Qua đ&oacute;, c&aacute;c sự cố về tai nạn lao động vẫn c&ograve;n xảy ra v&agrave; nguy cơ ch&aacute;y nổ vẫn c&ograve;n tiềm ẩn ở một số DN tr&ecirc;n địa b&agrave;n th&agrave;nh phố.<br />\r\n<br />\r\nMong rằng, c&aacute;c cấp, c&aacute;c ng&agrave;nh chức năng, nhất l&agrave; ở từng DN c&oacute; biện ph&aacute;p hữu hiệu đẩy mạnh c&ocirc;ng t&aacute;c ATVSLĐ, nhằm bảo đảm cho mọi CN-LĐ trong DN đều c&oacute; điều kiện l&agrave;m việc an to&agrave;n, bảo đảm sức khỏe l&acirc;u d&agrave;i.<br />\r\n<ul>\r\n    <li><em><strong>Theo baodanang.vn</strong></em></li>\r\n</ul>', 1, 1247999399, 1, 46, 0),
(20, 2, 'Vì mục tiêu an toàn chạy tàu', 'Với phương châm “Giữ vững công lệnh tốc độ chạy tàu, không có trở ngại chạy tàu do chủ quan gây ra”, 6 tháng đầu năm 2009, Đội Giám sát an toàn đường sắt Quảng Nam- Đà Nẵng (GSAT ĐS QNĐN) đã tổ chức kiểm tra 242 lượt, làm công tác an toàn và hành lang ATĐS, phối hợp với chính quyền phường Hòa Hiệp Nam giải tỏa 50m2 lều quán, 40m hàng rào xây; làm 170m hàng rào ray ngăn lấn chiếm hành lang để họp chợ từ km780+230 đến km780+420 và 260m hàng rào ray bảo vệ từ km 897+538- km897+800; lập biên bản 1 trường hợp đối với đơn vị thông tin quân đội chôn cáp quang trái phép trong HL ATGTĐS tại km 785+320. ', '20.jpg', '', 'Bằng mối quan hệ chặt chẽ với ch&iacute;nh quyền địa phương qua sổ Hợp đồng bảo vệ ĐS, Đội GSAT đ&atilde; cương quyết giải tỏa c&aacute;c trường hợp t&aacute;i lấn chiếm HLATGTĐS, bảo vệ h&agrave;nh lang đ&atilde; được giải tỏa, đảm bảo tầm nh&igrave;n c&aacute;c đường ngang kh&ocirc;ng g&aacute;c. C&ocirc;ng ty QLĐS QNĐN thực hiện nghi&ecirc;m t&uacute;c Chỉ thị 1951/ĐS-CSHT của Ban QLCSHT ĐS về việc &ldquo;Đảm bảo ATGTĐS&rdquo;; tăng cường c&ocirc;ng t&aacute;c kiểm tra kỹ thuật cầu, đường, hầm, ghi; ch&uacute; &yacute; c&aacute;c điểm xung yếu, kiểm tra sửa chữa kịp thời c&aacute;c điểm xấu, g&acirc;y x&oacute;c l&oacute;c đảm bảo an to&agrave;n chạy t&agrave;u (ATCT). Kiểm tra nghi&ecirc;m ngặt lực lượng l&agrave;m c&ocirc;ng t&aacute;c an to&agrave;n theo quy định 1779/CT-ĐS của Tổng gi&aacute;m đốc Tổng c&ocirc;ng ty ĐSVN.<br />\r\n<br />\r\n<div style="text-align: center;"><img width="400" height="300" src="/uploads/image/Tin-tuc/Antoan_chaytau_01.JPG" alt="" /><br />\r\n&nbsp;</div>\r\nĐội trưởng Đội GSAT Nguyễn Thanh Chiến cho biết, 6 th&aacute;ng đầu năm, t&igrave;nh h&igrave;nh ATCT tr&ecirc;n tuyến được đảm bảo tuyệt đối, kh&ocirc;ng xảy ra vụ việc trở ngại do nguy&ecirc;n nh&acirc;n chủ quan g&acirc;y ra. An to&agrave;n lao động sinh hoạt được đảm bảo, trạng th&aacute;i kỹ thuật cầu đường được ổn định. Qua c&aacute;c đợt kiểm tra bằng m&aacute;y Halat v&agrave; m&aacute;y Matisa đều cho kết quả tốt. Đ&atilde; ph&aacute;t hiện v&agrave; xử l&yacute; nhanh ch&oacute;ng kịp thời c&aacute;c sự cố như sạt lở đường do mưa lũ, tai nạn t&agrave;u trật b&aacute;nh hay tai nạn do &ocirc; t&ocirc; t&ocirc;ng v&agrave;o ĐS g&acirc;y lệch phương hướng đường...<br />\r\n<br />\r\nVề duy tu sửa chữa thường xuy&ecirc;n nhằm đảm bảo ATCT, &ocirc;ng Nguyễn Văn T&yacute;- Gi&aacute;m đốc C&ocirc;ng ty QLĐS QNĐN cho biết, 2 qu&yacute; đầu năm C&ocirc;ng ty đ&atilde; tiến h&agrave;nh đại tu s&agrave;ng đ&aacute; ph&aacute; cốt, thay ray, thay t&agrave; vẹt BTDƯL từ km 801+800- km 803+400, ho&agrave;n th&agrave;nh ước đạt 80%. Đ&atilde; ho&agrave;n th&agrave;nh 11 đường ngang theo QĐ 1856. Ho&agrave;n th&agrave;nh c&ocirc;ng tr&igrave;nh cảnh b&aacute;o tự động đường ngang km 808+370 v&agrave; đường ngang 828+086. Thường xuy&ecirc;n tăng cường kiểm tra kỹ thuật cầu đường, chỉ đạo c&ocirc;ng t&aacute;c sửa xấu chống x&oacute;c lắc.<br />\r\n<br />\r\n<div style="text-align: center;"><img width="400" height="300" src="/uploads/image/Tin-tuc/Antoan_chaytau_02.JPG" alt="" /><br />\r\n&nbsp;</div>\r\nBước sang 6 th&aacute;ng cuối năm, đặc biệt l&agrave; từ cuối qu&yacute; III trở đi l&agrave; thời gian cao điểm của m&ugrave;a mưa b&atilde;o tại miền Trung. Gi&aacute;m đốc Nguyễn Văn T&yacute; đ&atilde; &ldquo;ra lệnh&rdquo; tất cả c&aacute;n bộ kỹ thuật, đội trưởng, cung trưởng phải thường xuy&ecirc;n kiểm tra nắm chắc trạng th&aacute;i kỹ thuật cầu đường tr&ecirc;n tuyến, kịp thời ph&aacute;t hiện sửa chữa điểm xấu ph&aacute;t sinh. Lu&ocirc;n lu&ocirc;n đốc th&uacute;c kiểm tra lực lượng c&ocirc;ng t&aacute;c an to&agrave;n, tăng ban tuần đường ở c&aacute;c khu vực trọng yếu trong m&ugrave;a b&atilde;o lũ, nhất l&agrave; kiểm tra theo d&otilde;i c&aacute;c c&ocirc;ng tr&igrave;nh gia cố đ&egrave;o Hải V&acirc;n.<br />\r\n<br />\r\nSong song C&ocirc;ng ty sẽ đẩy mạnh c&ocirc;ng t&aacute;c duy tu, sửa chữa, gấp r&uacute;t ho&agrave;n th&agrave;nh c&aacute;c c&ocirc;ng tr&igrave;nh đang l&agrave;m dở dang t&iacute;ch cực gia cố c&aacute;c điểm xung yếu. Chuẩn bị vật tư, nh&acirc;n lực, phương &aacute;n cứu chữa đảm bảo, đảm bảo an to&agrave;n GT trong mọi t&igrave;nh huống.<br />\r\n<br />\r\nVới những g&igrave; đ&atilde; đạt được qua 6 th&aacute;ng đầu năm sẽ l&agrave; đ&ograve;n bẩy, l&agrave; sức bật cho C&ocirc;ng ty QLĐS QNĐN l&agrave;m tốt hơn nhiệm vụ 6 th&aacute;ng cuối năm trong c&ocirc;ng t&aacute;c giữ vững ATCT, đảm bảo an to&agrave;n HLATGTĐS qua địa b&agrave;n QN- ĐN.\r\n<ul>\r\n    <li><em><strong>Theo Giaothongvantai.com.vn</strong></em></li>\r\n</ul>', 1, 1247999606, 1, 58, 0),
(21, 2, 'Đeo khẩu trang không đúng cách khó phòng được cúm', 'Khẩu trang ngả màu bẩn không thay, thi thoảng kéo xuống cằm để cười nói, đeo 2 khẩu trang nhưng vẫn hở mũi…, là những sai lầm thường thấy trong cách phòng bệnh hiện nay của nhiều người giữa cơn bão H1N1.', '21.jpg', '', '<div class="\\&quot;\\\\&quot;\\\\\\\\&quot;monograph_special_title\\\\\\\\&quot;\\\\&quot;\\&quot;"><strong><span><span>Đeo khẩu trang kh&ocirc;ng đ&uacute;ng c&aacute;ch kh&oacute; ph&ograve;ng được c&uacute;m</span></span></strong></div>\r\n<br />\r\n<span><em>B&agrave;i viết cập nhật l&uacute;c: 02:53 ng&agrave;y 30/07/2009</em> </span>\r\n<div class="\\&quot;\\\\&quot;\\\\\\\\&quot;monograph_special_content\\\\\\\\&quot;\\\\&quot;\\&quot;">\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Lead\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>Khẩu trang ngả m&agrave;u bẩn kh&ocirc;ng thay, thi thoảng k&eacute;o xuống cằm để cười n&oacute;i, đeo 2 khẩu trang nhưng vẫn hở mũi&hellip;, l&agrave; những sai lầm thường thấy trong c&aacute;ch ph&ograve;ng bệnh hiện nay của nhiều người giữa cơn b&atilde;o H1N1.<br />\r\n</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>V&agrave;o bệnh viện sợ bị l&acirc;y c&uacute;m, nhiều người d&ugrave;ng c&ugrave;ng l&uacute;c 2-3 khẩu trang y tế. Thế nhưng khi chuẩn bị v&agrave;o khu vực c&oacute; thể l&acirc;y nhiễm, kiểm tra, c&aacute;c b&aacute;c sĩ mới ph&aacute;t hiện khẩu trang đ&atilde; bị đeo ngược.</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>&ldquo;Thay v&igrave; đặt m&eacute;p khẩu trang c&oacute; thanh ch&igrave; l&ecirc;n tr&ecirc;n để giữ k&iacute;n sống mũi họ lại đeo ngược phần m&eacute;p c&oacute; thanh ch&igrave; xuống cằm. V&igrave; thế, kh&ocirc;ng kh&iacute; vẫn v&ocirc; tư bay v&agrave;o&rdquo;, một b&aacute;c sĩ Bệnh viện Nhiệt đới TP HCM n&oacute;i.</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>C&aacute;c điều dưỡng vi&ecirc;n tại Bệnh viện Phạm Ngọc Thạch th&igrave; cho hay, nhiều trường hợp &ldquo;chỉ cần nh&igrave;n thấy chiếc khẩu trang y tế m&agrave;u xanh nhạt chuyển sang v&agrave;ng ố m&agrave; người nu&ocirc;i bệnh đang đeo đủ biết n&oacute; c&oacute; thể chứa đầy vi khuẩn. Vậy m&agrave; khi được khuy&ecirc;n n&ecirc;n thay khẩu trang, một số người ng&acirc;y ng&ocirc; bảo: &lsquo;cứ tưởng c&oacute; khẩu trang che miệng che mũi, l&agrave; được&rsquo;&rdquo;.</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>Tại khoa Nhiễm Bệnh viện Nhi Đồng 2, TP HCM, theo c&aacute;c b&aacute;c sĩ, nhiều phụ huynh đeo khẩu trang cho con đang bị bệnh H1N1 nhưng kh&ocirc;ng thường xuy&ecirc;n ch&uacute; &yacute; để trẻ hiếu động d&ugrave;ng tay k&eacute;o l&ecirc;n k&eacute;o xuống n&ecirc;n khẩu trang cũng kh&ocirc;ng c&ograve;n t&aacute;c dụng g&igrave;.</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>Khảo s&aacute;t của <em>VnExpress.net</em> tại một số điểm c&ocirc;ng cộng v&agrave; c&aacute;c c&ocirc;ng sở buộc nh&acirc;n vi&ecirc;n đeo khẩu trang mấy ng&agrave;y qua cho thấy, kh&ocirc;ng &iacute;t người d&ugrave;ng khẩu trang tr&ecirc;n tinh thần &ldquo;đeo cho c&oacute;&rdquo;.</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>Cũng với khẩu trang y tế tr&ecirc;n mặt, song nhiều người vẫn v&ocirc; tư k&eacute;o l&oacute; phần miệng ra ngo&agrave;i để n&oacute;i chuyện với người kh&aacute;c. Một số người khi hắt hơi th&igrave; lại k&eacute;o khẩu trang ra để hắt thật to. Cũng c&oacute; người do đeo khẩu trang kh&ocirc;ng quen, cảm thấy kh&oacute; thở n&ecirc;n thường xuy&ecirc;n k&eacute;o lu&ocirc;n xuống dưới, chỉ che miệng nhưng để hở mũi.<br />\r\n<img width="450" height="350" alt="" src="/uploads/image/1248940405_q.jpg" /></span></p>\r\n<table border="\\&quot;\\\\&quot;\\\\\\\\&quot;0\\\\\\\\&quot;\\\\&quot;\\&quot;" cellspacing="\\&quot;\\\\&quot;\\\\\\\\&quot;0\\\\\\\\&quot;\\\\&quot;\\&quot;" cellpadding="\\&quot;\\\\&quot;\\\\\\\\&quot;3\\\\\\\\&quot;\\\\&quot;\\&quot;">\r\n    <tbody>\r\n        <tr>\r\n            <td><img border="\\&quot;\\\\&quot;\\\\\\\\&quot;1\\\\\\\\&quot;\\\\&quot;\\&quot;" alt="\\&quot;\\\\&quot;\\\\\\\\&quot;\\\\\\\\&quot;\\\\&quot;\\&quot;" width="\\&quot;\\\\&quot;\\\\\\\\&quot;450\\\\\\\\&quot;\\\\&quot;\\&quot;" height="\\&quot;\\\\&quot;\\\\\\\\&quot;350\\\\\\\\&quot;\\\\&quot;\\&quot;" src="\\&quot;\\\\&quot;\\\\\\\\&quot;http://media.tinmoi.vn/2009/07/30/1248940405_q.jpg\\\\\\\\&quot;\\\\&quot;\\&quot;" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="\\&quot;\\\\&quot;\\\\\\\\&quot;Image\\\\\\\\&quot;\\\\&quot;\\&quot;">Nhiều doanh nghiệp y&ecirc;u cầu nh&acirc;n vi&ecirc;n mang khẩu trang. Ảnh: <em>Thi&ecirc;n Chương.</em></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>Theo c&aacute;c b&aacute;c sĩ chuy&ecirc;n khoa Nhiễm, việc d&ugrave;ng khẩu trang kh&ocirc;ng đ&uacute;ng c&aacute;ch chẳng những kh&ocirc;ng ph&ograve;ng ngừa được m&agrave; c&ograve;n rước bệnh v&agrave;o th&acirc;n do suy nghĩ chủ quan &ldquo;m&igrave;nh đ&atilde; mang khẩu trang rồi, chắc kh&ocirc;ng sao&rdquo;.</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>B&aacute;c sĩ Phan Văn Nghiệm, Trưởng ph&ograve;ng Nghiệp vụ Y, Sở Y tế TP HCM, cũng cho rằng, <strong><span>khẩu trang vải th&ocirc;ng thường vẫn c&oacute; thể ph&ograve;ng bệnh, miễn l&agrave; đeo đ&uacute;ng c&aacute;ch</span></strong>.</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>Với tất cả loại khẩu trang, nguy&ecirc;n tắc đeo, theo b&aacute;c sĩ Nghiệm, l&agrave; phải k&iacute;n v&ugrave;ng mũi v&agrave; miệng sao cho người mang kh&ocirc;ng thể h&iacute;t được mầm bệnh từ b&ecirc;n ngo&agrave;i v&agrave;o v&agrave; cũng kh&ocirc;ng để mầm bệnh từ m&igrave;nh bay ra.</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>Theo hướng dẫn của c&aacute;c điều dưỡng Bệnh viện Bệnh Nhiệt đới, để <span>đạt được độ k&iacute;n, với khẩu trang y tế, người mang cần lưu &yacute; đặt m&eacute;p c&oacute; thanh ch&igrave; l&ecirc;n ph&iacute;a tr&ecirc;n sống mũi. D&ugrave;ng tay b&oacute;p thanh ch&igrave; sao cho &ocirc;m kh&iacute;t v&agrave;o sống mũi.</span> Cần kiểm tra c&aacute;c m&eacute;p khẩu trang ở m&aacute; xem đ&atilde; k&iacute;n cả miệng v&agrave; cằm chưa. Cũng n&ecirc;n kiểm tra xem c&oacute; bị đeo mặt tr&aacute;i quay ra ngo&agrave;i kh&ocirc;ng. Nếu d&ugrave;ng loại khẩu trang c&oacute; d&acirc;y cột, cần siết chặt vừa đủ để tạo độ kh&iacute;t. Tr&aacute;nh c&aacute;ch thắt d&acirc;y hờ hững.</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>Việc d&ugrave;ng khẩu trang chuy&ecirc;n dụng N95 cho cả m&ugrave;a c&uacute;m hoặc d&ugrave;ng khẩu trang y tế nhưng kh&ocirc;ng chịu thay l&agrave; kh&ocirc;ng n&ecirc;n, v&igrave; d&ugrave;ng l&acirc;u ng&agrave;y vi khuẩn sẽ b&aacute;m đầy khẩu trang.</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>Đối với loại khẩu trang y tế th&ocirc;ng thường (m&agrave;u xanh nhạt), chỉ n&ecirc;n d&ugrave;ng một lần (tối đa một ng&agrave;y). Mở khẩu trang n&ecirc;n d&ugrave;ng hai ng&oacute;n tay th&aacute;o cẩn thận. Tr&aacute;nh d&ugrave;ng khẩu trang vừa đeo lau mồ h&ocirc;i, lau tay, hay &ldquo;lưu luyến&rdquo; cho lu&ocirc;n v&agrave;o t&uacute;i quần t&uacute;i &aacute;o. Đặc biệt sau khi v&agrave;o c&aacute;c khu vực c&oacute; nguy cơ l&acirc;y bệnh ở bệnh viện, cần thay ngay khẩu trang v&agrave; cho v&agrave;o th&ugrave;ng r&aacute;c y tế. Tr&aacute;nh t&igrave;nh trạng vứt khẩu trang bừa b&atilde;i v&igrave; c&oacute; thể khiến mầm bệnh ph&aacute;t t&aacute;n.</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>Với khẩu trang vải th&ocirc;ng thường, độ k&iacute;n tuy hạn chế nhưng nếu chọn loại vừa vặn vẫn ngăn được mầm bệnh, song cần phải giặt khẩu trang mỗi ng&agrave;y bằng x&agrave; ph&ograve;ng v&agrave; n&ecirc;n c&oacute; &iacute;t nhất hai chiếc để thay đổi.</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>N&oacute;i về khả năng l&acirc;y nhiễm c&uacute;m H1N1, &ocirc;ng L&ecirc; Trường Giang, Ph&oacute; gi&aacute;m đốc Sở Y tế TP HCM cho rằng, ngo&agrave;i mang khẩu trang để tr&aacute;nh l&acirc;y bệnh qua tiếp x&uacute;c, <strong><span>người d&acirc;n cần ch&uacute; &yacute; nhiều hơn đến việc l&agrave;m th&ocirc;ng tho&aacute;ng nơi sinh hoạt v&agrave; l&agrave;m việc</span></strong>. Thay v&igrave; mở m&aacute;y lạnh trong ph&ograve;ng k&iacute;n, n&ecirc;n mở cửa hoặc v&eacute;n m&agrave;n để &aacute;nh nắng mặt trời c&oacute; thể chiếu v&agrave;o v&agrave; mầm bệnh kh&ocirc;ng khu tr&uacute; trong nơi ở.</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>Cũng theo &ocirc;ng Giang, &aacute;nh nắng mặt trời l&agrave; phương thuốc trị diệt c&uacute;m hiệu quả nhất bởi dưới &aacute;nh nắng, virus H1N1 sẽ bị chết giết trong v&agrave;i ph&uacute;t.</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span>Ri&ecirc;ng phương ph&aacute;p hỗ trợ ph&ograve;ng bệnh, ngo&agrave;i chiếc khẩu trang, c&aacute;c b&aacute;c sĩ Bệnh viện Bệnh Nhiệt Đới TP HCM khuy&ecirc;n người d&acirc;n n&ecirc;n giữ cơ thể khỏe mạnh bằng c&aacute;ch tập thể dục, nghỉ ngơi ăn uống điều độ, uống vitamin C v&agrave; uống thật nhiều nước khi thấy bắt đầu c&oacute; dấu hiệu bị c&uacute;m nhẹ. Việc tự &yacute; mua thuốc chống c&uacute;m Tamiflu uống l&agrave; điều kh&ocirc;ng cần thiết v&igrave; thuốc n&agrave;y kh&ocirc;ng c&oacute; gi&aacute; trị ph&ograve;ng bệnh l&acirc;u d&agrave;i v&agrave; c&oacute; thể g&acirc;y biến chứng.</span></p>\r\n<p class="\\&quot;\\\\&quot;\\\\\\\\&quot;Normal\\\\\\\\&quot;\\\\&quot;\\&quot;"><span><strong>Thi&ecirc;n Chương</strong></span></p>\r\n<p><em>Theo <strong>vnexpress.net</strong></em></p>\r\n</div>', 1, 1248948751, 2, 30, 0);
INSERT INTO `tgp_cms` (`id`, `cat`, `ten`, `chu_thich`, `hinh`, `hinh_note`, `noi_dung`, `hien_thi`, `time`, `user`, `luot_xem`, `noi_bat`) VALUES
(25, 2, 'Bối rối trước giá cả, chủng loại khẩu trang phòng dịch', ' -  Nhiều cửa hàng bán dụng cụ y tế tại TP.HCM và Hà Nội đã “cháy” khẩu trang phòng chống dịch cúm. Trong khi đó, lượng người mua ngày càng nhiều, khiến giá bị “thổi” lên gấp 5 đến 7 lần', '25.jpg', '', '<p>&nbsp;</p>\r\n<p class="MsoNormal" align="justify" style="margin: 0in 0in 0pt"><b style="mso-bidi-font-weight: normal"><font face="Arial"><font size="2">Bối rối &ldquo;chợ&rdquo; khẩu trang ph&ograve;ng dịch<o:p></o:p></font></font></b></p>\r\n<p class="MsoNormal" align="justify" style="margin: 0in 0in 0pt"><b style="mso-bidi-font-weight: normal"><o:p><font size="2" face="Arial">&nbsp;</font></o:p></b></p>\r\n<p class="MsoNormal" align="justify" style="text-align: justify; margin: 0in 0in 0pt"><font size="2" face="Arial">Theo quan s&aacute;t của PV VietNamNet, trong mấy ng&agrave;y qua, rất nhiều người d&acirc;n đ&atilde; t&igrave;m đến khu vực xung quanh Bệnh viện Bạch Mai (H&agrave; Nội) - nơi tập trung nhiều nh&agrave; thuốc&nbsp;để t&igrave;m mua khẩu trang nhằm đối ph&oacute; với c&uacute;m A/H1N1 đang diễn biến phức tạp.<br />\r\n</font><font face="Arial"><font size="2"><br />\r\n</font></font></p>\r\n<p>\r\n<table class="image center" width="400" align="center">\r\n    <tbody>\r\n        <tr>\r\n            <td><font size="2"><img border="2" alt="Khẩu trang ph&ograve;ng dịch? - Ảnh: Ki&ecirc;n Trung" align="middle" width="400" height="217" src="http://images.vietnamnet.vn/dataimages/200907/original/images1838955_3.jpg" /></font></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="image_desc"><font size="2">Một loại khẩu trang&nbsp;đang&nbsp;được&nbsp;b&aacute;n hiện nay tại HN&nbsp;- <em>Ảnh: Ki&ecirc;n Trung</em></font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<br />\r\nHiện nay, thị trường xuất hiện 4 loại khẩu trang để ph&ograve;ng chống c&uacute;m: loại m&agrave;u xanh v&agrave; m&agrave;u đen c&oacute; than hoạt t&iacute;nh, mỏng, sử dụng một lần rồi bỏ đi; khẩu trang than hoạt t&iacute;nh v&agrave; sợi hoạt t&iacute;nh bốn lớp của AnviLife; khẩu trang chuy&ecirc;n dụng chống c&uacute;m h&igrave;nh phễu chuy&ecirc;n dụng, &ocirc;m k&iacute;n miệng&quot;.&nbsp;<span style="mso-spacerun: yes">&nbsp;</span></p>\r\n<p>&nbsp;<font size="2">Nắm bắt được nhu cầu của người d&acirc;n, c&aacute;c cửa h&agrave;ng thi nhau trưng biển b&aacute;n khẩu trang. Gi&aacute; cả của c&aacute;c loại khẩu trang tại c&aacute;c cửa h&agrave;ng thuốc, trung t&acirc;m thiết bị y tế cũng kh&aacute;c nhau, mỗi nơi một loại. </font></p>\r\n<p class="MsoNormal" align="justify" style="text-align: justify; margin: 0in 0in 0pt"><o:p><font size="2" face="Arial">&nbsp;</font></o:p><font size="2"><font face="Arial">Khẩu trang N95<b style="mso-bidi-font-weight: normal"> </b>được coi l&agrave;<b style="mso-bidi-font-weight: normal"> </b>chuy&ecirc;n dụng chống c&uacute;m m&agrave;u trắng, h&igrave;nh phễu &ocirc;m k&iacute;n miệng c&oacute; gi&aacute; từ 30.000 - 50.000đ/c&aacute;i. Tại cửa h&agrave;ng số 111 (Phương Mai), gi&aacute; b&aacute;n khẩu trang n&agrave;y l&agrave; 50.000đ/c&aacute;i.&nbsp;<br />\r\n</font><o:p><font face="Arial">&nbsp;</font></o:p></font></p>\r\n<p class="MsoNormal" align="justify" style="text-align: justify; margin: 0in 0in 0pt"><font size="2" face="Arial">Khẩu trang than hoạt t&iacute;nh v&agrave; sợi than hoạt t&iacute;nh bốn lớp c&oacute; gi&aacute; từ 15 &ndash; 30 &ndash; 35 ng&agrave;n/c&aacute;i. Nhiều người d&acirc;n đ&atilde; bối rối v&igrave; c&oacute; qu&aacute; nhiều loại v&agrave; gi&aacute; cả lửng lơ&hellip; tr&ecirc;n trời.</font></p>\r\n<p>\r\n<table class="image center" width="130" align="center">\r\n    <tbody>\r\n        <tr>\r\n            <td rowspan="3"><font size="2"><img alt="Mu&ocirc;n h&igrave;nh c&aacute;ch quảng c&aacute;o khẩu trang ph&ograve;ng c&uacute;m. Ảnh: Ki&ecirc;n Trung" align="middle" width="380" height="253" src="http://images.vietnamnet.vn/dataimages/200907/original/images1838979_DSC01479.JPG" /></font></td>\r\n            <td><font size="2"><img alt="Mu&ocirc;n h&igrave;nh c&aacute;ch quảng c&aacute;o khẩu trang ph&ograve;ng c&uacute;m. Ảnh: Ki&ecirc;n Trung" align="middle" width="130" height="70" src="http://images.vietnamnet.vn/dataimages/200907/original/images1838962_5.jpg" /></font></td>\r\n        </tr>\r\n        <tr>\r\n            <td><font size="2"><img alt="Mu&ocirc;n h&igrave;nh c&aacute;ch quảng c&aacute;o khẩu trang ph&ograve;ng c&uacute;m. Ảnh: Ki&ecirc;n Trung" align="middle" width="130" height="70" src="http://images.vietnamnet.vn/dataimages/200907/original/images1838963_6.jpg" /></font></td>\r\n        </tr>\r\n        <tr>\r\n            <td><font size="2"><img alt="Mu&ocirc;n h&igrave;nh c&aacute;ch quảng c&aacute;o khẩu trang ph&ograve;ng c&uacute;m. Ảnh: Ki&ecirc;n Trung" align="middle" width="130" height="70" src="http://images.vietnamnet.vn/dataimages/200907/original/images1838964_2.jpg" /></font></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="image_desc" colspan="2">\r\n            <p align="center"><font size="2">Mu&ocirc;n h&igrave;nh c&aacute;ch quảng c&aacute;o khẩu trang ph&ograve;ng c&uacute;m. <em>Ảnh: Ki&ecirc;n Trung</em></font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n<p>&nbsp;<font size="2" face="Arial">B&agrave; Nguyễn Thị Mai (tr&uacute; tại phường Trung H&ograve;a, H&agrave; Nội)&nbsp;t&igrave;m đến phố Phương Mai mua khẩu trang n&oacute;i: &ldquo;T&ocirc;i nghe người ta m&aacute;ch ở đ&acirc;y b&aacute;n khẩu trang ph&ograve;ng chống c&uacute;m. Nhưng phải l&agrave; khẩu trang chuy&ecirc;n dụng của Bộ Y tế chứ kh&ocirc;ng d&aacute;m d&ugrave;ng khẩu trang th&ocirc;ng thường. T&ocirc;i sợ mua phải h&agrave;ng nh&aacute;i, h&agrave;ng giả th&igrave; kh&ocirc;ng c&oacute; t&aacute;c dụng g&igrave; để đối ph&oacute; với c&uacute;m&rdquo;.</font></p>\r\n<p class="MsoNormal" align="justify" style="text-align: justify; margin: 0in 0in 0pt"><o:p><font size="2" face="Arial">&nbsp;</font></o:p>Chị Nguyễn Thục Anh (ngụ quận B&igrave;nh Thạnh, TP.HCM) cho biết: &ldquo;Đ&atilde; đi hơn 2 tiếng đồng hồ để t&igrave;m mua khẩu trang cho 50 nh&acirc;n vi&ecirc;n c&ocirc;ng ty. Thế nhưng đến hiệu thuốc n&agrave;y, quầy thuốc kh&aacute;c đều kh&ocirc;ng c&ograve;n h&agrave;ng. Dịch th&igrave; l&acirc;y như vậy trong khi đ&oacute; khẩu trang t&igrave;m mua &ldquo;đỏ mắt&rdquo;. Như vậy l&agrave;m sao để ph&ograve;ng dịch?&rdquo;.</p>\r\n<p>\r\n<table class="image center" cellspacing="0" cellpadding="3" width="400" align="center" fck_template="imagecontener">\r\n    <tbody>\r\n        <tr>\r\n            <td><font size="2"><img border="2" alt="Nhiều cửa h&agrave;ng b&aacute;n dụng cụ y tế đ&atilde; treo biển hết h&agrave;ng - Ảnh: Tử Trực" width="400" height="225" src="http://images.vietnamnet.vn/dataimages/200907/original/images1839085_h1.jpg" /></font></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="image_desc" align="center"><font size="2">Nhiều cửa h&agrave;ng b&aacute;n dụng cụ y tế đ&atilde; treo biển hết h&agrave;ng - Ảnh: Tử Trực</font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n<p>&nbsp;<font size="2">Khảo s&aacute;t của PV VietNamNet, từ khu vực quận 1, 3, 5, 7&hellip;(TP.HCM) rất nhiều nh&agrave; thuốc, cửa h&agrave;ng dụng cụ y tế đ&atilde; hết h&agrave;ng. Đặc biệt l&agrave; loại khẩu trang giấy (d&ugrave;ng một lần). <br />\r\n<br />\r\nH&agrave;ng chục cửa h&agrave;ng ở đường Cống Quỳnh (Q.1) người t&igrave;m mua loại khẩu trang giấy ra&nbsp;v&agrave;o tấp nập, tuy nhi&ecirc;n đều nhận được c&aacute;i lắc đầu của nh&acirc;n vi&ecirc;n. <br />\r\n<br />\r\nTrong khi đ&oacute;, một số loại khẩu trang kh&aacute;c l&agrave;m bằng than hoạt t&iacute;nh, gi&aacute; thường ng&agrave;y chỉ 2.000đ/1c&aacute;i, đến thời điểm n&agrave;y tăng 8.000 đến 10.000đ/c&aacute;i. Một số loại kh&aacute;c như NC65 đẩy l&ecirc;n 100.000đ/1c&aacute;i v&agrave; hiện tại theo c&aacute;c nh&acirc;n vi&ecirc;n quầy thuốc cũng kh&ocirc;ng c&ograve;n nhiều. <br />\r\n</font></p>\r\n<p>\r\n<table class="image center" cellspacing="0" cellpadding="3" width="400" align="center" fck_template="imagecontener">\r\n    <tbody>\r\n        <tr>\r\n            <td><font size="2"><img border="2" alt="Người d&acirc;n khắp nơi đang &quot;l&ugrave;ng sục&quot; t&igrave;m mua khẩu trang ph&ograve;ng chống c&uacute;m - Ảnh: Tử Trực" width="400" height="225" src="http://images.vietnamnet.vn/dataimages/200907/original/images1839086_h3.jpg" /></font></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="image_desc" align="center">\r\n            <p align="justify"><font size="2">Người d&acirc;n khắp nơi đang &quot;l&ugrave;ng sục&quot; t&igrave;m mua khẩu trang ph&ograve;ng chống c&uacute;m - Ảnh: Tử Trực</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n<p>&nbsp;<font size="2">Nhiều nh&acirc;n vi&ecirc;n c&aacute;c quầy thuốc tại khu chợ Sĩ thuộc Q.10, TP.HCM (khu chợ chuy&ecirc;n b&aacute;n dụng cụ y tế - PV) cho biết: &ldquo;Sở dĩ khẩu trang đang &ldquo;sốt&rdquo; gi&aacute; l&agrave; c&oacute; rất nhiều người được thu&ecirc; đến c&aacute;c cửa h&agrave;ng c&ograve;n khẩu trang để &ldquo;l&ugrave;ng&rdquo; mua số lượng lớn. Sau đ&oacute;&nbsp;họ sẽ &ldquo;ghim&rdquo; h&agrave;ng lại chờ thời cơ&rdquo;. <br />\r\n<br />\r\nQuan s&aacute;t của ch&uacute;ng t&ocirc;i trong khoảng nửa giờ tại một cửa h&agrave;ng c&ograve;n khẩu trang trong khu chợ Sĩ, c&oacute; đến hơn 100 lượt người t&igrave;m mua khẩu trang. Trong đ&oacute; c&oacute; nhiều người mua đến hơn 10 hộp. <br />\r\n<br />\r\nViệc người d&acirc;n &ldquo;sốt&rdquo; với gi&aacute; khẩu trang ph&ograve;ng chống c&uacute;m đang trở th&agrave;nh &ldquo;m&oacute;n mồi&rdquo; b&eacute;o bở cho những kẻ &ldquo;cơ hội&rdquo;. Trong khi đ&oacute;, theo một số chủ quầy thuốc t&acirc;y cho biết: &ldquo;Trong khoảng 7 ng&agrave;y nữa, ch&uacute;ng t&ocirc;i sẽ lấy h&agrave;ng của c&ocirc;ng ty kh&aacute;c đang nhập về một số lượng lớn khẩu trang ph&ograve;ng chống c&uacute;m từ Nhật Bản, Trung Quốc&rdquo;. </font></p>\r\n<p class="MsoNormal" align="justify" style="text-align: justify; margin: 0in 0in 0pt"><font size="2" face="Arial"><strong>Nhiều c&ocirc;ng ty đổ x&ocirc; đi mua khẩu trang<br />\r\n</strong><br />\r\nTrước th&ocirc;ng tin xuất hiện trường hợp dương t&iacute;nh với c&uacute;m A/H1N1 đầu ti&ecirc;n tại Viettel v&agrave; Viglacera (H&agrave; Nội) v&agrave;o ng&agrave;y 27/7 v&agrave; 29/7, nhiều c&ocirc;ng ty, doanh nghiệp đ&atilde; lo xa đi mua khẩu trang chuy&ecirc;n dụng để trang bị cho nh&acirc;n vi&ecirc;n của m&igrave;nh.</font></p>\r\n<p>\r\n<table class="image center" width="400" align="center">\r\n    <tbody>\r\n        <tr>\r\n            <td><font size="2"><img border="2" alt="Rất nhiều người d&acirc;n đ&atilde; t&igrave;m đến phố Phương Mai để mua khẩu trang trang bị cho m&igrave;nh. Ảnh: Ki&ecirc;n Trung" align="middle" width="400" height="217" src="http://images.vietnamnet.vn/dataimages/200907/original/images1838969_9.jpg" /></font></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="image_desc">\r\n            <p align="justify"><font size="2">Rất nhiều người d&acirc;n đ&atilde; t&igrave;m đến phố Phương Mai để mua khẩu trang trang bị cho m&igrave;nh. <em>Ảnh: Ki&ecirc;n Trung</em></font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n<p class="MsoNormal" align="justify" style="text-align: justify; margin: 0in 0in 0pt"><font size="2">&Ocirc;ng Phạm Duy Hải &ndash; Gi&aacute;m đốc lĩnh vực bất động sản của t&ograve;a cao ốc 198 Trần Quang Khải cho biết: khi t&ograve;a nh&agrave; Vietcombank c&oacute; nh&acirc;n vi&ecirc;n c&oacute; dấu hiệu nghi c&uacute;m A/H1N1, BQL t&ograve;a nh&agrave; đ&atilde; chủ động đi mua số lượng lớn khẩu trang để ph&aacute;t cho nh&acirc;n vi&ecirc;n v&agrave; ph&ograve;ng bị.&nbsp;<br />\r\n<br />\r\nTuy nhi&ecirc;n, để gom đủ số lượng 5.000 khẩu trang n&agrave;y, nh&acirc;n vi&ecirc;n của BQL phải mất tới 2 ng&agrave;y đi &ldquo;l&ugrave;ng&rdquo; để gom đủ. V&agrave; mức gi&aacute; cũng rất... trời ơi.</font></p>\r\n<p>\r\n<table class="image center" width="400" align="center">\r\n    <tbody>\r\n        <tr>\r\n            <td><font size="2"><img border="2" alt="Khẩu trang chuy&ecirc;n dụng (m&agrave;u trắng) được b&aacute;n với gi&aacute; từ 30 - 50.000đ/chiếc. Loại chuy&ecirc;n dụng mỏng c&oacute; sợi than hoạt t&iacute;nh gi&aacute; từ 3,500 - 6.00đ/c&aacute;i. Trong chiều 30/7, n&oacute;i đ&atilde; được đưa l&ecirc;n với gi&aacute; 7.000đ, thậm ch&iacute; 15.000đ/chiếc. Ảnh: Ki&ecirc;n Trung" align="middle" width="400" height="217" src="http://images.vietnamnet.vn/dataimages/200907/original/images1838972_7.jpg" /></font></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="image_desc">\r\n            <p align="justify"><font size="2">Khẩu trang chuy&ecirc;n dụng (m&agrave;u trắng) được b&aacute;n với gi&aacute; từ 30.000 - 50.000đ/chiếc. Loại chuy&ecirc;n dụng mỏng c&oacute; sợi than hoạt t&iacute;nh gi&aacute; từ 3.500 - 6.000đ/c&aacute;i. Trong chiều 30/7, n&oacute; đ&atilde; được đưa l&ecirc;n với gi&aacute; 7.000đ, thậm ch&iacute; 15.000đ/chiếc. <em>Ảnh: Ki&ecirc;n Trung</em></font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\nCụ thể, ng&agrave;y 29/7, gi&aacute; của loại khẩu trang mỏng c&oacute; than hoạt t&iacute;nh, sử dụng 1 lần, BQL t&ograve;a nh&agrave; Vietcombank mua 3.500đ/chiếc. Thế nhưng, sang ng&agrave;y 30/7, gi&aacute; đ&atilde; đẩy l&ecirc;n 6.000đ/c&aacute;i.</p>\r\n<p class="MsoNormal" align="justify" style="text-align: justify; margin: 0in 0in 0pt"><o:p><font size="2" face="Arial">&nbsp;</font></o:p><font size="2" face="Arial">Chị Vương Thu H&agrave;, phụ tr&aacute;ch h&agrave;nh ch&iacute;nh C&ocirc;ng ty Điện m&aacute;y Hoa <st1:country-region><st1:place>Nam</st1:place></st1:country-region> cho biết: c&ocirc;ng ty của chị cũng đ&atilde; đi mua khẩu trang ph&ograve;ng c&uacute;m trang bị cho nh&acirc;n vi&ecirc;n. Loại khẩu trang được quảng c&aacute;o l&agrave; c&oacute; sợi than hoạt t&iacute;nh được b&aacute;n với gi&aacute; 6.000đ/chiếc tại thời điểm chiều 30/7 tr&ecirc;n một cửa h&agrave;ng thuốc ở phố L&ograve; Đ&uacute;c.</font></p>\r\n<p class="MsoNormal" align="justify" style="text-align: justify; margin: 0in 0in 0pt"><o:p><font size="2" face="Arial">&nbsp;</font></o:p></p>\r\n<p class="MsoNormal" align="justify" style="text-align: justify; margin: 0in 0in 0pt"><font size="2" face="Arial">Kh&ocirc;ng chỉ c&aacute;c cơ quan Nh&agrave; nước, c&aacute;c to&agrave; nh&agrave; cao tầng&hellip; l&agrave; những nơi c&oacute; số lượng c&aacute;n bộ nh&acirc;n vi&ecirc;n l&agrave;m việc, giao dịch nhiều&hellip;, nhiều c&ocirc;ng ty tư nh&acirc;n cũng đ&atilde; tr&iacute;ch một khoản tiền kh&ocirc;ng hề nhỏ để &ldquo;đầu tư&rdquo; mua thiết bị khẩu trang, với phương ch&acirc;m &ldquo;ph&ograve;ng bệnh hơn chữa bệnh&rdquo;.<br />\r\n</font><strong><font size="2"><br />\r\n</font></strong></p>\r\n<div align="justify">\r\n<table class="quote center" width="400" align="center">\r\n    <tbody>\r\n        <tr>\r\n            <td>\r\n            <p class="MsoNormal" style="text-align: justify; margin: 0in 0in 0pt"><span style="font-family: &rsquo;Times New Roman&rsquo;; font-size: 12pt"><font face="Arial"><font size="2">Theo &ocirc;ng Nguyễn Hồng H&agrave;, Ph&oacute; Viện trưởng Viện c&aacute;c bệnh truyền nhiễm v&agrave; Nhiệt đới Quốc gia,&nbsp;người d&acirc;n c&oacute; thể sử dụng một trong c&aacute;c loại khẩu trang sau đ&acirc;y để ph&ograve;ng c&uacute;m: khẩu trang d&ugrave;ng trong phẫu thuật, khẩu trang giấy (khẩu trang y tế th&ocirc;ng thường) v&agrave; khẩu trang đặc dụng N95.<o:p></o:p></font></font></span></p>\r\n            <p class="MsoNormal" style="text-align: justify; margin: 0in 0in 0pt"><span style="font-family: &rsquo;Times New Roman&rsquo;; font-size: 12pt"><o:p><font size="2" face="Arial">&nbsp;</font></o:p></span></p>\r\n            <p class="MsoNormal" style="text-align: justify; margin: 0in 0in 0pt"><span style="font-family: &rsquo;Times New Roman&rsquo;; font-size: 12pt"><font face="Arial"><font size="2">Trước lo ngại của nhiều người d&acirc;n rằng khẩu trang d&ugrave;ng trong phẫu thuật v&agrave; khẩu trang giấy qu&aacute; mỏng, liệu c&oacute; ph&ograve;ng được c&uacute;m hay kh&ocirc;ng, &ocirc;ng H&agrave; giải th&iacute;ch: &ldquo;Khả năng ph&ograve;ng bệnh kh&ocirc;ng nằm ở độ mỏng hay d&agrave;y m&agrave; nằm ở chỗ k&iacute;ch thước c&aacute;c lỗ kh&iacute; trong khẩu trang c&oacute; đạt ti&ecirc;u chuẩn hay kh&ocirc;ng. C&aacute;c lỗ kh&iacute; c&agrave;ng b&eacute;, v&agrave; mật độ lỗ c&agrave;ng d&agrave;y th&igrave; khả năng ngăn cản c&aacute;c vi-r&uacute;t c&agrave;ng cao&rdquo;.<o:p></o:p></font></font></span></p>\r\n            <p class="MsoNormal" style="text-align: justify; margin: 0in 0in 0pt"><span style="font-family: &rsquo;Times New Roman&rsquo;; font-size: 12pt"><o:p><font size="2" face="Arial">&nbsp;</font></o:p></span></p>\r\n            <p class="MsoNormal" style="text-align: justify; margin: 0in 0in 0pt"><span style="font-family: &rsquo;Times New Roman&rsquo;; font-size: 12pt"><font face="Arial"><font size="2">Loại khẩu trang N95, theo &ocirc;ng H&agrave;, chỉ phải d&ugrave;ng trong những trường hợp tiếp x&uacute;c trực tiếp với người đ&atilde; nhiễm bệnh. Đ&oacute; l&agrave; c&aacute;c b&aacute;c sỹ khi cần lấy đờm bệnh nh&acirc;n để x&eacute;t nghiệm, hoặc những người trực tiếp điều trị.<o:p></o:p></font></font></span></p>\r\n            <p class="MsoNormal" style="text-align: justify; margin: 0in 0in 0pt"><span style="font-family: &rsquo;Times New Roman&rsquo;; font-size: 12pt"><o:p><font size="2" face="Arial">&nbsp;</font></o:p></span></p>\r\n            <p class="MsoNormal" style="text-align: justify; margin: 0in 0in 0pt"><span style="font-family: &rsquo;Times New Roman&rsquo;; font-size: 12pt"><font face="Arial"><font size="2">&ldquo;Do đ&oacute;, người d&acirc;n c&oacute; thể sử dụng khẩu trang giấy th&ocirc;ng thường để ph&ograve;ng c&uacute;m, gi&aacute; phải chăng v&agrave; dễ t&igrave;m. Nếu c&oacute; ai v&igrave; qu&aacute; cẩn thận m&agrave; đeo khẩu trang N95 th&igrave; kh&ocirc;ng cần thiết về mặt khoa học. Điều đ&oacute; sẽ g&acirc;y l&atilde;ng ph&iacute;&rdquo;, &ocirc;ng H&agrave; khuyến c&aacute;o.<o:p></o:p></font></font></span></p>\r\n            <p class="MsoNormal" style="text-align: justify; margin: 0in 0in 0pt"><font size="2" face="Arial"><span style="font-family: &rsquo;Times New Roman&rsquo;; font-size: 12pt"><br />\r\n            <em><strong>(Cẩm Quy&ecirc;n)</strong></em></span></font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<p><strong><font size="2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tử Trực - Ki&ecirc;n Trung</font></strong></p>\r\n<p>&nbsp;</p>', 1, 1249029917, 2, 37, 0),
(26, 2, 'Tai nạn lao động nguy hiểm không kém tai nạn giao thông', '(LĐ) - "Những vụ tai nạn lao động liên tiếp xảy ra trong tuần qua không chỉ là sự việc chúng ta thấy được, mà đang là vấn đề cả xã hội cần quan tâm.\r\n', '26.jpg', '', '<table border="\\&quot;0\\&quot;" cellspacing="\\&quot;0\\&quot;" cellpadding="\\&quot;0\\&quot;">\r\n    <tbody>\r\n        <tr>\r\n            <td class="\\&quot;atc_hl\\&quot;" valign="\\&quot;top\\&quot;"><br />\r\n            <div class="\\&quot;atc_dt\\&quot;"><span id="\\&quot;_ctl0_rContent_lbEdition\\&quot;">Lao Động số 171 Ng&agrave;y 31/07/2009</span>&nbsp;Cập nhật: <span id="\\&quot;_ctl0_rContent_lbDate\\&quot;">8:40 AM, 31/07/2009</span></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign="\\&quot;top\\&quot;">\r\n            <div class="\\&quot;atc_textbox\\&quot;">\r\n            <table class="\\&quot;atc_imgWrap\\&quot;" cellspacing="\\&quot;0\\&quot;" cellpadding="\\&quot;0\\&quot;">\r\n                <tbody>\r\n                    <tr>\r\n                        <td><img alt="" width="236" height="200" src="/uploads/image/avatar.jpg" /></td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td><span id="\\&quot;_ctl0_rContent_lbImageCaption\\&quot;" class="\\&quot;atc_imgCaption\\&quot;">Bộ trưởng Bộ LĐTBXH Nguyễn Thị Kim Ng&acirc;n.</span></td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            <div class="\\&quot;atc_txt\\&quot;"><span id="\\&quot;_ctl0_rContent_lbBody\\&quot;">\r\n            <p>Đ&atilde; đến l&uacute;c cần xem <a href="\\&quot;http://www.laodong.com.vn/EventList.aspx?EventID=247\\&quot;">tai nạn lao động nghi&ecirc;m trọng</a> như tai nạn giao th&ocirc;ng&quot;- đ&oacute; l&agrave; khẳng định của Bộ trưởng Bộ LĐTBXH Nguyễn Thị Kim Ng&acirc;n b&ecirc;n lề Hội nghị giao ban trực tuyến sơ kết c&ocirc;ng t&aacute;c lao động, người c&oacute; c&ocirc;ng v&agrave; x&atilde; hội 6 th&aacute;ng đầu năm 2009 - ng&agrave;y 30.7.</p>\r\n            <p><strong>*&nbsp;Thưa b&agrave;, vừa qua, tại t&ograve;a nh&agrave; Keangnam Hanoi Landmark đang x&acirc;y dựng, trong v&ograve;ng một tuần li&ecirc;n tiếp để xảy ra 3 vụ tai nạn lao động (TNLĐ). Vậy Bộ LĐTBXH c&oacute; nắm bắt được t&igrave;nh h&igrave;nh sự việc kh&ocirc;ng?<br />\r\n            </strong><br />\r\n            - Hiện nay, c&ocirc;ng tr&igrave;nh đang phải tạm đ&igrave;nh chỉ thi c&ocirc;ng để l&agrave;m r&otilde; nguy&ecirc;n nh&acirc;n. Tuy nhi&ecirc;n, trong một tuần đ&atilde; để xảy ra 3 vụ tai nạn li&ecirc;n tục, điều n&agrave;y chứng tỏ c&ocirc;ng tr&igrave;nh t&ograve;a nh&agrave; n&agrave;y chưa thực hiện đảm bảo an to&agrave;n lao động, chưa chấp h&agrave;nh nghi&ecirc;m về ph&aacute;p luật lao động. Việc x&aacute;c định nguy&ecirc;n nh&acirc;n v&igrave; sao xảy ra tai nạn v&agrave; tr&aacute;ch nhiệm xử l&yacute; thế n&agrave;o l&agrave; bước sau, nhưng ngay sau khi sự việc xảy ra, ch&uacute;ng t&ocirc;i phải b&aacute;o c&aacute;o ngay l&ecirc;n Thủ tướng.<br />\r\n            <br />\r\n            Đ&acirc;y chỉ l&agrave; một sự việc cụ thể ch&uacute;ng ta biết th&ocirc;i, nhưng l&agrave; vấn đề m&agrave; cả x&atilde; hội cần quan t&acirc;m như TNGT, cũng nguy hiểm kh&ocirc;ng k&eacute;m TNGT khi thường xuy&ecirc;n g&acirc;y ra những vụ chết người, g&acirc;y thương tật suốt đời cho người lao động.<br />\r\n            <br />\r\n            <strong>*&nbsp;Xin b&agrave; cho biết, đ&acirc;u l&agrave; nguy&ecirc;n nh&acirc;n ch&iacute;nh để xảy ra TNLĐ v&agrave; tr&aacute;ch nhiệm thuộc về b&ecirc;n n&agrave;o?<br />\r\n            </strong><br />\r\n            - Thực tế hiện nay, cả người sử dụng lao động lẫn người lao động chưa thực hiện tốt c&ocirc;ng t&aacute;c ATLĐ. Bản th&acirc;n người lao động phải bảo vệ m&igrave;nh, nếu khi l&agrave;m việc trong c&ocirc;ng trường lao động m&agrave; người sử dụng lao động kh&ocirc;ng trang bị c&aacute;c phương tiện bảo hộ lao động cho m&igrave;nh, th&igrave; phải y&ecirc;u cầu được trang bị.<br />\r\n            <br />\r\n            Ngược lại, chủ DN cũng phải thực hiện đ&uacute;ng ph&aacute;p luật để đảm bảo điều kiện cho người lao động. Điều n&agrave;y trong hợp đồng lao động đ&atilde; ghi rất r&otilde;.<br />\r\n            &nbsp;<br />\r\n            Tuy nhi&ecirc;n, t&ocirc;i c&oacute; thể khẳng định, TNLĐ xảy ra ở c&aacute;c c&ocirc;ng trường x&acirc;y dựng đa phần l&agrave; do người sử dụng lao động.<br />\r\n            <br />\r\n            <strong>*&nbsp;Trong thời gian qua, trong nước xảy ra kh&aacute; nhiều vụ tai nạn lao động ở c&aacute;c c&ocirc;ng trường x&acirc;y dựng; vậy Bộ LĐTBXH c&oacute; cử đo&agrave;n thanh tra n&agrave;o đi kiểm tra c&aacute;c c&ocirc;ng tr&igrave;nh chưa? <br />\r\n            </strong><br />\r\n            - Ch&uacute;ng t&ocirc;i vẫn thanh tra thường xuy&ecirc;n, chứ kh&ocirc;ng phải khi c&oacute; tai nạn mới thanh tra. Tuy nhi&ecirc;n, việc thanh tra kh&ocirc;ng nhất thiết phải l&agrave; bộ đi thanh tra, m&agrave; c&aacute;c cơ quan thanh tra lao động ở địa phương, c&aacute;c tỉnh, huyện đi thanh tra. Đương nhi&ecirc;n, khi TNLĐ xảy ra th&igrave; thanh tra lao động phải c&oacute; mặt ngay để c&ugrave;ng với c&aacute;c cơ quan xem x&eacute;t, kết luận nguy&ecirc;n nh&acirc;n xảy ra tai nạn thuộc về DN hay người lao động.<br />\r\n            <br />\r\n            Tr&aacute;ch nhiệm của cơ quan quản l&yacute; nh&agrave; nước l&agrave; phải ban h&agrave;nh luật, phải ra những văn bản quy phạm ph&aacute;p luật để hướng dẫn, kiểm tra tổ chức thực hiện việc ATLĐ, chứ kh&ocirc;ng nhất thiết cứ phải đến xem người lao động c&oacute; đội mũ, đeo d&acirc;y bảo hiểm hay kh&ocirc;ng.<br />\r\n            <strong><br />\r\n            - Xin cảm ơn b&agrave;!</strong></p>\r\n            </span>\r\n            <div class="\\&quot;page\\&quot;">&nbsp;</div>\r\n            <div class="\\&quot;atc_aut\\&quot;"><span id="\\&quot;_ctl0_rContent_lbSource\\&quot;">N.Lan thực hiện</span></div>\r\n            </div>\r\n            </div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>', 1, 1249031708, 2, 27, 0),
(23, 2, 'Công tác phòng cúm A/H1N1 ở các cao ốc Đà Nẵng còn lúng túng, bị động ', '(Cadn.com.vn) - Thực tế diễn biến của dịch cúm A/H1N1 thời gian qua cho thấy, tại các cao ốc, khách sạn là nơi có nguy cơ xuất hiện dịch cúm nhất, bởi nơi đây có nhiều du khách thường xuyên lui tới. Thế nhưng công tác phòng cúm ở một số khách sạn tại Đà Nẵng còn khá lơ là, chủ quan. Theo tìm hiểu của chúng tôi, tại nhiều cao ốc, văn phòng, khu thương mại cho thuê ở Đà Nẵng, công tác phòng chống cúm A/H1N1 còn rất lúng túng. Ông Trần Ngọc Ánh, Phòng Hành chính–Tổng hợp, Cty CP Đức Mạnh, đơn vị chủ quản khu cao ốc Vĩnh Trung Plaza cho biết, Cty chưa nhận được văn bản hướng dẫn nào về phòng cúm A/H1N1 từ phía Sở Y tế TP vì thế rất lúng túng, không biết triển khai phòng chống như thế nào. ', '23.jpg', '', '<span class="msgQuotation" hasbox="2">&nbsp;\r\n<p class="MsoNormal" hasbox="2" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><span hasbox="2" style="line-height: 150%; font-family: Arial; font-size: 10pt; mso-bidi-font-family: ''Times New Roman''">(Cadn.com.vn) - Thực tế diễn biến của dịch c&uacute;m A/H1N1 thời gian qua cho thấy, tại c&aacute;c cao ốc, kh&aacute;ch sạn l&agrave; nơi c&oacute; nguy cơ xuất hiện dịch c&uacute;m nhất, bởi nơi đ&acirc;y c&oacute; nhiều du kh&aacute;ch thường xuy&ecirc;n lui tới. Thế nhưng c&ocirc;ng t&aacute;c ph&ograve;ng c&uacute;m ở một số kh&aacute;ch sạn tại Đ&agrave; Nẵng c&ograve;n kh&aacute; lơ l&agrave;, chủ quan. Theo t&igrave;m hiểu của ch&uacute;ng t&ocirc;i, tại nhiều cao ốc, văn ph&ograve;ng, khu thương mại cho thu&ecirc; ở Đ&agrave; Nẵng, c&ocirc;ng t&aacute;c ph&ograve;ng chống c&uacute;m A/H1N1 c&ograve;n rất l&uacute;ng t&uacute;ng. &Ocirc;ng Trần Ngọc &Aacute;nh, Ph&ograve;ng H&agrave;nh ch&iacute;nh&ndash;Tổng hợp, Cty CP Đức Mạnh, đơn vị chủ quản khu cao ốc Vĩnh Trung Plaza cho biết, Cty chưa nhận được văn bản hướng dẫn n&agrave;o về ph&ograve;ng c&uacute;m A/H1N1 từ ph&iacute;a Sở Y tế TP v&igrave; thế rất l&uacute;ng t&uacute;ng, kh&ocirc;ng biết triển khai ph&ograve;ng chống như thế n&agrave;o. <o:p></o:p></span></p>\r\n<p class="MsoNormal" hasbox="2" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><span hasbox="2" style="line-height: 150%; font-family: Arial; font-size: 10pt; mso-bidi-font-family: ''Times New Roman''">Khu cao ốc <st1:place w:st="on"><st1:placename w:st="on">Vĩnh</st1:placename> <st1:placename w:st="on">Trung</st1:placename> <st1:placetype w:st="on">Plaza</st1:placetype></st1:place> gồm 3 khu vực l&agrave; khu chung cư, khu văn ph&ograve;ng cho thu&ecirc; v&agrave; khu thương mại, trong đ&oacute;, khu thương mại được BigC thu&ecirc; nhiều năm nay. Hiện nay, đ&acirc;y l&agrave; điểm c&oacute; nguy cơ l&acirc;y nhiễm c&uacute;m A/H1N1 cao nhất v&igrave; c&oacute; nhiều người thường xuy&ecirc;n ra v&agrave;o. Đối với khu chung cư, &ocirc;ng &Aacute;nh cho biết, nhiều kh&aacute;ch h&agrave;ng l&agrave; người nước ngo&agrave;i đến hỏi thu&ecirc; trong thời gian gần đ&acirc;y nhưng do thiếu th&ocirc;ng tin hướng dẫn n&ecirc;n ch&uacute;ng t&ocirc;i ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; biện ph&aacute;p kiểm tra y tế đối với những người n&agrave;y. T&igrave;nh trạng l&uacute;ng t&uacute;ng, thiếu th&ocirc;ng tin, thậm ch&iacute; kh&aacute; chủ quan cũng diễn ra tại khu cao ốc Indochina Riverside b&ecirc;n bờ s&ocirc;ng H&agrave;n. Đ&acirc;y l&agrave; một trong những cao ốc lớn nhất của Đ&agrave; Nẵng, c&oacute; nhiều văn ph&ograve;ng cho thu&ecirc;, khu thương mại... <o:p></o:p></span></p>\r\n<p class="MsoNormal" hasbox="2" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><span hasbox="2" style="line-height: 150%; font-family: Arial; font-size: 10pt; mso-bidi-font-family: ''Times New Roman''">B&agrave; L&ecirc; Kim Uy&ecirc;n, Ch&aacute;nh văn ph&ograve;ng Cty TNHH Indochina Riverside cho biết, đến nay vẫn chỉ nghe th&ocirc;ng tin về c&uacute;m A/H1N1 tr&ecirc;n đ&agrave;i, b&aacute;o v&agrave; ph&ograve;ng chống theo đ&oacute; chứ chưa c&oacute; sự hướng dẫn của Sở Y tế. Khi ch&uacute;ng t&ocirc;i hỏi cụ thể đơn vị đ&atilde; triển khai ph&ograve;ng chống những g&igrave;, b&agrave; Uy&ecirc;n cho biết nh&acirc;n vi&ecirc;n chỉ biết vệ sinh sạch sẽ đến tối đa để kh&ocirc;ng nảy sinh mầm bệnh. C&uacute;m A/H1N1 l&acirc;y qua đường h&ocirc; hấp, từ người qua người n&ecirc;n việc kiểm tra y tế, c&aacute;ch ph&ograve;ng tr&aacute;nh, xử l&yacute; khi xảy ra sự cố ở khu cao ốc n&agrave;y theo như lời b&agrave; Uy&ecirc;n c&ograve;n kh&aacute; l&uacute;ng t&uacute;ng. Nếu xảy ra sự cố, Cty sẽ cho c&aacute;ch ly ph&ograve;ng, bệnh nh&acirc;n c&ograve;n c&aacute;c biện ph&aacute;p tiếp theo thế n&agrave;o th&igrave; vẫn chưa c&oacute;. </span>\r\n<p class="MsoNormal" hasbox="2" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><span hasbox="2" style="line-height: 150%; font-family: Arial; font-size: 10pt; mso-bidi-font-family: ''Times New Roman''">Cũng theo b&agrave; Uy&ecirc;n, do cao ốc n&agrave;y l&agrave; tổ hợp văn ph&ograve;ng, căn hộ, khu thương mại cho thu&ecirc; n&ecirc;n Cty chủ yếu chỉ đảm bảo c&aacute;c vấn đề vệ sinh, an ninh. C&ograve;n lại việc ph&ograve;ng c&uacute;m, tr&ecirc;n hết vẫn l&agrave; tr&aacute;ch nhiệm của từng tập thể, c&aacute; nh&acirc;n thu&ecirc; tại cao ốc. Kh&ocirc;ng ri&ecirc;ng g&igrave; 2 cao ốc đ&atilde; n&ecirc;u, rất nhiều khu cao ốc kh&aacute;c đều bị động với việc ph&ograve;ng, chống c&uacute;m. Đại diện một số cao ốc kiến nghị ng&agrave;nh Y tế th&agrave;nh phố sớm c&oacute; hướng dẫn cụ thể để tạo điều kiện cho họ sẵn s&agrave;ng đối ph&oacute; khi dịch xảy ra. Trao đổi với ch&uacute;ng t&ocirc;i, &ocirc;ng Nguyễn &Uacute;t, Ph&oacute; Gi&aacute;m đốc Sở<span style="mso-spacerun: yes">&nbsp; </span>Y tế TP Đ&agrave; Nẵng cho biết, do Đ&agrave; Nẵng chưa xuất hiện trường hợp nhiễm c&uacute;m n&agrave;o tại c&aacute;c khu cao ốc n&ecirc;n ng&agrave;nh vẫn chưa c&oacute; hướng dẫn, chỉ đạo. Sắp tới, ng&agrave;nh sẽ lưu t&acirc;m đến vấn đề n&agrave;y. <o:p></o:p></span></p>\r\n<p class="MsoNormal" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><span style="line-height: 150%; font-family: Arial; font-size: 10pt; mso-bidi-font-family: ''Times New Roman''">Trong khi c&aacute;c khu cao ốc kh&aacute; l&uacute;ng t&uacute;ng với việc ph&ograve;ng c&uacute;m A/H1N1 th&igrave; tại c&aacute;c kh&aacute;ch sạn, khu du lịch lại chủ động hơn. C&ocirc;ng t&aacute;c n&agrave;y đ&atilde; được c&aacute;c đơn vị triển khai ngay từ cuối th&aacute;ng 6 khi Đ&agrave; Nẵng ph&aacute;t hiện ca nhiễm đầu ti&ecirc;n. Anh Nguyễn Hữu Phước, Ph&ograve;ng nh&acirc;n sự, Kh&aacute;ch sạn Ho&agrave;ng Anh Gia Lai cho biết: &ldquo;Kh&aacute;ch sạn đ&atilde; chuẩn bị sẵn s&agrave;ng mọi phương &aacute;n từ khi dịch c&uacute;m A/H1N1 xuất hiện ở Đ&agrave; Nẵng bởi v&igrave; ng&agrave;nh Du lịch l&agrave; ng&agrave;nh nhạy cảm n&ecirc;n ch&uacute;ng t&ocirc;i phải chuẩn bị kỹ lưỡng. Tuy nhi&ecirc;n, kh&aacute;ch sạn chỉ nhận được một số hướng dẫn, chỉ đạo khi dịch c&uacute;m mới xuất hiện ở Đ&agrave; Nẵng, c&ograve;n thời gian gần đ&acirc;y, khi dịch c&uacute;m l&acirc;y lan nhanh ra cộng đồng th&igrave; ch&uacute;ng t&ocirc;i rất thiếu những th&ocirc;ng tin kịp thời từ ng&agrave;nh chức năng. Tất cả th&ocirc;ng tin về dịch c&uacute;m đều được cập nhật chủ yếu từ c&aacute;c phương tiện truyền th&ocirc;ng đại ch&uacute;ng&rdquo;. <o:p></o:p></span></p>\r\n<p class="MsoNormal" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><span style="line-height: 150%; font-family: Arial; font-size: 10pt; mso-bidi-font-family: ''Times New Roman''">Tại nhiều khu du lịch, kh&aacute;ch sạn kh&aacute;c cũng c&oacute; chung t&igrave;nh trạng thiếu th&ocirc;ng tin hướng dẫn cập nhật như tr&ecirc;n, đặc biệt dịch mỗi l&uacute;c một diễn biến phức tạp v&agrave; kh&oacute; lường. Khảo s&aacute;t nhiều nh&acirc;n vi&ecirc;n l&agrave;m việc tại c&aacute;c kh&aacute;ch sạn cho thấy, họ chỉ nhận được những th&ocirc;ng tin khuyến c&aacute;o chung chung, c&ograve;n th&ocirc;ng tin s&acirc;u về c&aacute;ch ph&ograve;ng tr&aacute;nh v&agrave; ứng ph&oacute; khi xuất hiện bệnh nh&acirc;n nghi nhiễm c&uacute;m trong kh&aacute;ch sạn th&igrave; kh&ocirc;ng c&oacute; nh&acirc;n vi&ecirc;n n&agrave;o c&oacute; thể trả lời chi tiết được. Theo kiến nghị một số kh&aacute;ch sạn th&igrave; ng&agrave;nh Y tế th&agrave;nh phố n&ecirc;n tổ chức tập huấn, diễn tập để c&oacute; thể sẵn s&agrave;ng ứng ph&oacute; với trường hợp xuất hiện bệnh nh&acirc;n nghi nhiễm c&uacute;m A/H1N1 ở<span style="mso-spacerun: yes">&nbsp; </span>c&aacute;c khu vực kinh doanh tại kh&aacute;ch sạn.<o:p></o:p></span></p>\r\n<p class="MsoNormal" align="right" hasbox="2" style="text-align: right; line-height: 150%; margin: 6pt 0in"><b hasbox="2" style="mso-bidi-font-weight: normal"><i hasbox="2" style="mso-bidi-font-style: normal"><span hasbox="2" style="line-height: 150%; font-family: Arial; font-size: 10pt; mso-bidi-font-family: ''Times New Roman''">Hải Hậu<o:p></o:p></span></i></b></p>\r\n<p class="MsoNormal" hasbox="2" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><span hasbox="2" style="line-height: 150%; font-family: Arial; font-size: 10pt; mso-bidi-font-family: ''Times New Roman''"><strong hasbox="2">*&nbsp;Ng&agrave;y 30-7, Gi&aacute;m đốc CATP Đ&agrave; Nẵng c&oacute; c&ocirc;ng văn gửi l&atilde;nh đạo CA c&aacute;c ph&ograve;ng, ban, quận, huyện, trại tạm giam, Trung t&acirc;m Huấn luyện v&agrave; Bồi dưỡng nghiệp vụ thuộc CATP Đ&agrave; Nẵng về việc ph&ograve;ng, chống đại dịch c&uacute;m A(H1N1). C&ocirc;ng văn n&ecirc;u r&otilde;, nhằm kh&ocirc;ng để dịch l&acirc;y lan trong lực lượng CA cũng như trong cộng đồng, kh&ocirc;ng để xảy ra trường hợp tử vong do dịch, BCĐ ph&ograve;ng, chống đại dịch c&uacute;m ở người của CATP Đ&agrave; Nẵng y&ecirc;u cầu CA c&aacute;c đơn vị, địa phương<span style="mso-spacerun: yes">&nbsp; </span>thực hiện nghi&ecirc;m t&uacute;c một số nội dung. <o:p></o:p></strong></span></p>\r\n<p class="MsoNormal" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><span style="line-height: 150%; font-family: Arial; font-size: 10pt; mso-bidi-font-family: ''Times New Roman''"><strong>Đ&oacute; l&agrave;, tại c&aacute;c đơn vị ăn, ở đ&ocirc;ng người như PC22, PC23, Trại Tạm giam H&ograve;a Sơn, Trung t&acirc;m Huấn luyện v&agrave; Bồi dưỡng nghiệp vụ thực hiện triệt để c&aacute;c biện ph&aacute;p ph&ograve;ng, chống dịch c&uacute;m A(H1N1) theo c&aacute;c quy định của Bộ Y tế. Đối với c&aacute;n bộ y tế phải đeo khẩu trang khi kh&aacute;m bệnh, thực hiện nghi&ecirc;m ngặt c&aacute;c biện ph&aacute;p c&aacute;ch ly v&agrave; chống nhiễm khuẩn. Khi ph&aacute;t hiện trường hợp nghi ngờ mắc c&uacute;m A(H1N1) phải kh&aacute;m, ph&acirc;n loại v&agrave; c&aacute;ch ly; kịp thời chuyển đến c&aacute;c bệnh viện tuyến tr&ecirc;n của ng&agrave;nh hoặc bệnh viện qu&acirc;n d&acirc;n y nơi gần nhất c&oacute; đủ điều kiện c&aacute;ch ly, điều trị bệnh.<o:p></o:p></strong></span></p>\r\n<p class="MsoNormal" align="right" style="text-align: right; line-height: 150%; margin: 6pt 0in"><i style="mso-bidi-font-style: normal"><span style="line-height: 150%; font-family: Arial; font-size: 10pt; mso-bidi-font-family: ''Times New Roman''"><strong>P.V<o:p></o:p></strong></span></i></p>\r\n</p>\r\n<p class="MsoNormal" hasbox="2" style="text-align: justify; line-height: 150%; margin: 6pt 0in">\r\n<table cellspacing="2" cellpadding="0" align="center" hasbox="2" style="width: 20px; height: 20px">\r\n    <tbody hasbox="2">\r\n        <tr hasbox="2">\r\n            <td hasbox="2"><img border="0" alt="" hasbox="2" src="http://www.cadn.com.vn/Images/uploadImages/2009/T_07/Ng_31/dh.jpg" /></td>\r\n        </tr>\r\n        <tr hasbox="2">\r\n            <td hasbox="2">\r\n            <p align="center" hasbox="2">&nbsp;Khu thương mại cho thu&ecirc; ở Vĩnh Trung Plaza dễ xuất hiện dịch c&uacute;m nhất do thường xuy&ecirc;n c&oacute; nhiều người ra v&agrave;o.</p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n<o:p></o:p></span>', 1, 1249026761, 2, 25, 0);
INSERT INTO `tgp_cms` (`id`, `cat`, `ten`, `chu_thich`, `hinh`, `hinh_note`, `noi_dung`, `hien_thi`, `time`, `user`, `luot_xem`, `noi_bat`) VALUES
(24, 2, 'Thông tin thêm về dự án cầu - đường Nguyễn Văn Linh nối dài ', '(Cadn.com.vn) - Sau khi Báo Công an TP Đà Nẵng đăng bài “Tháng 9-2008 sẽ khởi công dự án cầu - đường Nguyễn Văn Linh (NVL) nối dài” trên số báo ra ngày thứ bảy 12-7-2008, Tòa soạn Báo đã nhận được nhiều thư từ và điện thoại của bạn đọc, nhất là người dân ở trong khu vực dự án gửi và gọi đến muốn biết cụ thể dự án này có quy mô xây dựng như thế nào, tiêu chuẩn bố trí tái định cư (TĐC) ra sao, đặc biệt là bố trí TĐC tại mặt tiền đường NVL nối dài… Báo Công an TP Đà Nẵng thông tin cụ thể những vấn đề này để bạn đọc được rõ.\r\n\r\n', '24.jpg', '', '<p class="MsoNormal" hasbox="2" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><b hasbox="2" style="mso-bidi-font-weight: normal"><span hasbox="2" style="line-height: 150%; font-family: Arial; font-size: 10pt">C&ocirc;ng tr&igrave;nh Đại lộ đầu ti&ecirc;n ở TP Đ&agrave; Nẵng<o:p></o:p></span></b></p>\r\n<p class="MsoNormal" hasbox="2" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><span hasbox="2" style="line-height: 150%; font-family: Arial; font-size: 10pt">Theo quyết định ph&ecirc; duyệt dự &aacute;n đầu tư x&acirc;y dựng c&ocirc;ng tr&igrave;nh đường NVL nối d&agrave;i đến đường du lịch ven biển Sơn Tr&agrave; - Điện Ngọc của UBNDTP Đ&agrave; Nẵng: c&ocirc;ng tr&igrave;nh n&agrave;y được x&acirc;y dựng với cấp đường l&agrave; đại lộ, được chia l&agrave;m 2 đoạn: đoạn 1 nằm tr&ecirc;n địa b&agrave;n Q. Hải Ch&acirc;u (đi qua 3 phường: Nam Dương, Phước Ninh v&agrave; B&igrave;nh Hi&ecirc;n) từ n&uacute;t giao th&ocirc;ng NVL - Ho&agrave;ng Diệu - Triệu Nữ Vương - &Ocirc;ng &Iacute;ch Khi&ecirc;m đến n&uacute;t giao th&ocirc;ng ph&iacute;a t&acirc;y dự &aacute;n cầu mới qua s&ocirc;ng H&agrave;n (đoạn gần Trung t&acirc;m Truyền h&igrave;nh Việt Nam tại Đ&agrave; Nẵng) d&agrave;i 0,76km; c&oacute; bề rộng nền đường: 36m (6m + 10,5m + 3m + 10,5m + 6m), trong đ&oacute;, l&ograve;ng đường rộng 21m, dải ph&acirc;n c&aacute;ch ở giữa rộng 3m v&agrave; vỉa h&egrave; mỗi b&ecirc;n rộng 6m; được x&acirc;y dựng đồng bộ với c&aacute;c c&ocirc;ng tr&igrave;nh hạ tầng kỹ thuật kh&aacute;c như cấp nước, tho&aacute;t nước, điện chiếu s&aacute;ng, c&acirc;y xanh... với tổng mức đầu tư hơn 295 tỷ đồng, trong đ&oacute;, chi ph&iacute; đền b&ugrave; (tạm t&iacute;nh) hơn 222,8 tỷ đồng. <o:p></o:p></span></p>\r\n<p class="MsoNormal" hasbox="2" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><span hasbox="2" style="line-height: 150%; font-family: Arial; font-size: 10pt">Đoạn 2 nằm tr&ecirc;n địa b&agrave;n Q. Sơn Tr&agrave; (đi qua 3 phường: An Hải T&acirc;y, An Hải Đ&ocirc;ng v&agrave; Phước Mỹ) từ n&uacute;t giao th&ocirc;ng ph&iacute;a đ&ocirc;ng dự &aacute;n cầu mới qua s&ocirc;ng H&agrave;n đến n&uacute;t giao th&ocirc;ng cuối tuyến gi&aacute;p với đường du lịch ven biển Sơn Tr&agrave; - Điện Ngọc d&agrave;i 1,43km; c&oacute; bề rộng nền đường lớn hơn đoạn nằm tr&ecirc;n địa b&agrave;n Q. Hải Ch&acirc;u: 48m (6m + 15m + 6m + 15m + 6m), trong đ&oacute;, l&ograve;ng đường rộng 30m, dải ph&acirc;n c&aacute;ch ở giữa rộng 6m v&agrave; vỉa h&egrave; mỗi b&ecirc;n rộng 6m; được x&acirc;y dựng đồng bộ với c&aacute;c c&ocirc;ng tr&igrave;nh hạ tầng kỹ thuật kh&aacute;c như cấp nước, tho&aacute;t nước, điện chiếu s&aacute;ng, c&acirc;y xanh... với tổng mức đầu tư gần 314 tỷ đồng, trong đ&oacute;, chi ph&iacute; đền b&ugrave; giải tỏa (tạm t&iacute;nh): 185 tỷ đồng. <o:p></o:p></span></p>\r\n<div class="MsoNormal" hasbox="2" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><span hasbox="2" style="line-height: 150%; font-family: Arial; font-size: 10pt">Đối với dự &aacute;n cầu mới qua s&ocirc;ng H&agrave;n nối đoạn 1 v&agrave; đoạn 2 đường NVL nối d&agrave;i: được x&acirc;y dựng c&oacute; h&igrave;nh d&aacute;ng kiến tr&uacute;c cầu Rồng với đầu Rồng hướng ra biển Đ&ocirc;ng do Cty Louis Berger Group, Inc (Hoa Kỳ) thiết kế; c&oacute; quy m&ocirc; x&acirc;y dựng vĩnh cửu, cấp c&ocirc;ng tr&igrave;nh: cấp I; c&oacute; tĩnh kh&ocirc;ng: về khổ giới hạn th&ocirc;ng thuyền theo phương thẳng đứng lớn hơn hoặc bằng 7m v&agrave; theo phương ngang lớn hơn hoặc bằng 50m, về khổ giới hạn đường bộ t&iacute;nh từ điểm cao nhất của phần xe chạy lớn hơn hoặc bằng 4,75m... C&ocirc;ng tr&igrave;nh cầu mới qua s&ocirc;ng H&agrave;n d&agrave;i khoảng 1km, c&oacute; khổ cầu như sau: phần xe chạy rộng 24,5m, phần lề bộ h&agrave;nh mỗi b&ecirc;n rộng 2,5m v&agrave; dải ph&acirc;n c&aacute;ch rộng 2m với tổng mức đầu tư tối đa kh&ocirc;ng qu&aacute; 35 triệu USD.</span>\r\n<p class="MsoNormal" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><b style="mso-bidi-font-weight: normal"><span style="line-height: 150%; font-family: Arial; font-size: 10pt">Những trường hợp n&agrave;o được bố tr&iacute; mặt tiền đường Nguyễn Văn Linh nối d&agrave;i?<o:p></o:p></span></b></p>\r\n<p class="MsoNormal" hasbox="2" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><span hasbox="2" style="line-height: 150%; font-family: Arial; font-size: 10pt">Được bố tr&iacute; 1 l&ocirc; ch&iacute;nh mặt tiền đường NVL, bao gồm c&aacute;c trường hợp sau đ&acirc;y: vị tr&iacute; (VT) 1 đường Phan Ch&acirc;u Trinh (PCT) c&oacute; diện t&iacute;ch thu hồi đất ở (DTTHĐƠ) từ 70m2 trở l&ecirc;n v&agrave; chiều rộng mặt tiền (CRMT) từ 4,5m trở l&ecirc;n, VT 1 đường L&ecirc; Đ&igrave;nh Dương (LĐD) c&oacute; DTTHĐƠ từ 80m2 trở l&ecirc;n v&agrave; CRMT từ 4,5m trở l&ecirc;n, VT 1 đường Trưng Nữ Vương c&oacute; DTTHĐƠ từ 120m2 trở l&ecirc;n v&agrave; CRMT từ 4,5m trở l&ecirc;n; ngo&agrave;i ra, nếu VT 1 đường PCT c&oacute; DTTHĐƠ từ 150m2 đến 190m2 v&agrave; VT 1 đường LĐD c&oacute; DTTHĐƠ từ 160m2 đến 210m2 c&oacute; CRMT dưới 9m th&igrave; bố tr&iacute; th&ecirc;m 1 l&ocirc; ch&iacute;nh đường 33m Tuy&ecirc;n Sơn, c&oacute; CRMT tr&ecirc;n 9m th&igrave; bố tr&iacute; th&ecirc;m 1 l&ocirc; ch&iacute;nh đường 3-2; nếu VT 1 đường PCT c&oacute; DTTHĐƠ từ 190m2 đến 380m2 v&agrave; VT 1 đường LĐD c&oacute; DTTHĐƠ từ 210m2 đến 400m2 c&oacute; CRMT từ 7m trở l&ecirc;n được bố tr&iacute; th&ecirc;m 1 l&ocirc; ch&iacute;nh đường Đống Đa; nếu VT 1 đường PCT c&oacute; DTTHĐƠ từ 380m2 đến 500m2 v&agrave; VT 1 đường LĐD c&oacute; DTTHĐƠ từ 400m2 đến 520m2 c&oacute; CRMT từ 12m trở l&ecirc;n được bố tr&iacute; th&ecirc;m 1 l&ocirc; ch&iacute;nh đường Đống Đa v&agrave; 1 l&ocirc; phụ đường 15m Tuy&ecirc;n Sơn; nếu VT 1 đường PCT c&oacute; DTTHĐƠ từ 500m2 đến 650m2 v&agrave; VT 1 đường LĐD c&oacute; DTTHĐƠ từ 520m2 đến 700m2 c&oacute; CRMT từ 6m đến dưới 12m được bố tr&iacute; th&ecirc;m 1 l&ocirc; ch&iacute;nh đường Đống Đa v&agrave; 1 l&ocirc; phụ đường 15m Tuy&ecirc;n Sơn, c&oacute; CRMT từ 12m trở l&ecirc;n th&igrave; bố tr&iacute; th&ecirc;m 2 l&ocirc; ch&iacute;nh đường NVL nối d&agrave;i v&agrave; nếu ở VT 1 đường PCT th&igrave; bố tr&iacute; th&ecirc;m 1 l&ocirc; phụ đường 15m Tuy&ecirc;n Sơn; nếu VT 1 đường PCT c&oacute; DTTHĐƠ từ 650m2 trở l&ecirc;n v&agrave; VT 1 đường LĐD c&oacute; DTTHĐƠ từ 700m2 trở l&ecirc;n c&oacute; CRMT dưới 6m th&igrave; bố tr&iacute; th&ecirc;m 1 l&ocirc; ch&iacute;nh đường NVL nối d&agrave;i; trường hợp c&oacute; DTTHĐƠ 200m2 tại VT ng&atilde; tư đường PCT v&agrave; đường LĐD, c&oacute; CRMT 10,01m đối với đường PCT v&agrave; 20m đối với đường LĐD th&igrave; bố tr&iacute; 2 l&ocirc; ch&iacute;nh đường NVL nối d&agrave;i; trường hợp c&oacute; DTTHĐƠ hơn 60m2 tại VT ng&atilde; ba đường PCT v&agrave; L&ecirc; Đại H&agrave;nh, c&oacute; CRMT 5m được bố tr&iacute; 1 l&ocirc; ch&iacute;nh đường NVL nối d&agrave;i; trường hợp c&oacute; DTTHĐƠ hơn 80m2 đường PCT v&agrave; chiều rộng mặt tiền l&agrave; 3,66m được bố tr&iacute; 1 l&ocirc; ch&iacute;nh đường NVL nối d&agrave;i; trường hợp c&oacute; DTTHĐƠ hơn 150m2 đường LĐD v&agrave; c&oacute; CRMT l&agrave; 3,48m v&agrave; 4,19m được bố tr&iacute; 1 l&ocirc; ch&iacute;nh đường NVL nối d&agrave;i. <o:p></o:p></span></p>\r\n<p class="MsoNormal" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><span style="line-height: 150%; font-family: Arial; font-size: 10pt">Những trường hợp thuộc diện giải tỏa c&ograve;n lại của đoạn 1 nằm tr&ecirc;n địa b&agrave;n Q. Hải Ch&acirc;u được bố tr&iacute; TĐC tại c&aacute;c đường: Đống Đa, N&uacute;i Th&agrave;nh, Trưng Nữ Vương, 3-2, Trường Sa v&agrave; tại c&aacute;c khu vực sau đ&acirc;y: Khu TĐC đầu tuyến NVL (đoạn gần cổng S&acirc;n bay Quốc tế Đ&agrave; Nẵng), Khu d&acirc;n cư (KDC) ở số 17-H&agrave; Huy Tập, KDC Phan Th&agrave;nh T&agrave;i - Nguyễn Thiện Thuật, KDC Nam đường Phan Đăng Lưu, KDC Tuy&ecirc;n Sơn mở rộng, KDC số 2 đường Nguyễn Tri Phương (nay l&agrave; đường Nguyễn Hữu Thọ), KDC ven s&ocirc;ng H&agrave;n v&agrave; c&aacute;c KDC A, B, C ph&iacute;a Nam cầu Cẩm Lệ. <o:p></o:p></span></p>\r\n<p class="MsoNormal" style="text-align: justify; line-height: 150%; margin: 6pt 0in"><span style="line-height: 150%; font-family: Arial; font-size: 10pt">Đối với c&aacute;c hộ giải tỏa đoạn 2 nằm tr&ecirc;n địa b&agrave;n Q.Sơn Tr&agrave; được bố tr&iacute; TĐC như sau: c&aacute;c hộ giải tỏa tr&ecirc;n địa b&agrave;n P.An Hải T&acirc;y được bố tr&iacute; tại KDC Nam cầu Trần Thị L&yacute;, KDC Mỹ An, KDC Đại Địa Bảo v&agrave; một số dự &aacute;n kh&aacute;c tr&ecirc;n địa b&agrave;n Q.Sơn Tr&agrave;; c&aacute;c hộ giải tỏa tr&ecirc;n địa b&agrave;n 2 phường: An Hải Đ&ocirc;ng v&agrave; Phước Mỹ được bố tr&iacute; tại Khu TĐC ph&iacute;a Đ&ocirc;ng Xưởng 38 v&agrave; Xưởng 387, KDC An Nhơn I (Khu D84 giai đoạn II), KDC Đại Địa Bảo v&agrave; một số KDC kh&aacute;c tr&ecirc;n địa b&agrave;n Q.Sơn Tr&agrave;.<o:p></o:p></span></p>\r\n<p class="MsoNormal" align="right" hasbox="2" style="text-align: right; line-height: 150%; margin: 6pt 0in"><b hasbox="2" style="mso-bidi-font-weight: normal"><i hasbox="2" style="mso-bidi-font-style: normal"><span hasbox="2" style="line-height: 150%; font-family: Arial; font-size: 10pt"><span style="mso-spacerun: yes">&nbsp;</span>Phạm Ho&agrave;ng</span></i></b></p>\r\n</div>\r\n<div class="MsoNormal" hasbox="2" style="text-align: justify; line-height: 150%; margin: 6pt 0in" align="center">\r\n<table cellspacing="2" cellpadding="0" align="center" hasbox="2" style="width: 20px; height: 20px">\r\n    <tbody hasbox="2">\r\n        <tr hasbox="2">\r\n            <td hasbox="2"><img border="0" alt="" hasbox="2" src="http://cadn.com.vn/Images/Infor/Chung/2008/177/23.jpg" /></td>\r\n        </tr>\r\n        <tr hasbox="2">\r\n            <td hasbox="2">\r\n            <p align="center" hasbox="2">M&ocirc; h&igrave;nh kiến tr&uacute;c cầu Rồng thuộc Dự &aacute;n cầu - đường Nguyễn Văn Linh nối d&agrave;i.</p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<o:p></o:p>', 1, 1249027199, 2, 64, 0),
(27, 2, 'Các siêu thị đối phó với H1N1 ', 'Siêu thị tại Hà Nội và TP HCM đang bắt đầu áp dụng hàng loạt biện pháp phòng chống dịch cúm như khử trùng, yêu cầu nhân viên đeo khẩu trang… Các khu mua sắm này cũng lo đối phó với doanh thu có thể bị ảnh hưởng. ', '27.jpg', '', '&gt; <a class="Lead" rel="nofollow">Đảo lộn sinh hoạt v&igrave; c&uacute;m </a>\r\n<p class="Normal">Hiệp hội si&ecirc;u thị H&agrave; Nội c&oacute; 23 th&agrave;nh vi&ecirc;n với c&aacute;c khu mua sắm c&oacute; quy m&ocirc; lớn, tiếp đ&oacute;n v&agrave;i ngh&igrave;n lượt người mỗi ng&agrave;y. Từ nhiều ng&agrave;y qua, Chủ tịch Hiệp hội Si&ecirc;u thị Vũ Vinh Ph&uacute;, đ&atilde; thị s&aacute;t tại nhiều si&ecirc;u thị tr&ecirc;n địa b&agrave;n.</p>\r\n<p class="Normal">Lo ngại dịch c&uacute;m ng&agrave;y c&agrave;ng phức tạp, c&oacute; khả năng ảnh hưởng đến doanh thu v&agrave; lượng kh&aacute;ch mua sắm, Hiệp hội đ&atilde; y&ecirc;u cầu c&aacute;c đơn vị th&agrave;nh vi&ecirc;n thực hiện biện ph&aacute;p ph&ograve;ng ngừa. &ldquo;C&aacute;c si&ecirc;u thị phải khắc phục tư tưởng ngại kh&oacute; để bảo vệ m&igrave;nh trước ti&ecirc;n, sau đ&oacute; l&agrave; bảo vệ sức khỏe của kh&aacute;ch h&agrave;ng khi tới đ&acirc;y mua sắm&rdquo;, &ocirc;ng n&oacute;i.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n    <tbody>\r\n        <tr>\r\n            <td><img border="0" alt="" width="400" height="310" src="http://media.tinmoi.vn/2009/07/31/st.jpg" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="Image">Mỗi ng&agrave;y BigC Thăng Long, H&agrave; Nội đ&oacute;n nhận khoảng 15.000-20.000 lượt người tới mua sắm. Ảnh <em>Ho&agrave;ng H&agrave;.</em></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p class="Normal"><strong><font color="#4f4f4f">Nhiều si&ecirc;u thị ở H&agrave; Nội đ&atilde; bắt đầu &aacute;p dụng c&aacute;c biện ph&aacute;p ph&ograve;ng chống c&uacute;m.</font></strong> Đại diện trung t&acirc;m thương mại Intimex ở 131 H&agrave;o Nam cho hay, c&aacute;ch đ&acirc;y hai ng&agrave;y đ&atilde; đến Trung t&acirc;m Y tế dự ph&ograve;ng để mua khẩu trang. &ldquo;Ch&uacute;ng t&ocirc;i dự kiến sẽ trang bị khẩu trang cho nh&acirc;n vi&ecirc;n trong ng&agrave;y h&ocirc;m nay. C&aacute;c ca trực đến phi&ecirc;n ai người đ&oacute; sẽ phải đeo khẩu trang. Một số nh&acirc;n vi&ecirc;n nữ c&oacute; bầu đ&atilde; cẩn thận đeo khẩu trang từ mấy ng&agrave;y trước đ&oacute;&rdquo;, đại diện trung t&acirc;m n&oacute;i.</p>\r\nPh&oacute; gi&aacute;m đốc Si&ecirc;u thị BigC Thăng Long, H&agrave; Nội, Nguyễn Th&aacute;i Dũng, cho biết, từ tuần trước, trung t&acirc;m đ&atilde; nhờ cơ quan y tế tư vấn về c&aacute;ch thức ph&ograve;ng chống dịch c&uacute;m như vệ sinh sạch sẽ, y&ecirc;u cầu nh&acirc;n vi&ecirc;n đeo khẩu trang&hellip; Tuy nhi&ecirc;n, do nhạy cảm, si&ecirc;u thị vẫn chưa thực hiện c&aacute;c biện ph&aacute;p đồng loạt như y&ecirc;u cầu cả nh&acirc;n vi&ecirc;n v&agrave; kh&aacute;ch h&agrave;ng đeo khẩu trang. &ldquo;Ch&uacute;ng t&ocirc;i sẽ thực hiện c&ocirc;ng việc n&agrave;y nếu c&oacute; y&ecirc;u cầu hoặc khuyến c&aacute;o từ ph&iacute;a cơ quan y tế&rdquo;, &ocirc;ng Dũng n&oacute;i.\r\n<p class="Normal">Tại hệ thống si&ecirc;u thị CoopMart TP HCM, c&aacute;c khu vực c&oacute; nhiều người giao tiếp như quầy thu ng&acirc;n, dịch vụ kh&aacute;ch h&agrave;ng, c&aacute;c dụng cụ gắp thức ăn tại khu thực phẩm tươi sống, c&aacute;c tay nắm cửa ra v&agrave;o đều được vệ sinh bằng cồn 90 độ, nước s&aacute;t khuẩn tối thiểu 3 lần mỗi ca. Ngo&agrave;i ra, si&ecirc;u thị sẽ kiểm tra sức khỏe nh&acirc;n vi&ecirc;n mỗi ng&agrave;y v&agrave;o đầu giờ, cuối giờ v&agrave; cung cấp đầy đủ x&agrave; ph&ograve;ng, dung dịch vệ sinh tay cho mọi đối tượng tại nh&agrave; vệ sinh, khu vực c&oacute; v&ograve;i nước rửa tay. C&aacute;c nh&acirc;n vi&ecirc;n c&oacute; hiện tượng cảm c&uacute;m đều được cho nghỉ ở nh&agrave; hoặc c&aacute;ch ly ngay để đi kiểm tra y tế khi c&oacute; biểu hiện bệnh.</p>\r\n<p class="Normal"><font color="#4f4f4f"><strong>Ngo&agrave;i việc ph&ograve;ng c&uacute;m, c&aacute;c si&ecirc;u thị cũng đ&atilde; lo đối ph&oacute; với lượng kh&aacute;ch c&oacute; thể sụt giảm.</strong></font> Hiện nay, mỗi ng&agrave;y BigC Thăng Long đ&oacute;n khoảng 15.000-20.000 lượt kh&aacute;ch ra v&agrave;o. Theo &ocirc;ng Dũng, trước mắt c&aacute;c hoạt động kinh doanh vẫn ổn định song nếu dịch c&uacute;m diễn biến phức tạp, khả năng ảnh hưởng đến doanh thu cũng như m&atilde;i lực mua sắm ho&agrave;n to&agrave;n c&oacute; thể xảy ra. V&igrave; thế, Big C đ&atilde; chuẩn bị một lượng h&agrave;ng lớn sẵn s&agrave;ng phục vụ kh&aacute;ch h&agrave;ng c&oacute; nhu cầu mua h&agrave;ng về t&iacute;ch trữ trong trường hợp dịch c&uacute;m A/H1N1 b&ugrave;ng ph&aacute;t.</p>\r\n<p class="Normal">Hệ thống CoopMart v&agrave; Trung t&acirc;m thương mại Intimex H&agrave;o Nam đều khuyến kh&iacute;ch kh&aacute;ch h&agrave;ng v&agrave; đối t&aacute;c đặt h&agrave;ng qua điện thoại để nh&acirc;n vi&ecirc;n giao h&agrave;ng tận nơi, hạn chế kh&aacute;ch đến khu vực đ&ocirc;ng người. &ldquo;Chỉ những trường hợp bất đắc dĩ mới phải gặp trực tiếp đối t&aacute;c&rdquo;, vị đại diện trung t&acirc;m thương mại Intimex cho hay.</p>\r\n<p class="Normal" align="right"><strong>Hồng Anh - Ho&agrave;ng Lan</strong></p>\r\n<p align="right"><i>Theo <b>vnexpress.net</b></i></p>', 1, 1249092702, 2, 45, 0),
(31, 2, '“Hầu như ngày nào cũng có người ngã giàn giáo”', 'Theo thống kê của BV Việt Đức - Hà Nội, mỗi ngày bệnh viện tiếp nhận từ 60-80 ca cấp cứu, trong đó có 10-12 ca tai nạn lao động (TNLĐ); bệnh nhân nguy kịch do ngã giàn giáo ở các công trình xây dựng thì hầu như ngày nào cũng có.\r\n\r\n', 'no', '', '<p><strong>Thống k&ecirc; &ldquo;khi&ecirc;m tốn&rdquo;, thực tế... giật m&igrave;nh</strong></p>\r\n<p>Ng&agrave;y 31/7, BV Việt Đức tiếp nhận khoảng 8 ca cấp cứu do tai nạn lao động, trong đ&oacute; c&oacute; 2 trường hợp ng&atilde; gi&aacute;o.</p>\r\n<p>Bệnh nh&acirc;n N.T.L, 20 tuổi ở Nam Định ng&atilde; từ gi&agrave;n gi&aacute;o tầng 4 tại một c&ocirc;ng tr&igrave;nh đang thi c&ocirc;ng tr&ecirc;n địa b&agrave;n TP H&agrave; Nội, bị đa chấn thương, liệt tuỷ ho&agrave;n to&agrave;n.</p>\r\n<p>Bệnh nh&acirc;n L.V.H rơi từ ban c&ocirc;ng tầng 4 tại một c&ocirc;ng tr&igrave;nh tư nh&acirc;n, bị chấn thương sọ n&atilde;o, g&atilde;y ch&acirc;n, dập phổi, l&aacute; l&aacute;ch...</p>\r\n<p>Theo b&aacute;o c&aacute;o, mỗi ng&agrave;y bệnh viện n&agrave;y tiếp nhận từ 60-80 ca cấp cứu, trong đ&oacute; c&oacute; 10-12 ca tai nạn lao động (TNLĐ), bệnh nh&acirc;n nguy kịch do ng&atilde; gi&agrave;n gi&aacute;o ở c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng th&igrave; hầu như ng&agrave;y n&agrave;o cũng c&oacute;. Đ&aacute;ng lưu &yacute; l&agrave; BV Việt Đức chỉ l&agrave; một trong nhiều nơi tiếp nhận những ca TNLĐ trong x&acirc;y dựng đang diễn ra h&agrave;ng ng&agrave;y, tr&ecirc;n to&agrave;n quốc.</p>\r\n<p>Con số n&agrave;y ch&ecirc;nh lệch đ&aacute;ng kể so với thống k&ecirc; &ldquo;khi&ecirc;m tốn&rdquo; từ Bộ X&acirc;y dựng: Trong 6 th&aacute;ng đầu năm nay cả nước xảy ra 51 TNLĐ trong hoạt động x&acirc;y dựng, l&agrave;m chết 13 người, 60 người bị thương. Nguy&ecirc;n nh&acirc;n chủ yếu do điện, ng&atilde; cao, vật rơi, vật &eacute;p...</p>\r\n<p>Theo chuy&ecirc;n gia ph&ograve;ng An to&agrave;n lao động, Sở LĐ-TB-XH H&agrave; Nội, sở dĩ c&oacute; độ &ldquo;v&ecirc;nh&rdquo; trong b&aacute;o c&aacute;o so với thực tế l&agrave; bởi đại đa số c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng hiện nay kh&ocirc;ng được kiểm so&aacute;t đ&uacute;ng theo quy định, khoảng 80% số c&ocirc;ng tr&igrave;nh x&acirc;y dựng cao tầng vi phạm an to&agrave;n lao động. Rất nhiều tai nạn xảy ra kh&ocirc;ng được b&aacute;o c&aacute;o l&ecirc;n cơ quan quản l&yacute; do bị chủ c&ocirc;ng tr&igrave;nh t&igrave;m c&aacute;ch tự giải quyết với người lao động.</p>\r\n<p>Vấn đề thiếu an to&agrave;n lao động trong x&acirc;y dựng chỉ thực phơi bầy khi h&agrave;ng loạt TNLĐ li&ecirc;n tiếp xảy ra tại khu vực x&acirc;y dựng t&ograve;a nh&agrave; Keangnam Tower (H&agrave; Nội), g&acirc;y x&ocirc;n xao lo lắng trong x&atilde; hội.</p>\r\n<p>Mới đ&acirc;y nhất, Bộ LĐ-TB-XH đ&atilde; c&oacute; b&aacute;o nhanh về vụ việc Keangnam Landmark Tower gửi tới Thủ tướng. Việc thi c&ocirc;ng cũng đ&atilde; bị đ&igrave;nh chỉ ho&agrave;n to&agrave;n. Thanh tra lao động TP H&agrave; Nội đang gấp r&uacute;t tập trung thanh - kiểm tra lại những c&ocirc;ng tr&igrave;nh trọng điểm, trong đ&oacute; đặc biệt ch&uacute; trọng đến c&aacute;c c&ocirc;ng tr&igrave;nh cao tầng.</p>\r\n<p><strong>Trăm thiệt th&ograve;i đổ đầu người lao động</strong></p>\r\n<p>Thống k&ecirc; từ khoa Chấn thương chỉnh h&igrave;nh BV Việt Đức cho thấy, nạn nh&acirc;n trong c&aacute;c vụ tai nạn lao động trong x&acirc;y dựng thường l&agrave; nam giới ở độ tuổi từ 18 - 50, l&agrave; trụ cột kinh tế của gia đ&igrave;nh sống ở n&ocirc;ng th&ocirc;n. C&oacute; tới 50% trường hợp tai nạn được chuyển đến trong t&igrave;nh trạng rất nặng nề: g&atilde;y đốt sống cổ, chấn thương sọ n&atilde;o&hellip; dẫn tới tử vong. Nhẹ hơn th&igrave; cũng để di chứng l&acirc;u d&agrave;i, mất đi khả năng lao động, sau thời gian điều trị với chi ph&iacute; tốn k&eacute;m.</p>\r\n<p>Theo Bộ trưởng Bộ LĐ-TB-XH Nguyễn Kim Ng&acirc;n, nguy&ecirc;n nh&acirc;n ch&iacute;nh khi TNLĐ xảy ra được x&aacute;c định l&agrave; do cả người sử dụng lao động lẫn người lao động chưa hiện tốt c&ocirc;ng t&aacute;c ATLĐ. Tuy nhi&ecirc;n lỗi phần nhiều l&agrave; do b&ecirc;n sử dụng lao động thường xuy&ecirc;n &ldquo;lờ&rdquo; những quy định thực thi an to&agrave;n lao động, nhằm tiết kiệm chi ph&iacute;.</p>\r\n<p>Thực tế đang diễn tại trong c&aacute;c c&ocirc;ng tr&igrave;nh x&acirc;y dựng cao tầng l&agrave; t&igrave;nh trạng c&aacute;c doanh nghiệp th&ocirc;ng qua cai thầu kho&aacute;n trắng c&ocirc;ng tr&igrave;nh. Nhận phần việc cai thầu thường về c&aacute;c tỉnh tuyển c&ocirc;ng nh&acirc;n. Họ hầu hết l&agrave; những người chưa c&oacute; kiến thức v&agrave; &yacute; thức về an to&agrave;n lao động n&ecirc;n cũng kh&ocirc;ng mấy khi đ&ograve;i hỏi về thiết bị an to&agrave;n khi l&agrave;m việc tại c&ocirc;ng tr&igrave;nh.</p>\r\n<p>Rất nhiều người trong số họ l&agrave;m việc theo kiểu kho&aacute;n ng&agrave;y c&ocirc;ng. Đến khi tai nạn xảy ra, trong tay kh&ocirc;ng c&oacute; hợp đồng lao động, lại kh&ocirc;ng am hiểu ph&aacute;p luật n&ecirc;n kh&ocirc;ng biết cầu cứu ở đ&acirc;u, đ&agrave;nh ngậm ng&ugrave;i nhận những khoản tiền đền b&ugrave; &iacute;t ỏi.</p>\r\n<p>Trả lời c&acirc;u hỏi v&igrave; sao c&aacute;c c&ocirc;ng tr&igrave;nh kh&ocirc;ng chấp h&agrave;nh quy định thực thi an to&agrave;n lao động đ&atilde; được quy định r&otilde; r&agrave;ng, &ocirc;ng Vũ Như Văn - Ph&oacute; Cục trưởng Cục An to&agrave;n lao động (Bộ LĐ-TB-XH) cho biết, bởi mức phạt với c&aacute;c doanh nghiệp vi phạm quy định về ATLĐ hiện qu&aacute; nhẹ. Hiện nay, ch&aacute;nh thanh tra được phạt tối đa 20 triệu đồng. Cần nhanh ch&oacute;ng n&acirc;ng mức xử phạt nghi&ecirc;m hơn nữa để đạt mục đ&iacute;ch răn đe v&agrave; ngăn chặn vi phạm.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, bản th&acirc;n người lao động cũng cần n&acirc;ng cao &yacute; thức tự bảo vệ m&igrave;nh. Y&ecirc;u cần được cung cấp đầy đủ phương tiện bảo hộ lao động cho m&igrave;nh. Hơn ai hết, người lao động phải hiểu khi tai nạn xảy ra, chịu thiệt th&ograve;i nhiều nhất ch&iacute;nh l&agrave; bản th&acirc;n m&igrave;nh./.</p>\r\n<div class="cssDefaultSupportLinks" style="padding-right: 5px; height: 24px" align="right"><i>Nguồn:</i>&nbsp; <b>D&acirc;n tr&iacute; 1/8/2009</b></div>', 1, 1249533179, 2, 17, 0),
(28, 2, 'Khởi tố hình sự vụ vi phạm tại công trình Keangnam', 'Trong báo cáo gửi lãnh đạo thành phố Hà Nội, ông Ha Jong Suk, Chủ tịch Keangnam Vina cam kết, sau khi kiểm tra, đánh giá “đảm bảo an toàn”, các nhà thầu mới được tiếp tục triển khai thi công', 'no', '', '<font size="2" face="Arial">Ng&agrave;y 31/7, Cơ quan điều tra C&ocirc;ng an huyện Từ Li&ecirc;m (H&agrave; Nội) đ&atilde; ra quyết định khởi tố h&igrave;nh sự đối với h&agrave;nh vi vi phạm quy định x&acirc;y dựng g&acirc;y hậu quả nghi&ecirc;m trọng tại c&ocirc;ng tr&igrave;nh x&acirc;y dựng to&agrave; nh&agrave; Keangnam - Vina (đường Phạm H&ugrave;ng- H&agrave; Nội) theo điều 229 Bộ luật H&igrave;nh sự.</font>\r\n<p align="justify"><font size="2" face="Arial">Cụ thể, trong qu&aacute; tr&igrave;nh thi c&ocirc;ng to&agrave; nh&agrave; Keangnam, li&ecirc;n tiếp trong một tuần từ 21 &ndash; 27/7/2009, đ&atilde; xảy ra 7 vụ tai nạn đặc biệt nghi&ecirc;m trọng l&agrave;m 4 c&ocirc;ng nh&acirc;n, c&aacute;n bộ kỹ thuật tử vong trong l&uacute;c đang thi c&ocirc;ng ở c&aacute;c tầng cao v&agrave; l&agrave;m bị thương 3 c&ocirc;ng nh&acirc;n, c&aacute;n bộ kỹ thuật kh&aacute;c. </font></p>\r\n<p align="justify"><font size="2" face="Arial">Trong b&aacute;o c&aacute;o gửi l&atilde;nh đạo th&agrave;nh phố H&agrave; Nội, &ocirc;ng Ha Jong Suk, Chủ tịch Keangnam Vina cam kết, sau khi kiểm tra, đ&aacute;nh gi&aacute; &ldquo;đảm bảo an to&agrave;n&rdquo;, c&aacute;c nh&agrave; thầu mới được tiếp tục triển khai thi c&ocirc;ng.</font></p>\r\n<p align="justify"><font size="2" face="Arial">Tuy nhi&ecirc;n, trưa 27/7, ngay sau khi thi c&ocirc;ng trở lại, 3 c&ocirc;ng nh&acirc;n v&agrave; c&aacute;n bộ kỹ thuật l&agrave;m việc tại hầm thang m&aacute;y tầng 13 t&ograve;a th&aacute;p A Hanoi Landmark Tower đ&atilde; trượt ch&acirc;n, rơi c&ugrave;ng tấm cốp pha v&agrave; bị thương./.</font></p>\r\n<p>&nbsp;</p>\r\n<p class="textbody" align="right"><font color="#999999"><em>Theo TTXVN/VietNam+</em></font></p>\r\n<p align="right"><i>Theo <b>www.ktdt.com.vn</b></i></p>', 1, 1249093197, 2, 18, 0),
(29, 2, 'Cúm H1N1 ‘tấn công’ cao ốc thứ hai ở TP HCM', '40 nhân viên Công ty phân phối Khí áp thấp có văn phòng tại lầu 4 cao ốc Citilight ở đường Võ Thị Sáu, được y tế dự phòng quận 1 lấy danh sách giám sát chiều nay, sau khi một nhân viên của công ty được xác định mắc H1N1', '29.jpg', '', '&gt;<a class="Lead" rel="nofollow">&lsquo;C&uacute;m đang lan với cấp số nh&acirc;n&rsquo;</a>\r\n<p class="Normal">Bệnh nh&acirc;n cho hay, sau 3 ng&agrave;y bị l&ecirc;n cơn sốt, vi&ecirc;m họng v&agrave; ho, lo sợ, h&ocirc;m qua anh đ&atilde; tự c&aacute;ch ly với mọi người trong gia đ&igrave;nh, kh&ocirc;ng đến l&agrave;m việc v&agrave; đi x&eacute;t nghiệm. Kết quả chiều nay cho thấy, anh dương t&iacute;nh với c&uacute;m H1N1.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n    <tbody>\r\n        <tr>\r\n            <td><img border="1" width="450" height="350" alt="" src="http://media.tinmoi.vn/2009/07/31/2.jpg" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="Image">T&ograve;a cao ốc văn ph&ograve;ng Citilight. Ảnh:<em> Thi&ecirc;n Chương.</em></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p class="Normal">Đang được điều trị tại Bệnh viện Bệnh Nhiệt đới TP HCM trong t&igrave;nh trạng sức khỏe ổn định, bệnh nh&acirc;n cho biết, cuối tuần trước anh c&oacute; ngồi &ldquo;lai rai&rdquo; với năm người bạn, một trong những người n&agrave;y l&agrave;m việc c&ugrave;ng văn ph&ograve;ng với một người đ&atilde; nhiễm c&uacute;m tại <a class="Normal" rel="nofollow"><font color="#0f0f0f">t&ograve;a nh&agrave; Petro Viet Nam</font></a>. Như vậy Citilight l&agrave; cao ốc thứ hai tại TP HCM c&oacute; người bị c&uacute;m H1N1, sau t&ograve;a nh&agrave; Petro.</p>\r\n<p class="Normal">Trao đổi với <em>VnExpress.net</em>, đại diện C&ocirc;ng ty ph&acirc;n phối Kh&iacute; &aacute;p thấp cho hay, văn ph&ograve;ng c&oacute; hơn 40 nh&acirc;n vi&ecirc;n. Nguy cơ l&acirc;y nhiễm rộng l&agrave; rất cao v&igrave;, thứ nhất, cả t&ograve;a nh&agrave; d&ugrave;ng chung hệ thống m&aacute;y lạnh, thang m&aacute;y. Thứ hai trước khi trở bệnh, bệnh nh&acirc;n đ&atilde; tiếp x&uacute;c với rất nhiều người trong ph&ograve;ng. Đặc biệt ng&agrave;y 29/7, nh&acirc;n vi&ecirc;n nhiễm c&uacute;m đ&atilde; ngồi họp c&ugrave;ng hơn 10 người tại ph&ograve;ng họp rộng khoảng 10 m2.</p>\r\n<p class="Normal">Một giờ đồng hồ sau khi bệnh nh&acirc;n được x&aacute;c định dương t&iacute;nh, khoảng 5 người tiếp x&uacute;c gần với bệnh nh&acirc;n đ&atilde; c&oacute; triệu chứng mặt đỏ bừng, sốt tr&ecirc;n 38 độ C v&agrave; vi&ecirc;m họng.</p>\r\n<p class="Normal">Đại diện cao ốc văn ph&ograve;ng Citilight cho hay, hiện chưa thể thống k&ecirc; hết số lượng nh&acirc;n vi&ecirc;n l&agrave;m việc tại t&ograve;a nh&agrave;, song c&oacute; hơn 20 c&ocirc;ng ty đ&oacute;ng trụ sở tại đ&acirc;y. Hiện cao ốc đ&atilde; được vệ sinh to&agrave;n bộ khu vực bằng dung dịch khử khuẩn. Trong khi đ&oacute;, Trung t&acirc;m y tế Dự ph&ograve;ng quận 1 chỉ mới đến khảo s&aacute;t, lấy danh s&aacute;ch địa chỉ của c&aacute;c nh&acirc;n vi&ecirc;n.</p>\r\n<p class="Normal">Sau khi hay tin c&oacute; đồng nghiệp mắc c&uacute;m, nhiều nh&acirc;n vi&ecirc;n C&ocirc;ng ty ph&acirc;n phối Kh&iacute; &aacute;p thấp tỏ ra hoang mang. &ldquo;T&ocirc;i c&oacute; n&oacute;i chuyện nhiều lần với anh ấy. Bản th&acirc;n t&ocirc;i v&agrave; nhiều người trong cơn quan c&oacute; con nhỏ v&agrave; cha mẹ gi&agrave;, kh&ocirc;ng biết phải t&iacute;nh sao đ&acirc;y&rdquo;, một nh&acirc;n vi&ecirc;n n&oacute;i.</p>\r\n<p class="Normal">Trung t&acirc;m Y tế dự ph&ograve;ng quận 1 cho biết sẽ gi&aacute;m s&aacute;t chặt những ca sốt nghi c&uacute;m v&agrave; nh&acirc;n vi&ecirc;n sinh sống tr&ecirc;n địa b&agrave;n. Những người sinh sống ở quận kh&aacute;c sẽ được gửi danh s&aacute;ch về địa phương để gi&aacute;m s&aacute;t.</p>\r\n<table border="0" cellspacing="0" cellpadding="3" width="1" align="center">\r\n    <tbody>\r\n        <tr>\r\n            <td><img border="1" width="450" height="350" alt="" src="http://media.tinmoi.vn/2009/07/31/1249051344_3.jpg" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="Image">Nhiều nh&acirc;n vi&ecirc;n hoang mang khi biết đồng nghiệp nhiễm H1N1. Ảnh: <em>Thi&ecirc;n Chương.</em></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p class="Normal">S&aacute;ng c&ugrave;ng ng&agrave;y, Sở Y tế TP HCM cho hay, <strong><font color="#4f4f4f">bệnh viện d&atilde; chiến tại trường trung học tư thục Ng&ocirc; Thời Nhiệm quận 9 đ&atilde; ch&iacute;nh thức th&ocirc;i giữ nhiệm vụ chữa trị c&uacute;m v&igrave; c&aacute;c ca bệnh đ&atilde; khỏi</font></strong>. Dự kiến trong v&agrave;i ng&agrave;y tới, bệnh viện d&atilde; chiến trường tư thục Nguyễn Khuyến (T&acirc;n B&igrave;nh) cũng sẽ được &ldquo;giải ph&oacute;ng&rdquo;.</p>\r\n<p class="Normal">Theo đ&aacute;nh gi&aacute; của &ocirc;ng Nguyễn Văn Ch&acirc;u, Gi&aacute;m đốc Sở Y tế TP HCM, ng&agrave;nh y tế th&agrave;nh phố đang gi&aacute;m s&aacute;t tốt, kh&ocirc;ng để bệnh l&acirc;y lan đối với những trường hợp đ&atilde; được x&aacute;c định dương t&iacute;nh.</p>\r\n<p class="Normal">Chiều nay, Sở Y tế tiếp tục kiểm tra t&igrave;nh h&igrave;nh ph&ograve;ng chống dịch H1N1 tại c&aacute;c khu c&ocirc;ng nghiệp, c&aacute;c khu nh&agrave; ở c&ocirc;ng nh&acirc;n tại quận T&acirc;n B&igrave;nh.</p>\r\n<p class="Normal">B&aacute;o c&aacute;o trong buổi họp giao ban gi&aacute;m đốc c&aacute;c bệnh viện tại TP HCM chiều nay, tiến sĩ Trần Tịnh Hiền một lần nữa khẳng định, cả 400 ca bệnh tại th&agrave;nh phố đều ở dạng nhẹ. Kh&ocirc;ng c&oacute; trường hợp n&agrave;o tử vong. Hai ca nặng l&agrave; một phụ nữ mang thai v&agrave; một người bị vi&ecirc;m phổi, tuy nhi&ecirc;n cả hai cũng đ&atilde; xuất viện. Việc hầu hết bệnh nh&acirc;n khỏi bệnh, theo &ocirc;ng Hiền l&agrave; do ph&aacute;t hiện sớm, điều trị kịp thời.</p>\r\n<p class="Normal">C&ograve;n theo Gi&aacute;m đốc Sở Y tế Nguyễn Văn Ch&acirc;u, việc tăng cường nhiều bệnh viện quận huyện trong điều trị c&uacute;m H1N1 c&ugrave;ng với ph&acirc;n loại bệnh nh&acirc;n nặng - nhẹ để điều trị tại từng bệnh viện, đ&atilde; g&oacute;p phần l&agrave;m giảm khả năng l&acirc;y ch&eacute;o. C&aacute;c bệnh nh&acirc;n cũng mau khỏi bệnh hơn do m&ocirc;i trường điều trị th&ocirc;ng tho&aacute;ng.</p>\r\n<p class="Normal" align="right"><strong>Thi&ecirc;n Chương</strong></p>\r\n<p align="right"><i>Theo <b>vnexpress.net</b></i></p>', 1, 1249093679, 2, 20, 0),
(30, 2, 'Trường học, doanh nghiệp, trung tâm thương mại cần quyết liệt phòng, chống cúm A/H1N1', '(ĐNDT) - Đó là chỉ đạo của Phó Chủ tịch UBND thành phố Nông Thị Ngọc Minh tại cuộc họp BCĐ phòng, chống dịch cúm A/H1N1 thành phố tổ chức ngày 31-7. \r\n', '30.jpg', '', 'Cuộc họp c&oacute; sự tham dự của l&atilde;nh đạo c&aacute;c Sở Y tế, Gi&aacute;o dục - Đ&agrave;o tạo, C&ocirc;ng thương, Văn h&oacute;a, Thể thao v&agrave; Du lịch, c&aacute;c quận, huyện nhằm siết chặt c&ocirc;ng t&aacute;c ph&ograve;ng, chống dịch c&uacute;m A/H1N1 c&aacute;c cấp, c&aacute;c ng&agrave;nh tr&ecirc;n địa b&agrave;n th&agrave;nh phố trong thời gian tới. <br />\r\n<br />\r\n<table align="center" style="width: 400px">\r\n    <tbody>\r\n        <tr>\r\n            <td><img onclick="return openNewImage(this.src)" style="cursor: hand" alt="" src="http://media.baodanang.vn/Library/images/14/2009/08/phun%20thuoc%20cum.JPG" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td><font color="#0000ff" size="1">Chuẩn bị m&aacute;y bơm phun thuốc xử l&yacute; m&ocirc;i trường trong trường hợp xuất hiện trường hợp nghi nhiễm c&uacute;m A/H1N1 trong cộng đồng.</font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<br />\r\nTheo b&aacute;o c&aacute;o của Sở Y tế, t&iacute;nh đến ng&agrave;y 30-7, trong số 8 trường hợp dương t&iacute;nh với c&uacute;m A/H1N1 được&nbsp;ph&aacute;t hiện v&agrave; thu dung điều trị tại Bệnh viện Đ&agrave; Nẵng, hiện đ&atilde; c&oacute; 7 trường hợp x&eacute;t nghiệm lại &acirc;m t&iacute;nh với virus c&uacute;m A/H1N1 v&agrave; được xuất viện. 1 trường hợp đang điều trị c&aacute;ch ly v&agrave; 2 trường hợp kh&aacute;c đang được gi&aacute;m s&aacute;t c&aacute;ch ly v&igrave; c&oacute; những biểu hiện nghi nhiễm c&uacute;m. <br />\r\n<br />\r\nCho đến nay, c&ocirc;ng t&aacute;c gi&aacute;m s&aacute;t v&agrave; ph&aacute;t hiện trường hợp nghi nhiễm đ&atilde; được ng&agrave;nh y tế mở rộng thực hiện tại những cơ sở h&agrave;nh nghề y dược tư nh&acirc;n như ở c&aacute;c ph&ograve;ng kh&aacute;m tư, bệnh viện tư cũng như tại c&aacute;c cửa khẩu đường kh&ocirc;ng, đường thủy. <br />\r\n<br />\r\nTuy vậy, trước khả năng l&acirc;y lan nhanh của dịch, nhất l&agrave; bệnh đ&atilde; bắt đầu x&acirc;m nhập tại c&aacute;c trường học v&agrave; c&ocirc;ng sở tại H&agrave; Nội v&agrave; th&agrave;nh phố Hồ Ch&iacute;nh Minh trong thời gian gần đ&acirc;y, n&ecirc;n số lượng bệnh nh&acirc;n mắc c&uacute;m A/H1N1 tại Đ&agrave; Nẵng được nhận định sẽ tiếp tục tăng v&agrave; diễn biến phức tạp hơn trong thời gian tới, nhất l&agrave; thời điểm gần khai giảng năm học mới 2009-2010. <br />\r\n<br />\r\nPh&aacute;t biểu chỉ đạo tại cuộc họp,&nbsp;b&agrave; N&ocirc;ng Thị Ngọc Minh nhấn mạnh, Sở Gi&aacute;o dục- Đ&agrave;o tạo cần phải nhanh ch&oacute;ng thực hiện th&agrave;nh lập BCĐ ph&ograve;ng, chống dịch c&uacute;m A/H1N1 ng&agrave;nh v&agrave; tại trường học thuộc c&aacute;c cấp học trước ng&agrave;y 15-8 theo tinh thần chỉ đạo của Bộ Gi&aacute;o dục- Đ&agrave;o tạo. C&aacute;c Sở C&ocirc;ng thương, Kế hoạch-Đầu tư, Văn h&oacute;a, Thể thao v&agrave; Du lịch cần chủ động triển khai quyết liệt c&aacute;c phương &aacute;n ph&ograve;ng, chống ngăn ngừa v&agrave; cảnh gi&aacute;c với dịch bệnh tại c&aacute;c trung t&acirc;m thương mại, BQL c&aacute;c Khu chế xuất v&agrave;&nbsp;c&ocirc;ng nghiệp, c&aacute;c điểm vui chơi giải tr&iacute;, c&aacute;c cơ sở du lịch. <br />\r\n<br />\r\nSở Y tế khẩn trương tiến h&agrave;nh khảo s&aacute;t c&aacute;c trạm&nbsp;y tế x&atilde;, phường được chọn l&agrave;m điểm để triển khai thực hiện c&ocirc;ng t&aacute;c ph&ograve;ng, chống dịch c&uacute;m A/H1N1; kiểm tra chặt chẽ phương &aacute;n c&aacute;ch ly gi&aacute;m s&aacute;t v&agrave; điều trị bệnh tại c&aacute;c bệnh viện tuyến quận, huyện v&agrave; c&oacute; th&ecirc;m những văn bản hướng dẫn cụ thể về bệnh v&agrave; c&ocirc;ng t&aacute;c ph&ograve;ng ngừa căn bệnh n&agrave;y để n&acirc;ng cao hiểu biết v&agrave; chủ động ph&ograve;ng bệnh cho người d&acirc;n. <br />\r\n<br />\r\n<p>&nbsp;</p>\r\n<div align="justify">\r\n<table align="center" style="width: 450px">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor="#add8e6">Ng&agrave;y 31-7, Việt Nam đ&atilde; ghi nhận th&ecirc;m 56 trường hợp dương t&iacute;nh với c&uacute;m A/H1N1, (miền Nam: 45 ca, miền Bắc: 6 ca, miền Trung: 4 ca, T&acirc;y Nguy&ecirc;n: 1 ca), n&acirc;ng tổng số&nbsp;trường hợp nhiễm c&uacute;m l&ecirc;n&nbsp;850, kh&ocirc;ng c&oacute; tử vong. Số bệnh nh&acirc;n đ&atilde; ra viện l&agrave; 452, 398 trường hợp c&ograve;n lại hiện đang được c&aacute;ch ly, điều trị tại c&aacute;c bệnh viện, cơ sở điều trị, gi&aacute;m s&aacute;t cộng đồng trong t&igrave;nh trạng sức khỏe ổn định, kh&ocirc;ng c&oacute; biến chứng nặng.</td>\r\n        </tr>\r\n        <tr>\r\n            <td>&nbsp;</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<div align="justify">Tin v&agrave; ảnh: <strong>D.M </strong></div>', 1, 1249354364, 2, 24, 0),
(32, 2, 'Hà Nội \\''cháy\\'' khẩu trang chống cúm', '(24h) - Công sở phát khẩu trang cho nhân viên, nhà nhà cũng tìm mua để phòng dịch cúm. Khẩu trang bỗng trở thành mặt hàng khan hiếm và giá đội thêm 3-5 lần.', '32.jpg', '', '<div class="link-top-content"><span class="link_external">●&nbsp;&nbsp;&nbsp; <a target="_blank" href="http://www.24h.com.vn/news/detail/46/243906/Truong-hoc-thu-hai-tai-Ha-Noi-nhiem-H1N1.24h">Trường học thứ hai tại H&agrave; Nội nhiễm H1N1</a> &nbsp;</span><br />\r\n<span class="link_external">●&nbsp;&nbsp;&nbsp; <a target="_blank" href="http://www.24h.com.vn/news/detail/46/243742/Benh-nhan-H1N1-tu-vong-dau-tien-o-Viet-Nam.24h?news_nextnews=0">Bệnh nh&acirc;n H1N1 tử vong đầu ti&ecirc;n ở Việt Nam</a>&nbsp;&nbsp;</span><br />\r\n&nbsp;</div>\r\n<div class="content-noidung"><SCRIPT>window.onload = function () {resizeNewsImage("news-image", 500);}</SCRIPT>\r\n<p style="text-align: justify"><font style="font-size: 10pt"><font style="font-family: Verdana">Trong v&ograve;ng hơn một tuần gần đ&acirc;y, dịch c&uacute;m A/H1N1 ho&agrave;nh h&agrave;nh, nhiều t&ograve;a nh&agrave; chung cư, si&ecirc;u thị đ&atilde; phải tức tốc mua khẩu trang cho nh&acirc;n vi&ecirc;n. Lượng cầu tăng đột biến khiến khẩu trang y tế trở th&agrave;nh mặt h&agrave;ng khan hiếm. Đại diện trung t&acirc;m thương mại Intimex (131 H&agrave;o Nam, H&agrave; Nội) cho hay, khi nghe tin về dịch c&uacute;m A/H1N1, Intimex đ&atilde; đến Trung t&acirc;m Y tế dự ph&ograve;ng t&igrave;m mua nhưng đ&atilde; phải tay trắng về kh&ocirc;ng v&igrave; hết h&agrave;ng.<br />\r\n<br />\r\n</font></font><font style="font-size: 10pt"><font style="font-family: Verdana">Hiện c&oacute; nhiều loại khẩu trang ph&ograve;ng chống c&uacute;m được b&aacute;n tr&ecirc;n thị trường như khẩu trang y tế chống c&uacute;m d&ugrave;ng một lần, sợi hoạt t&iacute;nh Kissy, Annilife, đặc chủng N95 v&agrave; Kingstar của Trung Quốc...</font></font></p>\r\n<p style="text-align: center"><font style="font-size: 10pt"><font style="font-family: Verdana"><img class="news-image" alt="H&agrave; Nội ''ch&aacute;y'' khẩu trang chống c&uacute;m, Tin tức - Sự kiện, " onclick="return openNewImage(this, '''')" src="http://www16.24h.com.vn/upload/news/2009-08-06/1249523750-Hanoi-chay-hang-khau-trang-1.jpg" /></font></font></p>\r\n<p style="text-align: center"><font style="color: #0000ff"><em><font style="font-size: 10pt"><font style="font-family: Verdana">C&ocirc;ng sở, trường học đồng loạt mua khẩu trang khiến cầu tăng vọt. </font></font></em></font></p>\r\n<p style="text-align: justify"><font style="font-size: 10pt"><font style="font-family: Verdana">Theo c&ocirc;ng ty tr&aacute;ch nhiệm dược phẩm Tr&iacute; H&ugrave;ng (H&agrave; Nội), hiện tr&ecirc;n thị trường chỉ c&ograve;n khẩu trang hoạt t&iacute;nh Kissy v&agrave; loại nhập khẩu từ Trung Quốc. C&ograve;n khẩu trang giấy v&agrave; đặc chủng N95 đ&atilde; hết c&aacute;ch đ&acirc;y 3, 4 h&ocirc;m. &quot;Khẩu trang Kissy với 3 lớp l&oacute;t c&oacute; khả năng chống kh&iacute; &ocirc; nhiễm, lọc sạch kh&ocirc;ng kh&iacute;, bảo vệ da mặt v&agrave; phổi khỏi bụi. Loại khẩu trang n&agrave;y c&oacute; thể giặt sạch sử dụng nhiều lần n&ecirc;n được nhiều người lựa chọn. Ri&ecirc;ng khẩu trang nhập từ Trung Quốc rất &iacute;t người mua&quot;, nh&acirc;n vi&ecirc;n c&ocirc;ng ty Tr&iacute; H&ugrave;ng cho hay.</font></font></p>\r\n<p style="text-align: justify"><font style="font-size: 10pt"><font style="font-family: Verdana">Gi&aacute; của c&aacute;c loại khẩu trang tại khu Th&agrave;nh C&ocirc;ng, Khương Trung, Giải Ph&oacute;ng, H&agrave; Nội cũng kh&aacute;c nhau. Nhiều khẩu trang đồng loạt tăng gi&aacute; 3-5 lần. Khẩu trang Trung Quốc trước kia khoảng 1.000 đồng th&igrave; nay l&ecirc;n 3.000- 5.000 đồng. C&aacute; biệt, loại khẩu trang c&oacute; chứa chất h&oacute;a học hữu cơ v&agrave; than hoạt t&iacute;nh l&ecirc;n 135.000 đồng một chiếc.</font></font></p>\r\n<p style="text-align: justify"><font style="font-size: 10pt"><font style="font-family: Verdana">Theo một số cửa h&agrave;ng dược phẩm, khẩu trang tr&ecirc;n thị trường H&agrave; Nội được nhập chủ yếu từ c&ocirc;ng ty Cổ phần Y tế Danameco (Đ&agrave; Nẵng), c&ocirc;ng ty TNHHH sản xuất Bảo Thạch (TP HCM). Một số &iacute;t c&ograve;n lại c&oacute; nguồn gốc từ Trung Quốc.</font></font></p>\r\n<p style="text-align: justify"><font style="font-size: 10pt"><font style="font-family: Verdana">B&agrave; Phạm Thị Minh Trang, Chủ tịch hội đồng quản trị kiểm Tổng gi&aacute;m đốc c&ocirc;ng ty Cổ phần Y tế Danameco cho hay, những ng&agrave;y cuối th&aacute;ng 7, lượng khẩu trang sản xuất l&ecirc;n tới 60.000 - 100.000 chiếc mỗi ng&agrave;y. Trong đ&oacute;, b&aacute;n ra H&agrave; Nội v&agrave; TP HCM lần lượt l&agrave; 30.000 v&agrave; 20.000 chiếc. Lượng cung đưa ra kh&ocirc;ng &iacute;t, tuy nhi&ecirc;n, xảy ra t&iacute;nh trạng khan hiếm do nhiều tỉnh th&agrave;nh đồng loạt đặt h&agrave;ng. Ngo&agrave;i ra c&ograve;n do một số tiểu thương ghim h&agrave;ng chờ gi&aacute; cao mới tung ra thị trường.</font></font></p>\r\n<p style="text-align: justify"><font style="font-size: 10pt"><font style="font-family: Verdana">B&agrave; Trang cho biết, giữa th&aacute;ng 8, lượng sản xuất sẽ tăng gấp 3- 5 lần. &quot;Ch&uacute;ng t&ocirc;i dự kiến sản xuất khoảng 400.000- 500.000 chiếc mỗi ng&agrave;y. Số lượng nhiều như vậy sẽ đ&aacute;p ứng nhu cầu người ti&ecirc;u d&ugrave;ng v&agrave; khẩu trang chắc chắn sẽ hạ gi&aacute;&rdquo;, b&agrave; Trang khẳng định.</font></font></p>\r\n<p style="text-align: justify"><font style="font-size: 10pt"><font style="font-family: Verdana">&Ocirc;ng Nguyễn C&ocirc;ng Tảo, Trưởng ph&ograve;ng xử l&yacute; dịch, Sở Y tế H&agrave; Nội cho hay, mang khẩu trang l&agrave; c&aacute;ch tốt để ph&ograve;ng chống c&uacute;m H1N1. Trong trường hợp khẩu trang khan hiếm, ngo&agrave;i khẩu trang đặc chủng ph&ograve;ng chống dịch H1N1, người d&acirc;n n&ecirc;n sử dụng c&aacute;c loại khẩu trang vải th&ocirc;ng thường khi giao lưu tiếp x&uacute;c hằng ng&agrave;y với điều kiện phải giặt giũ, s&aacute;t khuẩn hằng ng&agrave;y, trước khi sử dụng. &quot;Khi đeo khẩu trang từ ngo&agrave;i đường về nh&agrave;, bạn n&ecirc;n giặt sạch bằng x&agrave; ph&ograve;ng hoặc ng&acirc;m bằng c&aacute;c chất s&aacute;t khuẩn trước khi sử dụng lần thứ hai&quot;, &ocirc;ng Tảo khuy&ecirc;n.</font></font></p>\r\n<div class="nguontin">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 24H.COM.VN (Theo VNE)</div>\r\n</div>', 1, 1249533531, 2, 61, 0);
INSERT INTO `tgp_cms` (`id`, `cat`, `ten`, `chu_thich`, `hinh`, `hinh_note`, `noi_dung`, `hien_thi`, `time`, `user`, `luot_xem`, `noi_bat`) VALUES
(33, 2, 'Bất cẩn trong lúc thi công, một công nhân tử vong (Tuổi Trẻ)', 'TTO - Một tai nạn lao động đã xảy ra vào lúc 14g30 ngày 6-8 tại căn nhà số 557 đường Đỗ Xuân Hợp (P.Phước Long B Q.9 TP.HCM).\r\n', '33.jpg', '', '<p class="pHead">\r\n<table class="tLegend" border="0" cellspacing="0" cellpadding="0" width="40" align="right" style="border-collapse: separate">\r\n    <tbody>\r\n        <tr>\r\n            <td><img border="1" hspace="0" width="250" height="194" hyperlink="" alt="" src="http://www.tuoitre.com.vn/Tianyon/ImageView.aspx?ThumbnailID=352858" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td>Hiện trường nơi xảy ra tai nạn</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n<p class="pHead">&nbsp;</p>\r\n<p class="pBody">Nạn nh&acirc;n được x&aacute;c định l&agrave; anh Phạm Văn Khoa (27 tuổi, ngụ Ninh B&igrave;nh) l&agrave; c&ocirc;ng nh&acirc;n c&ocirc;ng ty TNHH Phong Ch&acirc;u. Theo nhiều người chứng kiến, căn nh&agrave; tr&ecirc;n đang được th&aacute;o dỡ để chuẩn bị x&acirc;y mới, trong l&uacute;c anh Khoa đang l&agrave;m việc ph&iacute;a trước mặt tiền nh&agrave;, bất ngờ từ b&ecirc;n trong, cần cẩu đang hoạt động đ&atilde; va chạm với khối mặt dựng b&ecirc;n tr&ecirc;n c&oacute; k&iacute;ch thước 4mx1m bằng gạch rơi tr&uacute;ng khiến anh chết tại chỗ.</p>\r\n<p class="pBody">Cơ quan chức năng thuộc phường Phước Long B v&agrave; Quận 9 đ&atilde; kịp thời c&oacute; mặt điều tra l&agrave;m r&otilde;.</p>\r\n<p class="pAuthor">Tin, ảnh: TRẦN CH&Aacute;NG NGHĨA</p>', 1, 1249700263, 2, 22, 0),
(34, 2, '14 bác sĩ, điều dưỡng viên ở TP.HCM nhiễm cúm A/H1N1 (Tuổi Trẻ)', '* WHO: 1.154 trường hợp tử vong do cúm A/H1N1\r\n\r\n', 'no', '', '<p class="\\&quot;pHead\\&quot;">Ng&agrave;y 5-8, b&aacute;c sĩ Nguyễn Đức C&ocirc;ng, Gi&aacute;m đốc bệnh viện Thống Nhất TP.HCM cho biết: đ&atilde; c&oacute; 14 b&aacute;c sĩ v&agrave; điều dưỡng vi&ecirc;n của bệnh viện bị nhiễm c&uacute;m A/H1N1. Đa số c&aacute;c nh&acirc;n vi&ecirc;n n&agrave;y đều l&agrave;m việc tại Khoa kh&aacute;m chữa bệnh v&agrave; kh&ocirc;ng c&oacute; triệu chứng l&acirc;m s&agrave;ng của c&uacute;m A/H1N1. Ngo&agrave;i ra, 9 bệnh nh&acirc;n kh&aacute;c đang bị l&acirc;y ch&eacute;o do tiếp x&uacute;c gần với c&aacute;c nh&acirc;n vi&ecirc;n tr&ecirc;n.</p>\r\n<p class="\\&quot;pBody\\&quot;">Bệnh viện đ&atilde; c&aacute;ch ly to&agrave;n bộ nh&acirc;n vi&ecirc;n y tế bị nhiễm c&uacute;m, tiến h&agrave;nh x&eacute;t nghiệm hơn 90 trường hợp tiếp x&uacute;c gần với c&aacute;c trường hợp nhiễm bệnh v&agrave; theo d&otilde;i chặt chẽ t&igrave;nh h&igrave;nh sức khỏe của tất cả c&aacute;c c&aacute;n bộ c&ocirc;ng nh&acirc;n vi&ecirc;n. To&agrave;n bộ bệnh viện cũng đ&atilde; được phun xịt thuốc s&aacute;t khuẩn.</p>\r\n<p class="\\&quot;pBody\\&quot;">C&aacute;c khoa, ph&ograve;ng đ&atilde; được th&ocirc;ng b&aacute;o mở cửa để th&ocirc;ng tho&aacute;ng kh&iacute;, ph&aacute;t khẩu trang cho bệnh nh&acirc;n, khuyến c&aacute;o bệnh nh&acirc;n c&aacute;c biện ph&aacute;p ph&ograve;ng chống dịch c&uacute;m A/H1N1 theo hướng dẫn của Bộ Y tế. Đồng thời, th&ocirc;ng b&aacute;o cho bệnh nh&acirc;n tại c&aacute;c khoa hạn chế đi lại, đặc biệt l&agrave; những khoa điều trị cho c&aacute;n bộ cao tuổi.</p>\r\n<p class="\\&quot;pBody\\&quot;">\r\n<table class="\\&quot;tLegend\\&quot;" border="\\&quot;0\\&quot;" cellspacing="\\&quot;3\\&quot;" cellpadding="\\&quot;0\\&quot;" style="&quot;border-collapse: ">\r\n    <tbody>\r\n        <tr>\r\n            <td><img border="\\&quot;1\\&quot;" alt="\\&quot;\\&quot;" hyperlink="\\&quot;\\&quot;" src="\\&quot;http://www.tuoitre.com.vn/Tianyon/ImageView.aspx?ThumbnailID=352640\\&quot;" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td>Kiểm tra sức khỏe học sinh bị nghi nhiễm c&uacute;m A/H1N1 tại khu c&aacute;ch ly Bệnh viện Phạm Ngọc Thạch, TP.HCM - <em>Ảnh:</em> N.C.T. - <em>Tuổi Trẻ</em></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n<p class="\\&quot;pBody\\&quot;">* Ng&agrave;y 5-8, Tổ chức Y tế Thế giới (WHO) đ&atilde; c&ocirc;ng bố c&aacute;c số liệu thống k&ecirc; mới nhất cho biết số người chết do c&uacute;m A/H1N1 tr&ecirc;n thế giới kể từ khi dịch bệnh n&agrave;y b&ugrave;ng ph&aacute;t hồi th&aacute;ng 4 vừa qua đ&atilde; vượt qu&aacute; con số 1.000 người.</p>\r\n<p class="\\&quot;pBody\\&quot;"><strong>Theo WHO, đến nay c&uacute;m A/H1N1 đ&atilde; lan tới 168 quốc gia v&agrave; v&ugrave;ng l&atilde;nh thổ với tổng số ca nhiễm bệnh được thống k&ecirc; l&agrave; 162.380 người</strong> <strong>v&agrave; đ&atilde; c&oacute;</strong> <strong>1.154 trường hợp tử vong</strong>. WHO n&ecirc;u r&otilde; số bệnh nh&acirc;n c&uacute;m đặc biệt tăng mạnh trong th&aacute;ng 7 vừa qua. Ch&acirc;u Mỹ l&agrave; khu vực bị ảnh hưởng nặng nhất với số trường hợp bị nhiễm bệnh cho đến nay l&agrave; 98.242 người v&agrave; đ&atilde; c&oacute; tới 1.008 ca tử vong.</p>\r\n<p class="\\&quot;pBody\\&quot;">Chỉ trong v&ograve;ng 1 tuần (từ ng&agrave;y 26 đến 31-7) ở ch&acirc;u lục n&agrave;y đ&atilde; c&oacute; th&ecirc;m 338 ca tử vong do c&uacute;m A/H1N1. Khu vực c&oacute; số người chết do c&uacute;m A/H1N1 nhiều thứ hai l&agrave; ch&acirc;u &Aacute; v&agrave; Đ&ocirc;ng Nam &Aacute; với 65 ca (trong tổng số 9.858 trường hợp mắc bệnh). Số ca dương t&iacute;nh với c&uacute;m A/H1N1 ở ch&acirc;u &Acirc;u mặc d&ugrave; lớn hơn so với con số n&agrave;y ở ch&acirc;u &Aacute;, với 26.089 trường hợp, nhưng cho đến nay số ca tử vong l&agrave; 41 người. C&ograve;n ở khu vực T&acirc;y Th&aacute;i B&igrave;nh Dương c&oacute; 26.661 trường hợp mắc bệnh v&agrave; số ca tử vong cho đến nay l&agrave; 39.</p>\r\n<p class="\\&quot;pBody\\&quot;">Trong khi đ&oacute;, diễn biến dịch bệnh ng&agrave;y c&agrave;ng đ&aacute;ng lo ngại với số người mắc bệnh tăng nhanh v&agrave; số trường hợp tử vong cũng ng&agrave;y c&agrave;ng được thống k&ecirc; ở nhiều nước. WHO n&oacute;i rằng con số c&ocirc;ng bố n&oacute;i tr&ecirc;n chắc chắn c&ograve;n thấp hơn thực tế bởi nhiều nước bị ảnh hưởng nặng của dịch bệnh chưa c&oacute; điều kiện để ph&acirc;n t&iacute;ch v&agrave; thống k&ecirc; ch&iacute;nh x&aacute;c c&aacute;c trường hợp mắc bệnh.</p>\r\n<p class="\\&quot;pBody\\&quot;">Bộ Y tế Mexico (nước đầu ti&ecirc;n bị ảnh hưởng của c&uacute;m A/H1N1) ng&agrave;y 5-8 cho biết chỉ trong v&ograve;ng 5 ng&agrave;y, đ&atilde; c&oacute; th&ecirc;m 974 người bị mắc bệnh, n&acirc;ng tổng số người bị c&uacute;m A/H1N1 ở nước n&agrave;y l&ecirc;n tới 17.416 người v&agrave; đến nay đ&atilde; c&oacute; tới 146 ca tử vong.</p>\r\n<p class="\\&quot;pAuthor\\&quot;">TTXVN</p>', 1, 1249700593, 2, 30, 0),
(35, 2, 'Đà Nẵng: Một bệnh nhân cúm A/H1N1 vẫn ở ngoài cộng đồng  ', '  \r\n(TNO) Sáng 17.8, bác sĩ Nguyễn Út - Phó giám đốc Sở Y tế TP Đà Nẵng cho biết mẫu bệnh phẩm của ông T.H.P (42 tuổi, trú tổ 39, phường Hòa Khê, quận Thanh Khê) đã cho kết quả dương tính với vi rút cúm A/H1N1 nhưng hiện nay ông P. vẫn ở ngoài cộng đồng', 'no', '', '<p>Việc lấy mẫu bệnh phẩm của &ocirc;ng T.H.P được tiến h&agrave;nh ngẫu nhi&ecirc;n v&agrave;o ng&agrave;y 30.7 theo dự &aacute;n gi&aacute;m s&aacute;t trọng điểm c&uacute;m m&ugrave;a của quận Thanh Kh&ecirc;. Ng&agrave;y 15.8, Viện Pasteur Nha Trang ph&aacute;t hiện mẫu bệnh phẩm n&agrave;y nhiễm vi r&uacute;t c&uacute;m A/H1N1 nhưng&nbsp;từ đ&oacute; đến nay, nh&acirc;n vi&ecirc;n y tế của Trung t&acirc;m y tế dự ph&ograve;ng TP Đ&agrave; Nẵng vẫn chưa t&igrave;m thấy &ocirc;ng T.H.P tại địa phương.</p>\r\n<p>Trong một diễn kh&aacute;c, Sở Y tế&nbsp;Đ&agrave; Nẵng&nbsp;vừa x&aacute;c định th&ecirc;m hai trường hợp nhiễm c&uacute;m A/H1N1, n&acirc;ng tổng số ca nhiễm c&uacute;m A/H1N1 tại Đ&agrave; Nẵng l&ecirc;n 15 ca. Cả hai bệnh nh&acirc;n cho biết họ đ&atilde; v&agrave;o TP.HCM chơi v&agrave; tiếp x&uacute;c trực tiếp với một người bạn bị nhiễm c&uacute;m A/H1N1. Ng&agrave;y 12.8, ph&aacute;t hiện cơ thể c&oacute; biểu hiện nghi nhiễm c&uacute;m, hai người n&agrave;y đ&atilde; v&agrave;o Bệnh viện Đ&agrave; Nẵng để được c&aacute;ch ly. T&iacute;nh đến s&aacute;ng 17.8, Bệnh viện Đ&agrave; Nẵng đang c&aacute;ch ly 5 bệnh nh&acirc;n nghi nhiễm c&uacute;m, lấy mẫu bệnh phẩm đưa đi x&eacute;t nghiệm.</p>\r\n<p>Hiện nay, học sinh c&aacute;c trường tr&ecirc;n địa b&agrave;n TP Đ&agrave; Nẵng đ&atilde; l&ecirc;n trường l&agrave;m thủ tục nhập học v&agrave;&nbsp;được phổ biến kiến thức ph&ograve;ng chống c&uacute;m A/H1N1. Đồng thời, Sở Y tế đ&atilde; cấp ph&aacute;t t&agrave;i liệu ph&ograve;ng chống c&uacute;m A/H1N1 tại 28 cao ốc c&oacute; đ&ocirc;ng người l&agrave;m việc.</p>\r\n<p align="right"><strong>Nguyễn T&uacute;</strong></p>', 1, 1250570636, 2, 28, 0),
(36, 2, 'Dịch cúm A/H1N1 lan ra cộng đồng  ', 'Vụ 41 học sinh và giáo viên trường Trung học tư thục Ngô Thời Nhiệm (P.Phước Long A, Q.9, TP.HCM) bị nhiễm cúm A/H1N1 được Bộ Y tế nhận định dịch cúm bắt đầu chuyển qua giai đoạn lây lan ra cộng đồng. \r\n\r\n', '36.jpg', '', '<div class="pageContent">\r\n<p>&nbsp;</p>\r\n<div id="PublishingImage_Container" class="pageDes">\r\n<p><strong><font color="#a52a2a">* Tạm đ&oacute;ng cửa 1 trường trung học, ngừng hoạt động 2 trường mầm non<br />\r\n</font><font color="#000080">* Ghi nhận 408 trường hợp dương t&iacute;nh với vi-r&uacute;t c&uacute;m A/H1N1</font> </strong></p>\r\n<table>\r\n    <tbody>\r\n        <tr>\r\n            <td><img alt="" width="250" height="167" src="/uploads/image/p1a-moi.jpg" /></td>\r\n            <td><font color="#003b8c">&nbsp;</font><a target="_blank" href="/Media/Pages/Audio.aspx?IDS=2548"><font color="#003b8c">Mời nghe đọc b&agrave;i</font></a></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>&nbsp;<strong><font color="#808080">&nbsp;</font></strong>H&ocirc;m qua 20.7, đ&iacute;ch th&acirc;n Bộ trưởng Bộ Y tế Nguyễn Quốc Triệu c&ugrave;ng l&atilde;nh đạo Bộ, Sở Y tế TP.HCM, Viện Pasteur v&agrave; nhiều nh&agrave; chuy&ecirc;n m&ocirc;n đ&atilde; đến trường Trung học tư thục Ng&ocirc; Thời Nhiệm khảo s&aacute;t, b&agrave;n c&aacute;c biện ph&aacute;p ph&ograve;ng chống dịch l&acirc;y lan. Theo b&aacute;o c&aacute;o của b&agrave; Phạm Thị Th&uacute;y Vĩnh, Hiệu trưởng trường Ng&ocirc; Thời Nhiệm, ng&agrave;y 17.7 trường ph&aacute;t hiện 5 học sinh (HS) v&agrave; 1 gi&aacute;o vi&ecirc;n c&oacute; những triệu chứng nghi ngờ, n&ecirc;n chuyển tất cả đến Bệnh viện Phạm Ngọc Thạch. Qua x&eacute;t nghiệm, c&oacute; 5 ca dương t&iacute;nh c&uacute;m A/H1N1.&nbsp;</p>\r\n</div>\r\n<div class="pageContent">\r\n<table style="width: 369px; height: 304px">\r\n    <tbody>\r\n        <tr>\r\n            <td>&nbsp;<img alt="" width="400" height="268" src="/uploads/image/p3a1.jpg" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td><font color="#808080">HS đang được c&aacute;ch ly tại trường Ng&ocirc; Thời Nhiệm - Ảnh: Thanh T&ugrave;ng</font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>Ngay sau đ&oacute;, nh&agrave; trường tiếp tục ph&aacute;t hiện th&ecirc;m nhiều ph&ograve;ng HS nữ ở nội tr&uacute; trong trường c&oacute; em bị nhiễm bệnh. Ng&agrave;y 18.7, khi tiếp tục đưa 17 HS đến Bệnh viện Phạm Ngọc Thạch kiểm tra th&igrave; c&oacute; đến 15 ca dương t&iacute;nh c&uacute;m A/H1N1. Đến chủ nhật (ng&agrave;y 19.7), qua l&agrave;m việc với Sở Y tế TP.HCM, trường cho ngừng giảng dạy...</p>\r\n<p>&nbsp;</p>\r\n<table border="0" rules="all" cellspacing="2" cellpadding="3" align="right" style="width: 250px; height: 25px">\r\n    <tbody>\r\n        <tr>\r\n            <td valign="middle" align="left" style="background-color: #f3b204; height: 2px">&nbsp;</td>\r\n        </tr>\r\n        <tr>\r\n            <td align="left" style="background-color: #fae6ab"><strong>Người d&acirc;n kh&ocirc;ng n&ecirc;n qu&aacute; hoang mang! <br />\r\n            </strong><br />\r\n            Trả lời c&acirc;u hỏi của PV <em>B&aacute;o Thanh Ni&ecirc;n</em> &ldquo;Đến thời điểm hiện tại Bộ đ&atilde; c&oacute; x&aacute;c nhận dịch c&uacute;m A/H1N1 l&acirc;y lan ra cộng đồng?&rdquo;, Bộ trưởng Nguyễn Quốc Triệu nh&igrave;n nhận &ldquo;Dịch đang ở giai đoạn 2A, bắt đầu l&acirc;y lan ra cộng đồng&rdquo;. Tuy nhi&ecirc;n, Bộ trưởng Triệu cho rằng đ&acirc;y l&agrave; t&igrave;nh huống nằm trong dự b&aacute;o diễn biến dịch bệnh của Bộ Y tế, người d&acirc;n kh&ocirc;ng qu&aacute; hoang mang. Hiện phần lớn bệnh nh&acirc;n c&uacute;m A/H1N1 qua điều trị đ&atilde; khỏi bệnh, xuất viện.</td>\r\n        </tr>\r\n        <tr>\r\n            <td valign="middle" align="left" style="background-color: #f3b204; height: 2px">&nbsp;</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>&nbsp;Đến cuối giờ chiều 20.7, Sở Y tế TP.HCM cho biết đ&atilde; c&oacute; 41 trường hợp nhiễm c&uacute;m A/H1N1 xảy ra tại trường Ng&ocirc; Thời Nhiệm, gồm 38 HS v&agrave; 3 gi&aacute;o vi&ecirc;n. Theo b&aacute;c sĩ Nguyễn Văn Ch&acirc;u, Gi&aacute;m đốc Sở Y tế, nguồn l&acirc;y bệnh xuất ph&aacute;t từ em HS t&ecirc;n Đ. học sinh lớp 10 của trường. Trước đ&oacute; Đ. c&oacute; về nh&agrave; ở ấp Việt Kiều, x&atilde; Xu&acirc;n Hiệp (H.Xu&acirc;n Lộc, tỉnh Đồng Nai &ndash; ấp c&oacute; 23 người nhiễm c&uacute;m A/H1N1, B&aacute;o Thanh Ni&ecirc;n đ&atilde; th&ocirc;ng tin) v&agrave; bị l&acirc;y bệnh, khi trở lại trường th&igrave; l&acirc;y lan cho c&aacute;c HS kh&aacute;c trong khu nội tr&uacute;.</p>\r\n<p>Cũng theo b&aacute;c sĩ Nguyễn Văn Ch&acirc;u, ngay sau khi nhận tin b&aacute;o c&oacute; ổ dịch c&uacute;m A/H1N1 tại trường, Sở lập danh s&aacute;ch v&agrave; cho điều trị dự ph&ograve;ng đối với những HS v&agrave; gi&aacute;o vi&ecirc;n tiếp x&uacute;c gần với c&aacute;c ca bệnh; triển khai thực hiện &ldquo;nội bất xuất, ngoại bất nhập&rdquo; đối với trường; lập hai ph&ograve;ng c&aacute;ch ly ngay trong trường (một cho nam, một cho nữ); khử khuẩn, xử l&yacute; to&agrave;n bộ khu&ocirc;n vi&ecirc;n, dụng cụ của trường, xả hết nước hồ bơi để phơi nắng...</p>\r\n<p>Tuy nhi&ecirc;n, mối nguy dịch c&uacute;m A/H1N1 l&acirc;y lan rộng ra cộng đồng rất lớn, bởi ngo&agrave;i 41 HS, gi&aacute;o vi&ecirc;n của trường nhiễm A/H1N1, gần 80 HS kh&aacute;c (c&oacute; tiếp x&uacute;c gần với những ca bệnh) được c&aacute;ch ly ngay tại trường, c&ograve;n h&agrave;ng trăm HS kh&aacute;c đ&atilde; đi đến nhiều tỉnh th&agrave;nh. Được biết, trường Ng&ocirc; Thời Nhiệm c&oacute; 900 HS theo học trong dịp h&egrave;, phần lớn c&aacute;c em đến từ c&aacute;c tỉnh, nhiều nhất l&agrave; Đắk Lắk, L&acirc;m Đồng, B&igrave;nh Dương..., v&agrave; đ&atilde; c&oacute; hơn 600 em về qu&ecirc; rải r&aacute;c trước khi ph&aacute;t hiện dịch xảy ra. Ngo&agrave;i ra, nhiều người c&ograve;n lo ngại việc những HS đang được c&aacute;ch ly tại trường sẽ l&acirc;y lan bệnh cho nhau, bởi c&aacute;c em trong l&uacute;c ăn uống (ăn tập thể) kh&ocirc;ng thể mang khẩu trang n&ecirc;n dễ l&acirc;y bệnh.</p>\r\n<p align="center">&nbsp;</p>\r\n<table style="width: 335px; height: 304px">\r\n    <tbody>\r\n        <tr>\r\n            <td><img alt="" width="400" height="268" src="/uploads/image/p3a2.jpg" />&nbsp;</td>\r\n        </tr>\r\n        <tr>\r\n            <td>\r\n            <p><font color="#808080">Dự kiến đ&oacute;ng cửa trường Ng&ocirc; Thời Nhiệm từ 7 - 10 ng&agrave;y - Ảnh: Thanh T&ugrave;ng</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>&nbsp;Bộ trưởng Nguyễn Quốc Triệu y&ecirc;u cầu trường đ&oacute;ng cửa, ngừng giảng dạy từ 7-10 ng&agrave;y, nh&agrave; trường c&ugrave;ng Sở Y tế nắm thật kỹ danh s&aacute;ch, nơi cư tr&uacute; của c&aacute;c em ở địa phương để b&aacute;o cho y tế địa phương gi&aacute;m s&aacute;t chặt chẽ sức khỏe c&aacute;c em.</p>\r\n<p>Ngo&agrave;i việc tạm đ&oacute;ng cửa trường trung học, b&agrave; Phạm Thị Th&uacute;y Vĩnh c&ograve;n cho biết v&igrave; lo ngại nhiều phụ huynh HS trường Mầm non Ng&ocirc; Thời Nhiệm (c&oacute; 200 em theo học) tr&ecirc;n địa b&agrave;n quận đ&atilde; xin cho con tạm nghỉ học, n&ecirc;n nh&agrave; trường cũng tạm ngừng hoạt động.</p>\r\n<table border="2" rules="none" cellspacing="0" bordercolor="#f3b204" cellpadding="2" align="center" style="width: 450px; height: 27px">\r\n    <tbody>\r\n        <tr>\r\n            <td align="left" style="background-color: #fae6ab">\r\n            <p>H&ocirc;m qua 20.7, Ban chỉ đạo ph&ograve;ng chống dịch c&uacute;m A/H1N1 tỉnh Đồng Nai đ&atilde; họp khẩn cấp triển khai kế hoạch Ph&ograve;ng chống dịch. B&aacute;o c&aacute;o của Trung t&acirc;m Y tế dự ph&ograve;ng (TTYTDP) Đồng Nai, tr&ecirc;n địa b&agrave;n đ&atilde; c&oacute; 39 ca dương t&iacute;nh với c&uacute;m A/H1N1. Đ&aacute;ng lo ngại nhất l&agrave; ổ dịch tại ấp Việt Kiều đến nay bệnh nh&acirc;n nhiễm c&uacute;m A/H1N1 đ&atilde; l&ecirc;n đến 23 trường hợp. Hiện ng&agrave;nh y tế v&agrave; C&ocirc;ng an Đồng Nai đang tiến h&agrave;nh x&aacute;c minh chuyến xe bu&yacute;t chở em Đ. (học sinh l&acirc;y c&uacute;m ở trường Ng&ocirc; Thời Nhiệm) từ ấp Việt Kiều về TP.HCM để kiểm tra, c&aacute;ch ly những người c&ugrave;ng đi. Trường hợp b&eacute; V&otilde; Minh Nhật (19 th&aacute;ng tuổi, ngụ tại ấp Việt Kiều) c&oacute; kết quả dương t&iacute;nh với c&uacute;m A/H1N1 (c&ocirc;ng bố v&agrave;o ng&agrave;y h&ocirc;m qua) đang học tại trường Mầm non x&atilde; Suối C&aacute;t (H.Xu&acirc;n Lộc) cũng đ&aacute;ng lo ngại v&igrave; đ&atilde; tiếp x&uacute;c với 34 ch&aacute;u kh&aacute;c (hiện đang được c&aacute;ch ly). UBND H.Xu&acirc;n Lộc đ&atilde; y&ecirc;u cầu trường mầm non ngưng hoạt động để xử l&yacute; dịch bệnh. <br />\r\n            <br />\r\n            Sở Y tế Kh&aacute;nh H&ograve;a cho biết đ&atilde; c&oacute; th&ecirc;m 3 trường hợp dương t&iacute;nh với c&uacute;m A/H1N1, đ&oacute; l&agrave;: L&ecirc; Th&uacute;y P. (37 tuổi, người Việt quốc tịch Bỉ, sống tại Mỹ); L&ecirc; Th&uacute;y Kiều O. (12 tuổi, tr&uacute; tại TP Đ&agrave; Lạt, L&acirc;m Đồng - ch&aacute;u chị P.); Phạm Thị H. (55 tuổi, tr&uacute; tại đường Trần Quang Khải, TP Nha Trang). <br />\r\n            <br />\r\n            Ng&agrave;y 20.7, VN đ&atilde; ghi nhận th&ecirc;m 25 trường hợp dương t&iacute;nh với c&uacute;m A/H1N1 (miền Nam: 25 ca, miền Bắc: 1 ca, miền Trung: 5 ca). Như vậy, đến 17g c&ugrave;ng ng&agrave;y, Việt Nam đ&atilde; ghi nhận 408 trường hợp dương t&iacute;nh vi-r&uacute;t c&uacute;m A/H1N1, kh&ocirc;ng c&oacute; tử vong.</p>\r\n            <p align="right"><strong>Ho&agrave;ng Tuấn - Li&ecirc;n Ch&acirc;u - Văn Kỳ</strong></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>Thanh T&ugrave;ng</strong></p>\r\n</div>\r\n</div>', 1, 1250571368, 2, 32, 0),
(37, 2, 'Thêm một bệnh nhân nhiễm cúm A/H1N1 tử vong  ', '(TNO) Hôm nay (11.8), Bệnh viện Phạm Ngọc Thạch (TP.HCM) báo cáo có một ca tử vong do suy hô hấp, suy đa tạng trên bệnh nhân nghi nhiễm cúm A/H1N1 tại bệnh viện này. \r\n', '37.jpg', '', '<div class="pageContent">\r\n<p>Bệnh nh&acirc;n t&ecirc;n T.T.B, 52 tuổi, ngụ tại đường 3 th&aacute;ng 2, Q.10. TP.HCM. Theo th&ocirc;ng tin từ cơ quan y tế, bệnh nh&acirc;n T.T.B đ&atilde; c&oacute; hồ sơ bệnh &aacute;n từ trước v&agrave; bị hội chứng down (thiểu năng tr&iacute; tuệ). Trước khi nhập viện ba ng&agrave;y, bệnh nh&acirc;n c&oacute; biểu hiện sốt li&ecirc;n tục, ho khạc đ&agrave;m trắng, ti&ecirc;u chảy v&agrave; &oacute;i. T.T.B đ&atilde; được đưa đến kh&aacute;m tại Bệnh viện Q.10 sau đ&oacute; tự uống thuốc ba ng&agrave;y tại nh&agrave; nhưng kh&ocirc;ng khỏi v&agrave; tiếp tục v&agrave;o nhập viện Bệnh viện Q.10.</p>\r\n<p>Đến ng&agrave;y 6.8, T.T.B được chuyển đến Bệnh viện 115 với chuẩn đo&aacute;n ban đầu l&agrave; vi&ecirc;m phổi.</p>\r\n<p>Đến ng&agrave;y 9.8, sau qu&aacute; tr&igrave;nh điều trị m&agrave; bệnh t&igrave;nh kh&ocirc;ng thuy&ecirc;n giảm, c&aacute;c b&aacute;c sĩ&nbsp;đ&atilde; được hội chuẩn v&agrave; lấy mẫu x&eacute;t nghiệm c&uacute;m A/H1N1. Sau đ&oacute;, bệnh nh&acirc;n tiếp tục được chỉ định chuyển sang điều trị tại Bệnh viện Phạm Ngọc Thạch v&agrave;o l&uacute;c 20 giờ ng&agrave;y 10.8 v&agrave; tử vong l&uacute;c 22 giờ 25 ph&uacute;t c&ugrave;ng ng&agrave;y ngay tại đ&acirc;y.</p>\r\n<p>Ngay lập tức, Sở Y tế TP.HCM, Viện Pasteur, TT Y tế dự ph&ograve;ng TP.HCM v&agrave; Bệnh viện Phạm Ngọc Thạch đ&atilde; lấy mẫu bệnh phẩm, x&eacute;t nghiệm tử thi của bệnh nh&acirc;n T.T.B. Kết quả x&eacute;t nghiệm được kiểm chứng hai lần tại hai ph&ograve;ng x&eacute;t nghiệm của Bệnh viện Bệnh nhiệt đới v&agrave; Viện Pasteur đều cho kết quả dương t&iacute;nh với c&uacute;m A/H1N1.</p>\r\n<p>Trong ng&agrave;y h&ocirc;m nay, Sở Y tế TP.HCM đ&atilde; chỉ đạo TT Y tế dự ph&ograve;ng TP.HCM, Bệnh viện Q.10, Bệnh viện 115 v&agrave; Bệnh viện Phạm Ngọc Thạch tiến h&agrave;nh khử tr&ugrave;ng m&ocirc;i trường đồng thời giữ x&aacute;c bệnh nh&acirc;n tại nh&agrave; đại thể Bệnh viện Phạm Ngọc Thạnh để ph&ograve;ng c&uacute;m l&acirc;y lan.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, cơ quan y tế cũng đang điều tra dịch tễ c&aacute;c trường hợp c&oacute; tiếp x&uacute;c với bệnh nh&acirc;n T.T.B để c&aacute;ch ly, ph&ograve;ng ngừa.</p>\r\n<p>Như vậy đ&acirc;y được xem l&agrave; bệnh nh&acirc;n nhiễm c&uacute;m A/H1N1&nbsp;tử vong đầu ti&ecirc;n tại&nbsp;TP.HCM.&nbsp;T&iacute;nh đến thời điểm hiện nay cả nước đ&atilde; c&oacute; 2 ca tử vong do nhiễm c&uacute;m A/H1N1. Trước đ&oacute;, v&agrave;o l&uacute;c 23 giờ 45 ph&uacute;t ng&agrave;y 3.8, bệnh nh&acirc;n Trần Thị Kim Loan, 29 tuổi, bị nhiễm c&uacute;m A/H1N1, đ&atilde; <a target="_blank" href="/News/Pages/200932/20090805002824.aspx"><span style="text-decoration: underline"><font color="#0000ff">tử vong</font></span></a> tại Bệnh viện Đa khoa tỉnh Kh&aacute;nh H&ograve;a.</p>\r\n<p align="right"><strong>Nguy&ecirc;n Mi</strong></p>\r\n</div>', 1, 1250656162, 2, 23, 0),
(38, 2, 'An toàn lao động: Phụ thuộc nhận thức của DN ', 'Theo điều tra mới nhất của Cục An toàn lao động (Bộ LĐ-TB-XH) về cải thiện điều kiện lao động của các DNNVV, chỉ 20% DN có điều kiện lao động đạt yêu cầu; 80% DN có điều kiện lao động chưa đạt yêu cầu. Điều này một lần nữa cho thấy công tác tuyên truyền, trang bị kiến thức về VSATLĐ trong các DNNVV trở lên cần thiết hơn bao giờ hết.', '38.jpg', '', 'C&ugrave;ng với c&aacute;c bộ, ban, ng&agrave;nh li&ecirc;n quan thực hiện chương tr&igrave;nh quốc gia về bảo hộ lao động, an to&agrave;n lao động, vệ sinh lao động, với nhiệm vụ thực hiện dự &aacute;n &quot;N&acirc;ng cao chất lượng c&ocirc;ng t&aacute;c BHLĐ trong c&aacute;c DNNVV&quot; VCCI cũng đang nỗ lực hỗ trợ, đ&agrave;o tạo, n&acirc;ng cao nhận thức về ATVSLĐ cho c&aacute;c DNNVV.<br />\r\n<br />\r\n<strong>Th&aacute;ch thức từ ph&iacute;a DN</strong><br />\r\n<br />\r\nTheo c&aacute;c chuy&ecirc;n gia về ATVSLĐ, con số 80% DNNVV chưa quan t&acirc;m tới ATVSLĐ c&oacute; nhiều nguy&ecirc;n nh&acirc;n, nhưng một trong những nguy&ecirc;n nh&acirc;n ch&iacute;nh l&agrave; do đa số họ l&agrave; c&aacute;c DN mới th&agrave;nh lập, vốn &iacute;t, quy m&ocirc; nhỏ hẹp (dưới 10 tỷ đồng) n&ecirc;n DN n&agrave;y thường yếu k&eacute;m trong việc đ&aacute;p ứng với nhu cầu v&agrave; y&ecirc;u cầu thay đổi đầu tư, cập nhật n&acirc;ng cấp cơ sở.\r\n<p style="text-align: justify">&nbsp;</p>\r\n<p style="text-align: justify">Thậm ch&iacute; nhiều DN c&ograve;n cố t&igrave;nh lơ l&agrave;, bỏ qua việc huấn luyện an to&agrave;n lao động. Nguy&ecirc;n nh&acirc;n kh&aacute;c được x&aacute;c định l&agrave; do nhận thức việc&nbsp; lợi &iacute;ch của việc tham gia huấn luyện ATVSLĐ c&ograve;n chưa s&aacute;t với quy m&ocirc; thực tế, n&ecirc;n nhiều nơi chưa nhận thức được vai tr&ograve; quan trọng của vấn đề n&agrave;y. Hậu quả l&agrave; nhiều vụ tai nạn thương t&acirc;m v&agrave; đ&aacute;ng tiếc đ&atilde; li&ecirc;n tục diễn ra. Chịu thiệt th&ograve;i nhiều nhất ch&iacute;nh l&agrave; người lao động.</p>\r\n<p style="text-align: justify"><br />\r\nNhiều DN cho biết, họ chưa ho&agrave;n to&agrave;n sẵn s&agrave;ng cho việc trả ph&iacute; v&agrave; chủ động thu&ecirc; tổ chức c&aacute;c lớp huấn luyện về cải thiện điều kiện lao động, mặc d&ugrave; họ vẫn thừa nhận c&aacute;c hoạt động của về ATVSLĐ trong DN rất hữu &iacute;ch cho sự ph&aacute;t triển của DN. B&ecirc;n cạnh đ&oacute;, cũng phải khẳng định rằng sự quan t&acirc;m của bản th&acirc;n người lao động về c&ocirc;ng t&aacute;c n&agrave;y chưa đ&uacute;ng mức. Họ kh&ocirc;ng y&ecirc;u cầu hay đ&ograve;i hỏi chủ sử dụng lao động phải trang bị cho họ những vật dụng bảo hộ lao động tối thiểu khi l&agrave;m việc, thậm ch&iacute; ở một số DN c&oacute; trang bị nhưng cũng chỉ l&agrave;m v&igrave;, người lao động kh&ocirc;ng sử dụng c&aacute;c trang bị bảo hộ lao động n&agrave;y.<br />\r\n<br />\r\n<strong>Chia sẻ</strong><br />\r\n<br />\r\nTrước thực trạng tr&ecirc;n, c&aacute;c bộ, ban, ng&agrave;nh li&ecirc;n quan đ&atilde; c&oacute; nhiều nỗ lực nhằm cải thiện v&agrave; n&acirc;ng cao nhận thức của cộng đồng DN n&oacute;i chung, đặc biệt l&agrave; c&aacute;c DNNVV trong c&ocirc;ng t&aacute;c ATVSLĐ. Với chức năng, nhiệm vụ của m&igrave;nh, VCCI cũng đang thực hiện nhiều chương tr&igrave;nh v&agrave; được sự hưởng ứng nhiệt t&igrave;nh của c&aacute;c DN.</p>\r\n<p style="text-align: justify">&nbsp;</p>\r\n<p style="text-align: justify">C&aacute;c hoạt động về tuy&ecirc;n truyền, th&ocirc;ng tin n&acirc;ng cao nhận thức cho người sử dụng lao động trong lĩnh vực an to&agrave;n lao động đang được VCCI đẩy mạnh. Nhiều hoạt động về đ&agrave;o tạo huấn luyện cũng đ&atilde; được triển khai mạnh mẽ. Mục ti&ecirc;u m&agrave; VCCI đặt ra l&agrave; phấn đấu tr&ecirc;n 60% người lao động của c&aacute;c DN hội vi&ecirc;n VCCI được l&agrave;m việc trong m&ocirc;i trường an to&agrave;n v&agrave; vệ sinh, tr&ecirc;n 60% DN hội vi&ecirc;n của VCCI được n&acirc;ng cao hiệu quả quản l&yacute; ATVSLĐ.<br />\r\n<br />\r\n&nbsp;&Ocirc;ng Nguyễn Quang Vinh - Ph&oacute; Gi&aacute;m đốc VP DN v&igrave; sự ph&aacute;t triển bền vững VCCI, đơn vị đầu mối thực hiện dự &aacute;n &quot;N&acirc;ng cao chất lượng c&ocirc;ng t&aacute;c BHLĐ trong c&aacute;c DNNVV&quot; của VCCI cho biết, VCCI đang đẩy mạnh nhiều hoạt động tuy&ecirc;n truyền, đặc biệt l&agrave; c&ocirc;ng t&aacute;c đ&agrave;o tạo trang bị kiến thức về ATVSLĐ được VCCI đẩy mạnh như: tổ chức kh&oacute;a đ&agrave;o tạo &ldquo;N&acirc;ng cao chất lượng c&ocirc;ng t&aacute;c bảo hộ lao động trong c&aacute;c DN nhỏ v&agrave; vừa&rdquo;; tập huấn &ldquo;N&acirc;ng cao chất lượng cộng t&aacute;c BHLĐ trong c&aacute;c DNNVV của khu vực đồng bằng s&ocirc;ng Cửu Long tại Cần Thơ; tổ chức Hội thảo &ldquo;An to&agrave;n vệ sinh lao động &ndash; Ch&igrave;a kh&oacute;a th&agrave;nh c&ocirc;ng cho c&aacute;c DN&rdquo; tại Hải Ph&ograve;ng...<br />\r\n<br />\r\nGần đ&acirc;y nhất, VCCI đ&atilde; tổ chức kh&oacute;a đ&agrave;o tạo &quot;Cải thiện điều kiện l&agrave;m việc, an to&agrave;n vệ sinh lao động: c&ocirc;ng cụ để n&acirc;ng cao hoạt động kinh doanh của DN&quot; tại Hải Ph&ograve;ng, kh&oacute;a học đ&atilde; mang lại cho 128 DN tham gia nhiều kiến thức bổ &iacute;ch, nhất l&agrave; c&aacute;c vấn đề như:&nbsp; Tr&aacute;ch nhiệm x&atilde; hội DN: cơ hội, th&aacute;ch thức v&agrave; giải ph&aacute;p,&nbsp; bản chất của tr&aacute;ch nhiệm x&atilde; hội DN, l&agrave;m thế n&agrave;o để tổ chức thực hiện c&ocirc;ng t&aacute;c bảo hộ lao động ở cơ sở cũng như những nội dung hoạt động BHLĐ tại DN... Qua đ&oacute; c&aacute;c DN đ&atilde; hiểu lợi &iacute;ch của việc thực hiện ATVSLĐ trong DN để từ đ&oacute; x&acirc;y dựng c&aacute;c chiến lược cụ thể cho DN m&igrave;nh.</p>\r\n<p style="text-align: justify"><br />\r\nC&aacute;c chuy&ecirc;n gia về VSATLĐ thuộc VCCI một lần nữa khuy&ecirc;n rằng, để ph&aacute;t triển bền vững, đảm bảo an to&agrave;n trong sản xuất kinh doanh, kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o kh&aacute;c, c&aacute;c DN n&ecirc;n quan t&acirc;m hơn nữa tới c&ocirc;ng t&aacute;c ATVSLĐ,&nbsp; bởi thực&nbsp; hiện tốt c&ocirc;ng t&aacute;c An to&agrave;n &ndash; BHLĐ, quan t&acirc;m cải thiện điều kiện l&agrave;m việc cho người lao động, đ&aacute;p ứng được ti&ecirc;u chuẩn quốc tế v&agrave; điều kiện lao động sẽ n&acirc;ng cao uy t&iacute;n, sức cạnh tranh của DN tr&ecirc;n thị trường trong nước v&agrave; quốc tế, đảm bảo t&iacute;nh bền vững của DN.<br />\r\n<br />\r\nDDDN</p>', 1, 1250657263, 2, 44, 0),
(39, 2, 'An toàn lao động đang bị xem nhẹ ', 'Người sử dụng lao động "phớt lờ" những quy chuẩn về an toàn, người lao động thì thiếu kiến thức, xem nhẹ vấn đề bảo hộ. Vì thế, hàng năm tai nạn lao động đã cướp đi sinh mạng của hàng trăm nhân lực. Xung quanh vấn đề siết chặt công tác an toàn lao động, ông Nguyễn Văn Tiến, Chánh thanh tra Bộ Lao động Thương binh và Xã hội đã có cuộc trao đổi với báo chí.\r\n', '39.jpg', '', '<span id="tdStoryBody">&nbsp;\r\n<p id="Story_Body">&nbsp;</p>\r\n<p style="text-align: justify">&nbsp;</p>\r\n<p style="text-align: justify"><b>PV:</b> <i>Thực tế cho thấy, rất nhiều doanh nghiệp &quot;phớt lờ&quot; những quy chuẩn về an to&agrave;n, vi phạm Luật Lao động dẫn đến t&igrave;nh trạng gia tăng số vụ tai nạn lao động. &Ocirc;ng c&oacute; nhận định g&igrave; về thực trạng n&agrave;y?</i></p>\r\n<p style="text-align: justify"><b>&Ocirc;ng Nguyễn Văn Tiến:</b> Kết quả thanh tra mới đ&acirc;y cho thấy, chưa một doanh nghiệp (DN) n&agrave;o thực hiện đầy đủ c&aacute;c nội dung cơ bản của Bộ luật Lao động (BLLĐ) v&agrave; b&igrave;nh qu&acirc;n một DN mắc tới 7 lỗi. Sai phạm chủ yếu l&agrave;: Người sử dụng lao động kh&ocirc;ng k&yacute; kết HĐLĐ cho người lao động; kh&ocirc;ng x&acirc;y dựng định mức lao động, kh&ocirc;ng x&acirc;y dựng v&agrave; đăng k&yacute; hệ thống thang, bảng lương; trả lương l&agrave;m th&ecirc;m giờ kh&ocirc;ng đầy đủ; kh&ocirc;ng đ&oacute;ng BHXH... Theo t&ocirc;i, một phần kh&ocirc;ng nhỏ nguy&ecirc;n nh&acirc;n của những sai phạm tr&ecirc;n l&agrave; do BLLĐ c&oacute; qu&aacute; nhiều điều khoản phải c&oacute; văn bản hướng dẫn mới thi h&agrave;nh được, trong khi văn bản hướng dẫn chậm, kh&ocirc;ng đồng bộ hoặc chưa s&aacute;t thực tế. Kh&aacute; nhiều DN chỉ c&oacute; c&aacute;n bộ ki&ecirc;m nhiệm lao động tiền lương, kh&ocirc;ng được đ&agrave;o tạo về c&ocirc;ng t&aacute;c lao động n&ecirc;n kh&ocirc;ng nắm bắt được c&aacute;c văn bản hướng dẫn, dẫn đến việc thực hiện kh&ocirc;ng đ&uacute;ng. Điều đ&aacute;ng n&oacute;i, kh&ocirc;ng chỉ c&aacute;c DN cố t&igrave;nh vi phạm m&agrave; ngay cả người lao động cũng &quot;l&aacute;ch&quot; luật. Kh&ocirc;ng thực hiện đầy đủ c&aacute;c quy định hiện h&agrave;nh dẫn đến số vụ tai nạn lao động ng&agrave;y c&agrave;ng gia tăng.</p>\r\n<p style="text-align: justify"><b>PV:</b> <i>Vậy thưa &ocirc;ng, trong c&aacute;c vụ tai nạn lao động xảy ra, tr&aacute;ch nhiệm chủ yếu thuộc về b&ecirc;n n&agrave;o?</i></p>\r\n<p style="text-align: justify"><b>&Ocirc;ng Nguyễn Văn Tiến:</b> Thực tế hiện nay cả người sử dụng lao động lẫn người lao động chưa thực hiện tốt c&ocirc;ng t&aacute;c an to&agrave;n lao động. Bản th&acirc;n người lao động phải bảo vệ m&igrave;nh, nếu khi l&agrave;m việc trong c&ocirc;ng trường lao động m&agrave; người sử dụng lao động kh&ocirc;ng được trang bị bảo hộ lao động, th&igrave; phải c&oacute; y&ecirc;u cầu, đ&ograve;i hỏi được trang bị. Ngược lại, chủ doanh nghiệp cũng phải thực hiện đ&uacute;ng ph&aacute;p luật để đảm bảo điều kiện cho người lao động. Điều n&agrave;y trong hợp đồng lao động đ&atilde; ghi rất r&otilde;. Tuy nhi&ecirc;n, t&ocirc;i c&oacute; thể khẳng định, tai nạn lao động xảy ra ở c&aacute;c c&ocirc;ng trường x&acirc;y dựng đa phần l&agrave; do người sử dụng lao động.</p>\r\n<p style="text-align: justify"><b>PV:</b> <i>Phải chăng chế t&agrave;i xử phạt nhẹ khiến DN &quot;nhờn thuốc&quot; thưa &ocirc;ng?</i></p>\r\n<p style="text-align: justify"><b>&Ocirc;ng Nguyễn Văn Tiến:</b> Đ&uacute;ng l&agrave; hiện nay mức xử phạt vi phạm h&agrave;nh ch&iacute;nh về lĩnh vực lao động c&ograve;n qu&aacute; nhẹ (mức thấp nhất 200 ngh&igrave;n đồng, cao nhất l&agrave; 20 triệu đồng). D&ugrave; l&agrave; mức cao nhất đem &aacute;p dụng với một doanh nghiệp th&igrave; thực sự c&ograve;n qu&aacute; nhỏ. Theo t&ocirc;i, cần phải xử l&yacute; nghi&ecirc;m người đứng đầu doanh nghiệp nếu để xảy ra tai nạn lao động th&igrave; phải chịu tr&aacute;ch nhiệm v&agrave; c&ocirc;ng bố doanh nghiệp vi phạm tr&ecirc;n phương tiện th&ocirc;ng tin đại ch&uacute;ng. Phải đ&aacute;nh v&agrave;o uy t&iacute;n của doanh nghiệp, họ mới sợ.</p>\r\n<p style="text-align: justify"><b>PV:</b> <i>C&oacute; một thực tế năm n&agrave;o cũng xảy ra tai nạn, c&oacute; thống k&ecirc; nhưng dường như cơ quan quản l&yacute; chưa c&oacute; giải ph&aacute;p khắc phục t&igrave;nh trạng n&agrave;y, thưa &ocirc;ng?</i></p>\r\n<p style="text-align: justify"><b>&Ocirc;ng Nguyễn Văn Tiến:</b> Về mặt quản l&yacute; nh&agrave; nước đ&atilde; l&agrave;m rất quyết liệt, c&oacute; nhiều loại văn bản quy chuẩn. Nhưng đưa những quy chuẩn n&agrave;y xuống dưới địa phương th&igrave; c&oacute; vấn đề, n&oacute;i thẳng ra l&agrave; chỉ mới đến được cấp l&atilde;nh đạo c&aacute;c bộ phận, c&ograve;n xuống đến người lao động th&igrave; r&otilde; l&agrave; thiếu. Muốn xem cơ sở, doanh nghiệp c&oacute; thực hiện tốt quy chuẩn kh&ocirc;ng th&igrave; lại phải c&oacute; lực lượng thanh tra. Từng bộ chuy&ecirc;n ng&agrave;nh phải c&oacute; người để kiểm tra trong lĩnh vực an to&agrave;n lao động. Thế nhưng hiện nay lực lượng thanh tra qu&aacute; mỏng, kh&ocirc;ng thể kiểm tra đầy đủ được. Ngo&agrave;i việc thanh tra an to&agrave;n lao động, thực hiện ch&iacute;nh s&aacute;ch ph&aacute;p luật về lao động ph&aacute;p luật về lao động, lực lượng n&agrave;y c&ograve;n phải thanh tra việc thực hiện ch&iacute;nh s&aacute;ch đối với người c&oacute; c&ocirc;ng, trẻ em v&agrave; gần đ&acirc;y l&agrave; triển khai ph&ograve;ng chống tham nhũng, &aacute;p dụng cơ chế một cửa trong tiếp c&ocirc;ng d&acirc;n, giải quyết đơn thư khiếu nại. Với lực lượng mỏng v&agrave; &ocirc;m nhiều việc như hiện nay, việc ph&aacute;t hiện ra vi phạm, nhất l&agrave; những vi phạm trong an to&agrave;n lao động, l&agrave; rất hiếm. Đ&acirc;y ch&iacute;nh l&agrave; một trong nhiều nguy&ecirc;n nh&acirc;n khiến t&igrave;nh h&igrave;nh tai nạn lao động, đ&igrave;nh c&ocirc;ng li&ecirc;n tục gia tăng.</p>\r\n<p style="text-align: justify">Mặc d&ugrave; ch&uacute;ng t&ocirc;i c&oacute; thanh tra thường xuy&ecirc;n chứ kh&ocirc;ng phải khi c&oacute; tai nạn rồi mới thanh tra nhưng cũng kh&ocirc;ng thể l&agrave;m xuể. Khi xảy ra tai nạn lao động th&igrave; thanh tra lao động phải c&oacute; mặt ngay để tiến h&agrave;nh thanh tra, để c&ugrave;ng với c&aacute;c cơ quan xem x&eacute;t, kết luận nguy&ecirc;n nh&acirc;n xảy ra tai nạn để xem tr&aacute;ch nhiệm thuộc về doanh nghiệp hay người lao động.</p>\r\n<p style="text-align: justify"><b>PV</b>: <i>Xin cảm ơn &ocirc;ng!</i></p>\r\n<p align="right" style="text-align: right"><b>N.Giang (Thực hiện)</b></p>\r\n<p style="text-align: justify">&nbsp;</p>\r\n<p>&nbsp;</p>\r\n</span>', 1, 1250657482, 2, 36, 0),
(40, 2, '  Bệnh nhân thứ 5 nhiễm cúm A/H1N1 tử vong ', 'Chiều 9.9 tại cuộc họp ban chỉ đạo phòng chống đại dịch cúm, Bộ Y tế chính thức xác nhận trong ngày có thêm 1 ca nhiễm cúm H1N1 tử vong. ', 'no', '', '<div class="pageContent">\r\n<p>Đ&oacute; l&agrave; bệnh nh&acirc;n nam t&ecirc;n Thổ Lực (9 tuổi, ngụ x&atilde; H&agrave;ng G&ograve;n, TX Long Kh&aacute;nh, Đồng Nai) nhưng theo gia đ&igrave;nh l&agrave;m rẫy v&agrave; học tập tại x&atilde; Gia Huynh, H.T&aacute;nh Linh (B&igrave;nh Thuận). Đ&acirc;y l&agrave; ca nhiễm c&uacute;m H1N1 thứ 5 tử vong tại VN. &Ocirc;ng Nguyễn Huy Nga - Cục trưởng Cục Y tế dự ph&ograve;ng v&agrave; M&ocirc;i trường, Bộ Y tế cho biết, BN ph&aacute;t bệnh ng&agrave;y 29.8 với biểu hiện sốt. Sau 2 ng&agrave;y điều trị tại nh&agrave;, ng&agrave;y 2.9 được chuyển v&agrave;o điều trị tại Bệnh viện H.T&aacute;nh Linh, sau đ&oacute; được chuyển đến BV Đa khoa khu vực Long Kh&aacute;nh. Ng&agrave;y 3.9, BN tiếp tục được chuyển đến Bệnh viện Nhi Đồng Nai trong t&igrave;nh trạng li b&igrave;, được chẩn đo&aacute;n vi&ecirc;m n&atilde;o - vi&ecirc;m m&agrave;ng n&atilde;o. Tại đ&acirc;y bệnh nh&acirc;n được điều trị th&ecirc;m bằng Tamiflu v&agrave; x&eacute;t nghiệm dương t&iacute;nh với c&uacute;m H1N1.</p>\r\n<p>Chiều c&ugrave;ng ng&agrave;y, Sở Y tế Quảng Ng&atilde;i đ&atilde; cử đo&agrave;n c&ocirc;ng t&aacute;c đến kiểm tra, gi&aacute;m s&aacute;t t&igrave;nh h&igrave;nh dịch c&uacute;m A/H1N1 đang c&oacute; nguy cơ b&ugrave;ng ph&aacute;t tại trường THPT số 1 Tư Nghĩa (H.Tư Nghĩa). Đ&acirc;y l&agrave; ổ dịch c&uacute;m A/H1N1 xuất hiện trong trường học đầu ti&ecirc;n ở Quảng Ng&atilde;i.</p>\r\n<p>Sở Y tế Ph&uacute; Y&ecirc;n x&aacute;c nhận trong ng&agrave;y c&oacute; th&ecirc;m 1 trường học c&oacute; HS nhiễm c&uacute;m A/H1N1 l&agrave; trường Tiểu học Nguyễn Trung Trực (TP Tuy H&ograve;a). Đến nay, Ph&uacute; Y&ecirc;n đ&atilde; ghi nhận c&oacute; 25 ca nhiễm c&uacute;m A/H1N1 tại 7 trường học.</p>\r\n<p>Sở GD - ĐT L&acirc;m Đồng cho biết đến nay đ&atilde; c&oacute; 88 HS tại 37 trường học tr&ecirc;n địa b&agrave;n dương t&iacute;nh với c&uacute;m A/H1N1, trong đ&oacute; 8 trường phải cho HS nghỉ học.</p>\r\n<p>B&aacute;o c&aacute;o của Sở Y tế TP.HCM cho biết, trong ng&agrave;y h&ocirc;m qua ph&aacute;t hiện mới 27 trường hợp dương t&iacute;nh với c&uacute;m A/H1N1, n&acirc;ng tổng số người nhiễm c&uacute;m A/H1N1 ở TP.HCM l&ecirc;n 1.816 người. C&oacute; hai ổ dịch mới vừa xảy ra l&agrave; ở trường THCS Quang Trung (Q.G&ograve; Vấp) v&agrave; KTX của trường ĐH B&aacute;ch khoa (Q.10).</p>\r\n<p>Theo Bộ Y tế, ng&agrave;y 9.9, VN đ&atilde; ghi nhận th&ecirc;m 110 trường hợp dương t&iacute;nh với c&uacute;m A/H1N1 (miền Bắc 16 ca, miền Trung 3, miền Nam 91). Đến nay VN đ&atilde; ghi nhận 4.060 trường hợp dương t&iacute;nh; 5 trường hợp tử vong; 3.296 bệnh nh&acirc;n đ&atilde; ra viện.</p>\r\n<p align="right"><strong>Nh&oacute;m PV</strong></p>\r\n</div>', 1, 1252548509, 2, 22, 0),
(44, 2, 'Vắc-xin ngừa cúm A/H1N1 có thể được sử dụng vào tháng 9 tới  ', 'TNO) Tổ chức Y tế Thế giới (WHO) một lần nữa cam đoan rằng quá trình nghiên cứu vắc-xin ngừa cúm A/H1N1 đang được tiến hành một cách hết sức khẩn trương và chặt chẽ. Theo đó, những liều vắc-xin đầu tiên có thể sẽ được đưa vào sử dụng từ tháng 9.2009. ', '44.jpg', 'Nghiên cứu vắc-xin cúm A/H1N1 tại một phòng thí nghiệm ở Anh - Ảnh: AFP', '<table class="pagepic" cellspacing="0" cellpadding="0">\r\n    <tbody>\r\n        <tr>\r\n            <td class="pagepic-img">\r\n            <div class="general-image"><img alt="" src="http://www.thanhnien.com.vn/News/Pictures/Vac-xin-bia-ok.jpg" /></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td class="pagepic-des">\r\n            <div>Nghi&ecirc;n cứu vắc-xin c&uacute;m A/H1N1 tại một ph&ograve;ng th&iacute; nghiệm ở Anh - Ảnh: AFP</div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div class="article-Des-Cont">\r\n<div class="pageDes" id="PublishingImage_Container">\r\n<p><a href="http://www.thanhnien.com.vn/News/Pages/200931/20090729074334.aspx"><strong><font color="#808080">&gt;&gt; <span style="text-decoration: underline">Tự bảo vệ m&igrave;nh khỏi c&uacute;m A/H1N1</span></font></strong></a></p>\r\n</div>\r\n<div class="pageContent">\r\n<p>B&agrave; Marie-Paule Kieny, Gi&aacute;m đốc nghi&ecirc;n cứu&nbsp;vắc-xin của WHO cho biết, c&aacute;c cuộc thử nghiệm vắc-xin c&uacute;m A/H1N1 đ&atilde; được thực hiện ở một số nước như: &Uacute;c, Anh, Trung Quốc, Đức v&agrave; Mỹ.</p>\r\n<p>&quot;Ch&uacute;ng t&ocirc;i hy vọng rằng sẽ c&oacute; th&ecirc;m những thử nghiệm điều trị được tiến h&agrave;nh trong những ng&agrave;y sắp tới. Kết quả đầu ti&ecirc;n của những cuộc thử nghiệm sẽ c&oacute; v&agrave;o th&aacute;ng&nbsp;9.2009. Nếu c&aacute;c thử nghiệm c&oacute; kết quả tốt th&igrave; vắc-xin&nbsp;ngừa c&uacute;m A/H1N1 c&oacute; thể được bắt đầu sử dụng ngay trong th&aacute;ng 9&quot;, b&agrave; Marie-Paule Kieny cho biết th&ecirc;m.</p>\r\n<p align="right"><strong>Nguy&ecirc;n Mi<br />\r\n</strong><em>(Theo AFP)</em></p>\r\n</div>\r\n</div>', 1, 1252549589, 2, 20, 0);
INSERT INTO `tgp_cms` (`id`, `cat`, `ten`, `chu_thich`, `hinh`, `hinh_note`, `noi_dung`, `hien_thi`, `time`, `user`, `luot_xem`, `noi_bat`) VALUES
(45, 2, 'Đà Nẵng: Trường học đầu tiên có ca nhiễm cúm A/H1N1', '(VnMedia) - Tính đến ngày 8/9, toàn TP Đà Nẵng có 24 ca nhiễm cúm A/H1N1, trong đó 22 ca đã xuất viện. Còn một ca nghi nhiễm đang theo dõi tại bệnh viện Đa khoa Đà Nẵng và một học sinh trường THPT Phan Châu Trinh nhập viện từ 4/9. Đây là trường hợp đầu tiên học sinh nhiễm cúm A/H1N1 tại TP Đà Nẵng.\r\n\r\n', '45.jpg', 'Phun thuốc khử trùng tại trường học', '<p align="justify"><font size="2" face="Arial">\r\n<table border="1" cellspacing="0" cellpadding="0" width="1" align="left" style="border-collapse: collapse">\r\n    <tbody>\r\n        <tr>\r\n            <td><img border="0" alt="" src="http://vnmedia.vn/images_upload/small_229740.jpg" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td align="center"><font size="1" face="Arial">Phun thuốc khử tr&ugrave;ng tại trường học</font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<br />\r\nBệnh nh&acirc;n l&agrave; học sinh T.D.D (tr&uacute; tại Khu&ecirc; Trung, Thanh Kh&ecirc;, Đ&agrave; Nẵng), học lớp 10 tự nhi&ecirc;n trường THPT Phan Ch&acirc;u Trinh, TP Đ&agrave; Nẵng. <br />\r\n</font><font size="2" face="Arial"><br />\r\nTrước ng&agrave;y khai giảng một ng&agrave;y, phụ huynh em D. xin ph&eacute;p cho em được nghỉ học với l&yacute; do bị ốm. Gia đ&igrave;nh đ&atilde; đưa em đi kh&aacute;m tại bệnh viện C Đ&agrave; Nẵng. Đến ng&agrave;y 7/9, trung t&acirc;m Y tế dự ph&ograve;ng TP Đ&agrave; Nẵng đ&atilde; th&ocirc;ng b&aacute;o tới trường học em đ&atilde; nhiễm c&uacute;m A/H1N1. Điều tra dịch tễ của D. kh&ocirc;ng x&aacute;c định được nguồn l&acirc;y c&uacute;m A/H1N1. <br />\r\n</font><font size="2" face="Arial"><br />\r\nNhận được th&ocirc;ng tin, Thầy L&ecirc; Ph&uacute; Kỳ - Hiệu trưởng trường THPT Phan Ch&acirc;u Trinh cho biết: Chiều c&ugrave;ng ng&agrave;y, to&agrave;n bộ học sinh lớp D. học đ&atilde; được kiểm tra th&acirc;n nhiệt, kết quả cho thấy kh&ocirc;ng c&oacute; học sinh n&agrave;o c&oacute; triệu chứng sốt cao hay nhiễm c&uacute;m. Đồng thời, kết hợp với b&ecirc;n y tế ph&ograve;ng dịch tiến h&agrave;nh phun thuốc khử tr&ugrave;ng to&agrave;n bộ d&atilde;y ph&ograve;ng học xung quanh lớp n&agrave;y v&agrave; tầng lầu của trường học.<br />\r\n</font><font size="2" face="Arial"><br />\r\nThầy Kỳ cho biết th&ecirc;m hiện nay c&aacute;c học sinh c&oacute; dấu hiệu sốt cao hay c&uacute;m được ph&eacute;p nghỉ ở nh&agrave; để theo d&otilde;i. C&ograve;n lại c&aacute;c học sinh kh&aacute;c vẫn đi học b&igrave;nh thường từ ng&agrave;y khai giảng. </font><font size="2" face="Arial"><br />\r\n<br />\r\nTrao đổi với <strong><em>VnMedia</em></strong>, &ocirc;ng Phạm H&ugrave;ng Chiến - Gi&aacute;m đốc Sở Y tế Đ&agrave; Nẵng cho biết: Cho đến thời điểm n&agrave;y, to&agrave;n TP c&oacute; 24 ca nhiễm c&uacute;m H1N1, nhưng đ&atilde; xuất viện 22 ca, c&ograve;n 2 ca của em D. v&agrave; 1 ca đang nghi nhiễm, nhưng n&oacute;i chung sức khoẻ của 2 trường hợp n&agrave;y kh&ocirc;ng đ&aacute;ng ngại. </font></p>\r\n<p align="justify"><font size="2" face="Arial">&quot;Dịch c&uacute;m đ&atilde; l&acirc;y lan với tốc độ rất nhanh, 55/ 63 tỉnh th&agrave;nh cả nước. Ri&ecirc;ng Đ&agrave; Nẵng hơn 10 ng&agrave;y nay chưa xuất hiện nhiều ổ dịch như c&aacute;c th&agrave;nh phố kh&aacute;c. Nhưng trong khoảng 15 - 18/9, thời gian c&aacute;c t&acirc;n sinh vi&ecirc;n nhập học, đ&acirc;y l&agrave; mối lo cho ch&uacute;ng t&ocirc;i. Nhưng với c&aacute;c biện ph&aacute;p tuy&ecirc;n truyền ph&ograve;ng dịch, th&agrave;nh lập Ban chỉ đạo.. Trung t&acirc;m Y tế dự ph&ograve;ng lu&ocirc;n sẵn s&agrave;ng để chống dịch cho cộng đồng, tr&aacute;nh l&acirc;y lan th&ecirc;m c&aacute;c ca nhiễm c&uacute;m kh&aacute;c&quot; - &ocirc;ng H&ugrave;ng Chiến n&oacute;i.</font><span style="mso-bidi-font-size: 14.0pt"><o:p></o:p></span></p>\r\n<p>&nbsp;</p>\r\n<br />\r\n<p class="textbody" align="right"><strong><font color="#999999"><em>Th&ugrave;y Linh</em></font></strong></p>\r\n<p>&nbsp;</p>', 1, 1252638137, 2, 30, 0),
(43, 2, 'Đổ xô đi mua khẩu trang để phòng cúm  ', 'Sau khi có thông tin dịch cúm A/H1N1 bùng phát tại Hà Nội, một số ngân hàng, công ty đã yêu cầu nhân viên bắt buộc phải đeo khẩu trang khi làm việc. ', '43.jpg', 'Nhiều cơ quan khuyến cáo nhân viên đeo khẩu trang khi làm việc và giao dịch với khách - Ảnh: Ngọc Thắng', '<div class="pageContent">\r\n<p>\r\n<table class="pagepic" cellspacing="0" cellpadding="0">\r\n    <tbody>\r\n        <tr>\r\n            <td class="pagepic-img">\r\n            <div class="general-image"><img alt="" src="http://www.thanhnien.com.vn/News/Pictures/123972622.jpg" /></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td class="pagepic-des">\r\n            <div>Nhiều cơ quan khuyến c&aacute;o nh&acirc;n vi&ecirc;n đeo khẩu trang khi l&agrave;m việc v&agrave; giao dịch với kh&aacute;ch - Ảnh: Ngọc Thắng</div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<br />\r\nC&oacute; mặt tại trụ sở ch&iacute;nh của Techcombank tr&ecirc;n phố B&agrave; Triệu chiều ng&agrave;y h&ocirc;m qua 29.7, ch&uacute;ng t&ocirc;i quan s&aacute;t thấy hầu hết c&aacute;c nh&acirc;n vi&ecirc;n tại đ&acirc;y đều đeo khẩu trang y tế. Ngay ph&iacute;a ngo&agrave;i t&ograve;a nh&agrave;, đội bảo vệ cũng được trang bị khẩu trang sẵn s&agrave;ng đeo trong suốt ca trực. Một nh&acirc;n vi&ecirc;n tr&ocirc;ng xe n&oacute;i với ch&uacute;ng t&ocirc;i: &ldquo;Tất cả c&aacute;c nh&acirc;n vi&ecirc;n phải đeo khẩu trang từ h&ocirc;m 28.7. Nếu chị muốn th&igrave; cũng c&oacute; thể đeo khi giao dịch&rdquo;. Chị L&ecirc; Bảo Ng&acirc;n (tổ 70 phường Thanh Nh&agrave;n), tới giao dịch tại đ&acirc;y cho biết: &ldquo;Tới đ&acirc;y thấy c&aacute;c nh&acirc;n vi&ecirc;n đeo khẩu trang thế n&agrave;y t&ocirc;i cũng thấy m&igrave;nh qu&aacute; chủ quan, c&oacute; khi mai t&ocirc;i v&agrave; gia đ&igrave;nh cũng phải đeo khẩu trang khi ra đường&rdquo;</p>\r\n<p>Kh&aacute;ch h&agrave;ng đến giao dịch tại c&ocirc;ng ty chứng kho&aacute;n S&agrave;i G&ograve;n (1C Ng&ocirc; Quyền) cũng được khuyến c&aacute;o đeo khẩu trang. Ngay quầy lễ t&acirc;n c&oacute; ghi r&otilde; một số quy định về s&aacute;t tr&ugrave;ng trước khi kh&aacute;ch h&agrave;ng v&agrave;o c&aacute;c s&agrave;n giao dịch chứng kho&aacute;n. Chị Vũ Thị Tuyết (quầy Mở t&agrave;i khoản) cho biết: &ldquo;Ch&uacute;ng t&ocirc;i đeo khẩu trang được mấy h&ocirc;m rồi. Chỉ c&oacute; buổi trưa kh&ocirc;ng c&oacute; kh&aacute;ch mới d&aacute;m bỏ ra cho tho&aacute;ng. Kh&aacute;ch tới đ&acirc;y cũng phải tu&acirc;n thủ một số quy định trước khi v&agrave;o giao dịch. N&oacute;i chung giải th&iacute;ch th&igrave; họ cũng th&ocirc;ng cảm. Cũng v&igrave; mục đ&iacute;ch an to&agrave;n cả th&ocirc;i&rdquo;.</p>\r\n<p>Tại trung t&acirc;m bảo h&agrave;nh JVJ Việt Nam (80 L&ecirc; Thanh Nghị), chị Nguyễn Thị May, nh&acirc;n vi&ecirc;n kế to&aacute;n cũng đeo khẩu trang từ v&agrave;i ng&agrave;y gần đ&acirc;y khi giao dịch với kh&aacute;ch h&agrave;ng. Chị cho biết: &ldquo;Đ&acirc;y l&agrave; quy định của trung t&acirc;m hơn nữa, tại đ&acirc;y c&oacute; 18 người nhưng 10 người đang bị ốm, d&ugrave; kh&ocirc;ng phải c&uacute;m H1N1 nhưng cẩn thận vẫn hơn. Kh&aacute;ch h&agrave;ng sẽ cảm thấy kh&oacute; chịu một ch&uacute;t nhưng t&ocirc;i nghĩ rồi họ sẽ quen&rdquo;.</p>\r\n<p><strong>&quot;Ch&aacute;y&quot; khẩu trang</strong></p>\r\n<p>C&aacute;c cửa h&agrave;ng b&aacute;n thiết bị y tế tr&ecirc;n phố Phương Mai s&aacute;ng h&ocirc;m qua rất đ&ocirc;ng kh&aacute;ch tới t&igrave;m mua khẩu trang y tế. Gi&aacute; khẩu trang tăng l&ecirc;n 120.000 đồng/hộp 50 chiếc (gi&aacute; b&igrave;nh thường l&agrave; 80.000 đồng). Trao đổi với ch&uacute;ng t&ocirc;i, chủ cửa h&agrave;ng 109 Phương Mai chỉ v&agrave;o th&ugrave;ng khẩu trang cuối c&ugrave;ng cũng đ&atilde; gần hết cho biết: &ldquo;Buổi s&aacute;ng c&ograve;n c&oacute; h&agrave;ng m&agrave; b&aacute;n chứ tới chiều c&oacute; đi cả phố cũng kh&ocirc;ng c&ograve;n h&agrave;ng m&agrave; b&aacute;n. Gọi điện l&ecirc;n c&ocirc;ng ty sản xuất cũng hết! Do ch&aacute;y h&agrave;ng n&ecirc;n chiều nay, ch&uacute;ng t&ocirc;i chỉ b&aacute;n lẻ th&ocirc;i, mua nhiều th&igrave; chịu&rdquo;.</p>\r\n<p>Thị trường khẩu trang sợi hoạt t&iacute;nh cũng &quot;n&oacute;ng&quot; kh&ocirc;ng k&eacute;m. Mặc d&ugrave; chưa c&oacute; bất cứ khẳng định n&agrave;o từ&nbsp; ph&iacute;a nh&agrave; sản xuất cũng như nh&agrave; ph&acirc;n phối về t&aacute;c dụng chống c&uacute;m A/H1N1 của loại khẩu trang n&agrave;y, nhưng nhiều người vẫn đổ x&ocirc; đi mua. Kh&aacute;ch h&agrave;ng chủ yếu l&agrave; giới trẻ v&agrave; những b&agrave; nội trợ, mua về để ph&ograve;ng c&uacute;m cho cả nh&agrave;.&nbsp;</p>\r\n<p>Chỉ trong ng&agrave;y 28.7, chị Đặng Thị Hoa (117 Cầu Diễn) đ&atilde; b&aacute;n được 12 chiếc khẩu trang hoạt t&iacute;nh. Trong khi h&agrave;ng th&aacute;ng trước đ&oacute;, mặt h&agrave;ng n&agrave;y kh&ocirc;ng c&oacute; người hỏi mua. Chị Hoa cho biết: &ldquo;Khẩu trang cho người lớn &ldquo;ch&aacute;y&rdquo; h&agrave;ng rồi, chỉ c&ograve;n 6 chiếc trẻ em th&ocirc;i&rdquo;. Tại cửa h&agrave;ng số 268 Cầu Giấy, b&agrave; chủ t&ecirc;n Dung đang tức tốc gọi điện thoại đặt h&agrave;ng khẩu trang hoạt t&iacute;nh Kissy.</p>\r\n<p>Chị Dung cho hay, một tuần trở lại đ&acirc;y, loại khẩu trang n&agrave;y bỗng dưng đắt kh&aacute;ch, cửa h&agrave;ng chị b&aacute;n 10 chiếc/ ng&agrave;y. C&ograve;n theo khẳng định của dược sĩ Đỗ Thị Li&ecirc;n (nh&agrave; thuốc 104 Kh&acirc;m Thi&ecirc;n), những ng&agrave;y n&agrave;y, doanh thu b&aacute;n khẩu trang hoạt t&iacute;nh cao hơn so với b&igrave;nh thường 5 - 10%. Chị Li&ecirc;n cho biết, chỉ trong ng&agrave;y 28.7, hiệu thuốc của chị đ&atilde; b&aacute;n được 15 chiếc khẩu trang hoạt t&iacute;nh c&aacute;c loại. Chị Nhung, nh&acirc;n vi&ecirc;n ph&acirc;n phối khẩu trang hoạt t&iacute;nh Kissy khu vực Cầu Giấy cho biết, từ ng&agrave;y 1.8, gi&aacute; khẩu trang Kissy sẽ ch&iacute;nh thức n&acirc;ng l&ecirc;n 35.000 đồng/chiếc thay v&igrave; 32.000 đồng hiện tại.</p>\r\n<p align="right"><strong>Thanh Lan - L&ecirc; Qu&acirc;n</strong></p>\r\n</div>', 1, 1252549311, 2, 40, 0),
(42, 2, 'Chloramin B mùa cúm A/H1N1: “Kẻ vô danh” thành hàng “sốt”  ', '(TNO) Chloramin B là hóa chất dạng bột, khi dùng pha loãng trong nước với nồng độ thấp để tẩy trùng, sát khuẩn môi trường, phòng ốc, các dụng cụ như bàn ghế, giường tủ... Trước đây, chloramin B chỉ được biết đến trong ngành y tế hay các lĩnh vực vệ sinh môi trường. Thế nhưng, trước tình hình cúm A/H1N1 lan rộng, cái tên, nhu cầu và các vấn đề về hóa chất đặc dụng này đang “nóng” lên. \r\n', '42.jpg', '', '<div class="pageDes" id="PublishingImage_Container"><strong><font color="#000080">\r\n<table class="pagepic" cellspacing="0" cellpadding="0">\r\n    <tbody>\r\n        <tr>\r\n            <td class="pagepic-img">\r\n            <div class="general-image"><img alt="" src="http://www.thanhnien.com.vn/News/Pictures/ChloraminB.jpg" /></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td class="pagepic-des">\r\n            <div>Chloramin B được b&aacute;n tr&ecirc;n thị trường - Ảnh: V.An</div>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<br />\r\n<br />\r\nTrường học l&uacute;ng t&uacute;ng với chloramin B</font> </strong></div>\r\n<div class="pageContent">\r\n<p>Chuẩn bị cho m&ugrave;a khai giảng, 10 tấn chloramin B đ&atilde; được Sở Y tế TP.HCM chuyển giao cho Sở GD-ĐT TP.HCM. Tuy nhi&ecirc;n, ở c&aacute;c trường, việc tiếp nhận v&agrave; sử dụng chloramin B vẫn c&ograve;n nhiều l&uacute;ng t&uacute;ng.</p>\r\n<p>Theo b&aacute;c sĩ Nguyễn T&agrave;i Dũng, chuy&ecirc;n tr&aacute;ch y tế học đường, Sở GD-ĐT TP.HCM, to&agrave;n bộ số chloramin B tr&ecirc;n được ph&acirc;n phối về từng trường với chỉ ti&ecirc;u l&agrave; 6kg/trường. Việc ph&acirc;n phối n&agrave;y được Sở GD-ĐT TP.HCM thực hiện th&ocirc;ng qua c&aacute;c Ph&ograve;ng GD-ĐT quận, huyện. Trường trực thuộc đơn vị quận huyện n&agrave;o th&igrave; đến Ph&ograve;ng GD-ĐT quận huyện đ&oacute; nhận.</p>\r\n<p>Hiện nay, nhiều Ph&ograve;ng GD-ĐT lại đang &ldquo;k&ecirc;u&rdquo; về những kh&oacute; khăn trong ph&acirc;n phối, quản l&yacute; v&agrave; bảo quản chloramin B cũng như cảnh b&aacute;o về độ an to&agrave;n khi cất giữ chloramin B tại trường học.</p>\r\n<p>Chloramin B l&agrave; một h&oacute;a chất diệt khuẩn, c&oacute; thể g&acirc;y nguy hiểm cho sức khỏe, rất nhạy cảm v&agrave; dễ k&iacute;ch ứng với mắt, cơ quan h&ocirc; hấp v&agrave; da. Khi l&agrave;m việc với chloramin B nhất thiết phải c&oacute; phương tiện bảo hộ lao động v&agrave; tu&acirc;n thủ theo c&aacute;c quy tắc vệ sinh, nồng độ để tr&aacute;nh g&acirc;y độc hại. Khi sử dụng kh&ocirc;ng được để tiếp x&uacute;c trực tiếp giữa da v&agrave; h&oacute;a chất, phải c&oacute; trang thiết bị bảo vệ da, bảo vệ mắt v&agrave; nhất thiết phải d&ugrave;ng găng tay.</p>\r\n<p>Thế nhưng, b&aacute;c sĩ Nguyễn Hữu Nghị, chuy&ecirc;n tr&aacute;ch y tế học đường, Ph&ograve;ng GD-ĐT Q.T&acirc;n B&igrave;nh cho biết: &quot;H&oacute;a chất khi được chuyển về c&aacute;c ph&ograve;ng gi&aacute;o dục để ph&acirc;n chia lại cho c&aacute;c trường học đều c&ograve;n nguy&ecirc;n trong đai, trong kiện, chưa được chia nhỏ với hai lớp bao ni-l&ocirc;ng. Ph&ograve;ng GD-ĐT lại phải chia nhỏ cho c&aacute;c trường v&agrave; nhiều trường phải tiếp tục chia nhỏ ra để sử dụng. Hiện nay, c&aacute;c trường học v&agrave; Ph&ograve;ng GD-ĐT đều kh&ocirc;ng c&oacute; dụng cụ ph&acirc;n chia, bảo hộ lao động. Khi mở bao b&igrave;, h&oacute;a chất c&oacute; m&ugrave;i rất hăn v&agrave; kh&oacute; chịu cũng như độc hại&rdquo;.</p>\r\n<p>Cũng theo gi&aacute;m s&aacute;t của ng&agrave;nh gi&aacute;o dục, c&oacute; trường hợp, trường sau khi nhận chloramin B về th&igrave; lại giao h&oacute;a chất n&agrave;y cho gi&aacute;o vi&ecirc;n chủ nhiệm của từng lớp cất giữ, bảo quản v&agrave; l&agrave;m vệ sinh. Để tr&aacute;nh xa tầm tay của học sinh, gi&aacute;o vi&ecirc;n lại thường đem h&oacute;a chất cất v&ocirc; tủ v&agrave;... kh&ocirc;ng biết l&agrave;m g&igrave; (!?).</p>\r\n<p align="center">\r\n<table>\r\n    <tbody>\r\n        <tr>\r\n            <td>\r\n            <p align="center">&nbsp;<img alt="" src="http://www.thanhnien.com.vn/News/Pictures200902/VienAn/CumH1N1-truong/NguyenKhuyen400.jpg" /><br />\r\n            <font color="#808080">Phun h&oacute;a chất khử tr&ugrave;ng trường học -&nbsp;Ảnh: Vi&ecirc;n An</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n<p>&Ocirc;ng Huỳnh C&ocirc;ng Minh, Gi&aacute;m đốc Sở GD-ĐT TP.HCM cũng thừa nhận, qua c&aacute;c đợt tập huấn, kh&ocirc;ng phải trường n&agrave;o, gi&aacute;o vi&ecirc;n n&agrave;o cũng c&oacute; kiến thức đầy đủ v&agrave; nắm vững về dự ph&ograve;ng c&uacute;m A/H1N1, vẫn c&oacute; một số c&ograve;n lờ mờ về c&aacute;c chuy&ecirc;n m&ocirc;n y tế dự ph&ograve;ng.</p>\r\n<p>C&aacute;c ph&ograve;ng GD-ĐT v&agrave; hiệu trưởng c&aacute;c trường đều c&oacute; &yacute; kiến n&ecirc;n để chloramin B cho trung t&acirc;m y tế dự ph&ograve;ng quản l&yacute;, ph&acirc;n chia, xử l&yacute; v&agrave; c&oacute; kế hoạch sử dụng, l&ecirc;n lịch khử tr&ugrave;ng trường học. V&igrave; đ&acirc;y l&agrave; cơ sở y tế c&oacute; đầy đủ phương tiện v&agrave; nghiệp vụ chuy&ecirc;n m&ocirc;n. L&agrave;m vậy&nbsp;cũng sẽ dễ huy động chloramin B hơn trong trường hợp ưu ti&ecirc;n h&oacute;a chất khử tr&ugrave;ng cho một trường học n&agrave;o đ&oacute; khi xuất hiện ổ dịch.</p>\r\n<p>Nhưng như thế, lại th&ecirc;m một g&aacute;nh nặng cho ng&agrave;nh y tế trong m&ugrave;a dịch trong khi ng&agrave;nh y tế đang cần sự chia sẻ v&agrave; hợp sức của nhiều ng&agrave;nh li&ecirc;n quan v&agrave; cộng đồng.</p>\r\n<p><strong><font color="#000080">Chloramin B, m&aacute;y đo th&acirc;n nhiệt: &ldquo;N&oacute;ng&rdquo; dần</font></strong></p>\r\n<p>Hiện nay, ngo&agrave;i số chloramin B được cấp, một số trường học đ&atilde; tự mua th&ecirc;m h&oacute;a chất n&agrave;y&nbsp;để dự trữ v&agrave; s&aacute;t khuẩn trường lớp. Ngo&agrave;i ra, nhu cầu chloramin B cũng tăng nhanh do c&aacute;c cơ quan, x&iacute; nghiệp, c&aacute;c đơn vị mua về để s&aacute;t khuẩn cơ sở, đơn vị của m&igrave;nh.</p>\r\n<p>Một chủ cửa h&agrave;ng b&aacute;n dụng cụ y tế tr&ecirc;n đường Cống Quỳnh (Q.1, TP.HCM) cho biết, từ khi dịch c&uacute;m A/H1N1 lan rộng, chloramin B trở th&agrave;nh mặt h&agrave;ng quen thuộc v&agrave; được mua với số lượng lớn. Mỗi ng&agrave;y, cửa h&agrave;ng b&aacute;n ra cả trăm kg chloramin B cho c&aacute;c c&ocirc;ng ty, doanh nghiệp, đơn vị tập thể c&oacute; nhu cầu. Ngay thời điểm ch&uacute;ng t&ocirc;i đến hỏi, cửa h&agrave;ng của chị chỉ c&ograve;n khoảng 7kg v&agrave; muốn mua số lượng lớn phải chờ đặt h&agrave;ng th&ecirc;m.</p>\r\n<p>Tại khu vực chuy&ecirc;n kinh doanh h&oacute;a chất v&agrave; dụng cụ y tế tr&ecirc;n đường T&ocirc; Hiến Th&agrave;nh (Q.10) t&igrave;nh h&igrave;nh cũng tương tự. Từ một h&oacute;a chất kh&ocirc;ng được nhiều người&nbsp;biết m&agrave; giờ đ&acirc;y chloramin B đang trở th&agrave;nh một mặt h&agrave;ng quen thuộc được quan t&acirc;m tại c&aacute;c cửa h&agrave;ng dụng cụ y tế.</p>\r\n<p>Do&nbsp;nhu cầu cao n&ecirc;n gi&aacute;&nbsp;chloramin B cũng được đẩy l&ecirc;n. C&ugrave;ng tr&ecirc;n đường Cống Quỳnh (Q.1) nhưng mỗi cửa h&agrave;ng dụng cụ y tế lại b&aacute;n chloramin B với một gi&aacute; kh&aacute;c nhau, từ 110.000 - 130.000 đồng/kg. Trong khi đ&oacute;, gi&aacute; chloramin B trước kia chỉ ở khoảng 80.000 đồng/kg.</p>\r\n<p>C&ocirc; L&ecirc; Qu&yacute;, phụ tr&aacute;ch y tế trường THPT L&ecirc; Qu&yacute; Đ&ocirc;n (Q.3) cho biết: &quot;Mỗi tuần v&agrave;o chiều thứ 7, trường đều phun h&oacute;a chất khử tr&ugrave;ng, s&aacute;t khuẩn. Với 45 lớp học v&agrave; khu&ocirc;n vi&ecirc;n của trường th&igrave; lượng chloramin B được Sở ph&acirc;n phối kh&ocirc;ng thể đủ n&ecirc;n trường đ&atilde; chủ động chi ng&acirc;n s&aacute;ch mua gần 100kg chloramin B cho việc vệ sinh trường học&quot;. C&ocirc; Qu&yacute; cho biết th&ecirc;m, nhu cầu nhiều, gi&aacute; cao, trường cũng kh&oacute; chi ng&acirc;n s&aacute;ch, may m&agrave; nhờ biết địa chỉ c&ocirc;ng ty ph&acirc;n phối n&ecirc;n mới lấy được gi&aacute; trực tiếp ưu ti&ecirc;n l&agrave; 83.000 đồng/kg.</p>\r\n<p>Dịch vụ phun xịt chloramin B cũng đang nở rộ, đặc biệt l&agrave; tr&ecirc;n c&aacute;c trang rao vặt, mua b&aacute;n tr&ecirc;n mạng, với gi&aacute; phun xịt khoảng 1.500 - 2.000 đồng/m<sup>2</sup> s&agrave;n.</p>\r\n<p>\r\n<table align="center" style="width: 408px; height: 355px">\r\n    <tbody>\r\n        <tr>\r\n            <td>\r\n            <p align="center"><img alt="" src="http://www.thanhnien.com.vn/News/Pictures200902/VienAn/CumH1N1-truong/KhamSucKhoe1.jpg" />&nbsp;<br />\r\n            <font color="#808080">Kh&aacute;m sức khỏe cho học sinh ng&agrave;y tập trung tại trường trung học d&acirc;n lập Trương Vĩnh K&yacute; - Ảnh: Vi&ecirc;n An</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n<p>Cũng như chloramin B, sau cơn &quot;sốt&quot; khẩu trang, giờ thị trường m&aacute;y đo th&acirc;n nhiệt cũng bắt đầu &quot;n&oacute;ng&quot;. Đ&oacute;ng giả người đi hỏi mua m&aacute;y đo th&acirc;n nhiệt cho trường học tại khu y cụ T&ocirc; Hiến Th&agrave;nh v&agrave; Cống Quỳnh, ch&uacute;ng t&ocirc;i đều được c&aacute;c cửa h&agrave;ng b&aacute;o l&agrave; hết h&agrave;ng, chỉ c&ograve;n 1 - 2 chiếc v&agrave; nếu mua nhiều (khoảng 5 - 10 chiếc) th&igrave; phải chờ, c&oacute; thể từ hai tuần đến một th&aacute;ng.</p>\r\n<p>Kh&ocirc;ng những hết h&agrave;ng, gi&aacute; m&aacute;y đo th&acirc;n nhiệt đ&atilde; bắt đầu nh&iacute;ch l&ecirc;n. Tại khu b&aacute;n dụng cụ y tế tr&ecirc;n đường T&ocirc; Hiến Th&agrave;nh, loại m&aacute;y đo th&acirc;n nhiệt tr&ecirc;n tr&aacute;n l&uacute;c trước chỉ c&oacute; gi&aacute; khoảng 400.000 đồng/m&aacute;y th&igrave; giờ đ&atilde; l&agrave; hơn 500.000 đồng/m&aacute;y. Chủ cửa h&agrave;ng bảo cũng chỉ c&ograve;n hai c&aacute;i. Anh cho biết, gi&aacute; b&aacute;n c&aacute;c loại m&aacute;y đo th&acirc;n nhiệt hiện nay loại n&agrave;o cũng tăng như thế v&igrave; h&agrave;ng hết m&agrave; c&aacute;c đơn vị, cơ sở, trường học đều c&oacute; nhu cầu mua nhiều.</p>\r\n<p>B&aacute;c sĩ&nbsp;Nguyễn Đắc Thọ, Gi&aacute;m đốc Trung t&acirc;m Y tế dự ph&ograve;ng TP.HCM cho biết, việc nhiều trường đo th&acirc;n nhiệt cho tất cả học sinh l&agrave; phản ứng th&aacute;i qu&aacute; v&agrave; kh&ocirc;ng hợp l&yacute;. Bởi số lượng học sinh đ&ocirc;ng v&agrave; theo dịch tễ vừa qua, 40% ca mắc c&uacute;m A/H1N1 kh&ocirc;ng c&oacute; triệu chứng r&otilde; rệt, thời gian ủ bệnh chưa bộc ph&aacute;t dấu hiệu.</p>\r\n<p>Cơ quan y tế cũng khuyến c&aacute;o, điều quan trọng nhất vẫn l&agrave; &yacute; thức tự vệ sinh c&aacute; nh&acirc;n, th&ocirc;ng tho&aacute;ng ph&ograve;ng ốc với&nbsp;khoảng c&aacute;ch sinh hoạt giữa c&aacute;c học sinh l&agrave; 1,5 - 2m. B&ecirc;n cạnh đ&oacute;, Sở Y tế TP.HCM cũng sẽ tiếp tục hỗ trợ về h&oacute;a chất, thuốc điều trị,&nbsp;tờ bướm cho c&aacute;c trường trong dự ph&ograve;ng c&uacute;m A/H1N1.</p>\r\n<p align="right"><strong>Vi&ecirc;n An</strong></p>\r\n</div>', 1, 1252548872, 2, 29, 0),
(46, 2, 'Việt Nam đã có 5 ca tử vong vì cúm A/H1N1 ', '(VnMedia) - Chiều 9/9, Bộ Y tế chính thức xác nhận, đến nay cả nước có 5 ca tử vong do nhiễm cúm A/H1N1. Ca thứ 5 mới tử vong là 1 bệnh nhi (5 tuổi) ở Đồng Nai. Trước đó, Thành phố Hồ Chí Minh có 3 ca và Khánh Hòa có 1 ca tử vong .\r\n', '46.jpg', '', '<font size="2" face="Arial"><a href="http://www.vnmedia.vn/newsdetail.asp?NewsId=173964&amp;CatId=73"><img border="0" align="left" width="311" height="277" alt="" src="http://vnmedia.vn/images_upload/small_229983.jpg" /></a><br />\r\nQua xem x&eacute;t c&aacute;c trường hợp tử vong&nbsp;tr&ecirc;n&nbsp;cho thấy,&nbsp;những ca tử vong do c&uacute;m A/H1N1 ở Việt Nam đều c&oacute; tiền sử mắc c&aacute;c bệnh m&atilde;n t&iacute;nh như t&acirc;m thần, suy thận, đ&aacute;i th&aacute;o đường, cao huyết &aacute;p. Th&ecirc;m v&agrave;o đ&oacute;, đa phần người bệnh khi mới ph&aacute;t bệnh đều điều trị tại nh&agrave;, chỉ sau khi bệnh diễn biến nặng mới nhập viện n&ecirc;n hiệu quả điều trị kh&ocirc;ng cao.<br />\r\n<br />\r\nV&igrave; v&acirc;y, Thứ trưởng Bộ Y tế Trịnh Qu&acirc;n Huấn y&ecirc;u cầu Tiểu ban gi&aacute;m s&aacute;t v&agrave; Tiểu ban điều trị tiếp tục t&igrave;m hiểu, nghi&ecirc;n cứu v&agrave; đ&aacute;nh gi&aacute; to&agrave;n diện 5 trường hợp tử vong để gi&uacute;p c&aacute;c b&aacute;c sĩ r&uacute;t kinh nghiệm trong chẩn đo&aacute;n, điều trị c&uacute;m A/H1N1. V&agrave; Thứ trưởng cũng y&ecirc;u cầu nội dung c&aacute;c khuyến c&aacute;o ph&ograve;ng chống c&uacute;m A/H1N1 cần được bổ sung, điều chỉnh ph&ugrave; hợp với diễn tiến thực tế hiện nay.<br />\r\n<br />\r\nXung quanh việc lo ngại thiếu sinh phẩm x&eacute;t nghiệm trong khi dịch c&uacute;m A/H1N1 diễn biến ng&agrave;y c&agrave;ng phức tạp, số người nhiễm mới li&ecirc;n tục tăng cao, Tiến sĩ Nguyễn Văn K&iacute;nh - Viện trưởng Viện c&aacute;c bệnh truyền nhiễm v&agrave; nhiệt đới Quốc gia cho biết, viện vẫn c&oacute; đầy đủ sinh phẩm x&eacute;t nghiệm. Nếu trong trường hợp dịch l&acirc;y lan rộng, thiếu sinh phẩm x&eacute;t nghiệm viện vẫn c&oacute; c&aacute;ch ph&aacute;t hiện v&agrave; điều trị hiệu quả c&uacute;m A/H1N1.<br />\r\n<br />\r\nĐược biết sinh phẩm x&eacute;t nghiệm tại Việt Nam đang sử dụng do Tổ chức Y tế thế giới (WHO) t&agrave;i trợ. Trong t&igrave;nh h&igrave;nh dịch bệnh l&acirc;y lan tr&ecirc;n to&agrave;n thế giới, khả năng thiếu sinh phẩm l&agrave; c&oacute; thể xảy ra nhưng theo WHO kh&ocirc;ng qu&aacute; lo ngại. V&igrave; nhiều nước kh&aacute;c đ&atilde; ở giai đoạn bệnh ph&aacute;t triển mạnh, số ca nhiễm cao n&ecirc;n chẩn đo&aacute;n chủ yếu dựa v&agrave;o l&acirc;m s&agrave;ng, dịch tễ để ph&aacute;t hiện v&agrave; điều trị vẫn đạt hiệu quả. <br />\r\n<br />\r\nHiện thời tiết tại Việt Nam đang chuyển sang m&ugrave;a Đ&ocirc;ng, l&agrave; điều kiện thuận lợi để vi r&uacute;t ph&aacute;t triển mạnh, c&aacute;c Viện đang tiếp tục nghi&ecirc;n cứu phương ph&aacute;p test nhanh cho hiệu quả cao hơn, giảm gi&aacute; th&agrave;nh v&agrave; gi&uacute;p người d&acirc;n x&eacute;t nghiệm ngay khi nghi ngờ. Gi&aacute; x&eacute;t nghiệm bằng phương ph&aacute;p test nhanh c&uacute;m A(H1N1) cho mỗi ca chỉ bằng 1/6 phương ph&aacute;p x&eacute;t nghiệm bằng PCR hiện nay.<br />\r\n<br />\r\nTheo thống k&ecirc; của Cục Y tế dự ph&ograve;ng v&agrave; m&ocirc;i trường (Bộ Y tế), ng&agrave;y 9/9, Việt Nam đ&atilde; ghi nhận th&ecirc;m 110 trường hợp dương t&iacute;nh với c&uacute;m A/H1N1). Trong đ&oacute;, khu vực ph&iacute;a Nam 91 ca, miền Bắc 16 ca, miền Trung 3 ca. Như vậy, t&iacute;nh đến 18 giờ c&ugrave;ng ng&agrave;y cả nước đ&atilde; c&oacute; 4060 người nhiễm c&uacute;m A/H1N1, 5 ca tử vong. Số bệnh nh&acirc;n đ&atilde; khỏi ra viện l&agrave; 3296 người, c&aacute;c trường hợp c&ograve;n lại đang được c&aacute;ch ly, điều trị tại c&aacute;c bệnh viện, cơ sở điều trị, gi&aacute;m s&aacute;t cộng đồng trong t&igrave;nh trạng sức khỏe ổn định.</font>\r\n<p>&nbsp;</p>\r\n<br />\r\n<p class="textbody" align="right"><font color="#999999"><em>Th&ugrave;y Hoa</em></font></p>', 1, 1252638432, 2, 17, 0),
(47, 2, 'Thêm một bệnh nhân cúm H1N1 tử vong', 'VnExpress - Chủ Nhật, 13/9', '47.jpg', '', '<p class="first">S&aacute;ng 12/9, một bệnh nh&acirc;n nữ 19 tuổi ở huyện Chợ L&aacute;ch, tỉnh Bến Tre đ&atilde; tử vong. X&eacute;t nghiệm ban đầu cho kết quả dương t&iacute;nh với c&uacute;m H1N1.<br />\r\n<br />\r\nBệnh nh&acirc;n v&agrave;o Bệnh viện C&ugrave; Lao Minh (huyện Mỏ C&agrave;y Nam, Bến Tre) ng&agrave;y 5/9. Sau khi c&oacute; kết quả x&eacute;t nghiệm dương t&iacute;nh với c&uacute;m H1N1, chị đ&atilde; đuợc điều trị bằng Tamiflu nhưng do bệnh nặng bệnh nh&acirc;n đ&atilde; tử vong sau 7 ng&agrave;y điều trị.</p>\r\n<p>Bộ Y tế đ&atilde; nhận được b&aacute;o c&aacute;o của Sở Y tế Bến Tre về trường hợp n&agrave;y v&agrave; đang đợi x&aacute;c minh th&ecirc;m th&ocirc;ng tin trước khi c&ocirc;ng bố ch&iacute;nh thức.</p>\r\n<p>Về 5 ca trước đ&oacute; tử vong v&igrave; nhập viện muộn, mang sẵn bệnh, Thứ trưởng Bộ Y tế Trịnh Qu&acirc;n Huấn cho biết: &ldquo;D&ugrave; l&agrave; mắc c&uacute;m H1N1 tr&ecirc;n cơ địa bệnh m&atilde;n t&iacute;nh nhưng nếu điều trị tốt th&igrave; rất c&oacute; thể bệnh nh&acirc;n sẽ kh&ocirc;ng tử vong. V&igrave; thế, cần phải r&uacute;t kinh nghiệm trong việc chẩn đo&aacute;n, điều trị&rdquo;. Bộ Y tế sẽ cử một đo&agrave;n c&aacute;n bộ đến những địa phương đ&atilde; c&oacute; ca tử vong, xem x&eacute;t việc điều trị, chẩn đo&aacute;n, từ đ&oacute; r&uacute;t ra b&agrave;i học cho cả hệ thống điều trị.</p>\r\n<p>Như vậy, hiện cả nước sẽ c&oacute; 6 ca tử vong v&igrave; c&uacute;m H1N1, trong số 4.444 ca dương t&iacute;nh.</p>\r\n<p>Nam Phương</p>', 1, 1252912650, 2, 15, 0),
(48, 2, 'WHO: cúm A/H1N1 đã giết hơn 3.200 người', 'Tuổi Trẻ Online - Chủ Nhật, 13/9', '48.jpg', 'WHO: cúm A/H1N1 đã giết hơn 3.200 người', '<p>TT- - TTO - &Iacute;t nhất 3.205 người tr&ecirc;n thế giới đ&atilde; bị virus c&uacute;m A/H1N1 cướp đi mạng sống kể từ khi virus n&agrave;y được ph&aacute;t hiện v&agrave;o th&aacute;ng 4-2009, theo Tổ chức Y tế thế giới (WHO) ng&agrave;y 11-9.</p>\r\n<p>Trong số n&agrave;y, c&oacute; 2.467 ca xảy ra ở ch&acirc;u Mỹ, 306 ca ở khu vực T&acirc;y Th&aacute;i B&igrave;nh Dương, 221 ca ở Đ&ocirc;ng Nam &Aacute;, 125 ở ch&acirc;u &Acirc;u, 51 ở Đ&ocirc;ng Địa Trung Hải v&agrave; 35 ở ch&acirc;u Phi.</p>\r\n<p>Như vậy chỉ trong v&ograve;ng chưa đầy một tuần, số ca tử vong do c&uacute;m A/H1N1 tr&ecirc;n thế giới do WHO c&ocirc;ng bố đ&atilde; tăng vọt từ 2.840 người l&ecirc;n 3.205 người, một con số rất đ&aacute;ng lo ngại.</p>\r\n<p>Số ca nhiễm c&uacute;m A/H1N1 được x&aacute;c nhận tr&ecirc;n to&agrave;n cầu l&agrave; tr&ecirc;n 277.607 người, tuy nhi&ecirc;n theo WHO, con số nhiễm c&uacute;m tr&ecirc;n thực tế c&ograve;n cao hơn nhiều v&agrave; virus c&uacute;m A/H1N1 vẫn đang tiếp tục l&acirc;y lan ở cả bắc v&agrave; nam b&aacute;n cầu.</p>\r\n<p>D&ugrave; vậy, WHO khẳng định chưa c&oacute; dấu hiệu cho thấy virus n&agrave;y biến thể th&agrave;nh một chủng nguy hiểm hơn hoặc g&acirc;y chết người nhiều hơn.</p>\r\n<p>WHO cũng cho biết hiện họ chưa thay đổi hướng dẫn về việc sử dụng thuốc chống virus d&ugrave; đ&atilde; xuất hiện một số trường hợp virus c&uacute;m A/H1N1 kh&aacute;ng thuốc như Tamiflu. Theo WHO, đến nay cả thế giới đ&atilde; ghi nhận 21 trường hợp kh&aacute;ng Tamiflu v&agrave; họ đang c&ugrave;ng với c&aacute;c đối t&aacute;c tiếp tục theo d&otilde;i s&aacute;t sao t&igrave;nh h&igrave;nh.</p>\r\n<p>TƯỜNG VY (Theo Xinhua)</p>\r\n<p><SCRIPT>\r\nYAHOO.EU.Messenger = new Messenger();\r\nvar sStoryHeadline=''%0A'';\r\nvar sStoryLink="http://vn.news.yahoo.com/tto/20090912/twl-who-cum-a-h1n1-da-giet-hon-3-200-ngu-5727bc2.html"+''%0A'';\r\nvar sDefaultMsg = "Xem+b%C3%A0i+n%C3%A0y+tr%C3%AAn+Yahoo%21+Tin+t%E1%BB%A9c%3A";\r\n</SCRIPT></p>', 1, 1252912850, 2, 21, 0),
(49, 2, 'Ca thứ 11 dương tính cúm A/H1N1 tử vong  ', '* Sốt xuất huyết tăng 13% so với cùng kỳ 2008 ', 'no', '', '<p>Cuối ng&agrave;y h&ocirc;m qua 24.9, c&oacute; th&ecirc;m một trường hợp dương t&iacute;nh với c&uacute;m A/H1N1 bị tử vong.</p>\r\n<div class="pageContent">\r\n<p>Bệnh nh&acirc;n (BN) l&agrave; nam 30 tuổi, l&agrave;m nghề thợ kh&oacute;a (ngụ Q.B&igrave;nh Thạnh, TP.HCM), v&agrave;o BV Nh&acirc;n d&acirc;n Gia Định l&uacute;c 21 giờ 30 ng&agrave;y 21.9, v&igrave; trước đ&oacute; bị sốt, ho, đau họng... C&aacute;c b&aacute;c sĩ nghi ngờ BN nhiễm c&uacute;m A/H1N1 n&ecirc;n cho điều trị thuốc Tamiflu lu&ocirc;n khi chưa l&agrave;m x&eacute;t nghiệm. Tuy nhi&ecirc;n, sau khi nhập viện được v&agrave;i giờ đồng hồ th&igrave; BN trở nặng, suy h&ocirc; hấp, v&agrave; diễn biến nặng, đến 17 giờ chiều qua th&igrave; tử vong. Kết quả x&eacute;t nghiệm tại Viện Pasteur (TP.HCM) sau đ&oacute; đ&atilde; x&aacute;c định BN dương t&iacute;nh c&uacute;m A/H1N1. BN n&agrave;y c&oacute; tiền căn bị bại liệt hai ch&acirc;n, nhưng thể trạng b&eacute;o ph&igrave; (nặng 80 kg). Như vậy, đến nay đ&atilde; c&oacute; 11 BN dương t&iacute;nh c&uacute;m A/H1N1 tử vong, phần lớn l&agrave; những người c&oacute; nhiều bệnh đi k&egrave;m.</p>\r\n<p>Bộ Y tế cho biết, trong ng&agrave;y h&ocirc;m qua, cả nước đ&atilde; ghi nhận th&ecirc;m 203 trường hợp dương t&iacute;nh với c&uacute;m A/H1N1, đưa số dương t&iacute;nh từ đầu vụ dịch l&ecirc;n 7.839 trường hợp. Đ&atilde; c&oacute; 5.211 bệnh nh&acirc;n ra viện.</p>\r\n<p>* C&ugrave;ng ng&agrave;y, Sở Y tế H&agrave; Nội cho biết, từ đầu năm đến nay, TP đ&atilde; c&oacute; 6.057 bệnh nh&acirc;n sốt xuất huyết (SXH), tăng gấp gần 15 lần so với c&ugrave;ng kỳ 2008. 90% số bệnh nh&acirc;n SXH đ&atilde; khỏi bệnh, kh&ocirc;ng c&oacute; tử vong. Từ đầu năm đến nay, cả nước ghi nhận hơn 57.000 ca SXH, tăng 13% so với c&ugrave;ng kỳ 2008.</p>\r\n<p align="right"><strong>Thanh T&ugrave;ng - Li&ecirc;n Ch&acirc;u</strong></p>\r\n</div>', 1, 1253929081, 2, 19, 0),
(50, 2, 'Rơi từ tầng 4, một nữ công nhân thiệt mạng', '(LĐĐT) - Hồi 7 giờ 30 phút sáng 19.10, một vụ tai nạn lao động thương tâm đã xảy ra: chị Nguyễn Thị Vân tử vong khi rơi từ tầng 4 công trình xây dựng khách sạn Best Western Dalat Plaza tại số 9, đường Lê Đại Hành, thành phố Đà Lạt (Lâm Đồng).\r\n', 'no', '', '<div class="atc_txt"><span id="_ctl0_rContent_lbBody">\r\n<p align="justify">Những người c&oacute; mặt tại hiện trường cho biết, chị V&acirc;n (chưa x&aacute;c định r&otilde; qu&ecirc; qu&aacute;n v&agrave; tuổi) l&agrave; thợ phụ hồ ki&ecirc;m c&ocirc;ng nh&acirc;n vệ sinh tại c&ocirc;ng tr&igrave;nh n&oacute;i tr&ecirc;n.</p>\r\n<p align="justify">Thời điểm xảy ra tai nạn, nạn nh&acirc;n vừa ra ngo&agrave;i mua b&aacute;nh m&igrave; ăn s&aacute;ng, mang l&ecirc;n tầng 4 để ăn rồi l&agrave;m việc nhưng chưa kịp ăn th&igrave; xảy ra tai nạn.</p>\r\n<p align="justify">Sau khi người đi đường ph&aacute;t hiện nạn nh&acirc;n rơi xuống đất, những người l&agrave;m c&ugrave;ng mới biết v&agrave; chạy xuống cấp cứu nhưng qu&aacute; muộn.</p>\r\n<p align="justify">Được biết, c&ocirc;ng tr&igrave;nh kh&aacute;ch sạn Best Western Dalat Plaza được x&acirc;y dựng theo ti&ecirc;u chuẩn 4 sao, do C&ocirc;ng ty Cổ phần Dịch vụ du lịch Đ&agrave; Lạt l&agrave;m chủ đầu tư, đơn vị thi c&ocirc;ng l&agrave; C&ocirc;ng ty Cổ phần S&ocirc;ng Đ&agrave; 9.06.</p>\r\n<p align="justify">C&ocirc;ng an th&agrave;nh phố Đ&agrave; Lạt đang điều tra l&agrave;m r&otilde; vụ việc.</p>\r\n</span>\r\n<div class="page">&nbsp;</div>\r\n<div class="atc_aut"><span id="_ctl0_rContent_lbSource">T.H (Theo TTXVN)</span></div>\r\n</div>', 1, 1256004886, 2, 32, 0),
(51, 2, 'Bổ sung gần 600 tỉ để phòng, chống dịch cúm A/H1N1', '(LĐ) - Thủ tướng Chính phủ Nguyễn Tấn Dũng vừa ký QĐ số 1603/QĐ-TTg về  việc bổ sung dự toán ngân sách năm 2009 để phòng, chống dịch cúm A/H1N1.\r\n', 'no', '', '<div class="atc_txt"><span id="_ctl0_rContent_lbBody">\r\n<p align="justify">Theo đ&oacute; bổ sung 594.150 triệu đồng cho 47 tỉnh, th&agrave;nh phố trực thuộc T.Ư v&agrave; c&aacute;c bộ, ng&agrave;nh T.Ư&nbsp; từ nguồn dự to&aacute;n chi sự nghiệp y tế thuộc ng&acirc;n s&aacute;ch T.Ư năm 2009 đ&atilde; được Quốc hội quyết định để phục vụ c&ocirc;ng t&aacute;c ph&ograve;ng, chống dịch c&uacute;m A/H1N1.&nbsp;</p>\r\n</span>\r\n<div class="page">&nbsp;</div>\r\n<div class="atc_aut"><span id="_ctl0_rContent_lbSource">S.Đ&agrave;</span></div>\r\n</div>', 1, 1256005122, 2, 19, 0),
(52, 2, 'Tai nạn lao động chết người tại TP.HCM tăng báo động', 'Vũ Như\r\nVietnamnet, 05/10/2009\r\n', '52.jpg', 'Công nhân công trình xây dựng làm việc dưới hệ thống cần cẩu nguy hiểm nhưng có người dụng cụ bảo vệ an toàn lao động chỉ là...cái nón lá. Ảnh chụp tại một công trình xây dựng trên đại lộ Ng', '<p>Theo thống k&ecirc; của Li&ecirc;n đo&agrave;n Lao động TP.HCM, 8 th&aacute;ng đầu năm nay, tr&ecirc;n địa b&agrave;n th&agrave;nh phố đ&atilde; xảy ra 70 vụ tai nạn lao động (TNLĐ) chết người, l&agrave;m chết 71 người, bị thương 18 người.</p>\r\n<p>Mặc d&ugrave; t&igrave;nh trạng mất an to&agrave;n lao động lu&ocirc;n được b&aacute;o động nhưng số vụ tai nạn lao động xảy ra tr&ecirc;n địa b&agrave;n TP.HCM vẫn tiếp tục gia tăng.</p>\r\n<p>H&agrave;ng năm, TP.HCM được coi l&agrave; một &ldquo;đại c&ocirc;ng trường&rdquo; với h&agrave;ng ng&agrave;n c&ocirc;ng tr&igrave;nh x&acirc;y dựng đua nhau mọc l&ecirc;n như nấm nhưng song song với n&oacute; l&agrave; t&igrave;nh trạng mất vệ sinh, thiếu an to&agrave;n lao động cũng kh&ocirc;ng ngừng tăng l&ecirc;n.</p>\r\n<p>Theo thống k&ecirc; của Li&ecirc;n đo&agrave;n Lao động TP.HCM, t&iacute;nh từ đầu năm đến ng&agrave;y 31/8/2009, tr&ecirc;n địa b&agrave;n th&agrave;nh phố đ&atilde; xảy ra 70 vụ tai nạn lao động chết người, l&agrave;m chết 71 người, bị thương 18 người.</p>\r\n<p align="center"><img border="0" alt="" style="width: 480px; height: 270px" src="http://www.antoanlaodong.gov.vn/Image.ashx/image=pjpeg/6718cc2ac8934d8b81d31e82d68d9b6d-14-10hcm.jpg/14-10hcm.jpg" /><br />\r\n<em>C&ocirc;ng nh&acirc;n c&ocirc;ng tr&igrave;nh x&acirc;y dựng l&agrave;m việc dưới hệ thống cần cẩu nguy hiểm nhưng c&oacute; người dụng cụ bảo vệ an to&agrave;n lao động chỉ l&agrave;...c&aacute;i n&oacute;n l&aacute;. Ảnh chụp tại một c&ocirc;ng tr&igrave;nh x&acirc;y dựng tr&ecirc;n đại lộ Nguyễn Văn Linh, quận 7. (Ảnh: Vũ Như)</em></p>\r\n<p>Trước đ&oacute;, theo thống k&ecirc; của cơ quan chức năng, 3 th&aacute;ng đầu năm 2009, th&agrave;nh phố xảy ra 20 vụ tai nạn lao động chết người, l&agrave;m chết 20 người v&agrave; l&agrave;m bị thương 2 người. Từ những con số thống k&ecirc; tr&ecirc;n cho thấy, những th&aacute;ng gần đ&acirc;y, số vụ tai nạn lao động xảy ra c&oacute; chiều hướng gia tăng đến mức b&aacute;o động.</p>\r\n<p>&Ocirc;ng Trương L&acirc;m Danh &ndash; Ph&oacute; Chủ tịch li&ecirc;n đo&agrave;n Lao động TP.HCM cho biết: TNLĐ xảy ra chủ yếu tại c&aacute;c doanh nghiệp l&agrave; c&ocirc;ng ty tr&aacute;ch nhiệm hữu hạn với tổng số 43/70 vụ, c&ocirc;ng ty cổ phần với tổng số 15/70 vụ, c&ograve;n lại thuộc c&aacute;c loại h&igrave;nh doanh nghiệp kh&aacute;c như tại c&aacute;c doanh nghiệp nh&agrave; nước, cơ sở sản xuất&hellip;</p>\r\n<p>C&aacute;c lĩnh vực xảy ra TNLĐ chết người dường như kh&ocirc;ng thay đổi, chủ yếu vẫn tập trung trong lĩnh vực x&acirc;y dựng, TNLĐ do c&oacute; yếu tố điện.</p>\r\n<p>Nguy&ecirc;n nh&acirc;n chủ yếu của t&igrave;nh trạng tr&ecirc;n được x&aacute;c định l&agrave; do người sử dụng lao động chưa tu&acirc;n thủ c&aacute;c quy định về vệ sinh, an to&agrave;n lao động; chưa trang bị đầy đủ, đ&uacute;ng ti&ecirc;u chuẩn c&aacute;c quy định về vệ sinh, an to&agrave;n lao động. Đặc biệt, ph&iacute;a người lao động chưa tự &yacute; thức bảo vệ m&igrave;nh./.</p>\r\n<div class="cssDefaultSupportLinks" style="padding-right: 5px; height: 24px" align="right"><i>Nguồn:</i>&nbsp; <b>Vietnamnet, 05/10/2009</b></div>', 1, 1256005480, 2, 23, 0),
(53, 2, 'Vinashin chú trọng an toàn lao động ', 'Qua 9 tháng, số tiền chi cho công tác bảo hộ lao động của tập đoàn đạt hơn 85 tỷ đồng  ', '53.jpg', '', '<span id="ctl00_mContent_lbBody">&nbsp;\r\n<p align="justify">&nbsp;</p>\r\n<p align="justify">&nbsp;</p>\r\nTừ đầu năm đến nay, mặc d&ugrave; hoạt động sản xuất kinh doanh của Tập đo&agrave;n C&ocirc;ng nghiệp t&agrave;u thủy Việt Nam (Vinashin) c&oacute; nhiều kh&oacute; khăn song đơn vị vẫn quan t&acirc;m đến đời sống v&agrave; cải thiện điều kiện l&agrave;m việc cho c&ocirc;ng nh&acirc;n. Hầu hết c&aacute;c đơn vị cảu tập đo&agrave;n quan t&acirc;m thực hiện tốt nội dung của c&ocirc;ng t&aacute;c bảo hộ lao động, cải thiện điều kiện v&agrave; m&ocirc;i trường l&agrave;m việc, đảm bảo chế độ, ch&iacute;ch s&aacute;ch đối với người lao động.\r\n<p>&nbsp;</p>\r\n<p align="justify">Đến nay, c&oacute; tr&ecirc;n 33.000 người được kh&aacute;m sức khỏe định kỳ v&agrave; tr&ecirc;n 32.000 người được huấn luyện về an to&agrave;n lao động. C&aacute;c đơn vị thực hiện tốt c&ocirc;ng t&aacute;c n&agrave;y l&agrave; c&aacute;c Tổng C&ocirc;ng ty c&ocirc;ng nghiệp t&agrave;u thủy Bạch Đằng, Nam Triệu, Ph&agrave; Rừng, c&aacute;c c&ocirc;ng ty đ&oacute;ng t&agrave;u Hạ Long, S&ocirc;ng Cấm, c&ocirc;ng nghiệp t&agrave;u thủy S&agrave;i G&ograve;n, Vận tải thủy Cần Thơ. C&ocirc;ng t&aacute;c bảo hộ lao động v&agrave; kỹ thuật an to&agrave;n đi v&agrave;o nền nếp v&agrave; c&oacute; chiều s&acirc;u ở nhiều đơn vị./.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n</span><span id="ctl00_mContent_lbSource" class="dtContentTxtAuthor">Nguyễn Thị Hưởng</span>', 1, 1256005712, 2, 26, 0),
(54, 2, 'Cháy ở toà chung cư cao nhất Hà Nội', '(LĐ) - • TP.Hồ Chí Minh: Cháy lớn tại quán càphê sân vườn.\r\nKhoảng 19h ngày 8.11, tại phòng 806, toà nhà 34T, khu đô thị Trung Hoà - Nhân Chính (Cầu Giấy, Hà Nội) đã xảy ra hoả hoạn. \r\n', '54.jpg', 'Người dân chen chúc thoát hiểm.', '<table class="atc_imgWrap" cellspacing="0" cellpadding="0" align="left">\r\n    <tbody>\r\n        <tr>\r\n            <td><img id="_ctl0_rContent_imgImage" class="atc_img" border="0" alt="" src="http://www.laodong.com.vn/avatar.aspx?ID=118887&amp;at=0&amp;ts=236&amp;lm=633933595357030000" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td><span id="_ctl0_rContent_lbImageCaption" class="atc_imgCaption">Người d&acirc;n chen ch&uacute;c tho&aacute;t hiểm.</span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div class="atc_desc"><span id="_ctl0_rContent_lbDesc">(LĐ) - &bull; TP.Hồ Ch&iacute; Minh: Ch&aacute;y lớn tại qu&aacute;n c&agrave;ph&ecirc; s&acirc;n vườn.<br />\r\nKhoảng 19h ng&agrave;y 8.11, tại ph&ograve;ng 806, to&agrave; nh&agrave; 34T, khu đ&ocirc; thị Trung Ho&agrave; - Nh&acirc;n Ch&iacute;nh (Cầu Giấy, H&agrave; Nội) đ&atilde; xảy ra hoả hoạn. </span></div>\r\n<div class="atc_txt"><span id="_ctl0_rContent_lbBody">\r\n<div style="text-align: justify" align="justify">Vụ ch&aacute;y đ&atilde; l&agrave;m n&aacute;o loạn một trong những khu chung cư cao tầng nhất H&agrave; Nội - 34 tầng. Theo t&igrave;m hiểu của PV, khi vụ ch&aacute;y xảy ra kh&ocirc;ng c&oacute; người b&ecirc;n trong, bởi đ&acirc;y l&agrave; văn ph&ograve;ng của một c&ocirc;ng ty. <br />\r\n<br />\r\nTại hiện trường, một số người d&acirc;n bức x&uacute;c cho biết, vụ ch&aacute;y đ&atilde; bộc lộ nhiều điểm yếu trong c&ocirc;ng t&aacute;c ph&ograve;ng, chống ch&aacute;y nổ tại c&aacute;c khu chung cư cao tầng: Mặc d&ugrave; nằm ngay s&aacute;t mặt đường Ho&agrave;ng Đạo Thu&yacute;, nhưng do hệ thống giao th&ocirc;ng tĩnh thiếu n&ecirc;n nhiều xe &ocirc;t&ocirc; của c&aacute;c hộ d&acirc;n đậu k&iacute;n l&ograve;ng đường - g&acirc;y cản trở c&ocirc;ng t&aacute;c cứu hoả của lực lượng cảnh s&aacute;t PCCC; hệ thống b&aacute;o ch&aacute;y hoạt động &ldquo;tậm tịt&rdquo; - thường xuy&ecirc;n c&oacute; hiện tượng b&aacute;o ch&aacute;y giả, nhưng khi c&oacute; ch&aacute;y thật như vụ việc xảy ra lần n&agrave;y th&igrave; kh&ocirc;ng b&aacute;o - Ban quản l&yacute; to&agrave; nh&agrave; phải th&ocirc;ng b&aacute;o bằng loa cho c&aacute;c hộ d&acirc;n; khi xảy ra vụ ch&aacute;y hệ thống thang m&aacute;y vẫn hoạt động, n&ecirc;n nhiều người d&acirc;n tho&aacute;t hiểm bằng thang m&aacute;y c&oacute; thể gặp nguy hiểm do kh&oacute;i lan toả khắp c&aacute;c buồng thang m&aacute;y g&acirc;y ngạt thở... <br />\r\n<br />\r\n<table class="atc_imgWrap" cellspacing="0" cellpadding="0" align="right">\r\n    <tbody>\r\n        <tr>\r\n            <td class="atc_img"><img alt="" src="http://www.laodong.com.vn/Uploaded_LAODONG/thanhbinh/20091109/chay.jpg" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td class="atc_imgCaption">Lửa l&agrave;m ch&aacute;y đen cửa sổ căn hộ 806. Ảnh: H.A.</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\nMột người d&acirc;n ở tầng 19 cho biết, khi được b&aacute;o xảy ra hoả hoạn, người d&acirc;n &agrave;o ra cầu thang bộ để tho&aacute;t hiểm, tuy nhi&ecirc;n hệ thống đ&egrave;n chiếu s&aacute;ng lại kh&ocirc;ng hoạt động, g&acirc;y hoảng loạn v&agrave; &ugrave;n tắc. Đặc biệt, khi người d&acirc;n đang tho&aacute;t xuống th&igrave; lại &ldquo;va&rdquo; với lực lượng cảnh s&aacute;t PCCC đang lao l&ecirc;n tầng 8 dập lửa. Sau khi nhận được tin b&aacute;o, lực lượng cảnh s&aacute;t PCCC TP.H&agrave; Nội đ&atilde; điều động 3 xe cứu hoả v&agrave; phong toả hiện trường, t&igrave;m nguy&ecirc;n nh&acirc;n g&acirc;y ch&aacute;y...&nbsp;<br />\r\n<br />\r\n&bull; Hồi 13 giờ ng&agrave;y 8.11, tại qu&aacute;n c&agrave;ph&ecirc; s&acirc;n vườn COC - đường Quang Trung, P.12, Q.G&ograve; Vấp - TPHCM bốc ch&aacute;y dữ dội. Lực lượng cảnh s&aacute;t PCCC đ&atilde; cố gắng khống chế đ&aacute;m ch&aacute;y, kh&ocirc;ng để ch&aacute;y lan sang khu d&acirc;n cư v&agrave; d&atilde;y cửa h&agrave;ng b&ecirc;n cạnh qu&aacute;n c&agrave;ph&ecirc; ph&aacute;t hỏa. Vụ ch&aacute;y đ&atilde; thi&ecirc;u trụi ng&ocirc;i nh&agrave; c&ugrave;ng to&agrave;n bộ t&agrave;i sản nằm trong khu vực s&acirc;n vườn, gồm tầng trệt v&agrave; s&acirc;n thượng được thiết kế kiểu c&aacute;ch cầu kỳ. Kh&ocirc;ng c&oacute; thiệt hại về người.</div>\r\n</span>\r\n<div class="page">&nbsp;</div>\r\n<div class="atc_aut"><span id="_ctl0_rContent_lbSource">H.A - P.B</span></div>\r\n</div>', 1, 1258774453, 2, 27, 0);
INSERT INTO `tgp_cms` (`id`, `cat`, `ten`, `chu_thich`, `hinh`, `hinh_note`, `noi_dung`, `hien_thi`, `time`, `user`, `luot_xem`, `noi_bat`) VALUES
(55, 2, 'Sắp có thuốc điều trị cúm A/H1N1 do Việt Nam sản xuất', '(LĐ) - Gần đây có nhiều thông tin về việc tại VN, một dự án nghiên cứu sản phẩm có chứa hoạt chất Arbidol dùng để điều trị cúm A/H1N1 mang tên Fludon H1 đã thành công. \r\n', '55.jpg', 'Mẫu nghiên cứu của Fludon H1.', '<table class="atc_imgWrap" cellspacing="0" cellpadding="0" align="left">\r\n    <tbody>\r\n        <tr>\r\n            <td><img id="_ctl0_rContent_imgImage" class="atc_img" border="0" alt="" src="http://www.laodong.com.vn/avatar.aspx?ID=121144&amp;at=0&amp;ts=236&amp;lm=633952545338730000" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td><span id="_ctl0_rContent_lbImageCaption" class="atc_imgCaption">Mẫu nghi&ecirc;n cứu của Fludon H1.</span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<div class="atc_desc"><span id="_ctl0_rContent_lbDesc">(LĐ) - Gần đ&acirc;y c&oacute; nhiều th&ocirc;ng tin về việc tại VN, một dự &aacute;n nghi&ecirc;n cứu sản phẩm c&oacute; chứa hoạt chất Arbidol d&ugrave;ng để điều trị c&uacute;m A/H1N1 mang t&ecirc;n Fludon H1 đ&atilde; th&agrave;nh c&ocirc;ng. </span></div>\r\n<div class="atc_txt"><span id="_ctl0_rContent_lbBody">\r\n<p align="justify">TS Nguyễn Hải Nam - Trưởng khoa Ho&aacute; dược, Đại học Dược H&agrave; Nội, phụ tr&aacute;ch dự &aacute;n d&agrave;nh ri&ecirc;ng cho B&aacute;o Lao Động cuộc phỏng vấn về vấn đề n&agrave;y.</p>\r\n<p align="justify"><strong><em>C&oacute; rất nhiều hoạt chất kh&aacute;ng virus c&uacute;m như Interferon; Zanamivir; Oseltamivir; Ramantadine; Amantadine, tại sao dự &aacute;n nghi&ecirc;n cứu lại chọn Arbidol, thưa TS?<br />\r\n</em></strong><br />\r\n- Sau một thời gian t&igrave;m kiếm, ph&acirc;n t&iacute;ch v&agrave; so s&aacute;nh, nh&oacute;m nghi&ecirc;n cứu đ&atilde; quyết định lựa chọn tập trung nghi&ecirc;n cứu sản phẩm c&oacute; chứa hoạt chất Arbidol. Đ&acirc;y l&agrave; một hoạt chất c&oacute; khả năng ức chế sự hợp nhất giữa m&agrave;ng lipid virus với m&agrave;ng tế b&agrave;o, ngăn ngừa sự x&acirc;m nhập của virus v&agrave;o tế b&agrave;o. Hoạt chất n&agrave;y đ&atilde; được chứng minh tr&ecirc;n l&acirc;m s&agrave;ng c&oacute; t&aacute;c dụng trong dự ph&ograve;ng v&agrave; điều trị SARS, vi&ecirc;m gan B v&agrave; C, Herpes, Dengue Fever, RSV, đặc biệt l&agrave; c&aacute;c chủng virus c&uacute;m bao gồm cả c&uacute;m A/H1N1 v&agrave; H5N1. <br />\r\n<br />\r\nĐược ph&aacute;t minh bởi nh&agrave; khoa học Nga Robert Nikolaevich Glushkov từ năm 1973, Arbidol sau đ&oacute; đ&atilde; được nhiều nh&agrave; khoa học quan t&acirc;m nghi&ecirc;n cứu v&agrave; đ&atilde; được cấp số đăng k&yacute; lưu h&agrave;nh dược phẩm ở nhiều quốc gia. Năm 2009, khi dịch c&uacute;m A/H1N1 b&ugrave;ng ph&aacute;t ở Nga, Bộ Y tế v&agrave; Ph&aacute;t triển x&atilde; hội Nga đ&atilde; lựa chọn Arbidol cho ph&aacute;c đồ ch&iacute;nh thức ph&ograve;ng v&agrave; điều trị c&uacute;m A/H1N1 tr&ecirc;n to&agrave;n quốc.<br />\r\n<br />\r\n<strong><em>So với c&aacute;c thuốc kh&aacute;ng virus c&uacute;m A/H1N1 hiện đang được sử dụng, Fludon H1 c&oacute; những điểm kh&aacute;c biệt g&igrave;, thưa TS?<br />\r\n<br />\r\n</em></strong>- Hiện nay ở Việt Nam sử dụng chủ yếu dựa v&agrave;o Oseltamivir (Tamiflu)&nbsp; trong điều trị c&uacute;m A/H1N1. Theo số liệu của WHO c&ocirc;ng bố tại website: <a target="_blank" href="http://www.who.int/csr/disease/influenza/H1N1ivrusresistance_table_15Jan09.pdf">http://www.who.int/csr/disease/influenza/H1N1ivrusresistance_table_15Jan09.pdf</a>,&nbsp; từ 1.4 - 30.9.2008 virus H1N1 kh&aacute;ng Oseltamivir (Tamiflu) đ&atilde; c&oacute; ở 44 quốc gia v&agrave; v&ugrave;ng l&atilde;nh thổ. Số liệu c&ugrave;ng kỳ của WHO cũng cho thấy ở VN trong 31 mẫu H1N1 được kiểm tra, c&oacute; tới 21 mẫu, chiếm tới 68% kh&aacute;ng Oseltamivir (Tamiflu). Ngo&agrave;i ra, Zanamivir được khuyến c&aacute;o sử dụng cũng c&ugrave;ng d&ograve;ng ức chế Neuraminidase với Oseltamivir. <br />\r\n<br />\r\nHoạt chất Arbidol của Fludon H1 thuộc d&ograve;ng ức chế Hemaglutilin c&oacute; tỉ lệ virus kh&aacute;ng thuốc thấp hơn d&ograve;ng tr&ecirc;n. Nếu&nbsp; Fludon H1 được cấp ph&eacute;p lưu h&agrave;nh sẽ gi&uacute;p cho ng&agrave;nh y tế c&oacute; th&ecirc;m lựa chọn điều trị cho c&aacute;c trường hợp virus kh&aacute;ng Tamiflu. Quan trọng hơn nữa l&agrave; sẽ tăng t&iacute;nh chủ động trong cung ứng thuốc điều trị c&uacute;m A/H1N1 v&igrave; nguồn cung Fludon H1 dồi d&agrave;o sẽ gi&uacute;p VN chủ động hơn trong việc dự trữ thuốc ph&ograve;ng v&agrave; điều trị c&uacute;m A/H1N1. <br />\r\n<br />\r\nC&aacute;c nghi&ecirc;n cứu cho thấy sản phẩm Arbodon l&agrave; rất &iacute;t t&aacute;c dụng phụ, độc t&iacute;nh thấp, ngo&agrave;i ra chi ph&iacute; điều trị cũng thấp hơn v&igrave; 1 đợt điều trị c&uacute;m A/H1N1 (theo ph&aacute;c đồ điều trị của Bộ Y tế Nga) dự kiến khoảng 140.000 đồng/đợt điều trị cho người lớn, so với điều trị chuẩn bằng Tamiflu hiện l&agrave; 630.000 đồng.&nbsp;<br />\r\n<br />\r\n<strong><em>Xin TS n&oacute;i r&otilde; hơn về đặc điểm của Fludon H1?<br />\r\n<br />\r\n</em></strong>- Chế phẩm l&agrave; thuốc kh&aacute;ng virus, c&oacute; hoạt t&iacute;nh điều chỉnh miễn dịch v&agrave; kh&aacute;ng c&uacute;m, đặc biệt l&agrave; kh&aacute;ng c&uacute;m nh&oacute;m A v&agrave; B v&agrave; hội chứng suy h&ocirc; hấp cấp (SARS). Thuốc ngăn chặn virus tiếp x&uacute;c v&agrave; x&acirc;m nhập v&agrave;o tế b&agrave;o người bằng c&aacute;ch ngăn cản sự kết hợp m&agrave;ng lipid của virus v&agrave;o m&agrave;ng của tế b&agrave;o.<br />\r\n&nbsp;<br />\r\nThuốc c&oacute; t&aacute;c dụng như một interferon, k&iacute;ch th&iacute;ch t&iacute;nh miễn dịch thể dịch v&agrave; li&ecirc;n gian b&agrave;o, trợ gi&uacute;p hoạt động thực b&agrave;o của c&aacute;c đại thực b&agrave;o, gi&uacute;p cơ thể tăng cường khả năng chống lại c&aacute;c l&acirc;y nhiễm. Thuốc l&agrave;m giảm tần số biến chứng c&oacute; li&ecirc;n quan đến c&aacute;c bệnh l&acirc;y nhiễm virus v&agrave; giảm bớt c&aacute;c hậu quả của bệnh nhiễm khuẩn mạn t&iacute;nh.<br />\r\n<br />\r\nVới c&aacute;c bệnh l&acirc;y nhiễm virus, t&iacute;nh hiệu quả điều trị được thấy ở mức độc t&iacute;nh thấp, giảm bớt c&aacute;c triệu chứng l&acirc;m s&agrave;ng, v&agrave; r&uacute;t ngắn thời gian điều trị. Arbidol được xếp v&agrave;o nh&oacute;m thuốc &iacute;t độc nhất (LD50 &gt; 4g/kg). Thuốc kh&ocirc;ng cho thấy c&aacute;c phản ứng phụ tr&ecirc;n người khi uống h&agrave;ng ng&agrave;y với liều đ&atilde; khuyến c&aacute;o.<br />\r\n<br />\r\nArbidol được hấp thu nhanh v&agrave; ph&acirc;n bố rộng khắp cơ thể. Nồng độ đỉnh trong m&aacute;u đạt sau 1,2 giờ khi uống&nbsp; liều 50mg; 1,5 giờ khi uống liều 100mg. Arbidol được chuyển h&oacute;a tại gan. Nửa đời của thuốc trong cơ thể khoảng 17 - 21 giờ. C&oacute; khoảng 40% liều được b&agrave;i thải ở dạng kh&ocirc;ng đổi, phần lớn qua mật (38,9%) v&agrave; một lượng kh&ocirc;ng đ&aacute;ng kể được b&agrave;i thải th&ocirc;ng qua thận (0,12%). Trong v&ograve;ng 24 giờ đầu d&ugrave;ng thuốc, c&oacute; khoảng 90% liều thuốc được b&agrave;i thải khỏi cơ thể.&nbsp;- Xin cảm ơn TS!<br />\r\n&nbsp;<br />\r\n<table class="inboxContent">\r\n    <tbody>\r\n        <tr>\r\n            <td>\r\n            <p align="justify"><font size="2"><strong>Những mốc ch&iacute;nh của qu&aacute; tr&igrave;nh nghi&ecirc;n cứu sản xuất Fludon H1<br />\r\n            </strong><br />\r\n            - Ng&agrave;y 2.5.2008, Cty dược Viễn Đ&ocirc;ng quyết định th&agrave;nh lập nh&oacute;m nghi&ecirc;n cứu sản phẩm điều trị c&uacute;m H5N1. <br />\r\n            <br />\r\n            - Ng&agrave;y 24.6.2008 quyết định nghi&ecirc;n cứu&nbsp; sản phẩm c&oacute; chứa hoạt chất Arbidol d&ugrave;ng điều trị c&uacute;m gia cầm H5N1, đặt t&ecirc;n l&agrave; Fludon.<br />\r\n            <br />\r\n            - Cuối th&aacute;ng 4.2009, trước diễn biến phức tạp của c&uacute;m H1N1, nh&oacute;m quyết định x&aacute;c định trọng t&acirc;m của chương tr&igrave;nh l&agrave; c&uacute;m A/H1N1 v&agrave; đổi t&ecirc;n sản phẩm th&agrave;nh Fludon H1.<br />\r\n            <br />\r\n            - Ng&agrave;y 12.8.2009, nh&oacute;m nghi&ecirc;n cứu đ&atilde; ho&agrave;n tất c&aacute;c qu&aacute; tr&igrave;nh: X&acirc;y dựng ti&ecirc;u chuẩn, nghi&ecirc;n cứu độ ổn định v&agrave; x&acirc;y dựng quy tr&igrave;nh SX.<br />\r\n            <br />\r\n            - Ng&agrave;y 21.9.2009,&nbsp; TS. Nguyễn Hải Nam th&ocirc;ng b&aacute;o việc tổng hợp th&agrave;nh c&ocirc;ng Arbidol trong ph&ograve;ng th&iacute; nghiệm.<br />\r\n            <br />\r\n            - Ng&agrave;y 15.10.2009, nh&oacute;m nghi&ecirc;n cứu đ&atilde; ho&agrave;n tất hồ sơ đăng k&yacute; sản phẩm.<br />\r\n            <em>(Nguồn: Cty dược phẩm Viễn Đ&ocirc;ng - đơn vị tổ chức nghi&ecirc;n cứu Fludon H1)</em></font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n</span>\r\n<div class="page">&nbsp;</div>\r\n<div class="atc_aut"><span id="_ctl0_rContent_lbSource">Huỳnh Th&acirc;n - Thu Tr&agrave; thực hiện</span></div>\r\n</div>', 1, 1259635422, 2, 23, 0),
(56, 2, 'Kỹ sư bảo hộ lao động - Nghề hot hậu WTO ', 'Việc đảm bảo về mặt an toàn cho công nhân, cán bộ làm việc trong một nhà máy là hết sức quan trọng. Chính vì vậy, ngành bảo hộ lao động (BHLĐ) đang ngày càng trở lên quan trọng hơn. ', '56.jpg', '', '<table border="0" cellspacing="0" cellpadding="0" width="100%">\r\n    <tbody>\r\n        <tr>\r\n            <th valign="top" scope="col" align="left" style="padding-bottom: 10px; padding-left: 0px; padding-right: 0px; padding-top: 10px">\r\n            <table border="0" cellspacing="0" cellpadding="3" width="1" align="left">\r\n                <tbody>\r\n                    <tr>\r\n                        <td align="left"><img id="ctl00_ContentPlaceHolderBody_UcTinTucDetails1_imgAnhTin" alt="" width="400" height="300" style="border-right-width: 0px; border-top-width: 0px; border-bottom-width: 0px; border-left-width: 0px" src="/uploads/image/Zai_BH-1544822584.jpg" /></td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td>&nbsp;</td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            <p><strong>BHLĐ - sao lại hot ?</strong><br />\r\n            <br />\r\n            Trước đ&acirc;y, khi nhắc đến BHLĐ, hầu hết mọi người lu&ocirc;n h&igrave;nh dung đ&acirc;y l&agrave; c&ocirc;ng việc trong c&aacute;c cơ sở quản l&yacute; bảo hộ của nh&agrave; nước, c&aacute;c cơ sở c&ocirc;ng đo&agrave;n. BHLĐ l&agrave; ng&agrave;nh nghề đ&atilde; từng &iacute;t được xem trọng tại Việt Nam. Nhưng sau khi gia nhập WTO, sức &eacute;p thi h&agrave;nh luật bảo vệ người lao động đối với c&aacute;c nh&agrave; m&aacute;y cũng như sự cạnh tranh giữa c&aacute;c c&ocirc;ng ty tại Việt Nam đ&atilde; l&agrave;m nhu cầu nh&acirc;n lực BHLĐ tăng đột biến. Th&ecirc;m một yếu tố l&agrave;m cho ng&agrave;nh n&agrave;y kh&aacute;t nh&acirc;n lực đ&oacute; l&agrave; rất &iacute;t c&aacute;c cơ sở đ&agrave;o tạo ĐH, cao đẳng trong to&agrave;n quốc đưa v&agrave;o chương tr&igrave;nh giảng dạy kỹ sư BHLĐ. Nguồn cung hạn chế, nhu cầu tuyển dụng tăng đột biến, kỹ sư bảo hộ lao động đang &acirc;m thầm l&agrave; một nghề hot trong ng&agrave;nh kỹ thuật hậu WTO.<br />\r\n            <br />\r\n            <strong>Học những g&igrave; ?</strong> <br />\r\n            <br />\r\n            Trong chương tr&igrave;nh đ&agrave;o tạo kỹ sư BHLĐ của trường ĐH T&ocirc;n Đức Thắng, sinh vi&ecirc;n sẽ được kiến thức chuy&ecirc;n s&acirc;u về m&ocirc;i trường lao động, m&ocirc;i trường doanh nghiệp, an to&agrave;n lao động, t&acirc;m sinh l&yacute; của người lao động, tổ chức khoa học lao động. Người kỹ sư bảo hộ lao động c&oacute; kỹ năng điều tra, khảo s&aacute;t, ph&acirc;n t&iacute;ch, đ&aacute;nh gi&aacute; v&agrave; quản l&yacute; m&ocirc;i trường lao động; đề xuất c&aacute;c giải ph&aacute;p c&ocirc;ng nghệ v&agrave; tổ chức để xử l&yacute; n&oacute;ng, bụi, ồn, rung&hellip; m&agrave; vẫn phải đảm bảo vấn đề năng suất trong qu&aacute; tr&igrave;nh sản xuất. Ngo&agrave;i ra, sinh vi&ecirc;n c&ograve;n được cung cấp những kiến thức về kỹ thuật, hỗ trợ cho việc hiểu r&otilde; những kh&oacute; khăn, cản trở trong việc đảm bảo an to&agrave;n cho người lao động.<br />\r\n            <br />\r\n            <strong>L&agrave;m việc ở đ&acirc;u ?</strong><br />\r\n            <br />\r\n            Kỹ sư BHLĐ ng&agrave;y nay khi ra trường c&oacute; thể dễ d&agrave;ng t&igrave;m được vị tr&iacute; ổn định trong c&aacute;c nh&agrave; m&aacute;y x&iacute; nghiệp tr&ecirc;n to&agrave;n quốc, kh&ocirc;ng nhất thiết phải l&agrave;m trong c&aacute;c tổ chức kiểm so&aacute;t&nbsp; quản l&yacute; của nh&agrave; nước như ng&agrave;y xưa. Do nhu cầu qu&aacute; lớn m&agrave; nguồn cung nh&acirc;n lực từ c&aacute;c trường ĐH vẫn chưa thể đ&aacute;p ứng, sinh vi&ecirc;n năm cuối trong ng&agrave;nh hiện nay đang nhận việc trước khi nhận bằng. Nhiều cơ sở sản xuất đ&atilde; gửi thư mời đến tận khoa BHLĐ trong c&aacute;c trường để t&igrave;m được người kỹ sư bảo hộ lao động cho doanh nghiệp của m&igrave;nh.<br />\r\n            <br />\r\n            Do đặc th&ugrave; c&ocirc;ng việc cần hiểu r&otilde; m&ocirc;i trường lao động, người lao động v&agrave; l&agrave; người tiếp x&uacute;c trực tiếp với c&aacute;c vấn đề trong sản xuất, người kỹ sư bảo hộ dễ d&agrave;ng t&iacute;ch lũy cho m&igrave;nh nhiều kinh nghiệm trong qu&aacute; tr&igrave;nh l&agrave;m việc. Nếu c&oacute; th&ecirc;m bằng cấp về quản l&yacute;, người kỹ sư BHLĐ c&oacute; thể sẽ được đề xuất l&ecirc;n nhưng vị tr&iacute; cao hơn. Th&ecirc;m một lợi thế cho người trong ng&agrave;nh đ&oacute; l&agrave; một kỹ sư BHLĐ c&oacute; thể l&agrave;m việc cho nhiều c&ocirc;ng ty kh&aacute;c nhau m&agrave; vẫn c&oacute; thể đảm bảo được nhiệm vụ của m&igrave;nh.<br />\r\n            <br />\r\n            <strong>C&oacute; dễ để ho&agrave;n th&agrave;nh c&ocirc;ng việc?</strong><br />\r\n            <br />\r\n            L&agrave; một nghề kỹ thuật nhưng chương tr&igrave;nh đ&agrave;o tạo kh&ocirc;ng qu&aacute; kh&oacute; nhằn, kiến thức tương đối l&yacute; thuyết, kh&ocirc;ng qu&aacute; nhiều con số, ng&agrave;nh học BHLĐ rất th&iacute;ch hợp cho nhiều bạn nam y&ecirc;u th&iacute;ch kỹ thuật nhưng kh&ocirc;ng ưa c&ocirc;ng việc t&iacute;nh to&aacute;n phức tạp h&agrave;ng giờ liền. B&ugrave; lại, muốn l&agrave;m việc tốt trong ng&agrave;nh, bạn cần phải l&agrave; người giỏi giao tiếp v&agrave; cảm th&ocirc;ng mới c&oacute; thể hiểu được những bất an trong m&ocirc;i trường lao động rồi dung h&ograve;a n&oacute; với nhu cầu sản xuất của nh&agrave; m&aacute;y. Một người kỹ sư BHLĐ giỏi kh&ocirc;ng phải chỉ biết đảm bảo an to&agrave;n cho người lao động m&agrave; kh&ocirc;ng nghĩ đến lợi &iacute;ch của doanh nghiệp. C&ocirc;ng việc BHLĐ v&igrave; vậy m&agrave; hấp dẫn hơn, t&iacute;ch cực hơn v&agrave; cũng thử th&aacute;ch hơn ng&agrave;y xưa.<br />\r\n            <br />\r\n            Ngo&agrave;i ra, để l&agrave;m việc tốt trong một ng&agrave;nh kỹ thuật, cẩn thận lu&ocirc;n l&agrave; đức t&iacute;nh kh&ocirc;ng thể thiếu khi người kỹ sư BHLĐ đ&aacute;nh gi&aacute;, c&acirc;n nhắc c&aacute;c phương ph&aacute;p bảo hộ, nhất l&agrave; khi đ&oacute; l&agrave; c&ocirc;ng việc đảm bảo an to&agrave;n cho người lao động.<br />\r\n            <strong><br />\r\n            Trở th&agrave;nh kỹ sư BHLĐ c&oacute; kh&oacute; ? <br />\r\n            </strong><br />\r\n            Để trở th&agrave;nh kỹ sư BHLĐ, bạn sẽ kh&ocirc;ng gặp phải một r&agrave;o cản qu&aacute; kh&oacute; khăn khi thi tuyển v&agrave;o ng&agrave;nh n&agrave;y. Hiện nay ng&agrave;nh đang được giảng dạy tại trường ĐH C&ocirc;ng đo&agrave;n với điểm tuyển sinh khối A năm 2008 l&agrave; 15. Ng&agrave;nh BHLĐ tại ĐH T&ocirc;n Đức Thắng năm 2008 c&oacute; điểm tr&uacute;ng tuyển cho khối A l&agrave; 14 v&agrave; khối B l&agrave; 15. Nếu cảm thấy sức học của m&igrave;nh kh&ocirc;ng qu&aacute; vượt trội, nhưng bản th&acirc;n l&agrave; con người cẩn thận, th&iacute;ch chăm lo cho mọi người v&agrave; y&ecirc;u th&iacute;ch c&ocirc;ng việc kỹ thuật, đ&aacute;nh gi&aacute;, ph&acirc;n t&iacute;ch, ng&agrave;nh BHLĐ c&oacute; thể l&agrave; ng&agrave;nh học th&iacute;ch hợp cho bạn đấy.</p>\r\n            </th>\r\n        </tr>\r\n        <tr>\r\n            <td align="right"><span id="ctl00_ContentPlaceHolderBody_UcTinTucDetails1_lblNguonTin" style="font-weight: bold">(Hieuhoc.com)</span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>', 1, 1259739408, 2, 29, 0),
(58, 2, 'Hội thảo Mạng thông tin Quốc gia về An toàn vệ sinh lao động- Phòng chống cháy nổ lần thứ 13 ', 'Sáng ngày 27/11/2009, tại thành phố Thái Nguyên đã diễn ra Hội thảo Mạng thông tin Quốc gia về An toàn vệ sinh lao động- Phòng chống cháy nổ lần thứ 13 do Bộ Lao động- Thương binh và Xã hội cùng Bộ Y tế, Tổng Liên đoàn Lao động Việt Nam phối hợp tổ chức. Đến dự, có Cục trưởng Cục An toàn Lao động, Đoàn Minh Hoà; Phó cục trưởng Cục An toàn Lao động, Phạm Gia Lượng; Phó Viện trưởng Viện Y học Lao động, Trần Thị Liên; Phó Viện trưởng Viện nghiên cứu Khoa học Kỹ thuật Bảo hộ Lao động, Vũ Mạnh Hùng cùng lãnh đạo Sở Lao động- Thương binh và Xã hội một số tỉnh thành trong cả nước. ', '58.jpg', '', '<table class="tblImageDetail" border="0" cellspacing="0" cellpadding="0" align="left">\r\n    <tbody>\r\n        <tr>\r\n            <td valign="top" align="left"><img id="dnn_ctr380_NewsDetail_imgImage" style="border-right-width: 0px; width: 439px; border-top-width: 0px; border-bottom-width: 0px; height: 294px; border-left-width: 0px" alt="" src="/uploads/image/mangTTveATVSLD.jpg" /><br />\r\n            &nbsp;</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p class="contentdetailnews"><span id="dnn_ctr380_NewsDetail_lblContent"><span style="font-family: Verdana"><span style="font-size: 12px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Đ&acirc;y l&agrave; hội thảo thường ni&ecirc;n nhằm bổ sung, cập nhật v&agrave; phổ biến c&aacute;c th&ocirc;ng tin về an to&agrave;n vệ sinh lao động v&agrave; ph&ograve;ng chống ch&aacute;y nổ, cũng như tăng cường khả năng hoạt động giữa c&aacute;c th&agrave;nh vi&ecirc;n v&agrave; với c&aacute;c tổ chức th&ocirc;ng tin quốc tế,&nbsp; nhằm cải thiện điều kiện l&agrave;m việc v&agrave; ngăn ngừa, hạn chế tai nạn lao động&nbsp; v&agrave; bệnh nghề nghiệp.<br />\r\n<br />\r\n</span></span><span style="font-family: ''Verdana'', ''sans-serif''"><o:p></o:p></span></span></p>\r\n<p><span style="font-family: Verdana"><span style="font-size: 12px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;C&aacute;c b&aacute;o c&aacute;o tại&nbsp; cuộc họp đ&atilde; tập trung đ&aacute;nh gi&aacute;&nbsp; c&aacute;c kết quả&nbsp;hoạt&nbsp;động của mạng an to&agrave;n vệ&nbsp; sinh lao động trong năm 2009 v&agrave;&nbsp;định hướng ph&aacute;t triển của mạng trong những năm tiếp theo, c&ugrave;ng với&nbsp;đ&oacute;, c&aacute;c&nbsp; đại biểu&nbsp;đ&atilde; s&ocirc;i nổi thảo luận, đề xuất nhiều &yacute; kiến nhằm t&igrave;m những giải ph&aacute;p để n&acirc;ng cao hiệu quả hoạt động của mạng th&ocirc;ng tin.<br />\r\n<br />\r\n</span></span><span style="font-family: ''Verdana'', ''sans-serif''"><o:p></o:p></span></p>\r\n<p><span style="font-family: Verdana"><span style="font-size: 12px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Ph&aacute;t biểu tại hội thảo, Ph&oacute; Cục trưởng cục An to&agrave;n Lao động, Phạm Gia Lượng cho biết, mạng an to&agrave;n vệ sinh lao động do Cục An to&agrave;n lao động của Bộ Lao động-Thương binh v&agrave; X&atilde; hội kết hợp với 2 viện nghi&ecirc;n cứu của Bộ Y rế v&agrave; Tổng Li&ecirc;n đo&agrave;n điều phối hoạt động chung.<br />\r\n<br />\r\n</span></span><span style="font-family: ''Verdana'', ''sans-serif''"><o:p></o:p></span></p>\r\n<p><span style="font-family: Verdana"><span style="font-size: 12px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Trong năm 2009, Mạng th&ocirc;ng tin An to&agrave;n vệ&nbsp;sinh lao động (ATVSLĐ) đ&atilde; tiến h&agrave;nh nhiều họat động như tuy&ecirc;n truyền c&aacute;c nội dung về an to&agrave;n tr&ecirc;n c&aacute;c phương tiện th&ocirc;ng tin đại ch&uacute;ng; tổ chức huấn luyện cho hơn 13.000 học vi&ecirc;n nhằm n&acirc;ng cao năng lực đội ngũ c&aacute;n bộ; n&acirc;ng cấp, ph&aacute;t triển kho dữ liệu; tham gia trao đổi kinh nghiệm c&aacute;c th&ocirc;ng tin, hoạt động ATVSLĐ với c&aacute;c tổ chức, c&aacute;c quốc gia tr&ecirc;n thế giới; c&ugrave;ng với c&aacute;c hoạt động đ&oacute; Mạng th&ocirc;ng tin đ&atilde; tiến h&agrave;nh tổ chức th&agrave;nh c&ocirc;ng Tuần lễ Quốc gia về An to&agrave;n vệ sinh lao động v&agrave; Ph&ograve;ng chống ch&aacute;y nổ, cũng như giải thưởng doanh nghiệp ti&ecirc;u biểu 3, phối hợp tổ chức c&aacute;c cuộc thi v&ecirc; ATVSLĐ tại c&aacute;c địa phương v&agrave; c&aacute;c doanh nghiệp. <br />\r\n<br />\r\n</span></span><span style="font-family: ''Verdana'', ''sans-serif''"><o:p></o:p></span></p>\r\n<p><span style="font-family: Verdana"><span style="font-size: 12px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Tuy nhi&ecirc;n, hiện nay hoạt&nbsp;động của Mạng th&ocirc;ng tin vẫn gặp một số kh&oacute; khăn như việc cung cấp, chia sẻ c&aacute;c th&ocirc;ng tin giữa c&aacute;c cơ quan đầu mối v&agrave; c&aacute;c th&agrave;nh vi&ecirc;n của Mạng chưa kịp thời v&agrave; chưa thường xuy&ecirc;n; c&aacute;c th&ocirc;ng tin c&oacute; t&iacute;nh chất hướng dẫn chi tiết cho c&ocirc;ng t&aacute;c ATVSLĐ trong việc tiếp cận c&aacute;c ti&ecirc;u chuẩn quốc tế, tr&aacute;ch nhiệm x&atilde; hội ở c&aacute;c doanh nghiệp c&ograve;n hạn chế; cơ sở vật chất, trang thiết bị th&ocirc;ng tin cũng như đội ngũ l&agrave;m c&ocirc;ng t&aacute;c th&ocirc;ng tin truyền th&ocirc;ng ở địa phương c&ograve;n nhiều hạn chế.<br />\r\n<br />\r\n</span></span><span style="font-family: ''Verdana'', ''sans-serif''"><o:p></o:p></span></p>\r\n<p><span style="font-family: Verdana"><span style="font-size: 12px">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;V&igrave;&nbsp; vậy, để ph&aacute;t triển Mạng th&ocirc;ng tin ATVSLĐ&nbsp;cần phải thực hiện những việc cụ thể như sau: Đổi mới cơ chế hoạt động; mở rộng hoạt động của mạng th&ocirc;ng tin; tăng cường x&acirc;y dựng kho dữ liệu điện tử ATVSLĐ; tiếp tục đẩy mạnh, đa dạng ho&aacute; c&aacute;c hoạt động th&ocirc;ng tin, tuy&ecirc;n truyền; đẩy mạnh ứng dụng c&aacute;c c&ocirc;ng nghệ th&ocirc;ng tin mới trong hoạt động th&ocirc;ng tin, tuy&ecirc;n truyền; n&acirc;ng cao năng lực truyền th&ocirc;ng cũng như huấn luyện cho c&aacute;c địa phương trong c&ocirc;ng t&aacute;c tuy&ecirc;n truyền, huấn luyện; tăng cường hợp t&aacute;c quốc tế, trao đổi kinh nghiệm về ATVSLĐ./.</span></span></p>', 1, 1259979366, 2, 25, 0),
(57, 2, 'Hội thảo An toàn Vệ sinh Lao động đối với lao động nông nghiệp ở nông thôn ', 'Ngày 4.12.2009, tại Hà Nội, Hội Khoa học Kỹ thuật An toàn và Vệ sinh Lao động Việt Nam phối hợp với Hội Nông dân Việt Nam tổ chức hội thảo với chủ đề An toàn Vệ sinh Lao động đối với lao động nông nghiệp ở nông thôn. Tham dự, có PGS.TS. Lê Vân Trình – Viện trưởng Viện nghiên cứu Khoa học Bảo hộ Lao động Việt Nam cùng nhiều đại biểu là đại diện các cơ quan, ban ngành liên quan như Viện Tài nguyên Môi trường, Giới Vì phát triển cộng đồng, Viện Y học, Liên hiệp các Hội Khoa học Kỹ thuật Việt Nam, Trường Đại học Công đoàn, Trường Đại học Y Hà Nội, Tổng Liên đoàn Lao động Việt Nam, Liên minh Hợp tác xã Việt Nam, Hội Nông Dân Việt Nam… ', '57.jpg', '', '<table class="tblImageDetail" border="0" cellspacing="0" cellpadding="0" align="left">\r\n    <tbody>\r\n        <tr>\r\n            <td valign="top" align="left"><img id="dnn_ctr380_NewsDetail_imgImage" alt="" style="border-right-width: 0px; width: 327px; border-top-width: 0px; border-bottom-width: 0px; height: 224px; border-left-width: 0px" src="/uploads/image/ATVSLDNongthon.jpg" /><br />\r\n            &nbsp;</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p class="cls_NewsSubject">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style="font-family: ''Verdana'', ''sans-serif''; font-size: 12pt">Mục đ&iacute;ch của hội thảo l&agrave; tiếp tục n&ecirc;u l&ecirc;n c&aacute;c vấn đề về ATVSLĐ trong n&ocirc;ng nghiệp nhằm th&uacute;c đẩy sự quan t&acirc;m hơn nữa của x&atilde; hội, của c&aacute;c cấp ch&iacute;nh quyền đến việc chăm lo, đảm bảo an to&agrave;n lao động, ph&ograve;ng tr&aacute;nh tai nạn lao động v&agrave; bệnh nghề nghiệp cho người lao động hoạt động trong n&ocirc;ng nghiệp.<o:p></o:p></span></p>\r\n<p class="MsoNormal" style="text-align: justify; text-indent: 0.5in"><span style="font-family: ''Verdana'', ''sans-serif''; font-size: 12pt">Hiện nay, lao động trong n&ocirc;ng nghiệp chiếm khoảng 70% lực lượng lao động của cả nước v&agrave; sản xuất ra nhiều sản phẩm kh&aacute;c nhau. N&ocirc;ng nghiệp đ&oacute;ng một vai tr&ograve; quan trọng trong tiến tr&igrave;nh ph&aacute;t triển kinh tế x&atilde; hội của Việt Nam v&agrave; trong những năm qua đ&atilde; đạt được những th&agrave;nh tựu to lớn, đưa Việt Nam từ chỗ thiếu gạo ăn trở th&agrave;nh một cường quốc số 2 thế giới về xuất khẩu gạo. C&ugrave;ng với sự ph&aacute;t triển của khoa học c&ocirc;ng nghệ, ng&agrave;nh n&ocirc;ng nghiệp Việt Nam cũng c&oacute; những bước ph&aacute;t triển đ&aacute;ng kể, c&aacute;c loại m&aacute;y m&oacute;c, thiết bị hiện đại, cơ giới h&oacute;a, điện kh&iacute; h&oacute;a c&aacute;c kh&acirc;u trong q&uacute;a tr&igrave;nh sản xuất, c&aacute;c biện ph&aacute;p th&acirc;m canh, c&aacute;c loại thuốc k&iacute;ch th&iacute;ch tăng trưởng được đưa v&agrave;o sản xuất đ&atilde; đem lại hiệu quả kinh tế cao, g&oacute;p phần cải thiện điều kiện sống cho người lao động trong lĩnh vực sản xuất n&ocirc;ng nghiệp.<o:p></o:p></span></p>\r\n<p class="MsoNormal" style="text-align: justify; text-indent: 0.5in"><span style="font-family: ''Verdana'', ''sans-serif''; font-size: 12pt">Tuy nhi&ecirc;n, b&ecirc;n cạnh việc thụ hưởng những th&agrave;nh tựu về khoa học kỹ thuật mang lại, người n&ocirc;ng d&acirc;n h&agrave;ng ng&agrave;y đ&atilde; phải tiếp x&uacute;c với rất nhiều nguy cơ rủi ro về an to&agrave;n v&agrave; sức khoẻ, c&oacute; li&ecirc;n quan đến lao động n&ocirc;ng nghiệp. Đ&oacute; l&agrave; việc sử dụng c&aacute;c loại h&oacute;a chất, ph&acirc;n b&oacute;n, thuốc trừ s&acirc;u, sử dụng điện v&agrave; m&aacute;y m&oacute;c kh&ocirc;ng an to&agrave;n, g&acirc;y ra những hậu quả nghi&ecirc;m trọng ảnh hưởng đến sức khoẻ v&agrave; t&iacute;nh mạng của người n&ocirc;ng d&acirc;n. Theo thống k&ecirc; của Bộ Lao động &ndash; Thương binh v&agrave; X&atilde; hội, năm 2008, cả nước c&oacute; 6.807 vụ nhiễm độc thuốc bảo vệ thực vật với 7.572 trường hợp, tử vong 137 trường hợp, cao hơn năm trước 1,4 lần. Trong đ&oacute; đa phần l&agrave; do nhiễm độc v&igrave; kh&ocirc;ng sử dụng trang thiết bị bảo hộ, c&oacute; một số &iacute;t l&agrave; do uống nhầm thuốc trừ s&acirc;u. Tai nạn lao động trong sử dụng điện v&agrave; m&aacute;y m&oacute;c n&ocirc;ng nghiệp chiếm tỷ lệ kh&aacute; cao, tần suất tai nạn hiện nay l&agrave; 7,99 (tức l&agrave; cứ 100.000 người lao động th&igrave; c&oacute; 799 lượt người bị tai nạn lao động), tần suất trong sử dụng m&aacute;y m&oacute;c thiết bị n&ocirc;ng nghiệp l&agrave; 8,56 (tức l&agrave; cứ 100.000 người lao động th&igrave; c&oacute; 856 trường hợp bị tai nạn lao động). Ri&ecirc;ng trong c&aacute;c trang trại đ&atilde; c&oacute; 22,6% số người bị tai nạn, trong đ&oacute; 6,2% bị m&aacute;y c&aacute;n kẹp.<o:p></o:p></span></p>\r\n<p class="MsoNormal" style="text-align: justify; text-indent: 0.5in"><span style="font-family: ''Verdana'', ''sans-serif''; font-size: 12pt">Theo c&aacute;c đại biểu, nguy&ecirc;n nh&acirc;n của thực trạng tr&ecirc;n l&agrave; do tr&igrave;nh độ nhận thức của người n&ocirc;ng d&acirc;n về vấn đề ATVSLĐ c&ograve;n thấp, người n&ocirc;ng d&acirc;n kh&ocirc;ng sử dụng phương tiện bảo vệ c&aacute; nh&acirc;n khi tiếp x&uacute;c với thuốc bảo vệ thực vật, vứt bỏ chai, bao chứa thuốc lung tung ngo&agrave;i đồng ruộng, lượng thuốc bảo vệ thực vật sử dụng vượt mức khuyến c&aacute;o, phần lớn m&aacute;y m&oacute;c, thiết bị n&ocirc;ng nghiệp đưa v&agrave;o sử dụng thiếu c&aacute;c bộ phận che chắn an to&agrave;n, đa số người lao động chưa hiểu r&otilde; v&agrave; chưa nắm được c&aacute;c nguy&ecirc;n tắc về an to&agrave;n trong sử dụng điện v&agrave; an to&agrave;n trong sử dụng m&aacute;y m&oacute;c n&ocirc;ng nghiệp. B&ecirc;n cạnh đ&oacute;, c&ocirc;ng t&aacute;c tuy&ecirc;n truyền về ATVSLĐ trong n&ocirc;ng nghiệp chưa s&acirc;u rộng đến n&ocirc;ng d&acirc;n. Những ch&iacute;nh s&aacute;ch ph&aacute;p luật của Nh&agrave; nước về vấn đề an to&agrave;n vệ sinh lao động trong n&ocirc;ng nghiệp chưa nhiều.<o:p></o:p></span></p>\r\n<p class="MsoNormal" style="text-align: justify; text-indent: 0.5in"><span style="font-family: ''Verdana'', ''sans-serif''; font-size: 12pt">Tại hội thảo, c&aacute;c giải ph&aacute;p nhằm hạn chế tai nạn lao dộng v&agrave; bảo đảm ATVSLĐ cho người lao động trong n&ocirc;ng nghiệp được n&ecirc;u ra l&agrave;: X&acirc;y dựng v&agrave; bổ sung, ho&agrave;n thiện c&aacute;c ch&iacute;nh s&aacute;ch ph&aacute;p luật về ATVSLĐ trong lĩnh vực n&ocirc;ng nghiệp; Tăng cường c&ocirc;ng t&aacute;c quản l&yacute; của Nh&agrave; nước về ATVSLĐ trong n&ocirc;ng nghiệp; Tăng cường c&aacute;c biện ph&aacute;p tổ chức, quản l&yacute; thanh kiểm tra ATVSLĐ đối với n&ocirc;ng nghiệp v&agrave; n&ocirc;ng d&acirc;n; Tăng cường th&ocirc;ng tin, tuy&ecirc;n truyền, huấn luyện về ATVSLĐ; Thiết lập hệ thống quản l&yacute; ATVSLĐ trong n&ocirc;ng nghiệp tại cơ sở v&agrave; n&acirc;ng cao năng lực cho c&aacute;n bộ l&agrave;m c&ocirc;ng t&aacute;c ATVSLĐ tại cơ sở&hellip;<o:p></o:p></span></p>\r\n<span style="font-family: ''Verdana'', ''sans-serif''; font-size: 12pt"><font face="">Mỹ Hạnh</font></span>', 1, 1259978907, 2, 24, 0),
(59, 2, 'Tai nạn lao động nghiêm trọng, 7 công nhân bị thương', 'qdnd.vn ', 'no', '', '<p class="MsoNormal" style="text-align: justify; margin: 6pt 0in">Rạng s&aacute;ng 28-11, một vụ tai nạn lao động nghi&ecirc;m trọng đ&atilde; xảy ra tại Ph&acirc;n xưởng 3 thuộc C&ocirc;ng ty cổ phần Cơ kh&iacute; Gang th&eacute;p Th&aacute;i Nguy&ecirc;n (tỉnh Th&aacute;i Nguy&ecirc;n). Trong qu&aacute; tr&igrave;nh nhấc th&ugrave;ng chứa th&eacute;p n&oacute;ng đổ v&agrave;o khu&ocirc;n th&ugrave;ng chứa đ&atilde; bị đổ nghi&ecirc;ng, khiến th&eacute;p n&oacute;ng văng ra ngo&agrave;i l&agrave;m cho 7 c&ocirc;ng nh&acirc;n trong xưởng bị bỏng, trong đ&oacute; một người bị g&atilde;y ch&acirc;n.</p>\r\n<p class="MsoNormal" style="text-align: justify; margin: 6pt 0in">Ngay sau khi tai nạn xảy ra, c&aacute;c c&ocirc;ng nh&acirc;n đ&atilde; được đưa v&agrave;o Bệnh viện Gang th&eacute;p cấp cứu. Hiện trường được giữ nguy&ecirc;n phục vụ c&ocirc;ng t&aacute;c điều tra. Đại diện c&ocirc;ng ty cho biết: 4/7 c&ocirc;ng nh&acirc;n bị thương đ&atilde; phải chuyển l&ecirc;n Viện bỏng Quốc gia.</p>\r\n<p class="MsoNormal" style="text-align: justify; margin: 6pt 0in">Tại hiện trường, th&ugrave;ng chứa th&eacute;p n&oacute;ng (dạng lỏng) bị đứt d&acirc;y tời rơi xuống, tạo hố s&acirc;u khoảng 3m. Sức n&oacute;ng của th&eacute;p khiến khoảng 500m<sup><font size="1">2</font></sup> m&aacute;i t&ocirc;n kim loại của ph&acirc;n xưởng bị thổi bay, một số dụng cụ xung quanh bị nung chảy. Được biết, th&ugrave;ng th&eacute;p rơi chứa khoảng 12 tấn th&eacute;p n&oacute;ng.</p>\r\n<p class="MsoNormal" style="text-align: justify; margin: 6pt 0in">Theo Đo&agrave;n thanh tra li&ecirc;n ng&agrave;nh về tai nạn lao động, nguy&ecirc;n nh&acirc;n ban đầu dẫn đến tai nạn l&agrave; do cầu trục kh&ocirc;ng bảo đảm an to&agrave;n. Bu-loong gắn một b&ecirc;n d&acirc;y tời đ&atilde; bị văng ra khiến th&ugrave;ng th&eacute;p đổ nghi&ecirc;ng ra ngo&agrave;i khi chưa đưa đến khu&ocirc;n. Hiện c&aacute;c cơ quan chức năng đang tiếp tục điều tra l&agrave;m r&otilde; vụ việc.</p>\r\n<p class="MsoNormal" style="text-align: justify; margin: 6pt 0in"><b>HO&Agrave;NG THẢO NGUYN</b></p>', 1, 1259980306, 2, 23, 0),
(60, 1, 'Bảng sơ tính chi phí cho một vụ tai nạn nếu xảy ra', 'Vấn đề tai nạn là không ai muốn, nhưng nhiều người lại cho rằng đầu tư cho an tòan là tốn kém và không cần thiết. Hãy sơ bộ xem một bảng tính tóan chi phí mà công ty phải bỏ ra khi có tai nạn xảy ra thì chúng ta sẽ có cái nhìn khác về vần đề này. Tuy nhiên do đặc thù, mức độ chi phí của từng tai nạn có khác nhau nên chúng tôi không thể đưa ra cái giá chính xác mà công ty phải trả, nhưng một bản tính với khá nhiều chi tiết cần, sẽ giúp chúng ta phần nào tự thống kê được phần nào. ', 'no', '', '<h1><span style="font-size: medium">1. chi ph&iacute; ban đầu</span></h1>\r\n<ul>\r\n    <li>Chi ph&iacute; thuốc men cho người bị nạn tại hiện trường</li>\r\n    <li>Chi ph&iacute; vận chuyển người bị nạn đến bệnh viện hoặc về nh&agrave;</li>\r\n    <li>Chi ph&iacute; bảo vệ hiện trường</li>\r\n    <li>Chi ph&iacute; chữa ch&aacute;y (nếu c&oacute;)</li>\r\n    <li>Ch&iacute; ph&iacute; do ngừng c&ocirc;ng việc tức thời</li>\r\n    <li>C&aacute;c chi ph&iacute; kh&aacute;c</li>\r\n</ul>\r\n<h1><span style="font-size: medium">2. Chi ph&iacute; điều tra tai nạn</span></h1>\r\n<ul>\r\n    <li>Thời gian cho nh&oacute;m điều tra</li>\r\n    <li>Chi ph&iacute; cho c&aacute;c ph&ograve;ng th&iacute; nghiệm trợ gi&uacute;p (nếu c&oacute;)</li>\r\n    <li>Chi ph&iacute; cho điều tra vi&ecirc;n chuy&ecirc;n nghiệp (nếu c&oacute;)</li>\r\n    <li>Chi ph&iacute; việc cộng t&aacute;c với c&aacute;c cơ quan điều tra li&ecirc;n quan</li>\r\n    <li>Chi ph&iacute; h&agrave;nh ch&iacute;nh kh&aacute;c..</li>\r\n</ul>\r\n<h1><span style="font-size: medium">3. Chi ph&iacute; phục hồi sản xuất</span></h1>\r\n<ul>\r\n    <li>Chi ph&iacute; hỗ trợ việc phục hồi&nbsp;tiến tr&igrave;nh sản xuất</li>\r\n    <li>Chi ph&iacute; cho việc dọn dẹp hiện trường</li>\r\n    <li>Chi ph&iacute; cho c&aacute;c c&ocirc;ng t&aacute;c nghi&ecirc;n cứu, thay đổi chu tr&igrave;nh l&agrave;m việc (nếu c&oacute;)</li>\r\n    <li>Chi ph&iacute; cho việc sửa chữa, thay thế m&aacute;y m&oacute;c, thiết bị mới</li>\r\n    <li>Chi ph&iacute; vật tư, m&aacute;y m&oacute;c nhằm ngăn ngừa tai nạn tương tự</li>\r\n    <li>Chi ph&iacute; đ&agrave;o tạo lại đội ngũ c&ocirc;ng nh&acirc;n</li>\r\n    <li>.v.v...</li>\r\n</ul>\r\n<h1><span style="font-size: medium">4. Chi ph&iacute; họat động doanh nghiệp</span></h1>\r\n<ul>\r\n    <li>Sản phẩm mất đi hoặc thiếu hụt&nbsp;do tai nạn</li>\r\n    <li>Chi ph&iacute; lương, chữa trị&nbsp;cho người bị nạn</li>\r\n    <li>Chi ph&iacute; tuyển người mới thay thế</li>\r\n    <li>Phạt do chậm tiến độ hợp đồng</li>\r\n    <li>Chi ph&iacute; do đ&aacute;nh mất cơ hội, hợp đồng mới</li>\r\n    <li>Đ&aacute;nh mất uy t&iacute;n doanh nghiệp</li>\r\n    <li>Tăng tiền đảm bảo, bảo hiểm</li>\r\n    <li>.v.v...</li>\r\n</ul>\r\n<h1><span style="font-size: medium">5. C&aacute;c chi ph&iacute; ph&aacute;p l&yacute;</span></h1>\r\n<ul>\r\n    <li>Chi ph&iacute; phạt từ c&aacute;c cơ quan quản l&yacute;</li>\r\n    <li>Chi ph&iacute; h&agrave;nh ch&iacute;nh giải quyết c&aacute;c thủ tục ph&aacute;p l&yacute;</li>\r\n    <li>Chi ph&iacute; đền b&ugrave; thiệt hại do ảnh hưởng của tai nạn</li>\r\n</ul>\r\n<p>.v.v...</p>\r\n<h1>&nbsp;<span style="font-size: medium">Kết luận: </span></h1>\r\n<p>Tuy nhi&ecirc;n do từng đặc th&ugrave; v&agrave; t&iacute;nh chất thiệt hại của mỗi tai nạn n&ecirc;n c&aacute;c vấn đề ch&iacute; ph&iacute; v&agrave; ph&aacute;t sinh c&oacute; kh&aacute;c nhau. Những&nbsp;chi ph&iacute; tối thiểu m&agrave; ch&uacute;ng t&ocirc;i&nbsp;đưa ra tr&ecirc;n&nbsp;đ&acirc;y&nbsp;đ&atilde; phản&nbsp;&aacute;nh&nbsp;được phần n&agrave;o đến sự&nbsp;ảnh hưởng&nbsp;của họat động của doanh nghiệp. ch&uacute;ng t&ocirc;i cũng mong rằng c&aacute;c doanh nghiệp h&atilde;y c&acirc;n nhắc v&agrave; quan t&acirc;m triệt để đến c&aacute;c vấn đề HSE. Ph&ograve;ng ngừa lu&ocirc;n dễ hơn chữa trị .</p>\r\n<p style="text-align: right">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nguồn HSEVN.COM</p>', 1, 1269225250, 2, 14, 0),
(61, 1, 'Xây dựng quy trình an toàn cho công trường xây dựng', 'Xây dựng quy trình an toàn cho công trường xây dựng \r\n', 'no', '', 'I C&Ocirc;NG T&Aacute;C CHUẨN BỊ<br />\r\n1. Mặt bằng c&ocirc;ng trường :<br />\r\n2. Chuẩn bị lễ khởi c&ocirc;ng<br />\r\n3. Chuẩn bị c&ocirc;ng t&aacute;c an to&agrave;n tr&ecirc;n c&ocirc;ng trường : bảng hiệu, bảng nội quy, logo..<br />\r\n7 ng&agrave;y trước thời gian khởi c&ocirc;ng <br />\r\n<br />\r\nBCH CT, Ban AT<br />\r\nII NHẬN DIỆN C&Aacute;C MỐI NGUY HIỂM, PH&Acirc;N T&Iacute;CH RỦI RO<br />\r\n<br />\r\n1.Đ&agrave;o đất<br />\r\n2.L&agrave;m việc tr&ecirc;n cao<br />\r\n3.Th&aacute;o dỡ cốt pha<br />\r\n<br />\r\nTrước 2 ng&agrave;y theo tiến độ<br />\r\nBan AT, BCH CT, tư vấn <br />\r\nIII C&Ocirc;NG T&Aacute;C HUẤN LUYỆN<br />\r\n<br />\r\n&bull; Huấn luyện nội quy c&ocirc;ng trường, nội quy an to&agrave;n lao động <br />\r\n&bull; Huấn luyện c&aacute;ch nhận biết bảng biểu, m&agrave;u sắc t&iacute;n hiệu tr&ecirc;n c&ocirc;ng trường <br />\r\n&bull; Huấn luyện c&aacute;ch sử l&yacute; một số t&igrave;nh huống, sự cố xảy ra đột xuất tr&ecirc;n c&ocirc;ng trường <br />\r\n&bull; C&aacute;ch nhận biết c&aacute;c mối nguy hiểm ảnh hưởng đến sức khỏe của người lao động <br />\r\n&bull; Huấn luyện c&aacute;ch sử dụng phương tiện bảo hộ lao động <br />\r\n&bull; Huấn luyện c&aacute;c tư thế, động t&aacute;c l&agrave;m việc an to&agrave;n <br />\r\n&bull; Huấn luyện c&aacute;c biện ph&aacute;p kỹ thuật an to&agrave;n <br />\r\n-An to&agrave;n đ&agrave;o đất<br />\r\n-An to&agrave;n điện<br />\r\n-An to&agrave;n l&agrave;m việc tr&ecirc;n cao<br />\r\n-An to&agrave;n thiết bị điện cầm tay<br />\r\n-An to&agrave;n thiết bị n&acirc;ng<br />\r\n-An to&agrave;n gi&agrave;n gi&aacute;o<br />\r\n-An to&agrave;n l&agrave;m việc trong hầm s&acirc;u hố k&iacute;n<br />\r\n-An to&agrave;n trong c&ocirc;ng t&aacute;c x&acirc;y t&ocirc;<br />\r\n&bull; Huấn luyện c&ocirc;ng t&aacute;c PCCC <br />\r\n-T&iacute;nh năng t&aacute;c dụng của c&aacute;c loại b&igrave;nh PCCC<br />\r\n-C&aacute;ch sử dụng c&aacute;c loại b&igrave;nh<br />\r\n-Nhận biết t&iacute;n hiệu b&aacute;o động v&agrave; di tản khẩn cấp<br />\r\n&bull; Vệ sinh lao động <br />\r\n-Bệnh nghề nghiệp<br />\r\n-C&aacute;c yếu tố nguy hiểm ảnh hưởng đến sức khỏe ( gồm : bụi, tiếng ồn, &aacute;nh s&aacute;ng, vi kh&iacute; hậu&hellip; )<br />\r\n&bull; Sơ cấp cứu <br />\r\n-Khi bị say nắng<br />\r\n-Khi bị điện giật<br />\r\n-Khi bị chấn thương<br />\r\n&bull; An ninh trật tự <br />\r\n-Thủ tục kiểm tra c&ocirc;ng nh&acirc;n ra v&agrave;o cổng<br />\r\n-Thủ tục c&aacute;c bước kiểm tra h&agrave;ng h&oacute;a xuất nhập<br />\r\n-Kiểm tra xe cộ ra v&agrave;o<br />\r\n-Thủ tục tiếp đ&oacute;n kh&aacute;ch<br />\r\n-Thủ tục xử l&yacute; h&agrave;nh ch&iacute;nh ( c&aacute;ch lập bi&ecirc;n bản sự việc )<br />\r\nThứ 7 h&agrave;ng tuần<br />\r\n<br />\r\nBan AT, BCH CT, to&agrave;n bộ c&ocirc;ng nh&acirc;n v&agrave; c&aacute;c nh&agrave; thầu phụ tr&ecirc;n c&ocirc;ng trường<br />\r\n<br />\r\nIV C&Ocirc;NG T&Aacute;C KIỂM TRA<br />\r\n1.Kiểm tra an to&agrave;n điện :<br />\r\n-D&acirc;y điện<br />\r\n-Tủ điện<br />\r\n-Ph&iacute;ch cắm, ổ cắm c&ocirc;ng nghiệp<br />\r\n2.Kiểm tra an to&agrave;n thiết bị n&acirc;ng<br />\r\n-Cẩu th&aacute;p, cẩu tự h&agrave;nh<br />\r\n-Vận thăng tải<br />\r\n-Hoist <br />\r\n-Tời<br />\r\n3.Kiểm tra an to&agrave;n gi&agrave;n gi&aacute;o <br />\r\n-Gi&agrave;n gi&aacute;o chống <br />\r\n-Gi&agrave;n gi&aacute;o bao che<br />\r\n-Gi&agrave;n gi&aacute;o thi c&ocirc;ng <br />\r\n4.Kiểm tra an to&agrave;n thiết bị điện cầm tay<br />\r\n-D&acirc;y nguồn<br />\r\n-D&ograve;ng r&ograve; ( vỏ m&aacute;y )<br />\r\n-Thiết bị bảo vệ<br />\r\n5.Kiểm tra c&ocirc;ng t&aacute;c an to&agrave;n h&agrave;n cắt<br />\r\n- Giấy ph&eacute;p h&agrave;n cắt<br />\r\n-Kiểm tra m&aacute;y h&agrave;n : d&ograve;ng r&ograve;, d&acirc;y h&agrave;n, k&igrave;m h&agrave;n, d&acirc;y tiếp đất<br />\r\n-Mặt nạ h&agrave;n, khẩu trang, găng tay<br />\r\n-Kiểm tra t&igrave;nh trạng vỏ b&igrave;nh oxy, gas, d&acirc;y dẫn kh&iacute;, van an to&agrave;n, phiếu bảo h&agrave;nh<br />\r\n6.Kiểm tra m&aacute;y m&oacute;c <br />\r\n-D&acirc;y nguồn <br />\r\n-D&ograve;ng r&ograve; ( vỏ m&aacute;y )<br />\r\n-D&acirc;y tiếp đất, nối kh&ocirc;ng<br />\r\n-Thiết bị bảo vệ<br />\r\n7.Kiểm tra c&ocirc;ng t&aacute;c an to&agrave;n khi thi c&ocirc;ng tr&ecirc;n cao<br />\r\n-Sức khỏe<br />\r\n-Phương tiện bảo vệ c&aacute; nh&acirc;n<br />\r\n-T&uacute;i đứng đồ nghề<br />\r\n-Lối đi lại l&ecirc;n xuống<br />\r\n-Lưới hứng<br />\r\n-Lan can an to&agrave;n<br />\r\n-Che chắn c&aacute;c lỗ th&ocirc;ng tầng<br />\r\n8.Kiểm tra c&ocirc;ng t&aacute;c an to&agrave;n khi thi c&ocirc;ng đ&agrave;o đất, hố s&acirc;u hầm k&iacute;n<br />\r\n-Giấy ph&eacute;p đ&agrave;o đất<br />\r\n-Bản vẽ thiết kế<br />\r\n-Biện ph&aacute;p thi c&ocirc;ng<br />\r\n-Biện ph&aacute;p xử l&yacute; : kh&iacute; độc, c&ocirc;ng tr&igrave;nh ngầm, th&ocirc;ng gi&oacute;, nước<br />\r\n9.Kiểm tra c&ocirc;ng t&aacute;c an to&agrave;n trong x&acirc;y t&ocirc;<br />\r\n-Vị tr&iacute; tập kết vật liệu<br />\r\n-Vị tr&iacute; thi c&ocirc;ng<br />\r\n-Gi&agrave;n gi&aacute;o x&acirc;y t&ocirc;<br />\r\n-Chất tải tr&ecirc;n s&agrave;n thao t&aacute;c<br />\r\n-Vận chuyển vật liệu<br />\r\n10.Kiểm tra an to&agrave;n PCCN<br />\r\n-Hệ thống cung cấp nước chữa ch&aacute;y<br />\r\n-Hệ thống b&aacute;o động<br />\r\n-Lối tho&aacute;t hiểm<br />\r\n-Hệ thống b&igrave;nh chữa ch&aacute;y<br />\r\n11.Kiểm tra vệ sinh lao động<br />\r\n-Bụi<br />\r\n-Ồn<br />\r\n-&Aacute;nh s&aacute;ng<br />\r\n12.Kiểm tra vệ sinh m&ocirc;i trường, y tế tủ thuốc<br />\r\n13.Kiểm tra an ninh trật tự<br />\r\n14.Kiểm tra phương tiện bảo vệ c&aacute; nh&acirc;n<br />\r\n<br />\r\nH&agrave;ng ng&agrave;y<br />\r\nĐịnh kỳ thứ 5 h&agrave;ng tuần<br />\r\nĐịnh kỳ d&aacute;n tem kiểm tra an to&agrave;n điện, m&aacute;y m&oacute;c thiết bị sử dụng điện ng&agrave;y 25 h&agrave;ng th&aacute;ng<br />\r\n<br />\r\nATV<br />\r\nBan AT, ATV<br />\r\nVC&Ocirc;NG T&Aacute;C QUẢN L&Yacute; THIẾT BỊ AN TO&Agrave;N<br />\r\n<br />\r\n&bull; Trang bị phương tiện bảo vệ c&aacute; nh&acirc;n <br />\r\n&Aacute;o BHLĐ, mũ BHLĐ, gi&agrave;y BHLĐ, d&acirc;y an to&agrave;n, k&iacute;nh bảo vệ mắt, găng tay, khẩu trang<br />\r\n&bull; Thiết bị phục vụ c&ocirc;ng t&aacute;c an to&agrave;n tr&ecirc;n c&ocirc;ng trường <br />\r\nC&aacute;c loại bảng hiệu an to&agrave;n ( c&oacute; danh mục k&egrave;m theo ph&iacute;a sau )<br />\r\n3. Hồ sơ lưu quản l&yacute; xuất nhập thiết bị, phương tiện bảo vệ c&aacute; nh&acirc;n<br />\r\n<br />\r\n<br />\r\nKiểm tra định kỳ ng&agrave;y 22 h&agrave;ng th&aacute;ng<br />\r\n<div style="text-align: right">Nguồn HSEVN.COM</div>', 1, 1269227521, 2, 31, 0);
