INSERT INTO `tgp_page` (`id`, `alias`, `ten`, `noi_dung`, `time`, `user`, `luot_xem`) VALUES
(1, 'gioi_thieu', 'Giới thiệu', '<span style="color: rgb(11,67,162)"><strong>C&ocirc;ng Ty TNHH MTV T&Acirc;N TRƯỜNG TO&Agrave;N</strong></span> l&agrave;&nbsp;c&ocirc;ng ty h&agrave;ng&nbsp;đầu về cung cấp c&aacute;c sản phẩm&nbsp;v&agrave; dịch vụ trong lĩnh&nbsp;vực:&nbsp;Thiết bị an&nbsp;to&agrave;n, bảo hộ lao&nbsp;động, hệ thống an to&agrave;n&nbsp;v&agrave; thiết bị&nbsp;ph&ograve;ng ch&aacute;y chữa ch&aacute;y......&nbsp;<br />\r\n<br />\r\n<span style="color: rgb(11,67,162)"><strong>SỰ ĐẢM&nbsp;BẢO&nbsp;CHẤT&nbsp;LƯỢNG</strong></span><br />\r\n<br />\r\nSản phẩm của c&ocirc;ng ty được cung cấp dưới nh&atilde;n hiệu nổi <leo_highlight id="leoHighlights_Underline_0" leohighlights_url="http%3A//thebrowserhighlighter.com/leonardo/highlights/keywords?keywords%3Dti" leohighlights_keywords="ti" onclick="leoHighlightsHandleClick(''leoHighlights_Underline_0'')" onmouseover="leoHighlightsHandleMouseOver(''leoHighlights_Underline_0'')" onmouseout="leoHighlightsHandleMouseOut(''leoHighlights_Underline_0'')"></leo_highlight>tiếng tr&ecirc;n thế giới như: <br />\r\n<br />\r\nBacou-dalloz (Ph&aacute;p, Mỹ) ,Blue Eagle (Đ&agrave;i Loan) ,Kings ( Malaysia), Frontier ( Malaysia).<br />\r\n<br />\r\nB&ecirc;n cạnh đ&oacute; c&ograve;n c&oacute; c&aacute;c sản phẩm Việt Nam chất lượng cao như : Th&ugrave;y Dương, <leo_highlight id="leoHighlights_Underline_1" leohighlights_url="http%3A//thebrowserhighlighter.com/leonardo/highlights/keywords?keywords%3Dti" leohighlights_keywords="ti" onclick="leoHighlightsHandleClick(''leoHighlights_Underline_1'')" onmouseover="leoHighlightsHandleMouseOver(''leoHighlights_Underline_1'')" onmouseout="leoHighlightsHandleMouseOut(''leoHighlights_Underline_1'')"></leo_highlight>Tiến Ph&aacute;t, Li&ecirc;n Cơ.....<br />\r\n<br />\r\nTất cả c&aacute;c sản phẩm ch&uacute;ng t&ocirc;i cung cấp đều c&oacute; xuất xứ, chứng nhận sản phẩm v&agrave; <leo_highlight id="leoHighlights_Underline_2" leohighlights_url="http%3A//thebrowserhighlighter.com/leonardo/highlights/keywords?keywords%3Dti" leohighlights_keywords="ti" onclick="leoHighlightsHandleClick(''leoHighlights_Underline_2'')" onmouseover="leoHighlightsHandleMouseOver(''leoHighlights_Underline_2'')" onmouseout="leoHighlightsHandleMouseOut(''leoHighlights_Underline_2'')"></leo_highlight>ti&ecirc;u chuẩn an to&agrave;n.<br />\r\n<span style="color: rgb(11,67,162)"><br />\r\n<strong>MỤC&nbsp;TI&Ecirc;U</strong></span><br />\r\n<br />\r\nNhằm g&oacute;p phần giảm thiểu rủi ro tai nạn trong nghề nghiệp xuống mức thấp nhất,&nbsp;đặc biệt l&agrave; tai nạn trong x&acirc;y dựng v&agrave; c&ocirc;ng nghiệp.<br />\r\n<br />\r\nNhiệm vụ ch&iacute;nh của ch&uacute;ng t&ocirc;i l&agrave; cung cấp sự an to&agrave;n cho tất cả mọi người bằng những giải ph&aacute;p ph&ugrave; hợp, chất lượng sản phẩm, sự chuy&ecirc;n t&acirc;m v&agrave; dịch vụ tốt nhất với gi&aacute; th&agrave;nh hợp l&yacute;&nbsp;theo ti&ecirc;u ch&iacute; :<br />\r\n<div style="text-align: center"><br />\r\n<span style="color: rgb(255,0,0)"><em><strong>&quot; Sản phẩm chất lượng, gi&aacute; cả cạnh tranh, giao h&agrave;ng nhanh ch&oacute;ng&nbsp;&quot;</strong></em></span></div>\r\n&nbsp;<br />\r\n<span style="color: rgb(11,67,162)"><strong>SẢN PHẨM&nbsp;CUNG&nbsp;CẤP</strong></span>\r\n<ul>\r\n    <li>N&oacute;n BHLĐ ( Safety Helmet)</li>\r\n    <li>K&iacute;nh BHLĐ ( Safety Glass)</li>\r\n    <li>K&iacute;nh H&agrave;n, Mặt Nạ H&agrave;n ( Full Facepiece Respirstor )</li>\r\n    <li>Khẩu Trang ( Disposable Respirstor )</li>\r\n    <li>N&uacute;t Tai, Chụp Tai Chống Ồn ( Hearing Protection )</li>\r\n    <li>Quần &Aacute;o BHLĐ ( Safety Cloths )</li>\r\n    <li>Găng Tay BHLĐ ( Safety Gloves)</li>\r\n    <li>D&acirc;y Đai An To&agrave;n ( Harnesses Landyard&nbsp;)&nbsp;</li>\r\n    <li>Gi&agrave;y BHLĐ ( Safety Shoes)&nbsp;</li>\r\n    <li>M&aacute;y D&ograve; Gas ( Gas Detector )</li>\r\n    <li>Thiết Bị PCCC ( Products for fires )</li>\r\n    <li>Lưới: Lưới An To&agrave;n ( Safety Net ), Lưới x&acirc;y dựng ( Green Net )</li>\r\n</ul>\r\n<div style="text-align: left"><span style="font-size: larger"><span style="color: #000080"><strong><font color="#0b43a2">V&Agrave; C&Aacute;C DỊCH VỤ KH&Aacute;C</font><br />\r\n</strong></span></span><span style="color: #0000ff"><strong><br />\r\n</strong></span></div>\r\n<div style="text-align: center"><span style="color: rgb(255,0,0)"><strong><br />\r\n</strong><span style="font-size: medium"><strong>SAFETY IS&nbsp;A&nbsp;WAY&nbsp;OF&nbsp;LIFE&nbsp;!</strong></span></span></div>', 1260433231, 1, 730),
(2, 'lien_he', 'Liên Hệ', '<table border="0" cellspacing="5" cellpadding="0" width="600" align="center">\r\n    <tbody>\r\n        <tr>\r\n            <td colspan="2" style="text-align: center"><span style="font-size: medium"><span style="font-family: Times New Roman"><span style="color: rgb(11,67,162)"><strong><br />\r\n            <br />\r\n            C&Ocirc;NG TY TNHH MTV TM-DV&nbsp; T&Acirc;N TRƯỜNG TO&Agrave;N<br />\r\n            <br />\r\n            </strong></span></span></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td colspan="2"><strong><span style="font-size: medium"><span style="color: #ff0000"><span style="font-family: Times New Roman"><em>Văn ph&ograve;ng li&ecirc;n lạc</em></span></span></span></strong><span style="font-size: medium"><span style="color: #ff0000"><span style="font-family: Times New Roman"><em><br />\r\n            <br />\r\n            </em></span></span></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">Địa chỉ</span></span></td>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">: 62 L&ecirc; Độ, Quận Thanh Kh&ecirc;, TP Đ&agrave; Nẵng.</span></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">Điện Thoại</span></span></td>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">: (0511)6.256.257 - <font size="4">(0511)6.256.267</font></span></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">Fax</span></span></td>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">: (0511)6.256.267</span></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">Hotline</span></span></td>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">: 0979.708.789 - 0935.477.775</span></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td colspan="2"><span style="color: #ff0000"><span style="font-size: medium"><span style="font-family: Times New Roman"><em><br />\r\n            <strong>Chi nh&aacute;nh</strong><br />\r\n            <br />\r\n            </em></span></span></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">Địa chỉ</span></span></td>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">: Ng&atilde; 3 Dốc S&otilde;i, KKT Dung Quất, B&igrave;nh Sơn, Tỉnh Qu&atilde;ng Ng&atilde;i.</span></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">Điện Thoại</span></span></td>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">: (055)3.630.084</span></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">Fax</span></span></td>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">: (055)3.630.084</span></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">Hotline</span></span></td>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">: 0979.708.789</span></span></td>\r\n        </tr>\r\n        <tr>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">Email</span></span></td>\r\n            <td><span style="font-size: medium"><span style="font-family: Times New Roman">: info@tantruongtoan.com</span></span></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<br />\r\n<br />', 1264124891, 1, 790),
(3, 'truc_tuyen', 'Hỗ trợ trực tuyến', '<span style="color: rgb(11,67,162)"><strong>Cty TNHH MTV TM-DV</strong> </span>\r\n<div style="text-align: right"><span style="color: rgb(11,67,162)"><strong>T&Acirc;N&nbsp;TRƯỜNG&nbsp;TO&Agrave;N</strong></span></div>\r\n<hr />\r\n<strong><em>62 L&ecirc; Độ, TP Đ&agrave; Nẵng</em></strong><br />\r\n<table border="0" cellspacing="0" cellpadding="0" width="175">\r\n    <tbody>\r\n        <tr>\r\n            <td width="50"><strong>Tel</strong></td>\r\n            <td width="125">: (0511)6.256.257</td>\r\n        </tr>\r\n        <tr>\r\n            <td width="50"><strong>Fax</strong></td>\r\n            <td width="125">: (0511)6.256.267</td>\r\n        </tr>\r\n        <tr>\r\n            <td width="50"><strong>Hotline</strong></td>\r\n            <td width="125">: 0979.708.789</td>\r\n        </tr>\r\n        <tr>\r\n            <td width="50">&nbsp;</td>\r\n            <td width="125">&nbsp;&nbsp;0935.477.775</td>\r\n        </tr>\r\n    </tbody>\r\n</table>', 1259892722, 1, 25262),
(4, 'gioi_thieu_home', 'Giới thiệu - Box', '<span style="color: rgb(11,67,162)"><strong>C&ocirc;ng Ty TNHH T&Acirc;N TRƯỜNG TO&Agrave;N</strong></span> l&agrave;&nbsp;c&ocirc;ng ty h&agrave;ng&nbsp;đầu về cung cấp c&aacute;c sản phẩm&nbsp;v&agrave; dịch vụ trong lĩnh&nbsp;vực:&nbsp;Thiết bị an&nbsp;to&agrave;n, bảo hộ lao&nbsp;động, hệ thống an to&agrave;n&nbsp;v&agrave; thiết bị&nbsp;ph&ograve;ng ch&aacute;y chữa ch&aacute;y......&nbsp;<br />\r\n<br />\r\n<span style="color: rgb(11,67,162)"><strong>SỰ ĐẢM&nbsp;BẢO&nbsp;CHẤT&nbsp;LƯỢNG</strong></span><br />\r\n<br />\r\nSản phẩm của c&ocirc;ng ty được cung cấp dưới nh&atilde;n hiệu nổi <leo_highlight id="leoHighlights_Underline_0" onclick="leoHighlightsHandleClick(''leoHighlights_Underline_0'')" onmouseover="leoHighlightsHandleMouseOver(''leoHighlights_Underline_0'')" onmouseout="leoHighlightsHandleMouseOut(''leoHighlights_Underline_0'')" leohighlights_url="http%3A//thebrowserhighlighter.com/leonardo/highlights/keywords?keywords%3Dti" leohighlights_keywords="ti"></leo_highlight>tiếng tr&ecirc;n thế giới như : <br />\r\n<br />\r\nBacou-dalloz (Ph&aacute;p, Mỹ) ,Blue Eagle (Đ&agrave;i Loan) ,Kings ( Malaysia), Frontier ( Malaysia).<br />\r\n<br />\r\nB&ecirc;n cạnh đ&oacute; c&ograve;n c&oacute; c&aacute;c sản phẩm Việt Nam chất lượng cao như : Th&ugrave;y Dương, <leo_highlight id="leoHighlights_Underline_1" onclick="leoHighlightsHandleClick(''leoHighlights_Underline_1'')" onmouseover="leoHighlightsHandleMouseOver(''leoHighlights_Underline_1'')" onmouseout="leoHighlightsHandleMouseOut(''leoHighlights_Underline_1'')" leohighlights_url="http%3A//thebrowserhighlighter.com/leonardo/highlights/keywords?keywords%3Dti" leohighlights_keywords="ti"></leo_highlight>Tiến Ph&aacute;t, Li&ecirc;n Cơ.....<br />\r\n<br />\r\nTất cả c&aacute;c sản phẩm ch&uacute;ng t&ocirc;i cung cấp đều c&oacute; xuất xứ, chứng nhận sản phẩm v&agrave; <leo_highlight id="leoHighlights_Underline_2" onclick="leoHighlightsHandleClick(''leoHighlights_Underline_2'')" onmouseover="leoHighlightsHandleMouseOver(''leoHighlights_Underline_2'')" onmouseout="leoHighlightsHandleMouseOut(''leoHighlights_Underline_2'')" leohighlights_url="http%3A//thebrowserhighlighter.com/leonardo/highlights/keywords?keywords%3Dti" leohighlights_keywords="ti"></leo_highlight>ti&ecirc;u chuẩn an to&agrave;n.<br />\r\n<span style="color: rgb(11,67,162)"><br />\r\n<strong>MỤC&nbsp;TI&Ecirc;U</strong></span><br />\r\n<br />\r\nNhằm g&oacute;p phần giảm thiểu rủi ro tai nạn trong nghề nghiệp xuống mức thấp nhất,&nbsp;đặc biệt l&agrave; tai nạn trong x&acirc;y dựng v&agrave; c&ocirc;ng nghiệp.<br />\r\n<br />\r\nNhiệm vụ ch&iacute;nh của ch&uacute;ng t&ocirc;i l&agrave; cung cấp sự an to&agrave;n cho tất cả mọi người bằng những giải ph&aacute;p ph&ugrave; hợp, chất lượng sản phẩm, sự chuy&ecirc;n t&acirc;m v&agrave; dịch vụ tốt nhất với gi&aacute; th&agrave;nh hợp l&yacute;&nbsp;theo ti&ecirc;u ch&iacute; :<br />\r\n<div style="text-align: center"><br />\r\n<span style="font-size: small"><span style="color: rgb(255,0,0)"><em><strong>&quot; Sản phẩm chất lượng, gi&aacute; cả cạnh tranh, giao h&agrave;ng nhanh ch&oacute;ng&nbsp;&quot;<br />\r\n<br />\r\n</strong></em></span></span></div>', 1264125138, 1, 3646),
(5, 'dich_vu', 'Dịch vụ', '<p><span style="font-size: small"><span style="color: rgb(11,67,162)"><em><u><strong>C&Aacute;C DỊCH VỤ CH&Uacute;NG T&Ocirc;I CUNG CẤP&nbsp;BAO GỒM :</strong></u></em></span></span></p>\r\n<ol>\r\n    <li><span style="font-size: small"><span style="color: rgb(255,0,0)">Cung cấp c&aacute;c trang thiết bị an to&agrave;n v&agrave; bảo hộ lao động.</span> </span><br />\r\n    &nbsp;</li>\r\n    <li><span style="font-size: small"><span style="color: rgb(255,0,0)">Thi c&ocirc;ng hệ thống an to&agrave;n cho c&aacute;c c&ocirc;ng tr&igrave;nh , t&ograve;a nh&agrave; cao tầng.</span> </span><br />\r\n    &nbsp;</li>\r\n    <li><span style="font-size: small"><span style="color: rgb(255,0,0)">Thi c&ocirc;ng hệ thống mạng, WiFi.</span></span> <br />\r\n    &nbsp;</li>\r\n    <li><span style="font-size: small"><span style="color: rgb(255,0,0)">Cung cấp baner an to&agrave;n, r&agrave;o chắn an t&ograve;an cho c&aacute;c c&ocirc;ng tr&igrave;nh.</span> </span><br />\r\n    &nbsp;</li>\r\n    <li><span style="font-size: small"><span style="color: rgb(255,0,0)">Thi c&ocirc;ng hệ thống ph&ograve;ng ch&aacute;y chữa ch&aacute;y, v&hellip;v</span> </span><br />\r\n    &nbsp;</li>\r\n    <li><span style="font-size: small"><span style="color: rgb(255,0,0)">Cho thu&ecirc; gi&agrave;n gi&aacute;o x&acirc;y dựng ........</span> </span></li>\r\n</ol>\r\n<span style="font-size: small"><strong><br />\r\nCh&uacute;ng t&ocirc;i h&acirc;n hạnh hợp t&aacute;c to&agrave;n diện với Qu&yacute;&nbsp;kh&aacute;ch h&agrave;ng theo </strong></span><leo_highlight id="leoHighlights_Underline_0" leohighlights_keywords="ti" leohighlights_url="http%3A//thebrowserhighlighter.com/leonardo/highlights/keywords?keywords%3Dti" onmouseout="leoHighlightsHandleMouseOut(''leoHighlights_Underline_0'')" onmouseover="leoHighlightsHandleMouseOver(''leoHighlights_Underline_0'')" onclick="leoHighlightsHandleClick(''leoHighlights_Underline_0'')"></leo_highlight><span style="font-size: small"><strong>ti</strong></span><span style="font-size: small"><strong>&ecirc;u ch&iacute; :&nbsp;&nbsp;</strong></span><strong>&nbsp;&nbsp;</strong>&nbsp;&nbsp;&nbsp;&nbsp;<o:p></o:p><br />\r\n&nbsp;&nbsp;&nbsp;<br />\r\n<div style="text-align: center"><span style="font-size: small"><span style="color: rgb(255,0,0)"><em><strong>&ldquo; Sản phẩm chất lượng, gi&aacute; cả cạnh tranh, giao h&agrave;ng nhanh ch&oacute;ng &ldquo; </strong></em></span></span></div>\r\n<p style="text-align: center"><span style="font-size: small"><strong><br />\r\nTr&acirc;n Trọng K&iacute;nh Ch&agrave;o Qu&yacute; Kh&aacute;ch!<br />\r\n<br />\r\n</strong></span></p>', 1259894155, 1, 361),
(6, 'tuyen_dung', 'Tuyển dụng', '<span style="font-size: medium"><span style="color: rgb(11,67,162)">\r\n<p><span style="color: #ff0000"><span style="font-family: Times New Roman"><span style="font-size: large"><em><strong>Th&ocirc;ng Tin Tuyển Dụng Th&aacute;ng 03/2010<br />\r\n</strong></em></span></span></span><span style="font-family: Times New Roman"><span style="color: rgb(11,67,162)"><em><strong><b style="mso-bidi-font-weight: normal"><br />\r\nDo nhu cầu <strong><em>mở rộng</em></strong> <strong><em>v&agrave;</em></strong> ph&aacute;t triển mạng lư&oacute;i kinh doanh.C</b><b style="mso-bidi-font-weight: normal">h&uacute;ng t&ocirc;i cần tuyển c&aacute;c vị tr&iacute; như sau:<br />\r\n</b></strong></em></span></span></p>\r\n<p><span style="color: #ff0000"><span style="font-family: Times New Roman"><b style="mso-bidi-font-weight: normal"><span style="mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA"><strong>1. </strong></span></b></span></span><span style="font-family: Times New Roman"><b style="mso-bidi-font-weight: normal"><span style="color: #666666"><span style="mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA"><span style="color: #ff0000"><strong>Trưởng</strong> ph&ograve;ng&nbsp;<b style="mso-bidi-font-weight: normal">kinh doanh : L&agrave;m việc tại Đ&agrave; Nẵng</b></span><b style="mso-bidi-font-weight: normal"><br />\r\n<br />\r\n</b></span></span></b><span style="color: #000000"><b style="mso-bidi-font-weight: normal"><span style="mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA"><b style="mso-bidi-font-weight: normal"><u>Y&ecirc;u Cầu:<br />\r\n<br />\r\n</u></b></span></b><b style="mso-bidi-font-weight: normal">- Tốt nghiệp <strong>đại học, </strong>cao đẳng chuy&ecirc;n ng&agrave;nh kinh tế, QTKD...hoặc c&aacute;c chuy&ecirc;n ng&agrave;nh c&oacute; li&ecirc;n&nbsp;quan.<br />\r\n<br />\r\n</b><b style="mso-bidi-font-weight: normal">- C&oacute; tối thiểu 02 năm kinh nghiệm, ưu ti&ecirc;n những người c&oacute; kinh nghiệm trong lĩnh vực kinh doanh c&aacute;c thiết bị phục vụ c&ocirc;ng tr&igrave;nh, dự &aacute;n x&acirc;y dựng.<br />\r\n<br />\r\n</b><b style="mso-bidi-font-weight: normal">- Th&agrave;nh thạo vi t&iacute;nh văn ph&ograve;ng.<br />\r\n<br />\r\n- </b><b style="mso-bidi-font-weight: normal">Tiếng Anh tr&igrave;nh độ B.</b><b style="mso-bidi-font-weight: normal"><br />\r\n<br />\r\n- Năng động, nhiệt t&igrave;nh v&agrave; c&oacute; tr&aacute;ch nhiệm với c&ocirc;ng việc.<br />\r\n<br />\r\n- Khả năng giao tiếp tốt.<br />\r\n</b></span><b style="mso-bidi-font-weight: normal"><span style="color: #666666"><b style="mso-bidi-font-weight: normal"><br />\r\n</b><span style="color: #ff0000"><b style="mso-bidi-font-weight: normal">2. Nh&acirc;n vi&ecirc;n kinh doanh :<br />\r\n</b></span></span></b></span><span style="font-family: Verdana; color: #666666"><st1:place w:st="on"><st1:country-region w:st="on"><span style="font-family: Times New Roman"><b style="mso-bidi-font-weight: normal"><br />\r\n</b></span></st1:country-region></st1:place><span style="color: #000000"><st1:place w:st="on"><st1:country-region w:st="on"><span style="font-family: Times New Roman"><b style="mso-bidi-font-weight: normal">- Nam</b></span></st1:country-region></st1:place><span style="font-family: Times New Roman"><b style="mso-bidi-font-weight: normal"> tốt nghiệp trung cấp trở l&ecirc;n c&aacute;c ng&agrave;nh kinh tế, marketing.<br />\r\n</b></span><span style="font-family: Times New Roman"><b style="mso-bidi-font-weight: normal"><br />\r\n- Anh Văn<span style="mso-spacerun: yes">&nbsp;</span>giao tiếp.<br />\r\n<br />\r\n- Khả năng l&agrave;m việc độc lập.<br />\r\n<br />\r\n-&nbsp;<strong>Khả năng</strong> giao tiết tốt.<br />\r\n<br />\r\n- Sẵn s&agrave;ng đi c&ocirc;ng t&aacute;c xa.<br />\r\n<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hồ sơ xin gởi về địa chỉ tr&ecirc;n.</b></span></span><span style="font-family: Times New Roman"><b style="mso-bidi-font-weight: normal"><br />\r\n</b></span></span></p>\r\n<p>&nbsp;</p>\r\n<p><span style="font-family: Times New Roman">&nbsp;</span></p>\r\n</span></span><span style="font-family: Times New Roman"><span style="font-size: medium"><br />\r\n</span><span style="color: rgb(11,67,162)"><em><strong><b style="mso-bidi-font-weight: normal"><span style="color: #666666; font-size: 11pt"><b style="mso-bidi-font-weight: normal"><br />\r\n</b></span></b></strong></em></span></span><span style="color: rgb(11,67,162)"><em><strong><b style="mso-bidi-font-weight: normal"><span style="font-family: Verdana; color: #666666; font-size: 11pt">\r\n<p><span style="font-family: Times New Roman"><br />\r\n&nbsp;</span></p>\r\n</span></b></strong></em></span>', 1267690293, 1, 440);
INSERT INTO `tgp_product` (`id`, `cat`, `ten`, `chu_thich`, `hinh`, `noi_dung`, `hien_thi`, `gia`, `time`, `luot_xem`, `noi_bat`, `user`) VALUES
(1, 12, 'Nón BH có nút vặn Đài Loan', '    * Nón Đài Loan được nhập khẩu.\r\n    * Nhẹ và dễ dàng điều chỉnh.\r\n    * Có 4 điểm giảm sốc.\r\n    * Dây giảm sốc được làm từ nhựa và có nút vặn điều chỉnh cho vừa với đầu.\r\n    * Màu: vàng, trắng, đỏ và xanh .\r\nCode: N01\r\n', '1.jpg', '<br />', 1, 0, 1247907528, 0, 1, 1),
(2, 12, 'Nón BH Việt Nam', '    * Có 2 màu: trắng và cam.\r\n    * Nón không có nút vặn, dành cho các công nhân công trường.\r\nCode : N07', '2.jpg', '<br />', 1, 0, 1247912933, 0, 0, 1),
(3, 12, 'Nón BH không có nút vặn Đài Loan', '* Nón Đài Loan được nhập khẩu.\r\n* Nhẹ và dễ dàng điều chỉnh.\r\n* Có 4 điểm giảm sốc.\r\n* Dây giảm sốc được làm từ nhựa.\r\n* Màu: vàng, trắng, đỏ và xanh .\r\nCode: N02\r\n', '3.jpg', '<br />', 1, 0, 1247913040, 0, 0, 1),
(4, 12, 'Nón BH có nút vặn Willson', '    * Nón Willson ( Mỹ ) Hàng nhập Khẩu.\r\n    * Có 3 màu: trắng, đỏ ,xanh.\r\n    * Tiêu chuẩn : ANSI Z89.1 – 1986 Class A.B.\r\n    * ANSI Z89.1 – 1997. Type1,Classe\r\nCode: N05', '4.gif', '<br />', 1, 0, 1247913301, 0, 0, 1),
(5, 12, 'Nón BH ko nút vặn Thùy Dương', '    * Nón BH không nút vặn Thùy Dưong( Việt Nam ).\r\n    * Được làm bằng nhựa HDPE.\r\n    * Có nhiều màu: trắng, vàng, xanh.\r\nCode : N03', '5.jpg', '<br />', 1, 0, 1247913353, 0, 0, 1),
(6, 12, 'Nón BH có nút vặn Thùy Dương', '    * Nón BH có nút vặn 6 dây Thùy Dương ( Việt Nam ).\r\n    * Được làm bằng nhựa HDPE.\r\n    * Có nhiều màu: trắng, vàng, xanh, đỏ.\r\nCode : N04', '6.jpg', '<br />', 1, 0, 1247913403, 0, 0, 1),
(7, 13, 'Nút tai chống ồn Billsom', '    * Nút tai chống ồn Billsom loại sử dụng nhiều lần.\r\n    * Có dây đeo.\r\n    * Hàng nhập khẩu.\r\n    * Tỷ lệ giảm tiếng ồn 27db.\r\n    * Tiêu chuẩn :ANSI S.3.19-1974.\r\nCode : NT01', '7.jpg', '<br />', 1, 0, 1247972147, 0, 0, 1),
(8, 13, 'Nút tai chống ồn Billsom', '    * Bông chống ồn Billsom loại sử dụng 1 lần.\r\n    * Hàng nhập khẩu.\r\n    * Tiêu chuẩn : CE EN 352-2* AS 1270-1988: SLC80: 33 Db; CE EN 352-2: 1993.\r\nCode : NT02', '8.jpg', '<br />', 1, 0, 1247972219, 0, 0, 1),
(9, 13, ' Chụp Tai chống ồn CH1 gắn vào nón bảo hộ(BILSOM)', '    * Chụp Tai chống ồn CH1( Bilsom)  có adapter gắn vào nón bảo hộ.\r\n    * Hàng nhập khẩu.\r\n    * Tỉ lệ giảm tiếng ồn 18%.\r\n    * Tiêu chuẩn : Canada (CSA) NRR 18db , Úc AS1270 Lic 1133, AU/NZ SLC80: 22dB.\r\nCode : CT01\r\n', '9.jpg', '<br />', 1, 0, 1247972272, 0, 0, 1),
(10, 13, 'Chụp Tai chống ồn Mach 1 (BILSOM)', '    * Chụp tai chống ồn Bilsom Mach 1 ( Thụy Điển ).\r\n    * Hàng Được nhập khẩu.\r\n    * Tỷ lệ giảm tiếng ồn 18%.\r\n    * Tiêu chuẩn: AS 1270 LIC 1133 (Standards Australia).\r\nCode : CT02', '10.jpg', '<br />', 1, 0, 1247972312, 0, 1, 1),
(11, 14, 'Kính chống Hóa Chất', '    * Kính chống hóa chất, Chất phun sơn.\r\n    * Kính chống hóa chất với góc nhìn siêu rộng.\r\n    * Tròng kính với chất lượng quang học cao.\r\n    * Có thể đeo bên ngòai các loại mắt kính khác.\r\n    * Vành kính bằng nhựa mềm mại và thoải mái khi sử dụng.\r\n    * Hàng nhập khẩu: Pháp.\r\nCode : K01\r\n', '11.jpg', '<br />', 1, 0, 1247972546, 0, 1, 1),
(12, 14, 'Kính BHLĐ Kings KY316', '    * Kính BHLĐ Kings KY316.\r\n    * Chống tia tử ngoại 99%UVA, 99% UVB.\r\n    * Kính làm bằng vật liệu Polycacbonat.\r\n    * Mặt kính có phủ một lớp tráng bạc.\r\n    * Tiêu chuẩn ANSI Z87+.\r\n    * Thời trang.\r\nCode : K02\r\n', '12.gif', '<br />', 1, 0, 1247972842, 0, 0, 1),
(13, 14, 'Kính BHLĐ Kings KY213', '    * Kính BHLĐ King''s KY213.\r\n    * Chống tia tử ngoại 99%UVA, 99% UVB.\r\n    * Kính làm bằng vật liệu Polycacbonat, trên mặt kính có phủ một lớp tráng bạc.\r\n    * Tiêu chuẩn ANSI Z87+.\r\n    * Hàng nhập khẩu: Malaysia.\r\nCode : K03\r\n', '13.jpg', '<br />', 1, 0, 1247972928, 0, 0, 1),
(14, 14, 'Kính BHLĐ Kings KY212', '    * Kính BHLĐ Kings KY212.\r\n    * Chống tia tử ngoại 99%UVA, 99% UVB.\r\n    * Kính làm bằng vật liệu Polycacbonat.\r\n    * Tiêu chuẩn ANSI Z87.\r\n    * Hàng nhập khẩu : Malaysia.\r\nCode : K04', '14.jpg', '<br />', 1, 0, 1247972997, 0, 0, 1),
(15, 14, 'Kính BHLĐ PULSAFE', '    * Kính BHLĐ FULSAFE (Pháp ).\r\n    * Hàng nhập khẩu.\r\n    * Kính trong không độ, gọng đen chống sốc, chống tích tụ hơi sương.\r\n    * Chống bụi,chống 99% tia UVA, 99%UVB.\r\n    * Tiêu chuẩn : Ansi Z87+.\r\nCode : K05\r\n', '15.jpg', '<br />', 1, 0, 1247973096, 0, 0, 1),
(16, 14, 'Kính BHLĐ PULSAFE', '    * Kính BHLĐ FULSAFE (Pháp ).\r\n    * Hàng nhập khẩu.\r\n    * Kính trong không độ, gọng đen chống sốc, chống tích tụ hơi sương.\r\n    * Chống bụi ,chống 99% tia UVA, 99%UVB.\r\n    * Tiêu chuẩn : Ansi Z87+.\r\nCode : K06\r\n', '16.jpg', '<br />', 1, 0, 1247973146, 0, 0, 1),
(17, 14, 'Kính tráng bạc BHLĐ PULSAFE', '    * Kính  tráng bạc BHLĐ PULSAFE.\r\n    * Gọng Silicon.\r\n    * Hàng nhập khẩu (Pháp ).\r\n    * Chống bụi,chống 99% tia UVA, 99%UVB.\r\n    * Tiêu chuẩn : Ansi Z87+.\r\nCode : K07', '17.jpg', '<br />', 1, 0, 1247973253, 0, 0, 1),
(18, 14, 'Kính Chống bụi', '    * Kính chống bụi LG 10.\r\n    * Làm bằng vật liệu dẽo, chống bụi.\r\n    * Tiêu chuẩn: CE 0194 Inspec; EN 166:2001.\r\nCode: K08', '18.jpg', '<br />', 1, 0, 1247973533, 0, 0, 1),
(19, 15, 'Kính hàn đeo mắt', '    * Kính hàn đeo mắt tròng đen và trắng.\r\n    * Sử dụng trong ngành hàn, cắt.\r\n    * Xuất xứ: Đài Loan.\r\nCode : KH01', '19.jpg', '<br />', 1, 0, 1247973941, 0, 0, 1),
(20, 15, 'Khung A3-A4', '    * Khung nhôm A3, A4 dùng để gắn vào nón sử dụng với FC 45.48.\r\nCode : KN01', '20.jpg', '<br />', 1, 0, 1247974022, 0, 0, 1),
